INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E563C8E2AB9B854184B4AAB14B584167', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 375 F. Grease two 8 or 9-inch square baking pans and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A09CB3FE5CD2D44B9CE11940A55BA21', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine 2 1/4 cups of the flour, yeast, dry milk, and salt. Mix well and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C68AA74DA41E240A6002C3C7DE085B1', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 2, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat the pineapple juice, 1/2 cup water, shortening, and honey until warmed to about 120 to 130 F. (Shortening does not need to melt.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C12C067E9F29F459A35EC0A1D250A60', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 3, NULL, NULL, 2, NULL, 0, 'Add the warmed liquid mixture to flour mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'897A1F663509A448ABFAEE1C0EED838A', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 4, NULL, NULL, 2, NULL, 0, 'Add the 2 eggs. Blend at low speed until moistened, then beat 3 minutes at medium speed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4BA3961F7D35A4EBF1403BBE0CABC87', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 5, NULL, NULL, 2, NULL, 0, 'By hand, gradually stir in enough of the remaining flour to make a soft dough. (You might not use all of the flour. Reserve any remaining flour for kneading and shaping the dough.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5739AF862939CD44AA1A9BCC4DF07D3D', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 6, NULL, NULL, 2, NULL, 0, 'Knead the dough on a floured surface until smooth and elastic, about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D28B9D7BE39A14796B05952F90DEAAB', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 7, NULL, NULL, 2, NULL, 0, 'Place in a greased bowl, turning once to grease top. Cover and let rise in a warm place for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A10FA599608ADE4695B253D2F570FC96', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 8, NULL, NULL, 2, NULL, 0, 'Punch down dough. On a lightly floured surface, divide the dough in half and shape it into a ball. Press one ball of dough evenly into each of the prepared baking pans.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93D1A14E4A50F441A5A663A2E93F617D', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of the dough lightly with flour. With a knife, cut the dough into 9 rolls in each pan, cutting almost to the bottom of the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FEDCDBB8738C44E9771AE360D779225', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 10, NULL, NULL, 2, NULL, 0, 'Cover and let rise in a warm place for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E42C9C1DEDD84449336DB571DEAFC35', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 11, NULL, NULL, 2, NULL, 0, 'Remove from pan. Brush with melted butter. Cool completely on a rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A12C2EA8A49E44297B1BD54986D3A91', X'B0DC37785AD76940A11FEFDD04CE7320', X'B0DC37785AD76940A11FEFDD04CE7320', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5764234C89B7F64482CCCC7A02CAA984', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F4E88917688EF4EAC78F7FE119304D0', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 1, NULL, NULL, 2, NULL, 0, 'Line rimmed baking sheet with parchment paper. Place the pepperoni sticks in a large bowl and microwave until the fat is rendered, about 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EEA83CE354BD34EA6541F16D046A4E0', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 2, NULL, NULL, 2, NULL, 0, 'Using tongs, transfer pepperoni to a paper towel–lined plate. Reserve 3 tablespoons of the pepperoni oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A3809AF4DA8C34C88C77827AFC7B694', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 3, NULL, NULL, 2, NULL, 0, 'Combine water, milk, and sugar in a medium bowl and microwave until temperature registers 110 F, 1 to 2 minutes. Stir in reserved pepperoni oil and yeast and let the yeast bloom for about 5 minutes or until foamy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0B0E338884AF4DA1DAE74DB311BCD3', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 4, NULL, NULL, 2, NULL, 0, 'Using a stand mixer fitted with a dough hook, mix flour, and salt on low speed until combined, about 10 seconds. With the mixer running, slowly add yeast mixture until incorporated. Increase speed to medium and mix until dough is shiny and smooth and pulls away from the sides of the bowl, about 8 minutes. If the dough feels wet add more flour 1 tablespoon at a time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10FD3108D1757D4EABDED6B2555B0672', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 5, NULL, NULL, 2, NULL, 0, 'Transfer dough to a lightly floured counter and divide into 8 equal pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A9C28EDABBC8F458EE7236621767B24', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 6, NULL, NULL, 2, NULL, 0, 'Working with 1 piece at a time while keeping the remaining pieces covered with plastic so as not to dry out, form dough into balls, cover with plastic, and let rest for 5 minutes. Toss pepperoni wedges with 1 tablespoon flour to coat. Working with 1 dough ball at a time, use your hands to press the ball into a 6 by 4-inch rectangle. Starting along the short side of the rectangle, lay 2 pieces of pepperoni side by side, 1/2 inch apart, and top with 2 tablespoons cheese and roll into a tight cylinder, pinching the ends of the rolls to seal.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88C967FF69E1A74B8693EC4B65AD7C79', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 7, NULL, NULL, 2, NULL, 0, 'Arrange rolls seam side down on the prepared baking sheet. Cover with plastic and let rise until doubled in size, about 40 minutes. While the rolls rise, preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FEA6AE78D171142933D1AFF07F2D879', X'55A282C213B4C0428989E67562DFADDD', X'55A282C213B4C0428989E67562DFADDD', 8, NULL, NULL, 2, NULL, 0, 'Brush rolls with egg mixture and bake until golden brown, 24 to 28 minutes. Transfer the baking sheet to a wire rack and let cool for at least 15 minutes and serve. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D236C939CF71384AB738686BB7B024D3', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A37EB47A73093748A5810DAFF4EAB144', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 1, NULL, NULL, 2, NULL, 0, 'Prepare the sponge cake: Separate the eggs by placing the yolks and whites into different bowls. Add 1/2 cup sugar to the yolks and 1/2 cup sugar to the whites. Beat the yolks until creamy and beat the whites until stiff peaks form.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026B5CA7567290419DAB605CAFE04760', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 2, NULL, NULL, 2, NULL, 0, 'Fold the whipped egg whites into the creamy yolk mixture. Gradually add the flour, then mix in the butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F0EDA76C585074EA2E0636F0BA1919B', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 3, NULL, NULL, 2, NULL, 0, 'Pour into a greased 10-inch cake pan. Bake in preheated oven for about 20 minutes, or until spongy and golden.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A7453670C7BF46A41FDEA48BDFE0BB', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFBC4D1DB61DF44D96BAEC0AEEA56992', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 5, NULL, NULL, 2, NULL, 0, 'Make the pastry cream: In a large saucepan, bring to a boil the butter, milk, and cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670CEED53937F84994192F1517E03D42', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 6, NULL, NULL, 2, NULL, 0, 'As this mixture is cooking, combine the sugar, cornstarch, and eggs in a bowl and whip until ribbons form.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFED96CE39F51E41A937C76DC3DA9731', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 7, NULL, NULL, 2, NULL, 0, 'When the cream, milk, butter mixture reaches the boiling point, gradually whisk in the egg mixture and cook to boiling. Boil for one minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D34745C7621A409C2F515A8D16654B', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 8, NULL, NULL, 2, NULL, 0, 'Assemble the Boston cream pie: Cut the sponge cake into two layers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D11A0B97706AE4797CC8ADBABCB68ED', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 9, NULL, NULL, 2, NULL, 0, 'Spread the flavored pastry cream over one layer. Top with the second cake layer. Reserve a small amount of the pastry cream to spread on sides to adhere to almonds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDF7BF2A09C63647B961E3692EFF2723', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 10, NULL, NULL, 2, NULL, 0, 'Place chocolate and water in a microwave-safe bowl and heat in 30-second intervals, stirring each time, until melted. Spread a thin layer of chocolate icing on the top of the cake. If some of the icing drips down the side of the cake, don’t worry. This will be covered up with the almonds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F1F8ABA5DF1144AA01016719527C9B4', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 11, NULL, NULL, 2, NULL, 0, 'Combine the white icing ingredients and warm to approximately 105 F. Adjust the consistency with water. The icing should flow freely. Spoon white icing into a pastry bag fitted with a small tip.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'339E40CF5AAC244A833A51E9E351009E', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 12, NULL, NULL, 2, NULL, 0, 'After pouring on the chocolate icing, follow immediately by piping spiral lines of the white icing starting from the center of the cake working your way outwards. Using a wooden skewer, pull the white lines through the chocolate layer to form a decorative web design. Start in the center of the cake and pull outward to the edge.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'597AA6D1E3EF2847A9FFEF048F67FC94', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 13, NULL, NULL, 2, NULL, 0, 'Spread sides of cake with a thin coating of the reserved pastry cream. Press on the toasted almonds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4918AD11250E41ADF5319D87655CBD', X'597D01DCE364DC499B323D947A94E110', X'597D01DCE364DC499B323D947A94E110', 14, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E261397B1FBBA469D961C5EB21F43E3', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'647D0B4A95ED8545AAC8D9BDDE48AB7E', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 1, NULL, NULL, 2, NULL, 0, 'Add the butter to a medium heavy-bottomed pot. Allow the butter to melt over medium heat and add the chopped carrots, celery, onion, and garlic.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CA717D970B0214A9273FFBF754D92AE', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 2, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of salt and saute the vegetables on medium heat until they are softened and slightly browned, about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6C8E20275091240B143CEE4EB63FA7B', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and thyme to the vegetables and pour it all into a blender and blend until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7733BBFB9C2CA4CAFE4396E17CF64DD', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 4, NULL, NULL, 2, NULL, 0, 'Pour the mixture back into the pot and add the beer. Heat on medium heat, whisking to incorporate the beer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDE7D321F0959C419F4B14838E241449', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 5, NULL, NULL, 2, NULL, 0, 'Add the half and half and softened cream cheese and gently whisk into the soup. Make sure the cream cheese is softened; it will incorporate into the soup more smoothly this way.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C3FFF140B84644790675BC5FB005DC2', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 6, NULL, NULL, 2, NULL, 0, 'Toss the shredded cheddar with the cornstarch until the cheese is coated evenly. Add one handful of cheese at a time, whisking it into the soup until it is melted and incorporated. If you add too much at one time, it can become clumpy, so do this slowly. Simmer for another five minutes until it has thickened, stirring frequently.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B7504401B8D7A49831130A23F7E4D6D', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 7, NULL, NULL, 2, NULL, 0, 'Season the soup with more salt as needed and add the pepper. If you want the soup even smoother you can pass it through a sieve or add it back to the blender and blend again.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B2F7E9ACF834D43AA8ED05BB41EC500', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 8, NULL, NULL, 2, NULL, 0, 'Serve the soup in hollowed out pretzel bread bowls or regular bread bowls. You can also serve in a plain old bowl with some delicious bread or pretzel sticks!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E545F42CE7BD4386914332CCB4ADCE', X'734CAED8EE3B8648BDED5CDFB98EC49A', X'734CAED8EE3B8648BDED5CDFB98EC49A', 9, NULL, NULL, 2, NULL, 0, 'Top with the chopped chives and bacon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3CA9118F405824294C9301FD6D81B29', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B537C1BE5C4864681DCD3DCBF224B55', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the Kosher salt and a few good grinds of black pepper onto your chopping board and spread. Press the chicken thighs, skin-side surface down into the salt and pepper then roll each thigh tight and place in a bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDAC637177F91A47B4BD1584568CDE46', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 2, NULL, NULL, 2, NULL, 0, 'In another small bowl mix the garlic, vinegar, brine, wine and sugar and lightly whisk. Add the olives, prunes and capers, stir, then pour everything over the chicken thighs. Cover the dish tightly with plastic or wax wrap and place into the fridge to marinate overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF7271A01814B4282D62E62BDB44C7E', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'292C8B96ACC03F47A44847D2225DCB4F', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 4, NULL, NULL, 2, NULL, 0, 'When ready to cook, lift the thighs from the dish and pat dry with kitchen paper. Heat the oil in a frying pan until hot but not smoking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E82DB83EDFE6424ABD5191CCC27A6EA8', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 5, NULL, NULL, 2, NULL, 0, 'Add the thighs and sear all over until the thighs are golden brown and slightly crisp on the skin-side surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7D781CC49EB14DB2ADE8AC2B71B769', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 6, NULL, NULL, 2, NULL, 0, 'Pack the browned thighs into shallow ovenproof baking then pour over the marinade, olives, prunes and capers. Bake in the center of the preheated oven for 45 minutes basting the chicken with the marinade from time to time. The marinade will reduce, and the chicken will turn dark golden and slightly caramelized.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'909D9C6F49F86846BA69D636B54DB7AF', X'6DBB14AE5F90AF47967D4052A3851940', X'6DBB14AE5F90AF47967D4052A3851940', 7, NULL, NULL, 2, NULL, 0, 'Serve while piping hot with extra olives and capers on the side and rice, or a mixture of rice and quinoa, or pasta. The Chicken Marbella also works well with creamy mashed potatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E21365443CF35148ACA1C9578B78F516', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAEB08D9D90C8448A31F411B44134E32', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 1, NULL, NULL, 2, NULL, 0, 'Wrap each hot dog with a slice of bacon so that the entire hot dog is covered in bacon. Secure with a toothpick and place them in the refrigerator for at least 30 minutes. This helps the bacon stick to the hot dog and will keep it from falling off when they’re cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0824DD2DB7F39042B619AB31C900769F', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 2, NULL, NULL, 2, NULL, 0, 'Heat the grill or stove on medium heat. Keep the temperature on medium heat so the bacon cooks without burning. Remove the toothpick from the hot dogs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7348FE47915ABB42880C20B949D33F01', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 3, NULL, NULL, 2, NULL, 0, 'While the hot dogs are cooking, prepare the crema.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32E71C1668126846B01C89FA0354CA73', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the sour cream, mayonnaise, lime juice, cilantro, salt, and pepper in a small bowl. You can also blend it in the food processor.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1E8BFD7D19BA542A26694210977675A', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 5, NULL, NULL, 2, NULL, 0, 'Cut a pocket into the center of each of the rolls.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ABD23D2E7B2584B9EAC20A8EC4E143A', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 6, NULL, NULL, 2, NULL, 0, 'Butter the outside of the rolls and then griddle them on the same grill or pan as the hot dogs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'436B6AAF7B66114CB721B2E4CFC5C71D', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 7, NULL, NULL, 2, NULL, 0, 'Kind of pull them apart a bit so the inside of the roll gets a little crisp and cook them cut side down. You can flatten them with a lid if you wish. Cook until they are golden brown and seared.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83D8E10A5FE0E944A67A2181593F407A', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 8, NULL, NULL, 2, NULL, 0, 'Warm the beans in the microwave or a small pot until steaming. Spread a few tablespoons of the refried beans into the bottom of each roll.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E4B7823E7214B40A8DB696645A763B8', X'26C287AC57ABB446819D056F57CD608C', X'26C287AC57ABB446819D056F57CD608C', 9, NULL, NULL, 2, NULL, 0, 'Add the hot dog into the roll and then top with diced avocado, tomato, red onion, jalapeño, cilantro, and a drizzle of the cilantro-lime crema. Serve immediately with your favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00BC9D9EBA99EF4897382F471EB436E2', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8B3686566BCBD4491C110E6F309AB28', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 1, NULL, NULL, 2, NULL, 0, 'Chop the sun-dried tomatoes. Toss with the chopped avocado, onion, cilantro, salt, and lime juice in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'048183C648680E4B96ED0B33C8631F91', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 2, NULL, NULL, 2, NULL, 0, 'Turn the egg roll wrappers on an angle, so that the corner is facing you. Place one to two tablespoons of filling towards the center of the wrapper. Brush the edges of the wrapper with water. You can use a pastry brush or your finger.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29F941146AB0DC44ACD661CFB16539B5', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 3, NULL, NULL, 2, NULL, 0, 'Fold the corner over the filling tightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'983B115F793984498C80E67E0E1E747F', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 4, NULL, NULL, 2, NULL, 0, 'Start rolling the egg roll in the same direction and fold in the two side corners as you roll. Keep the wrapper tight around the filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80AF4F64E65ED42B25875389941ACEF', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 5, NULL, NULL, 2, NULL, 0, 'Continue rolling until the filling is completely enclosed in the wrapper. Set it aside on a parchment-lined baking sheet. Continue filling wrappers until you run out of filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F5B041D1EDE45408CFE0203FCDEC443', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 6, NULL, NULL, 2, NULL, 0, 'Pour oil into a pot to a 2-inch depth and heat to 350 F. Add three or four rolls to the hot oil at a time. Cook for three to four minutes or until golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'270D2F1531701944979B15FA363FDE30', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 7, NULL, NULL, 2, NULL, 0, 'Remove the egg rolls from the oil and allow them to drain on a paper towel lined plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F3F00AD17AE224FABE1922B3FFFBD4F', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 8, NULL, NULL, 2, NULL, 0, 'While the egg rolls are frying, gather the ingredients for the dipping sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0125EA6A2E49342A05128C4E35EB2E5', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 9, NULL, NULL, 2, NULL, 0, 'Blend the dipping sauce ingredients together in a blender or food processor.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CABFBC23B48D54ABB0292104477BFD7', X'6BF4958E6D1F934AAB8B365C1E034388', X'6BF4958E6D1F934AAB8B365C1E034388', 10, NULL, NULL, 2, NULL, 0, 'Cut the hot egg rolls on an angle and serve them with the dipping sauce!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8323073444D2264C81E56BD4D903B335', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFEC373D4A32284482D4C0B5955A6C58', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 1, NULL, NULL, 2, NULL, 0, 'Shape the meat into four 6-ounce burgers. Make each patty a little larger than the bun since it will shrink while cooking. Add an indentation in the center of each burger with your thumb. This will prevent swelling in the center as they cook. Sprinkle lightly with kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AED7B74E3DB6B94AA9BDABB01509DB28', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 2, NULL, NULL, 2, NULL, 0, 'Heat the unsalted butter in a heavy skillet over medium to medium-low heat. Add one or two burgers to the skillet and cook for about 3 to 4 minutes on each side, waiting until the bottom of the burger releases easily from the pan before flipping.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24F8779830F7244A39D653B42F5D116', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 3, NULL, NULL, 2, NULL, 0, 'Continue to cook the burgers, flipping occasionally, until they register 160 F on an instant-read thermometer. Repeat with the remaining burgers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D7B64B65434C3499B8B804EE6838CB5', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 4, NULL, NULL, 2, NULL, 0, 'Assemble the burgers in the buns with your choice of sauce and toppings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA9CC03311CAA44FBA22C2B157000B34', X'3A438D5BBB99974A80DD5D8BC44DFC1D', X'3A438D5BBB99974A80DD5D8BC44DFC1D', 5, NULL, NULL, 2, NULL, 0, 'Enjoy your bison burgers!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC87608B1DB1084980B6271B70F73BDF', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EEB370AFB46754994C6E89D6EACB15A', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 1, NULL, NULL, 2, NULL, 0, 'Split each hot dog or sausage lengthwise while keeping the two halves together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40621DAD233A7341BD0E9E7666BEC721', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 2, NULL, NULL, 2, NULL, 0, 'Heat a sauté or grill pan on high heat. Add the oil, onions, and salt and sauté or grill for 2 to 3 minutes or until slightly softened and browned. Make sure to do this on high heat to get good golden color and flavor.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F02BCE64B55644781380A0C63B97155', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 3, NULL, NULL, 2, NULL, 0, 'Remove the onions from the pan and add the hot dogs. Sear for about 2 minutes per side or until heated through and lightly browned or charred. Cover to keep warm while you toast the buns.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3EA5421E20C5C45910A14F472CBA34D', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 4, NULL, NULL, 2, NULL, 0, 'Remove the dogs from the pan and add the buns. Toast them split-side down. You can open the buns as wide as you want. Toast them until just lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F3A803ED352BE4AA8E7093D7BAB5CD4', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 5, NULL, NULL, 2, NULL, 0, 'Add the dogs to the buns and add some grilled onions to the side of the dogs. Add the softened cream cheese to a plastic or piping bag. Cut the bottom off of the bag and squirt the cream cheese over the top of the dog. You can also just slather it into the bun before you add the hot dog. Then top with fresh jalapeno slices, Sriracha, and mustard. You can also add sauerkraut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787A660FBFF89746A2959EE45494B8D9', X'1B0512FF0DBA5440843620624F094647', X'1B0512FF0DBA5440843620624F094647', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately with your favorite chips or sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC41C7BF7CE36458014D17EAB39AA01', X'C39AD92533C5EF4E9434ED41D7E354EE', X'C39AD92533C5EF4E9434ED41D7E354EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1F504D25BC0AF47A0F4AE0829186E62', X'C39AD92533C5EF4E9434ED41D7E354EE', X'C39AD92533C5EF4E9434ED41D7E354EE', 1, NULL, NULL, 2, NULL, 0, 'Pour one shot glass full of whiskey and fill a separate shot glass with pickle juice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55566174892B7144A7B28612D8899A41', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80DA39AEDF29749B6722EBBBC3BBFE8', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 1, NULL, NULL, 2, NULL, 0, 'Heat a charcoal or gas grill to medium-hot (you should be able to hold your hand about an inch over the cooking grate for about 2 seconds)—if the grill is too hot the beets will burn before they cook through. If you need a hotter grill to cook other things, cook the beets on the cooler edges of the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E7775C1246D5F4AABA185F2F1D01172', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, peel and slice the beets.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE87E6F010EDA4DBD77BD1C2E616300', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 3, NULL, NULL, 2, NULL, 0, 'Brush the beets with olive oil or vegetable oil. (Or, put the slices in a large bowl, drizzle with the oil and toss to coat the beets evenly.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'308833015AAB0240B12CF86EA4E0CB2D', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 4, NULL, NULL, 2, NULL, 0, 'Place the beets on the grill and sprinkle lightly with salt, if you like.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0B14BDD666420428265371B8B974FF1', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 5, NULL, NULL, 2, NULL, 0, 'If using a gas grill, close the cover and cook 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EF25A397F1C1E4FA8BBFEF56E7B001B', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 6, NULL, NULL, 2, NULL, 0, 'Turn the beets over and continue cooking until the beets are tender and grill-marked, another 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47D17D1D54FC3149B7050E89E0147954', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 7, NULL, NULL, 2, NULL, 0, 'Serve the beets hot, warm, or at room temperature. Drizzle them with additional olive oil for serving, if you like. This is also a great time to use any nut oils (toasted walnut oil or hazelnut oil in particular) since they so perfectly complement the earthy-yet-sweet flavor of grilled beets. (That''s it. It''s really just slicing the beets and setting them on the grill until they''re tender!)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CC06E5667C01C41A6483FB8F0828881', X'25656D0B9F998546861FC212DADD59BB', X'25656D0B9F998546861FC212DADD59BB', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFFDAAD99735224A8E67610140B14853', X'373C8A339293D747B6E06C7B5F21871F', X'373C8A339293D747B6E06C7B5F21871F', 0, NULL, NULL, 2, NULL, 0, 'Pour oil to a depth of 2` in a 6 quart Dutch oven. Heat over medium-high heat until your deep-fry thermometer reads 350°.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E6C4F10F72874BB7E80B1679705471', X'373C8A339293D747B6E06C7B5F21871F', X'373C8A339293D747B6E06C7B5F21871F', 1, NULL, NULL, 2, NULL, 0, 'Divide mozzarella slices among 4 slices of bread. Season with salt and pepper, and cover with the remaining cheese slices to make 4 sandwiches. Trim the crusts.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45AE456098717F4CA4ECAD01E0531BBC', X'373C8A339293D747B6E06C7B5F21871F', X'373C8A339293D747B6E06C7B5F21871F', 2, NULL, NULL, 2, NULL, 0, 'Take out 3 bowls and place the flour in one bowl, the eggs (cracked and whisked) in another and the breadcrumbs in the last bowl. Dredge each sandwich in flour, coat in eggs and coat in breadcrumbs before putting on the frying pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE9A286C9A9B34B8FB4E661598753C5', X'373C8A339293D747B6E06C7B5F21871F', X'373C8A339293D747B6E06C7B5F21871F', 3, NULL, NULL, 2, NULL, 0, 'Fry until golden brown, about 1-2 minutes on each side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3C04EA52058904E87F4711B1679C269', X'373C8A339293D747B6E06C7B5F21871F', X'373C8A339293D747B6E06C7B5F21871F', 4, NULL, NULL, 2, NULL, 0, 'Let each sandwich rest for about a minute after it''s been removed from the oil, then spoon some sauce over the top and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77B99EC8A8643C4C866FF05F1176388D', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F9B548ED90C4459F3F67A70FD05BF0', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 1, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a glass mixing bowl and beat them until they turn a pale yellow color.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0894D636BED5438134803B5F58DF5F', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 2, NULL, NULL, 2, NULL, 0, 'Add the milk to the eggs and season to taste with salt and white pepper. Whisk the eggs like crazy. If you''re not up for that, you can use an electric beater or stand mixer with the whisk attachment. Whatever device you use, you''re trying to beat as much air as possible into the eggs. Note that it may be easier and quicker to beat the eggs in two batches (4 at a time) to make sure you don''t have any lumps.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B9B110A9233B479749EEAF11ECC5C9', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 3, NULL, NULL, 2, NULL, 0, 'Heat a heavy-bottomed, nonstick sauté pan over medium-low heat. Add the butter and let it melt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'297A7A1D7EB54C4A8D99D8AA764879A4', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 4, NULL, NULL, 2, NULL, 0, 'When the butter in the pan is hot enough to make a drop of water hiss, pour in the eggs. Don''t stir. Let the eggs cook for up to a minute or until the bottom starts to set but doesn''t brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B2E433F717A4D478B76F82B38901136', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 5, NULL, NULL, 2, NULL, 0, 'With a heat-resistant rubber spatula, gently push one edge of the egg into the center while tilting the pan to allow the still liquid egg to flow in underneath. Repeat with the other edges, until there''s no liquid left.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'413E41EC91AF4A42A117B55A7CD428E3', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 6, NULL, NULL, 2, NULL, 0, 'Turn off the heat and continue gently stirring and turning the egg until all the uncooked parts become firm. Don''t break up the egg, keeping the curds as large as possible. If you''re adding any other ingredients, quickly add them now.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22ECF2431C90CF46B264937E83B1926D', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 7, NULL, NULL, 2, NULL, 0, 'Transfer to a plate when the eggs are set but still moist and soft. Eggs are delicate, so they''ll continue to cook for a few moments after they''re on the plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E932A99119296469FA28E26EDA93FC6', X'B4A6C20DE1E2424699C06FC1FC55C476', X'B4A6C20DE1E2424699C06FC1FC55C476', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C40E4003688E34EAE881AA81C8DE817', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF61540EE01744790BD56190CCD00E4', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 1, NULL, NULL, 2, NULL, 0, 'Rinse the cherries then remove the pits using a cherry pitter or alternative objects such as a toothpick, chopstick, or unbent paper clip. Work over a bowl to catch the cherry juice along with the pits, placing the pitted cherries in a separate bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80ADE942FEEEC40B58605F6EBB52555', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, bring the water to a boil. Add the brown and white sugars and stir until completely dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBC3123528D146488D2F8DFA74727C9C', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 3, NULL, NULL, 2, NULL, 0, 'Reduce the heat to a simmer. Add the vanilla extract, cardamom, cloves, allspice, star anise, and cinnamon stick. Simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC22B2F555EDCC45A2903C3C249708C8', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 4, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and discard the spices. Stir in the brandy and add the pitted cherries along with any juice. Let cool to room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46C368956DDA84ABD6756D1E8B02FD7', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 5, NULL, NULL, 2, NULL, 0, 'Use a slotted spoon to transfer the cherries to a quart jar or two pint jars.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B1B2D6511DC8F4B9FBD6F1CEB2D0623', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 6, NULL, NULL, 2, NULL, 0, 'Pour the liquid over the cherries, covering them completely while leaving 1/2-inch of headspace in the jar. Secure the lid.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F8F27BE817AA0488D1C0F79DDBD20CB', X'99117ED5E90323448EC1CBEF2C9D8461', X'99117ED5E90323448EC1CBEF2C9D8461', 7, NULL, NULL, 2, NULL, 0, 'Place the jar in the refrigerator. If you prefer to use a traditional canning method, process the jar in boiling water for 10 minutes, allow it to cool, then store in a cool, dark place such as a cupboard or pantry. Either way, let the brandied cherries rest for 6 weeks before using to allow the flavor to develop.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6414103D7BDFA54EA80BD59F79F7DD64', X'8606310BF146EB41A31F7377FDB2D891', X'8606310BF146EB41A31F7377FDB2D891', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7799E40A4D63364DB0744F6136CFF9FA', X'8606310BF146EB41A31F7377FDB2D891', X'8606310BF146EB41A31F7377FDB2D891', 1, NULL, NULL, 2, NULL, 0, 'Place all of the ingredients into a saucepan and bring to a simmer at medium heat. Cook for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DBC3EF87A03B4FB6A5916F7136C209', X'8606310BF146EB41A31F7377FDB2D891', X'8606310BF146EB41A31F7377FDB2D891', 2, NULL, NULL, 2, NULL, 0, 'Place the ingredients in a blender and blend together being careful of the steam so that it doesn''t blow the lid off.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DCA5C42094F0468A80CED6B66665BC', X'8606310BF146EB41A31F7377FDB2D891', X'8606310BF146EB41A31F7377FDB2D891', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57368542B795234BAF42EBD891BAA33F', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D57589E64B1B4E9F6065564337D755', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 1, NULL, NULL, 2, NULL, 0, 'Prepare a gas or charcoal grill to medium heat: You should be able to hold your hand about an inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat. If you are also using the grill to cook something else that requires more heat, place the peaches around the edges; they''re cooler. Or cook dinner, let the fire cool down, and then grill the peaches.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'350AAE4CCB755E4DA1C7BC95E497C003', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 2, NULL, NULL, 2, NULL, 0, 'Cut the peaches in half and pit them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C0F37FD8B9BB4495ED06A79FB1DFD9', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 3, NULL, NULL, 2, NULL, 0, 'If you want to add cinnamon and/or sugar, grill entire peach halves; otherwise, you can cut them into wedges, if you like. Peeling the peaches isn''t necessary for the grill, but if you want to, you can.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46C2BA12D78CC04892F21E6CA9503D1C', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 4, NULL, NULL, 2, NULL, 0, 'Lightly brush the peaches with the oil, being particularly aware of coating the cut sides well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D722BB027F62A940A44E62BBDF7B553D', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 5, NULL, NULL, 2, NULL, 0, 'Set the oiled peaches on the medium hot grill. Make sure they aren''t touching so the hot air of the grill can circulate around each piece.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDF643BF10122F4DAECC48C02C63D573', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 6, NULL, NULL, 2, NULL, 0, 'Cook the peaches until grill marks form, turning as necessary to mark all cut sides for the best presentation. Turn the peach halves over, sprinkle with the cinnamon and sugar, if using. Cook until the peaches are tender, but not falling apart, about 8 minutes total.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D49E7F9F05BDD247866679F42468A4D3', X'9D7C4602E8531D479A610E69DDD96E58', X'9D7C4602E8531D479A610E69DDD96E58', 7, NULL, NULL, 2, NULL, 0, 'Serve grilled peaches hot, warm, or even at room temperature. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79F8381C7B1744B8E83136EF99F7A31', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3058BD1DE15F2C4EAFBBAFFAFC88BE61', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 1, NULL, NULL, 2, NULL, 0, 'While technically optional, the first step for the best tasting grilled steak is to salt it. Put the salt in a small bowl or ramekin. If using the pepper and/or cayenne, add them to the salt and mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E86D0765285A584EA2695B9AADD74F6F', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 2, NULL, NULL, 2, NULL, 0, 'Liberally sprinkle the skirt steak with sea salt or kosher salt and let it sit for at least 30 minutes at room temperature or up to overnight in the fridge. In any case, bring the meat to about room temperature, or at least let it sit out to take the chill off before cooking it to help ensure even cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB68D02B64957349A22E04123FBDB61C', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 3, NULL, NULL, 2, NULL, 0, 'While the meat sits, make sure the cooking grate on your charcoal or gas grill is cleaned and well-oiled, then heat the grill to high heat. You should be able to hold your hand about an inch over the grill grate for 1 second before it feels too hot. If using a charcoal grill, build the fire to one side so you''ll also have a cooler area where you can move the steak(s) if it''s seared and browned on the outside but not quite ready to take off the grill completely (if you''re using a gas grill you''ll be able to turn off a burner if need be).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'936C3851C9B97245B1C5EEFFA6D766E2', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 4, NULL, NULL, 2, NULL, 0, 'Set the steak(s) on the grill and cook, without moving, until that side is seared and releases from the grill of its own accord, about 3 minutes for medium-rare, 4 minutes for medium.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A7F5FBAFED9A49B34D0311819DBB56', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 5, NULL, NULL, 2, NULL, 0, 'Turn and cook on the other side to the same effect, about 3 to 4 more minutes. You can use a meat thermometer to get an accurate temperature if you like. For rare, remove the steak(s) at 120 F to 125 F; medium-rare 125 F to 130 F; and medium 130 F to 135 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2774D403EA69946B0C5506FD2950E45', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 6, NULL, NULL, 2, NULL, 0, 'Remove the steak(s) from the grill, cover loosely with foil, and let sit at least 10 minutes. This resting time allows the meat to finish cooking and the juices to redistribute evenly throughout the steak. It is extremely important to achieving a properly cooked steak when everything is said and done, so despite the temptation, don''t skip this step!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADEE31DC746E2D48A1213C5116514C9F', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 7, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to slice the steak against the grain and serve hot, warm, or room temperature. Skirt steak is particularly flavorful, so any cold leftovers are more than usually delicious.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AE8FAABBB328548AF0A596ED91FBD67', X'13560061BF1F3542B79F499D411C6109', X'13560061BF1F3542B79F499D411C6109', 8, NULL, NULL, 2, NULL, 0, 'Perfectly grilled steak is perfectly tasty with just salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966FDE857C0FBD498580A332E6B14E2F', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F5AF20E1A43924E84B1DAEFE69DD97A', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 1, NULL, NULL, 2, NULL, 0, 'Separate the yolks from the whites. Reserve the yolks for another use.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7C323484C7135458EF57E1EE4E54B70', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 2, NULL, NULL, 2, NULL, 0, 'Beat the whites until they are a little frothy. Add salt and pepper, about a 1/2 teaspoon of each.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5368C18D1BEAF4D8A11EEE29CD36E17', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 3, NULL, NULL, 2, NULL, 0, 'Chop the vegetables to your desired size.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74D8E3DB254E0140B6DF8E41A7DA155E', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a non-stick or cast iron skillet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3A7A048C0C3144EB4EFBCBA9B7285F0', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 5, NULL, NULL, 2, NULL, 0, 'Lower the heat to low. Pour the egg whites into the pan and swirl them until they completely coat the bottom of the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66D436180D7D7D42AC43E62320FD9D7D', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 6, NULL, NULL, 2, NULL, 0, 'Use your spatula to pull the egg whites to the center, exposing parts of the pan and allowing the eggs to fill the exposed parts so that it cooks completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACEF826D358D2E4D9428FE9B03F5AAA6', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 7, NULL, NULL, 2, NULL, 0, 'When the egg has almost set add the chopped vegetables and cheese to half of the omelet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D78C262E8354124FA9F51B4317B9F7D4', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 8, NULL, NULL, 2, NULL, 0, 'Fold the half of the omelet that doesn''t have filling over the filling. Cook for another 30 seconds to a minute or until the filling is warmed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88829C9002BCB34C95802D1F98E644B2', X'8888556CCBC53B4A813732AB62A6BC45', X'8888556CCBC53B4A813732AB62A6BC45', 9, NULL, NULL, 2, NULL, 0, 'Serve with more chopped veggies and dig in!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90E2B81DE312814689FF6589580C840B', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB870316FA85045A13F5AAC29AABD9C', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 1, NULL, NULL, 2, NULL, 0, 'Beat the eggs with a whisk until the yolks and whites are completely combined. Season the eggs with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16540FA40C90A348BE366DE760A98856', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a non-stick or cast iron skillet over medium-low heat. Swirl the olive oil to completely coat the pan. Then turn the heat down to low.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'119D20D8BC328C43B85957C222342C1E', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 3, NULL, NULL, 2, NULL, 0, 'Add the eggs to the pan, swirling quickly to cover the bottom. Adjust the heat if needed so that the bottom doesn''t brown too quickly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68F8637EB3A5FB4A8F215EA4F148897C', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 4, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula to move the eggs from the edge to the center, exposing parts of the pan so that the egg can completely cook. Then leave the eggs undisturbed and allow them to cook until the omelet is almost set.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F8C84588225ED42A4F2606D3F93AEB6', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 5, NULL, NULL, 2, NULL, 0, 'When the omelet is almost set, spoon the warmed tomato sauce over half of it. Cover the tomato sauce with the shredded cheese and pepperoni. Cook for 30 seconds, until the bottom is just about to brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA3AB43E01A5AB45B0EFE43AE8C7FCBA', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 6, NULL, NULL, 2, NULL, 0, 'Fold the omelet in half. Cook for another 30 seconds or so, until the cheese is melted and everything is heated through.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27694FF5B09B9F47AFC842384F11665B', X'EA1CDE1C4812254D869A9C070D2A7EBC', X'EA1CDE1C4812254D869A9C070D2A7EBC', 7, NULL, NULL, 2, NULL, 0, 'Serve with a little chopped basil or parsley if you wish and dig in!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5171F3D8D9888498DECAA71E74207F8', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05C7AC16108AC04F91C71DFDCA0BB205', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the eggs, milk, salt, sugar, vanilla, cinnamon, and allspice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27E95F3C65292D4B805C87B0987BD980', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 2, NULL, NULL, 2, NULL, 0, 'Slice the French bread into slices that are at least 1-inch thick. You can use a whole-grain French loaf, but any French or Italian bread should work nicely. Slice at a slight angle to make a longer piece of bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12C82217FCD14D49AFADA1BB9D8B88B9', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 3, NULL, NULL, 2, NULL, 0, 'Soak each slice in the egg custard mixture. Turn the slices until all of the mixture has been absorbed into the bread. Depending on how stale the bread is, this may take from 5 to 10 minutes. The secret to this recipe is to completely saturate the bread. This is why thick slices of stale bread are used since thinner fresh bread would fall apart too easily. Make sure to flip the bread so all sides can soak evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE5A695643B9CA4FBD4C2260A8AB413F', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. In a large non-stick skillet over medium heat, very lightly brown the slices in the butter and oil for about 2 minutes per side. Don''t make it too dark as it will also be baking in the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6471C92F9157E4F8EF9F82069C08CB9', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 5, NULL, NULL, 2, NULL, 0, 'From the frying pan or skillet, transfer to a baking sheet or casserole dish. Bake for 10 minutes. After 10 minutes, remove from the oven, turn over, and put back in the oven for another 5 minutes to brown on the other side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F0151B6593EDE4F818E9C46037342BC', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 6, NULL, NULL, 2, NULL, 0, 'Traditionally, pain perdu is served with powdered sugar sprinkled over the top. If you want it to look like it would in the French Quarter, then dust away and dig in. You can also top it with maple syrup or fruit sauces like jellies, jams, or marmalades.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCF986D9BCA77749A4BAABB40A1494B6', X'038457AF0DA9724D98C994F4CBE3F339', X'038457AF0DA9724D98C994F4CBE3F339', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EE5FC76F6F4444CB5433319F2EC9E2E', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF3F43B7F184D4D977E1FCA4F69CC21', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, combine the flour, cocoa powder, sugar, baking soda, and salt. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'344327B4BE1B6E46A3C6FA01F21F3103', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, combine the milk, egg, canola oil, and peppermint extract (if using). Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EFFA212AE979942AF3E772440F20BF4', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 3, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet ingredients and mix just until combined. The mixture will be lumpy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F437E5AAC7AD634BBE2722EA988461E6', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 4, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium heat. Once hot, add 1 teaspoon of the butter and let melt, coating the pan. Add about 1/4 cup of batter to form a pancake. Repeat 1 to 3 more times, depending on the size of the skillet, and space the pancakes evenly apart without touching.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E538B1C14D95B49BA7A3F9580FD5656', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 5, NULL, NULL, 2, NULL, 0, 'Let cook for 1 to 2 minutes, or until the edges are setting up and bubbles have formed on the top of the pancake and are beginning to pop.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8446EFF080A8447AD827F49C041CFC1', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 6, NULL, NULL, 2, NULL, 0, 'Flip and cook another 1 to 2 minutes, or until cooked through. Keep pancakes warm under a kitchen towel or in a low heat oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06EF56A86C42D444B1A5075F978B849B', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 7, NULL, NULL, 2, NULL, 0, 'Add another teaspoon of butter and repeat until all of the batter is cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEB2970ED9AE7D43A63BE697F0236885', X'282281023B1CDE48ACB1B7DBD43BB6B4', X'282281023B1CDE48ACB1B7DBD43BB6B4', 8, NULL, NULL, 2, NULL, 0, 'To serve, top each pile of warm pancakes with chocolate sauce, chocolate chips, mini marshmallows, and peppermint candy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'980367B6661BBA4689BE51F5B6450D90', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879197F377FA294CA1717E612610DB9A', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Lightly oil a 9 x 5-inch loaf pan and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBB6CDAA4478A45A12A742C9D4E73A9', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 2, NULL, NULL, 2, NULL, 0, 'In a medium-sized mixing bowl, sift together the flour, white sugar, dark brown sugar, baking soda, salt, and cinnamon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388CBBBD9F065743BC5ABB63663166E9', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the soy milk and cider vinegar and let it stand for 2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F5F926E6D8077479ACC50B888611CDB', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 4, NULL, NULL, 2, NULL, 0, 'Add the mashed banana, canola oil, maple syrup, and vanilla extract, whisking until well combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2A5260643E2464FB9A0B4E66DCA3656', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet, mixing until just combined (do not overmix).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'288EA33321E75E43970438A9A3F05D82', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 6, NULL, NULL, 2, NULL, 0, 'Fold in the chopped nuts (if using) and pour the batter into the prepared loaf pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70BFCFAF3424434DA4300863CB9A9396', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 1 hour, or until a toothpick inserted into the center emerges clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D764E8CA8005B64DA1A13D4B77F0831E', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 8, NULL, NULL, 2, NULL, 0, 'Allow the bread to cool on a wire cooling rack for 20 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D61F19124B2354EA8F8E4C061565CF7', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 9, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4870EE7A4F51DA4F946A8E111F7C205C', X'86111A64C9575F4C9843AFB040240583', X'86111A64C9575F4C9843AFB040240583', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E8DFCACE9B86F46A1EE92459660E84D', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50D0355C6C295546BFEFC1FE131FEB75', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 1, NULL, NULL, 2, NULL, 0, 'Line a muffin tin with paper baking cups. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AD2068E58DB39458E2B8D992E9DA359', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 2, NULL, NULL, 2, NULL, 0, 'Melt butter for muffin base, set aside to cool. Combine all of the streusel ingredients together in a small bowl and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B65E83B587FE7F42AD79F2C135BA80A5', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 3, NULL, NULL, 2, NULL, 0, 'Combine cooled, melted butter with sour cream, whole milk, and egg in a large measuring cup. Beat until completely combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42657DF68FE1A4439DB090F7F43085AE', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 4, NULL, NULL, 2, NULL, 0, 'Blend flour, baking powder, sugar, and salt in a mixing bowl. Stir until well-combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56018DCFF48D894EB2FBC7F5F2C59D5B', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 5, NULL, NULL, 2, NULL, 0, 'Gently fold the wet mixture into the dry mixture. Be really careful to not over mix. Just make sure there aren''t any dry ingredients left in the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F865CDFCD75249A03508A8B999C870', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 6, NULL, NULL, 2, NULL, 0, 'Then gently fold in the chopped strawberries and rhubarb.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAEE928B5993774E9A19DD163C98A431', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 7, NULL, NULL, 2, NULL, 0, 'Use a small ice cream scoop or rubber spatula to scoop the batter into the paper lined muffin tins, fill to about 3/4 of the way full.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34501B9C031265489016C879A83992E5', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 8, NULL, NULL, 2, NULL, 0, 'Top each muffin with about a tablespoon of the streusel topping.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ABB11CF49E29142A3F0CA5B12E51342', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 9, NULL, NULL, 2, NULL, 0, 'Bake in the oven for about 20 minutes, or until a toothpick inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'728ECD964561C048AD7DDE8D2DFF7B54', X'7A0F34CA8198384B9373005ABA56278C', X'7A0F34CA8198384B9373005ABA56278C', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F04AE1F38B64F438138E384AB17E06D', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A893FAA7B8874AAA89E60AB2F2DDCB', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 1, NULL, NULL, 2, NULL, 0, 'Heat a cast iron griddle or pan on medium heat before you begin. Make sure it is nice and hot and then lower the temperature before you begin cooking the pancakes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927E00C8D5AAA649946ACE96F509145F', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 2, NULL, NULL, 2, NULL, 0, 'Beat together the egg, milk, and melted butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD6B5FBCE88E844948A76CFB71D8F18', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 3, NULL, NULL, 2, NULL, 0, 'Whisk in the vanilla and coconut extracts and shredded coconut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A15568A70E2D6E4BA17C9523B1EE520B', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, baking powder, and salt in a separate bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A8EDBA7F0EED4FA7360E452E13A9D3', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 5, NULL, NULL, 2, NULL, 0, 'Slowly whisk in the egg and milk mixture. It''s okay for the batter to be a little lumpy; be careful not to over mix. Let it sit for about 5 minutes. The batter will puff up. If it is too thick, add a little more milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AFC55DE0ADB7F43945A0CB41B9D39DD', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 6, NULL, NULL, 2, NULL, 0, 'Grease the griddle. Spoon the batter in about a 1/4 cup portions on the griddle and let it cook for a few minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'059FE6F0B09FC34DA9C857D69E488E7C', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 7, NULL, NULL, 2, NULL, 0, 'Keep the pancakes warm in a 200 F oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02D0B94AAEFF494EB4146F905513A4F0', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the whole milk, powdered sugar, vanilla extract, and coconut extract together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39708C170F46FF4F8BA7174D985FFD36', X'C20285CE60A01F4EA3CCFA73927A3AC6', X'C20285CE60A01F4EA3CCFA73927A3AC6', 9, NULL, NULL, 2, NULL, 0, 'Drizzle the glaze over the pancakes and top them with the toasted coconut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33EEE82A36077E4A8ACE8FA1278B2914', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DB7CF9BEFC77C4F94FF3EFA7F179F90', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 1, NULL, NULL, 2, NULL, 0, 'Heat the milk, butter, and sugar in a saucepan over medium heat, getting it to a simmer, but not a boil. Remove from the heat and let it cool to lukewarm. This may take 10 minutes or so. You can transfer it to the fridge in another bowl if you need to speed up the process.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5DBE1E39399C6499562733126C138C9', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 2, NULL, NULL, 2, NULL, 0, 'Pour the yeast over the top of the milk mixture and stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5F062ED5EFA344EAA0FB4A91C126A21', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 3, NULL, NULL, 2, NULL, 0, 'Add 7 cups of the flour and stir together gently.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB824C635C6F2439A76801505B908DC', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 4, NULL, NULL, 2, NULL, 0, 'Cover with a towel and put it in a warm place to rise for about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A28A867AB7F30499B8A310248177CE4', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 5, NULL, NULL, 2, NULL, 0, 'Add the rest of the flour and the baking powder and stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6084D21E40578419F6B07AC6644C2D0', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 6, NULL, NULL, 2, NULL, 0, 'Divide the dough into two or three large pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A391277770254D9F8B9CA2AD13E463', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 7, NULL, NULL, 2, NULL, 0, 'Roll out one piece at a time into a large rectangle. Leave it relatively thick, about 1/2 inch. Make sure the rectangle is long, width-wise. (Think hot dog, not hamburger.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EBA658EA5E26F46B9F29640106AEA7C', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 8, NULL, NULL, 2, NULL, 0, 'Pour about 3/4 cup of butter over the dough and sprinkle about half the cinnamon and sugar over the top.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C7FC7A4DEA503418FDD47AC1B68EA7D', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 9, NULL, NULL, 2, NULL, 0, 'Top with half of the dried fruits and chopped pecans.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B7A1E137CC79B4FB73EDDCC0DA3B9CC', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 10, NULL, NULL, 2, NULL, 0, 'Roll the dough away from you in a tight roll until it is completely rolled up. Pinch the seam together and then cut into 2-inch slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB06C47D61A33649BE2A5F40B301D2FF', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 11, NULL, NULL, 2, NULL, 0, 'Bring the two ends of the roll together and pinch them together. Gently transfer onto a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E120C7348BEE41AE6B5AE9AA72F060', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 12, NULL, NULL, 2, NULL, 0, 'Cut large slits 3/4 of the way through the dough, make about 10 cuts. Turn each cut section onto its side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FFCF1F7657EF84BB01BFA6D11771E21', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 13, NULL, NULL, 2, NULL, 0, 'Brush the top of the ring with melted butter and cover gently with a cloth. Repeat with the rest of the dough. Let it rise for about 30 minutes. While it is rising, preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCEC432B40606408751D7B86BADB222', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 14, NULL, NULL, 2, NULL, 0, 'Bake rolls for about 25 minutes, do not over bake. They should be lightly brown and slightly firm to the touch. Tent with some foil if it is browning too quickly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C6BEBBFCE459E47ADE811749EB59959', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 15, NULL, NULL, 2, NULL, 0, 'Whisk together the powdered sugar, milk, almond extract, and vanilla extract. If it is too thick add a little more milk, if it is too thin add a little more powdered sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1EB3628908ADD449A44544C45619A0E', X'3DADD0F4022CC342A6E64517B7A5ED20', X'3DADD0F4022CC342A6E64517B7A5ED20', 16, NULL, NULL, 2, NULL, 0, 'Remove rings from the oven and if you can manage let them cool a bit and then drizzle icing over the top, slice and serve!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB19EE9C26B4DF4DB45CB50BEE8EA6C6', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A221A36D08B744AE002A136613E575', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 1, NULL, NULL, 2, NULL, 0, 'Sift the flour to bring in more air and combine with sugar, baking powder, and salt in a large bowl until well mixed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31218B4D368837489816F54940EB5596', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 2, NULL, NULL, 2, NULL, 0, 'Mix in the soy milk or milk substitute of choice and oil and beat together until batter is smooth. If choosing to use the apple cider vinegar tablespoon remember to remove one tablespoon of the milk substitute before adding it to the mix to avoid a runny batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C7CB2AD2E1B3545BAFBF7D690CBEB68', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 3, NULL, NULL, 2, NULL, 0, 'Drop 1/4 cup of batter at a time onto a hot oiled griddle or a well greased frying pan over medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE73015BD54EBA45AB2B357B40B56F7D', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 4, NULL, NULL, 2, NULL, 0, 'When bubbles appear on the surface of the pancake, approximately 3 minutes, flip and cook the other side for another 2 minutes.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B793DE449E3F464392B93CE2F86E2D11', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', X'FE18FCD7C3EAF14C865D0795DAB1B1AE', 5, NULL, NULL, 2, NULL, 0, 'Serve with your favorite toppings and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC4D2549F8D334782649E79960845FF', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02CE52FDD4D3844B9AE1EC6E8186B123', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 1, NULL, NULL, 2, NULL, 0, 'Break the eggs into a wide, shallow bowl or pie plate and beat them lightly with a fork or whisk.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65E4518E174D48499BB9D8A1A2275633', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 2, NULL, NULL, 2, NULL, 0, 'Stir the milk, sugar, and salt into the beaten eggs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48DD8A57871F5B4FA520B5BD4CCCB5E8', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 3, NULL, NULL, 2, NULL, 0, 'Coat a skillet or griddle with a thin layer of butter. Place it over medium-low heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C894A5427427B04F9862DDEEA0932302', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 4, NULL, NULL, 2, NULL, 0, 'Place the bread slices, one at a time, into the bowl or plate. Let the bread soak up the egg mixture for a few seconds and then carefully turn to coat the other side. Coat only as many slices as you will be cooking at one time to prevent soggy French toast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'310AA59390A6584BA0FEE4320EC06552', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 5, NULL, NULL, 2, NULL, 0, 'Transfer the egg-coated bread slices to the hot skillet or griddle. Heat slowly until the bottom is golden brown. Turn and brown the other side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'606046DAA337C14EAFAE755BCD9930B4', X'A95D2DD6F367B34EBCC094078F742A48', X'A95D2DD6F367B34EBCC094078F742A48', 6, NULL, NULL, 2, NULL, 0, 'Serve French toast hot with butter and syrup, or your preferred toppings. Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41366451423FEC409EBB170312E81C7A', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B58C7E244782314993336D011F14B2A8', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 1, NULL, NULL, 2, NULL, 0, 'Generously grease a muffin tin with cooking spray or butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FBEE9F0B3C38345AA5A63002EE0E7BE', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 2, NULL, NULL, 2, NULL, 0, 'Press 2 to 3 tater tots or hash browns into each muffin well so that it completely covers the bottom.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5753F8CBEB92A04ABC2714F0B0F87B15', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the eggs, milk, and salt and pepper in a small bowl or measuring cup. Pour the egg mixture over each muffin well until the hash browns are completely covered.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD9709F557402644A4C3E4D64A7E5FB7', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 4, NULL, NULL, 2, NULL, 0, 'Top each omelet with an equal amount of the thawed, drained spinach and the shredded cheddar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'036CDA33A784734A8E4EF8E35BAD0794', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 5, NULL, NULL, 2, NULL, 0, 'Place in the oven for about 15 to 20 minutes. The top will be golden brown and the egg will be cooked all the way through. The bake time might be longer, depending on your oven!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B71EAD1A0345CF468CE2A0D9047E34BC', X'A9FFEC575906E74EB334DC4A573C499B', X'A9FFEC575906E74EB334DC4A573C499B', 6, NULL, NULL, 2, NULL, 0, 'Let the omelet bites cool slightly and then use a knife separate the omelets from the muffin tin. Serve with hot sauce or a garnish of chives if you wish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAB27052FA6F874D803410AA930BE498', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10980FFB37749448809787C62BFAD20D', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8074B973CDE5E144AEE6ACA0D10D8621', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 2, NULL, NULL, 2, NULL, 0, 'Brush each pita with the olive oil and place directly on the oven rack. Bake for about 7 minutes, or until lightly brown and toasty.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC8E9733288BB5418DA3A4FFF3CBC2F6', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 3, NULL, NULL, 2, NULL, 0, 'Beat together the cream cheese, vanilla extract, and sugar until smooth. If you make sure the cream cheese is softened, it will blend together much easier!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF57A0CF8A18E242B046BA29A8BF0053', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 4, NULL, NULL, 2, NULL, 0, 'Stir together the berries and sugar in a medium-sized bowl. Place in the refrigerator if necessary. You may also add lemon juice to the mixture if you like!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70ADEB236C6F524E94B86670EB79D1D0', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 5, NULL, NULL, 2, NULL, 0, 'Take the pita out of the oven and allow it to cool. Place on a cooling rack so that the pita does not get soggy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D6D15353EB7CF45B686D28F3EE2E027', X'C780268B919B734BAF227A7D6309F5E9', X'C780268B919B734BAF227A7D6309F5E9', 6, NULL, NULL, 2, NULL, 0, 'Spread the cream cheese mixture onto each pita and top with an even portion of the berry mixture. Sprinkle with powdered sugar and garnish with mint and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E41685219BE924EBA37E31D61BEC98C', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B68876E33815CA4496A6B253EAFEA2FB', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 1, NULL, NULL, 2, NULL, 0, 'In a heavy skillet, spread the sugar in an even layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86DAA5429A7C9E4095AE258430D7D2ED', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 2, NULL, NULL, 2, NULL, 0, 'Cook it over medium heat until it starts to melt and turn brown. Slowly swirl the pan or use a silicone or wooden spatula to gently fold the liquifying sugar from the edges into the center.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CECEAB994CB8F48B6FFF65CDD70C2E0', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 3, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat as soon as the sugar becomes an amber liquid. Reserve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2B09870A7A9C34584735BFFF4D4ABBD', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 4, NULL, NULL, 2, NULL, 0, 'Add the brown sugar to a heavy saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47A4AD19BD8AE4586D39D6EB1DD3ED2', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 5, NULL, NULL, 2, NULL, 0, 'Pour the boiling water over it and let it dissolve without stirring.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF09EC322B8234DACA3DD93114EF61C', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 6, NULL, NULL, 2, NULL, 0, 'Add the caramelized sugar to the melted brown sugar in the saucepan. Simmer, stirring frequently until the mixture thickens to a syrupy consistency.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E4282848ED80B41AEF381B833C20AFF', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 7, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and whisk in the butter and maple or vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F4DE671B75E240BEED915405452B72', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 8, NULL, NULL, 2, NULL, 0, 'Use immediately, or let cool before storing in an airtight container.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3447DF692920214C8D9CA1A4A0E08B37', X'8A76A64E2ABF30468CE1FF8E0CF77134', X'8A76A64E2ABF30468CE1FF8E0CF77134', 9, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25C6F3A400C15640B93B4C984708FD25', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CE4223783F53342AE24F7C65D02D3A5', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the eggs, milk, and salt and pepper together. Grease a casserole dish and pour the egg into the dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E204760C04EDC84AB8F4BF9435A0C97F', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 2, NULL, NULL, 2, NULL, 0, 'Place the breakfast sausage into another casserole dish. Flatten completely until it is one even layer. Place both baking dishes in the oven. Bake for about 15-20 minutes or until completely cooked. The sausage should be about 170 F in order to be completely cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E020B8C2489E0D478EFCACACD37A13E4', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 3, NULL, NULL, 2, NULL, 0, 'Slice the rolls so that you have a top and a bottom.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC616E4DEE15674EA516EEF2E70034A2', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 4, NULL, NULL, 2, NULL, 0, 'Lay the cooked sausage over the bottom of the rolls, top with the cooked egg and the slices of cheese. Put the top of the rolls back on and place in the oven again. Once the cheese has melted, take the rolls out and cut them into the sliders.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAE88DE6AA46264DA47ED5345EC527EC', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', X'6D6A74D8EB6C2B4FAD435F222DEE51B8', 5, NULL, NULL, 2, NULL, 0, 'You can also wait to bake them and wrap them individually and freeze them. Reheat them individually later.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DDC7A3D3DE4174490AB88C5662F3A24', X'F87ACF1B78A76B43AA69D5BDEFEFF910', X'F87ACF1B78A76B43AA69D5BDEFEFF910', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the pie filling, whole milk, and maple syrup in a medium sized bowl until smooth and completely combined. Make sure there aren''t any lumps of the pie filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A611E95019CE224798FB5FFA64E55728', X'F87ACF1B78A76B43AA69D5BDEFEFF910', X'F87ACF1B78A76B43AA69D5BDEFEFF910', 1, NULL, NULL, 2, NULL, 0, 'Stir the oats and any of the optional ingredients into the pumpkin pie mixture. Cover the bowl or put it the oatmeal in small glass jars with lids. Place them in the refrigerator overnight, or for at least 4 hours to soften the oats.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0FAD2D3D4B7343BB78F70985B7DFAE', X'F87ACF1B78A76B43AA69D5BDEFEFF910', X'F87ACF1B78A76B43AA69D5BDEFEFF910', 2, NULL, NULL, 2, NULL, 0, 'After it has sat overnight heat in the microwave if you wish, or just eat cold! Oatmeal will keep in the refrigerator for 3 or 4 days.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36B9B49A3540B644BDD36BA83F638C58', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38EF43D1359F2845908441F4F2FB73DC', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 360 F and prepare a 12-cup muffin tin either with liners or by lightly coating it with nonstick spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BAF1C8FCC7C8F4081A65D08B5B14F52', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, mash the bananas with a fork until soft.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E47E0BEECE5A441A6B3136A8D80E1CC', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 3, NULL, NULL, 2, NULL, 0, 'Add the oil or vegan margarine and sugar; mix until creamy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C4E82564EA39340B7DE8AE2E9EC4274', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine the flour, salt, and baking soda until well mixed. Add the flour mixture to the banana mixture, stirring gently just to combine. Don''t overmix. The batter will be thick.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78DE7A34002E1445BF5490DA7494F756', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 5, NULL, NULL, 2, NULL, 0, 'If you''re using the walnuts, now is the time to gently fold them in. Alternatively, you can sprinkle them on top of each individual muffin before baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3692F14E0CDD72488CD84F7A1B2F9216', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 6, NULL, NULL, 2, NULL, 0, 'Carefully spoon the batter into the muffin tins, filling each well about 2/3 full. If desired, sprinkle the organic sugar on top just before baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45BE012D6A1DB34A94608B0F7EBEF815', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 7, NULL, NULL, 2, NULL, 0, 'Bake the muffins for about 25 minutes or until a toothpick comes out clean. Let cool a few minutes and enjoy while still warm or cool completely for longer storage.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DC3E17B0A84D445840FEE111E5AF340', X'2949336758908C45B3E584E5680C5BD7', X'2949336758908C45B3E584E5680C5BD7', 8, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF129C37523FA24AAAEFFCBB382FC677', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', 0, NULL, NULL, 2, NULL, 0, 'Add the fresh cranberries to a small saucepan. Turn on the heat and add the granulated sugar and water. Stir to combine and allow to come to a boil. Once the cranberries are soft (about 5-10 minutes), turn off the heat. Let cool to lukewarm. Strain the cranberries over a fine mesh sieve. Allow the cranberry mixture to completely cool before making the cocktails.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA33FB6EB4DBC647BF7F0B7DCCD30CA0', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', 1, NULL, NULL, 2, NULL, 0, 'Stir the fresh cranberry juice, lime juice, and vodka in a large pitcher. Slowly add the ginger beer and gently stir. If making single serve use about 2 ounces of vodka, 1 Tablespoon of fresh lime juice and top with ginger beer!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CC218F9748B27418833A6A872FE78A3', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', X'8AF2AF6B9069BC4CBD188B6F1E6A0540', 2, NULL, NULL, 2, NULL, 0, 'Make sure to serve over ice in copper mugs! They not only look pretty but keep the cocktail super cold! Garnish with lime wedges and fresh cranberries.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B211D7541EB90B49B3967E8345CACFD3', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'189B73D131C66F46A959C4E2EBFBB6F5', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 1, NULL, NULL, 2, NULL, 0, 'Add the fresh cranberries to a small saucepan. Turn on the heat and add the granulated sugar and water. Stir to combine and allow to come to a boil. Once the cranberries are soft (about 5 to10 minutes), turn off the heat. Let cool to lukewarm.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3B7251B75673E4FA529A0F25106BF78', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 2, NULL, NULL, 2, NULL, 0, 'Strain the cranberries over a fine mesh sieve. Allow the cranberry mixture to completely cool before making the cocktails.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B1F0E3739F3834CAE8BFE70EAA1EB01', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 3, NULL, NULL, 2, NULL, 0, 'To make the drinks, shake or stir together the cranberry juice, lime juice, tequila, and orange liquor in a cocktail shaker filled with ice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D7DDC81AF006147B0E2F2B1A5EA458C', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 4, NULL, NULL, 2, NULL, 0, 'Salt or sugar the rim of the glasses.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1552F6F22C3BF7449EC422DF81B71376', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 5, NULL, NULL, 2, NULL, 0, 'Add ice to each glass and strain the cocktail over the top of the ice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A03D31F9E568C14F855903DE2918323D', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 6, NULL, NULL, 2, NULL, 0, 'Garnish with fresh cranberries and lime wedges.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CD2A57EF563F045BDF7F246C377E881', X'63F0634B161EBA458964987B1381DF18', X'63F0634B161EBA458964987B1381DF18', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0623ED08776FA84D97177524F8A93AF6', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DE14184AB09D04C8B2302B8560C9C3A', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 1, NULL, NULL, 2, NULL, 0, 'Sift together the flour and baking powder in a large mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02D83EA7BFBAE449C56F9CF80C873CB', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, beat the eggs thoroughly, then stir in the milk, sugar, and salt and mix until the sugar is dissolved. Stir in the vanilla.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFCB105D14526846A25B2BCC7134DA61', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 3, NULL, NULL, 2, NULL, 0, 'Add the wet ingredients to the dry and use a fork to gently and thoroughly combine all of the ingredients. Make sure not to overmix—you''ll still see some lumps. Gently break up some of the bigger lumps with your fork, but don''t mix for much longer than 20 seconds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'956469CE23C14C47B0B8912076DEF7A8', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 4, NULL, NULL, 2, NULL, 0, 'Allow the batter to rest at least 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AF623DB38AEBE4BBB8936F485D62F81', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 5, NULL, NULL, 2, NULL, 0, 'If making bigger batches, preheat the oven to 170 F to keep the pancakes warm while you cook the entirety of the batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D0C577587E472408DDBF0F4DB14AB1D', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 6, NULL, NULL, 2, NULL, 0, 'Preheat your griddle to 375 F, or heat a nonstick skillet over medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80BCDAE5B516FF409B47F72D3ECFED9A', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 7, NULL, NULL, 2, NULL, 0, 'When your cooking surface is hot, lightly spray it with cooking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A263BD2C1629874C857BE3146B5FE8F6', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 8, NULL, NULL, 2, NULL, 0, 'For each pancake, ladle 1/4 cup of batter onto the griddle or pan. Cook until the tops of the pancakes are full of bubbles and the bottoms are golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'975248CF1D13AD4296AB9933D749A3BF', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 9, NULL, NULL, 2, NULL, 0, 'Flip the pancakes and cook.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C9A5AFF2FC894C9B8DC6D9ABC6C513', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 10, NULL, NULL, 2, NULL, 0, 'Either serve right away or transfer to the oven to keep warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E41CE215F3FAD947AC28A1E023885D61', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 11, NULL, NULL, 2, NULL, 0, 'Top with the optional butter, fruit and maple syrup, or any toppings of your liking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B60CE8FF170A944BB2B180F7F7FA36D', X'924175D0641FE14E80F44EBB2F8B2B5B', X'924175D0641FE14E80F44EBB2F8B2B5B', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D80E2E7D0993D4FAC2ADF0B736B071A', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D162E9B628FA42A3E8FCE4A877F2D7', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0CE2F9E2984D4CB8BF7C7861D6974C', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 2, NULL, NULL, 2, NULL, 0, 'Sift together the flour, baking powder, salt, and sugar in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D7A099347162438C117F8AF54D1C65', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 3, NULL, NULL, 2, NULL, 0, 'Make sure you have cooled your melted butter to room temperature (oil may also be substituted). Beat together the egg, milk, vanilla, lemon zest, lemon juice, and melted butter together in a mixing cup until completely combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C93FF796785014DAA9B7184B89A84E0', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 4, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients with a whisk.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FC0C72770B64E4396DFE76574BB87D5', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 5, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients into the well, mixing as you pour. Completely combine the ingredients until you have a batter, be careful not to over-mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEEFB7127074F44E8E6D5B6B9BE0C442', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 6, NULL, NULL, 2, NULL, 0, 'Carefully fold in the raspberries. Be gentle when folding in the raspberries so they do not completely break apart in the batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA0E575045D05341AD5D7349378CC960', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 7, NULL, NULL, 2, NULL, 0, 'Line a muffin pan with paper cups, or grease the muffin wells with butter or canola spray. Fill the tins to 2/3 of the way full. You can also top the muffins with sparkling sugar before baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE41EEF7D7A4F43AE85C79912BCCC4E', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 8, NULL, NULL, 2, NULL, 0, 'Bake the muffins for 25 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2E1E6A00366FD438AA3D850FA842355', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 9, NULL, NULL, 2, NULL, 0, 'Allow them to cool slightly and then slide a knife around the edge of the muffins to release them, if you are not using paper cups.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8CA532D6F37494D8970808B7B9045B3', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 10, NULL, NULL, 2, NULL, 0, 'To make the glaze, whisk together the powdered sugar, vanilla, lemon juice, and milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C4EDE9445364A46899EF6913588A150', X'DFAC56C6A60F2144B9C6F09756D21567', X'DFAC56C6A60F2144B9C6F09756D21567', 11, NULL, NULL, 2, NULL, 0, 'Drizzle the lemon glaze over the warm muffins and serve!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB8909A579EAEA4DB215AB8D26E8C147', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C4D094BCE3E20418C971BC9DBC3EB6C', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the grapefruits.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00A2C759AB6D1B4686BADF293E565170', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 2, NULL, NULL, 2, NULL, 0, 'Use a sharp vegetable peeler or paring knife, remove the brightly colored zest–and only the brightly colored zest–from the grapefruit. Be sure to leave behind any and all of the white pith directly underneath, it is very bitter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A9126D7AB609F42AA98CE8FB9F9F406', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 3, NULL, NULL, 2, NULL, 0, 'Chop the resulting zest–bigger pieces for chunkier marmalade, ribbon-like strips for a more spreadable result. Set zest aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'847C15F58BEC2D41ACFFBBACF02662CE', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 4, NULL, NULL, 2, NULL, 0, 'Cut the ends off the zested grapefruits.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D7AA8C8ECC67F4AB1C4EA5C89104D89', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 5, NULL, NULL, 2, NULL, 0, 'Working with one fruit at a time, cut off the thick white pith from around each grapefruit.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D364F450FA7742BE36B4FDE4F0D8FC', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 6, NULL, NULL, 2, NULL, 0, 'Working over a bowl to catch the juices, hold a fully peeled grapefruit, and use a sharp knife to cut out each section of the membrane holding the sections together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B7FD8EE4C44D64CA64FAD310F1D5E07', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 7, NULL, NULL, 2, NULL, 0, 'Squeeze any juice out of the membrane once you''ve cut out all the fruit.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA85B47E231C9744A77F65BE95D785F9', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 8, NULL, NULL, 2, NULL, 0, 'Set the membrane aside, along with any seeds. The pectin in these will help `set` the marmalade later.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21F94579E1D3B94A8E5C831FABD1504A', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 9, NULL, NULL, 2, NULL, 0, 'Combine the zest, fruit, juice, 4 cups of water and sugar in a large heavy pot and bring to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F36BCA707B63A45AD48E24A0094B3A6', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, lay a double layer of cheesecloth in a medium bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D960003FBB0B34C9C4E806E0F88F28F', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 11, NULL, NULL, 2, NULL, 0, 'Put membranes and seeds in the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'003D5C6AF592154E82D7A25626E2B2F7', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 12, NULL, NULL, 2, NULL, 0, 'Lift up the corners and tie the cheesecloth into a bag to hold the membranes and seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82AA3E8347E8C74AA2958C0B605C6956', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 13, NULL, NULL, 2, NULL, 0, 'Add this `pectin bag` to the pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EE904E9AB48BC449618868F50F3C4DD', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 14, NULL, NULL, 2, NULL, 0, 'While the mixture comes to a boil, put a canning kettle full of water on to boil if you''re planning to can the marmalade.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C12DB504CE39F847993927BD8DB90AFC', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 15, NULL, NULL, 2, NULL, 0, 'In any case, put a few small plates in the freezer to chill them. When the canning kettle water boils, use it to sterilize the jars and lids.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'954B6A4EE23B8C4A8A55D869F7D0BBB8', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 16, NULL, NULL, 2, NULL, 0, 'Bring marmalade to 220 F and hold it there for 5 minutes. Be patient, this can take quite a while.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E420B92E761DF3449BD6A3A2901E745E', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 17, NULL, NULL, 2, NULL, 0, 'Put a dollop of the mixture on a chilled plate, swirl the plate to spread the mixture a bit, and drag your finger through the mixture. A `set` mixture will hold a clean track behind your finger.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60160A6CCE640D48B717F0EE9F929806', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 18, NULL, NULL, 2, NULL, 0, 'Remove `pectin bag,` squeezing any marmalade in it out and back into the pot before discarding the bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D187A6C300411C41B1D44379B566C49D', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 19, NULL, NULL, 2, NULL, 0, 'Take marmalade off the heat and let sit 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F7D3368E1F29A4CB0B2DE9F172F4458', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 20, NULL, NULL, 2, NULL, 0, 'Set up clean jars next to the pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'517066A16F739C4C89251522C21EE1F9', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 21, NULL, NULL, 2, NULL, 0, 'Stir marmalade to distribute the zest evenly in the mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD91764087CBD4FAD4B3CF6D3379051', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 22, NULL, NULL, 2, NULL, 0, 'Use a ladle to transfer the marmalade into the jars, leaving 1/2 inch of headspace at the top of each jar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F69AB2A07C5248BE805E4006CA896E', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 23, NULL, NULL, 2, NULL, 0, 'Put lids on the jars.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A644D048F8FB9489CD1C72555E9D967', X'AB0A9B46D801674D89CF9DE4878E44EB', X'AB0A9B46D801674D89CF9DE4878E44EB', 24, NULL, NULL, 2, NULL, 0, 'If you''re canning the marmalade, put the jars in the canning kettle and boil for 10 minutes. In any case, let jars cool to room temperature before putting in a cool dry cupboard (if you''ve hot water processed them) or the fridge (if you didn''t hot water process).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80DECE4696B9CC4F8D30A0614DF2F1D0', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC395EB1A756684DB7D04D6E6E4EE6E4', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 1, NULL, NULL, 2, NULL, 0, 'Combine all dry ingredients and butter in a food processor, pulse until it forms fine crumbs. If you don''t have a food processor use a pastry blender or two forks. Be careful not to over mix or to use your hands. You want the little chunks of butter to stay intact and not melt from the heat of your hands.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D781DEBA56A76340A114ECDFFC4381E3', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 2, NULL, NULL, 2, NULL, 0, 'Add buttermilk as you pulse or mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F32A37CD614064BA5391280A373CED4', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 3, NULL, NULL, 2, NULL, 0, 'Place the dough in a clean bowl. Cut in the shredded cheddar and cooled, cooked sausage until completely incorporated.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'860BA778A1A87F4BBA53B106C72F28F8', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 4, NULL, NULL, 2, NULL, 0, 'Separate the dough into 12 balls. Form quickly and gently with your hands so that it is rounded. Place the balls of dough snuggly into a 9 x 13 casserole dish. Brush the tops of each biscuit with melted butter if you wish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C0A2D288C0DF47AD8580657F592400', X'3E8FEE51D3C9E446B3043EFEA2458EC2', X'3E8FEE51D3C9E446B3043EFEA2458EC2', 5, NULL, NULL, 2, NULL, 0, 'Place the casserole dish in the oven and bake for about 15 to 20 minutes or until golden brown and cooked all the way through. Serve immediately or allow to cool and cover in aluminum foil and then freeze.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A1B9E3463A50A4796DC2EF642CD728B', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FD4E6C3254196499EC4A16D02DB6764', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984D51059703B94887155D7E5B07D68C', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 2, NULL, NULL, 2, NULL, 0, 'Sift together the dry ingredients in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AAD7F926EDF184FAB7EA3EE1D721535', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 3, NULL, NULL, 2, NULL, 0, 'Make sure you have cooled your melted butter to room temperature (oil may also be substituted). Beat together the egg, milk, sour cream, vanilla, marmalade, and melted butter together in a mixing cup.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F6271F1F7FA5049BB612A891C4940BE', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 4, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients. Pour the wet ingredients into the well, mixing as you pour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DF231459F02504CB17068F14F1BA82C', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 5, NULL, NULL, 2, NULL, 0, 'Completely combine the ingredients until you have a batter, be careful not to over-mix. Mix in the cranberries. As it sits, it will thicken slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C78BD97306B52E43A360A99C5D035DFF', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 6, NULL, NULL, 2, NULL, 0, 'Line a muffin pan with paper cups, or grease the muffin wells with butter or canola spray. Fill the tins to 2/3 of the way full. You can also top the muffins with sparkling sugar before baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E1A7A666A8AA9419C95CCA717D05B80', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 7, NULL, NULL, 2, NULL, 0, 'Bake the muffins for 25 minutes. Slide a knife around the edge of the muffins to release them, if you are not using paper cups.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F34A95BD23B8BF4E979DC60DAC90E57C', X'80D3ABE2A97C1E4592502B3DB1786306', X'80D3ABE2A97C1E4592502B3DB1786306', 8, NULL, NULL, 2, NULL, 0, 'Serve while still warm!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11C4E322FE7D8A4A8BDCA2AB3E3F01BE', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 0, NULL, NULL, 2, NULL, 0, 'In a 12-inch nonstick ovenproof skillet, melt the butter with the olive oil over medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5964BAEE49D42C4F8338E3722E8C0E1A', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 1, NULL, NULL, 2, NULL, 0, 'Add the onions, garlic, broccoli, and cauliflower. Cook over medium heat, stirring occasionally with a heatproof spatula until the vegetables are crisp-tender. Then add 2 tablespoons water to the skillet, cover, and steam for 2 minutes, at which point the vegetables should be tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C98F7922DCE3D14A93FF85E44576CF6A', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 2, NULL, NULL, 2, NULL, 0, 'Arrange the pasta on top of the vegetables. Pour the eggs over all and sprinkle with the thyme, salt, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A38965C511A324CA3DA633AA800F7B9', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 3, NULL, NULL, 2, NULL, 0, 'Cook the frittata, lifting the edges occasionally to let uncooked egg flow underneath and wiggling the spatula in the center of the frittata so the egg can flow under and cook evenly until the bottom is lightly browned and the frittata is almost set. Shake the pan occasionally so the frittata doesn''t stick.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F186101F44C7D479004F9E4EA18E647', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 4, NULL, NULL, 2, NULL, 0, 'Heat the broiler. Place the frittata under the broiler, about 6 inches from the heat source, and broil for another 4 to 8 minutes or just until the egg mixture is set. Remove the skillet from the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F5E6AD744C8F6418C460410302ABFD7', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the frittata with both kinds of cheese. Put it back under the broiler. Broil for 3 to 6 minutes longer, rotating the skillet occasionally so it cooks evenly until the cheese is melted and begins to brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFCF53F1F3CFD642A77258E77D9684AC', X'4FD7B595D64D7B4683442C83F10390A6', X'4FD7B595D64D7B4683442C83F10390A6', 6, NULL, NULL, 2, NULL, 0, 'Cut the frittata into wedges and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46C00A84AA959E49B7F403AFAAA274A9', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450 F');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9798920987FED74EB4BC7ED5CFCE69FD', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 1, NULL, NULL, 2, NULL, 0, 'Beat the eggs and milk together until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF11DE12CCE1A4DBD840DC537C87009', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 2, NULL, NULL, 2, NULL, 0, 'Add the flour, sugar, lemon zest, and vanilla and mix until there aren''t any lumps. Mix completely and quickly, trying to form a froth if possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE733A0A67102045BA1EE856FD4C1DDD', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 3, NULL, NULL, 2, NULL, 0, 'Place a skillet in the oven with the butter while it heats.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D769A6EAFC8AE4A8A52C4E4B538EADD', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 4, NULL, NULL, 2, NULL, 0, 'When butter is melted, pour the batter into the skillet. Bake for about 15 minutes until fluffy, golden, and not jiggly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76CA4D5CB905D64FA7CB2C14832F3337', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', X'D7FB0DB2AA413F4AA02FD92D8B96E3A7', 5, NULL, NULL, 2, NULL, 0, 'Whisk together the ingredients for the glaze. Pour the glaze over the hot dutch baby and serve immediately. You can also top with more powdered sugar if you wish!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F397CC569CD654C941C5D929BEFEE9B', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 0, NULL, NULL, 2, NULL, 0, 'Heat the bourbon in a small saucepan for about two minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ABBB97062B09445AB463BCDC4024ED2', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 1, NULL, NULL, 2, NULL, 0, 'Remove from the heat and set aside. Butter and flour a 9x5 inch loaf pan or line it with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0B5B61FE615F14A9EE6B14842C7B0EF', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F. Whisk together the flour, baking powder and salt in a medium-sized bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0B0A6BFCCCDB44A8050D6F50211B9FA', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 3, NULL, NULL, 2, NULL, 0, 'In the bowl of your stand mixer (or a large bowl) mix together the melted butter and sugar. Beat well until blended. Beat in the eggs one at a time. Then add in the mashed bananas, cooled bourbon, and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E9D82451C63344F85D83FD56F047ACE', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 4, NULL, NULL, 2, NULL, 0, 'Add the flour mixture in thirds to the wet mixture. Stirring gently after each bit. DO NOT OVER MIX.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44332F9FEB643744B25E871658CF8ADF', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 5, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the prepared loaf pan and bake on a middle rack in the oven for about one hour. Bake for less time if you are baking on convection.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A74A5B23DFA04A81EB3BFDD2A10751', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 6, NULL, NULL, 2, NULL, 0, 'Check its doneness with a toothpick. It should come out fairly clean, but it is ok if it looks a little sticky. This is a very dense and moist bread!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A44D12C08722646B168700ECBD2C491', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 7, NULL, NULL, 2, NULL, 0, 'Place the loaf pan on a cooling rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256F8438BCDE56469CADC1BCDD017E0D', X'28EA8E534A32A948A6D169F8796C4EF6', X'28EA8E534A32A948A6D169F8796C4EF6', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the bourbon and powdered sugar until thick and smooth. Once the bread is cooled and removed from the pan, drizzle the glaze over the top of the bread. The bread tastes even better the second day!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B45B16952A8054F919D50445A41F5E6', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7304E70271F19146AC83D9136125DEAD', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34BE4A1B673B16439CE4621E23BC3271', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the flour, cocoa powder, sugar, baking powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E9AB284C72B84BBF60D58CD2CE5568', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the milk, and the melted butter. Then add the oil, whisk in the beaten eggs, and stir in the vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'976CDA1B313DA540B840041BFC547F19', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 4, NULL, NULL, 2, NULL, 0, 'Thoroughly grease and flour a muffin pan (or better yet, you can use paper muffin liners).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A243C553249D6249A0E7E6C97C870CAC', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 5, NULL, NULL, 2, NULL, 0, 'Add the liquid ingredients to the dry ones and mix no more than 10 seconds. The batter should be visibly lumpy. It''s extremely important not to over-mix the batter, or the resulting muffins can be hard or misshapen.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79D3EF3E4ED14F43BDC582AA433A6D46', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 6, NULL, NULL, 2, NULL, 0, 'With a rubber spatula, gently fold 1 cup of chocolate chips into the batter. Don''t overwork the batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A369BB952E70F94F9FC939F72CF43CB2', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 7, NULL, NULL, 2, NULL, 0, 'Carefully spoon the batter into the prepared muffin pan (you can use a small ladle or even an ice-cream scoop).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D12714863706D40A24061A061877B7C', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the remaining chocolate chips onto the tops of the muffins, and transfer to the oven immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9498E935CCB8F7488C0A46B6E5EE8DEB', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 9, NULL, NULL, 2, NULL, 0, 'Bake 18 to 20 minutes or until a toothpick inserted into the center of a muffin comes out clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'745A66C73FD3694DA63CC4A6C2D3FF90', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 10, NULL, NULL, 2, NULL, 0, 'Remove the pan from the oven and let the muffins cool for five minutes in the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7431BB372B57604C9BCC8E41E03A2CED', X'2AB3DF4C5B70A240B9B7053126918720', X'2AB3DF4C5B70A240B9B7053126918720', 11, NULL, NULL, 2, NULL, 0, 'Then remove the muffins from the pan and let them cool another five minutes on a wire rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6BAA45CE614EC4BAAEF00D45F97C783', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F16F0A1FB4C8748A2734E092D07E68C', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 1, NULL, NULL, 2, NULL, 0, 'Grease a 9 x 12-inch casserole dish with cooking spray. If making to serve immediately, then preheat the oven to 375 F. If you are refrigerating the french toast overnight, then skip preheating the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5C6E5A80D3DB142ADD09A0984B04645', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 2, NULL, NULL, 2, NULL, 0, 'Cut the panettone into large slices and layer them into the greased casserole dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C502FECE30CB644ACAB419DEB688F95', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the eggs, milk, sugar, cinnamon, vanilla, and orange extract in a medium mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC5A45A16992074F8A806D474557EB69', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 4, NULL, NULL, 2, NULL, 0, 'Pour the egg mixture over the top of the panettone. Make sure to coat all of the slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869563928D1F7748930CFCA22348FB24', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 5, NULL, NULL, 2, NULL, 0, 'Place in the oven uncovered and bake for about 25 to 30 minutes. Let it cool slightly when you remove it from the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33DD12023189434AA0E72EA903823339', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle it with a little powdered sugar and a drizzle of real maple syrup.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01861C2D2300374EAD0CDDEB20F7DFE6', X'8F20B725EB22864797F8912562972080', X'8F20B725EB22864797F8912562972080', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF364B9688499B48818E27A001A1C3CF', X'A31959619ABD6E43B1A2B9D8E3024508', X'A31959619ABD6E43B1A2B9D8E3024508', 0, NULL, NULL, 2, NULL, 0, 'Cook the breakfast sausage according to package directions. Make sure it is completely cooked and that there is no pink, about 170 F is appropriate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F6D363EE691324A89F925C0AB9D1F74', X'A31959619ABD6E43B1A2B9D8E3024508', X'A31959619ABD6E43B1A2B9D8E3024508', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the eggs, milk, salt, and pepper. Cook in a nonstick skillet as for an omelet or scramble them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4379A9516E23E6429EC0FE97C8A94FE5', X'A31959619ABD6E43B1A2B9D8E3024508', X'A31959619ABD6E43B1A2B9D8E3024508', 2, NULL, NULL, 2, NULL, 0, 'Spread out the tortillas onto a baking sheet. Place an equal portion of the sausage and egg on each tortilla.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'094F6CF939272644808C2B1B61A3878D', X'A31959619ABD6E43B1A2B9D8E3024508', X'A31959619ABD6E43B1A2B9D8E3024508', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle an equal amount of shredded cheese on top of each tortilla. Place under the broiler of your oven to melt the cheese. Drizzle each burrito with hot sauce, taco sauce, or salsa.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BC1DC0EA34CFB44A243F1DB699BEB4D', X'A31959619ABD6E43B1A2B9D8E3024508', X'A31959619ABD6E43B1A2B9D8E3024508', 4, NULL, NULL, 2, NULL, 0, 'Roll up the burrito and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D57CD47813D3248A32499D3C6563933', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6D13632B2A6F7438D34C00409B2994E', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 1, NULL, NULL, 2, NULL, 0, 'Heat the milk with the butter until the mixture begins to simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7D172775554B4D8A2C5BE17B0D633D', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 2, NULL, NULL, 2, NULL, 0, 'Combine the flour, cornmeal, sugar, baking powder, and salt. Whisk to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'458C703B6067464488ACF9DA044CF727', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 3, NULL, NULL, 2, NULL, 0, 'Add the hot milk mixture to the dry ingredients and stir to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68BDAB372450D64083232B45E64DAF4A', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 4, NULL, NULL, 2, NULL, 0, 'Whisk in the beaten egg.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8031F03385DE5840A0915E07E7033B2C', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 5, NULL, NULL, 2, NULL, 0, 'Drop onto a hot, greased griddle or drop in an iron skillet and fry until golden brown on both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE3F31C850485459F68898A1EAD30A8', X'BDBDBC06CB335C4598CE5E8C88B7FC52', X'BDBDBC06CB335C4598CE5E8C88B7FC52', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with butter and syrup as for pancakes, or serve them as bread with butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DC8E05DA55F1D468DC378A661C8571F', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DCB5B5A08B70442ACD51FE86CCAA39F', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200 F — this is to keep them warm as you make more batches. Skip this step if you are serving them as they come out of the waffle iron.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020B56520A1A744B80ABFA25E5E7A816', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 2, NULL, NULL, 2, NULL, 0, 'Heat the waffle iron on a medium setting, following the instructions in your waffle iron brochure or manual.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E118C209C4BE304AA3A216954FB55D37', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine the flour, baking powder, salt, and sugar. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACE8C5037BE3A14AB2D85232E5097171', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 4, NULL, NULL, 2, NULL, 0, 'In another bowl, combine the eggs and milk; whisk until well blended. Whisk in the 1/4 cup of melted butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAE0FA41A0775542BAD50E05967B5C29', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 5, NULL, NULL, 2, NULL, 0, 'Add the egg and milk mixture to the flour mixture and blend thoroughly. Stir in the corn kernels. If necessary, add more milk to make the batter thin enough to flow when poured into the waffle iron.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C99648FDDF0A38439803EC34CF466251', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 6, NULL, NULL, 2, NULL, 0, 'Following the appliance directions, fill the waffle iron with the batter, gently spreading as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13999377CE5C4B44BFC02B61351B9840', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 7, NULL, NULL, 2, NULL, 0, 'When the first batch is done, remove them to a baking sheet and place them in the preheated oven to keep warm while you cook subsequent batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CB69693A2598C4A9049EA756179DD5A', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 8, NULL, NULL, 2, NULL, 0, 'To serve, drizzle the corn waffles with melted butter and maple syrup or dust them with powdered sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B3A4171756EB4478FF35A8CB96C5E78', X'7AF3080CDB953642B8D0C3DAC154B394', X'7AF3080CDB953642B8D0C3DAC154B394', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E11DC5612A7A9345A8C4F06E9C497842', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D5204F5A0EA9B479256A54063104793', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 1, NULL, NULL, 2, NULL, 0, 'Beat eggs with cream in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0468D7DA95F894F9166F33C43FF1072', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 2, NULL, NULL, 2, NULL, 0, 'Melt butter in large skillet over medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A23E6832B9BC48A8E8F43D8BC2A723', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 3, NULL, NULL, 2, NULL, 0, 'Pour eggs into skillet and cook, stirring frequently, until eggs are scrambled and just set, but still moist.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7ECB36DEA67184992B156F356E54CCC', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 4, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and stir in sour cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'426D805A1DF05343905D38C5B6F8A385', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 5, NULL, NULL, 2, NULL, 0, 'Spread eggs into a greased 7- x 12-inch glass baking dish and sprinkle with shredded cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020ED9B73D16674CABC3911D0FB876BC', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 6, NULL, NULL, 2, NULL, 0, 'Cover tightly and refrigerate overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EEA896ADB08514590702609105196D0', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 7, NULL, NULL, 2, NULL, 0, 'When ready to eat, preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C750AF169B723A499BEED8B71E8173A0', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 8, NULL, NULL, 2, NULL, 0, 'Uncover the baking dish and sprinkle eggs with pepper and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4A46656E949F4A8C9932F26280531F', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 9, NULL, NULL, 2, NULL, 0, 'Stir gently, then bake for 20 to 30 minutes or until cheese is melted and eggs are hot, at least 145 F on an instant-read thermometer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6B72A77FF2D54E8B273330913E8A3E', X'35B91D792B58EF43A880594CF02FC6A0', X'35B91D792B58EF43A880594CF02FC6A0', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33FA0D4ACA9A94781C17B494B956BC7', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 0, NULL, NULL, 2, NULL, 0, 'Pulse together the flour, shortening and butter, and salt in a food processor until small crumbs form. You can also do this by hand with a pastry blender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'424B8BFE977D3F4B817BF6D75A0A26D4', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 1, NULL, NULL, 2, NULL, 0, 'Gently mix in the beaten egg to the flour mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1AE50D1AB8CCD4A9B40EC5F54CC32D8', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 2, NULL, NULL, 2, NULL, 0, 'Mix vinegar with ice water. Then slowly (Very slowly and very gradually) pour water mixture into food processor while pulsing. Stop when the dough forms large soft crumbs and pulls away from the side of the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'146AEC898F0BEB48B3A0362311C91CAF', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 3, NULL, NULL, 2, NULL, 0, 'Remove dough from food processor and divide in half. Gently form the dough into a ball. Place on a piece of saran wrap and cover. Gently flatten with your hand so it makes a short disk. Repeat with the other half of the dough. It is really important not to overwork the dough, because it will become tough very easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FFE7FB0B61994C83680E9779F3BA80', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 4, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator for an hour (30 minutes works if you''re short on time!).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5BC4D79A2D1540986B4BBB178B1192', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 5, NULL, NULL, 2, NULL, 0, 'Roll one ball of dough into a long rectangle. Leave the other ball of dough in the refrigerator so it will stay cold while you work with this dough. Cut into eight even rectangles.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6039F43657492D498656C5F9B4413567', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 6, NULL, NULL, 2, NULL, 0, 'Place four of the rectangles on a parchment paper-lined baking sheet. Spread about a Tablespoon of jam onto each rectangle, leaving a nice little border around the edges. Lay a rectangle over each jammed rectangle. Use a fork to crimp the edges together. Then pierce the top of each pop tart about four times with the tines of a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E5EA005B4657488FA243CD01800675', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 7, NULL, NULL, 2, NULL, 0, 'Repeat with the other ball of dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACD39E9FCB1C7241AD15B84DDEC65BF8', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 8, NULL, NULL, 2, NULL, 0, 'Flash freeze the pop tarts in the freezer for about an hour (or longer if need be).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B8102660C38D43A327919EA49DD356', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 9, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Bake the pop tarts for about 25 minutes, or until very lightly brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1FE12DD8528FF4098658EBE93952BC6', X'B28991D5253C964C9756DD352C70E3A2', X'B28991D5253C964C9756DD352C70E3A2', 10, NULL, NULL, 2, NULL, 0, 'Whisk together the powdered sugar and milk. Spread the frosting over the cooled pop tarts and sprinkle with sprinkles if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E46EEEA254FB4488F3E35AAA73D30A', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and equipment.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7250C519E954549A6296405F4F418E2', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Position the cooking rack in the middle of the oven. There will be little to no splattering as the bacon cooks.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'271550816D02624B8EB691BA46562E35', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 2, NULL, NULL, 2, NULL, 0, 'Line the jelly roll pan with the tinfoil to catch all of the grease, so you can just lift off the tinfoil and throw it in the trash when you''re done.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C02170CEB61F44F8C9A85DCB5DDF9B5', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 3, NULL, NULL, 2, NULL, 0, 'Place a cookie cooling rack on top of the foil lined sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42DC4CEFFC7C424D8DDCE02DB0FAC441', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 4, NULL, NULL, 2, NULL, 0, 'Lay out the strips of bacon onto the cooling rack. The strips can touch as they will shrink when cooking. You may have to lay a few pieces of the bacon width-wise as well as length-wise.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E56D4A2C8A4724899B88EEAE618188B', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 5, NULL, NULL, 2, NULL, 0, 'Place the jelly roll pan in the oven on the middle rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16907DDA95E8E6418DE08026371AB5EA', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 6, NULL, NULL, 2, NULL, 0, 'Cook for about 10 to 12 minutes or until the bacon is cooked to your desired crispiness. It should lift off of the cooling rack easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42B3BD971F63D84686A9A17E9A71D9D9', X'18D2C624AED792498946F51F24449D79', X'18D2C624AED792498946F51F24449D79', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86C68DB93991154694C36FFA059F9CE4', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D4C230D81674E46A2F76515EC080DD5', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 to 2 inches of water in a saucepan over medium heat. Also, make sure your clarified butter is warm but not hot.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84471439D3B1DD4F93B612E33F82ABBF', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 2, NULL, NULL, 2, NULL, 0, 'Combine the egg yolks and the cold water in a glass or stainless steel mixing bowl (not aluminum) and whisk for 1 to 2 minutes, until the mixture is light and foamy. Whisk in a couple of drops of the lemon juice, too.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ABCBAC81913C04CB6AE89A6CEFAD92E', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 3, NULL, NULL, 2, NULL, 0, 'The water in the saucepan should have begun to simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BF66B01D0434442B87D1ABC445BE4B7', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 4, NULL, NULL, 2, NULL, 0, 'Set the bowl directly atop the saucepan of simmering water. The water itself should not come in contact with the bottom of the bowl. Whisk the eggs for 1 to 2 minutes, until they''re slightly thickened.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09CBEDDCB582594FA513314A45CEDAF4', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 5, NULL, NULL, 2, NULL, 0, 'Remove the bowl from the heat and begin adding the melted butter slowly at first, a few drops at a time, while whisking constantly. If you add it too quickly, the emulsion will break.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E3E878694EE9E4EA6B4FE48CAC8D1AC', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 6, NULL, NULL, 2, NULL, 0, 'Continue beating in the melted butter. As the sauce thickens, you can gradually increase the rate at which you add it, but at first, slower is better.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'133160E0EB20054A973084C1AA37BCB6', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 7, NULL, NULL, 2, NULL, 0, 'After you''ve added all the butter, whisk in the remaining lemon juice and season to taste with kosher salt and cayenne pepper (or a dash of Tabasco sauce).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4308168FB1C842A962FAF00A44B88F', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 8, NULL, NULL, 2, NULL, 0, 'The finished hollandaise sauce will have a smooth, firm consistency. If it''s too thick, you can adjust the consistency by whisking in a few drops of warm water.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBA82B4E1DDFB748ACFA2E07B628F92B', X'CAF8C5163F23A544A15F64AD673F26D2', X'CAF8C5163F23A544A15F64AD673F26D2', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD54EB15FA8EE48A5D3B9F9FA0CE3FC', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A283488C971C143A070687E5973DB82', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, gently mix the yeast with the warm water and let sit until frothy, about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBE322D730508B4D8F9E91FE1618A7A3', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a medium-sized bowl using an electric hand mixer, cream the dairy-free soy margarine and sugar until the fluffy, about 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'651F2F7BAC07E34A88617C06CF7B2D5A', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 3, NULL, NULL, 2, NULL, 0, 'Add the salt, beating until incorporated. Add the warm almond milk (or another non-dairy milk alternative) and Egg Replacer mixture and beat until combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AFC660F464B024AA2D1099E0265EB8C', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 4, NULL, NULL, 2, NULL, 0, 'Turn the hand mixer onto a low-speed setting and add the yeast mixture. Once the mixture is combined, add the flour, 1 cup at a time, until the dough is soft, but not sticky.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C2783EDDB6EB74EBC45F9E3006D3C7E', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 5, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a lightly floured surface and knead until elastic.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D74AFC687A4E204FBD460F178A889FC5', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 6, NULL, NULL, 2, NULL, 0, 'Place the dough in a lightly oiled bowl. Cover the bowl loosely with a dishtowel or plastic wrap and allow the dough to rise in a warm place for 1 1/2 hours. (We like to set our bowl over a small heatproof dish of hot water to help the dough proof. The bowl itself should not be hot, so we like to heat water in a kettle until steaming, pour about 1 to 2 cups of water in a deep but narrow dish, and set our dough bowl on top.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7D159FF29F7F0479084D5141C783E4C', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small bowl, mix the dark brown sugar and cinnamon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE99FA61898A74E89F1764595CFD71C', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 8, NULL, NULL, 2, NULL, 0, 'Grease (with dairy-free soy margarine) or oil a 9”x13” casserole dish. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'845BD831A2CD7340B167CA2955B6C001', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 9, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Punch down the dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05A4A4A7AB42F5448FEA1B1BAB465216', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 10, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a dry surface and roll the dough out into a 12-inch square.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07DBE3978F517F44B0425A4B0B665EC0', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 11, NULL, NULL, 2, NULL, 0, 'Brush with the melted soy margarine and then sprinkle with the cinnamon-sugar mixture, followed by the raisins, nuts or other desired toppings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FF36B650359FA40AE65F8C2BA235964', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 12, NULL, NULL, 2, NULL, 0, 'Carefully, using your hands, roll the dough into a log. Pinch together the edges to seal.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56DEF1D091737848BD18770644E91347', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 13, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, slice the log into 8-12 equal pieces. Arrange the pieces in the prepared dish and allow the rolls to rest until doubled in size, about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'658BC22F7C29984C96EE8866A44E4830', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 14, NULL, NULL, 2, NULL, 0, 'Bake the rolls in the preheated oven for about 18 to 22 minutes, or until golden brown. (A toothpick inserted into the center of one of the rolls should emerge clean when they''re ready to come out.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47E199DF8ED3946BC2DD0A9B4ED541A', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 15, NULL, NULL, 2, NULL, 0, 'Set the rolls on a wire cooling rack to cool slightly before icing with dairy-free icing.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'468ECE98A37DD041813D97C4C8B5107E', X'335A499C43A67E43A12225C6C1578BCC', X'335A499C43A67E43A12225C6C1578BCC', 16, NULL, NULL, 2, NULL, 0, 'Serve warm and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6774E9C44F9BD544B6EE26C98717B746', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBB30233C92231418347261A9C5B9089', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 1, NULL, NULL, 2, NULL, 0, 'If your loaf is very large (i.e. Pullman) then slice off the top 2 inches to make the loaf more square than rectangular. If your loaf is average-sized, slice 1/2-inch to an inch off the top.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66A7F21618C6D643BD4731B8CD1454FE', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 2, NULL, NULL, 2, NULL, 0, 'Slice the loaf in half lengthwise, Open the two halves like a book, so the interior of each half is facing you.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CF9AA92EC7274AB0925ED9792E5983', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 3, NULL, NULL, 2, NULL, 0, 'Place a baking sheet on the middle rack in the oven, and preheat to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF21475CFD5A1649A8EFFF36B2BB1061', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 4, NULL, NULL, 2, NULL, 0, 'Using a spoon, dig an indentation about 1/2-inch deep along the crust edges of each half, then remove a shallow layer of the bread''s interior by gently pulling it out towards you with your fingers. This will give the cheese a small well to melt into. Reserve the scooped-out bread to make breadcrumbs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27EFD6BF7BDBEA42B7A45CABE8E838B3', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 5, NULL, NULL, 2, NULL, 0, 'Cut a sheet of parchment paper that will fit both halves of the loaf and place it on your work surface. Flip both halves of the bread over so the exteriors are facing you.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3043915E32FE740BBD19725DB15CA5E', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 6, NULL, NULL, 2, NULL, 0, 'Generously spread the tops of the loaf halves (not the interior section with the scooped-out wells) with mayonnaise, then flip over once more and place both halves mayonnaise-side down on the parchment.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B0D7F3330A0D4F92BA9041F83386B2', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 7, NULL, NULL, 2, NULL, 0, 'Layer the cheese slices into the scooped-out wells in each half: American first, then cheddar, then mozzarella.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F33F8C593F04143B2E5ADCF5DB0C2B7', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 8, NULL, NULL, 2, NULL, 0, 'Remove the baking sheet from the oven, then quickly transfer the halves from the parchment to the baking sheet, using a spatula if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0822ABB35D3EC14C895EDAC7252928AC', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18-20 minutes, or until cheese on top of both halves is bubbly and browning.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B2524042DC2414381773D7B967F9460', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 10, NULL, NULL, 2, NULL, 0, 'Remove the baking sheet from the oven, then carefully flip the top half of the bread onto the bottom to make a sandwich, and transfer the whole thing to a cooling rack. Let rest for 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F084C2D9CA289B4683B9E24BBD391361', X'BC03FBCA6CA994479A380DCAEDCC534C', X'BC03FBCA6CA994479A380DCAEDCC534C', 11, NULL, NULL, 2, NULL, 0, 'Make the first slice in the middle (and check out that cheesy pull), then let rest for another 5-10 minutes before slicing individual sandwiches out from the middle of each half of the giant grilled cheese. This will ensure even distribution of cheese within each slice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07A01ADC9BC9964680E02207F12D3247', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C90B43BD56CE64EA0F2542E5D3A1B11', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 1, NULL, NULL, 2, NULL, 0, 'Place a trivet in the Instant Pot inner pot and add 3/4 cup of water or stock.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25575114D8239B4B8C88AEEB50FB52E1', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 2, NULL, NULL, 2, NULL, 0, 'If the chicken wings are whole, cut them at the joint and cut off the wing tips. Discard the wing tips or freeze them to use in stock. Sprinkle the chicken wings with kosher salt and arrange them on the trivet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBD2BE6577CCBB41A63EA43B6BC0F6EB', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 3, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release knob to the sealing position. Choose the manual or pressure cook button (high pressure) and set the timer for 10 minutes. When the time is up, let the pressure come down for 5 minutes and then carefully turn the knob to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5C60F0B4F47C428DFB050FC7E6A861', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 4, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the hot sauce, butter, vinegar, black pepper, garlic powder, and cayenne pepper (if using). Bring to a simmer and cook for 1 minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A032509DD225374898C3648C857B44DF', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 5, NULL, NULL, 2, NULL, 0, 'Drain the wings and transfer them to a bowl. Add about half of the sauce mixture and toss them gently.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17DF4CAC3D6B044499DC538B66B4DC05', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 6, NULL, NULL, 2, NULL, 0, 'Arrange the wings in a single layer on the prepared baking sheets. Broil about 4 to 6 inches from the heat source for 5 minutes. Turn the wings and continue to broil the other side for 4 to 5 minutes longer, or until golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A5567E2616324FAA1721F3125754B0', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 7, NULL, NULL, 2, NULL, 0, 'Remove the wings to a bowl and drizzle with the remaining sauce mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31C64E90FF21F648BD5C988704C4746A', X'57D191FECEFA054F8841C2B270F34253', X'57D191FECEFA054F8841C2B270F34253', 8, NULL, NULL, 2, NULL, 0, 'Serve Buffalo wings with celery and carrot sticks along with blue cheese or ranch dressing for dipping.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93BC49505EC11740B572CAF1368429B1', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46C8B7B3BC8B294FAEE728811C2F4F70', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 1, NULL, NULL, 2, NULL, 0, 'Slice the onions into thin half moons.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35C10A8889EE004CA506FC9F75D7D99B', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large sauté pan on medium high heat. Add the sliced onions and salt. Saute on medium low heat, stirring occasionally for 25 to 35 minutes or until the onions are deeply caramelized. They will be so soft that they can easily be mashed with the back of a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7C4DA49473094EA0E5EFDD9E404F05', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 3, NULL, NULL, 2, NULL, 0, 'Remove the onions from the pan and allow them to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17C0B4AA742C4E48AF797AA13AAEFEF3', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 4, NULL, NULL, 2, NULL, 0, 'Add in the minced shallots, chopped chives, and reserved caramelized onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F624AE0553A3C94AB1891FB0F496D5B1', X'37CB42A0A0CA5F439703823F9531C8F5', X'37CB42A0A0CA5F439703823F9531C8F5', 5, NULL, NULL, 2, NULL, 0, 'Serve with potato chips or crudité immediately or cover it with plastic wrap and store in the refrigerator for up to 4 days.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC561AB68461E49ADC49D1901CD5645', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BE595827E8FB944BB13B66DA1D556DE', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 1, NULL, NULL, 2, NULL, 0, 'Pat the wings dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE63C29F042B7E4BAF7D438C69BD2173', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 2, NULL, NULL, 2, NULL, 0, 'Spread them out in a single layer and sprinkle with the salt, pepper, garlic powder, and cayenne pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73A7DB79E7F9654DB3DDFFA99D201F9E', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 3, NULL, NULL, 2, NULL, 0, 'Place all of the wings in the air fryer and set for 380 F. Cook for 25 minutes. Pause the air fryer and toss the wings every five minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34F7B15EB230C74D9D0CEDEA7502A4F7', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 4, NULL, NULL, 2, NULL, 0, 'Turn the heat up on the air fryer to 400 F and cook for another 5 to 10 minutes until crispy and browned. You can cook the wings in a single layer in two batches for even more crispiness.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECDDD6D7D718334DA1F489E61E5EEB9A', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 5, NULL, NULL, 2, NULL, 0, 'To make the sauce, melt the butter in the microwave for one minute or until melted. Whisk in the garlic powder and hot sauce until completely combined. Place it back in the microwave for one to two minutes to heat thoroughly. Make sure the sauce is at least warm before you toss it with the wings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF6881632980D5448ACC76B29AF680AC', X'C98EC1F7C19971409C21B50E6016F928', X'C98EC1F7C19971409C21B50E6016F928', 6, NULL, NULL, 2, NULL, 0, 'Add the wings to a large bowl. Drizzle the sauce over the top and toss to coat. Serve the wings immediately topped with chopped chives and alongside your favorite dipping sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093DFCF663015A44B48859C6C41715E9', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients for the tots.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EB891EF9DE3A0469A2D0D20C5AD3B22', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 1, NULL, NULL, 2, NULL, 0, 'Spread the tater tots over an aluminum-lined baking sheet. Bake in a 425 F oven until golden brown, about 25 minutes.Turn halfway through baking to make sure they are crispy on all sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DF30B3241D52C488D589B8EFA7BE712', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 2, NULL, NULL, 2, NULL, 0, 'Gather the ingredients for the chipotle queso.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'957929B717B3E14B98CE67F6A4CF4B02', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 3, NULL, NULL, 2, NULL, 0, 'While the tots are baking, heat the queso in a small crockpot or in the microwave until smooth and completely melted. Stir the queso frequently until it has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A17C53407890A48B6864BDA6EE52572', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 4, NULL, NULL, 2, NULL, 0, 'Layer the pepper jack cheese over the crispy tots and place the pan back in the oven until the cheese has melted and is browned slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C13CF25FFB38048891FD379E1AFB825', X'D2C680AEC808824EA5197FBB8596F1A8', X'D2C680AEC808824EA5197FBB8596F1A8', 5, NULL, NULL, 2, NULL, 0, 'Heat up your taco or al pastor meat if needed. Spread the meat over the totchos and then top with some of the chipotle queso, onions, jalapenos, scallions, and more pepper jack if you want them extra cheesy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E7C8BA66FF7BE4C81FBE0556556A14C', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCB7CB6B5D469459F806A35EFA7BEA3', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 1, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until completely smooth and fluffy. Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C01853FFF05F840A99AF065A0291964', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 2, NULL, NULL, 2, NULL, 0, 'Add the chopped scallions, garlic, sour cream, mayonnaise, crab, salt, and pepper to taste, and 1 tablespoon of sweet chili sauce to the bowl of whipped cream cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB1DED03ADED8447858B943E7C53F1AB', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 3, NULL, NULL, 2, NULL, 0, 'Fold the ingredients together in the bowl with a spatula until completely combined. Break up the crab as finely as you like.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E52750B44A60B844BBCF363F7B3A565C', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 4, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a greased baking dish. You can decide what size dish you want. An 8-inch round casserole will work well, but choose the shape you like best.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCE27627D87933489BE8464E3C6330DF', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 5, NULL, NULL, 2, NULL, 0, 'If you are adding the mozzarella cheese, sprinkle it over the top of the dip before popping it in the preheated oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A3AF76FE1F9594BBA544C6348923606', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 6, NULL, NULL, 2, NULL, 0, 'While the dip is baking, prepare the wonton chips. Cut the wrappers into triangles or chip like shapes. Using egg roll wrappers work just as well as wonton wrappers and they are often a little thicker.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10324007AA2C974D877B5A9F6BAB3F6B', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 7, NULL, NULL, 2, NULL, 0, 'Heat the oil to the deep frying temperature of 375 F and monitor it with a frying or candy thermometer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E077A23BCDFB4B99E9F66953A38E5D', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 8, NULL, NULL, 2, NULL, 0, 'Add the wrappers into the hot oil in batches of 4 or 5 at a time, depending on the size of your pot. Flip the wontons quickly so that they are lightly golden and fried on both sides. Drain them on a paper towel-lined plate or baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E8BE8780FE974DA58B29651A0D866C', X'BF679A40B41FB2488F378ADFD474AFA0', X'BF679A40B41FB2488F378ADFD474AFA0', 9, NULL, NULL, 2, NULL, 0, 'Take the dip out of the oven when the cheese is brown and the dip is heated through the center, about 20 minutes. Drizzle the remaining sweet chili sauce over the top of the dip. Then top with more chopped scallions. Start dipping and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B13A9BF19B8DA345866C0DA1D3D9C8FA', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9892CFE12B977D4DA4EB69191F271355', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 1, NULL, NULL, 2, NULL, 0, 'Cook the bacon bits in a frying pan until brown and crispy. Drain on a paper towel and set aside. Leave the bacon grease in the pan and set it aside as well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'387D41BEBFFDCD4CAE00F67B1343627E', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 2, NULL, NULL, 2, NULL, 0, 'Leave about half of the bacon grease in the frying pan. Add the breadcrumbs to the pan and toast over low heat until evenly browned. Shake the pan frequently to prevent the breadcrumbs from burning.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2C8641878A9F649B3BC6AC3D2753624', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 3, NULL, NULL, 2, NULL, 0, 'Add the cream cheese, jalapenos, scallions, cheddar, salt, and garlic powder to a large bowl. Make sure the cream cheese is soft enough to stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9397917E3F7F4CBD4DB23EB83D2DA4', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 4, NULL, NULL, 2, NULL, 0, 'Mix together the ingredients with a spoon or hand mixer until they are completely incorporated and evenly distributed. Taste to make sure it''s salty and spicy enough to your liking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3911BD3E5F9824438B3748DF6F623FD7', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 5, NULL, NULL, 2, NULL, 0, 'Form the cream cheese mixture into a ball and then flatten the top and pinch the sides into a football shape. Using slightly damp hands will prevent the cream cheese from sticking to your hands too much.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC65075D42A0894BB08651EC0481A05B', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 6, NULL, NULL, 2, NULL, 0, 'Cover the cheese ball with plastic wrap and place it in the refrigerator for 20 minutes to firm up slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'822386C392BF3C43924A6FEFB4E56A8E', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 7, NULL, NULL, 2, NULL, 0, 'Cover the cheeseball with the toasted breadcrumbs and then press in the bacon bits.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0CDEBA7E47974E90C4FB3071F3C42D', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 8, NULL, NULL, 2, NULL, 0, 'Cut the cheese into strips to make the football laces. Place the smaller strips horizontally with a half inch in between them. Then lay the longer strip vertically over the horizontal strips.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82E64B5CE871E247B8460214A882ED11', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 9, NULL, NULL, 2, NULL, 0, 'For a more vibrant look (and so people know it''s spicy!), add the chopped fresh jalapenos and some scallions to the top of the football.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF92701173A9F844B078702CF2497FD6', X'ACF70B15C0DC1341BBE945C01CEAB390', X'ACF70B15C0DC1341BBE945C01CEAB390', 10, NULL, NULL, 2, NULL, 0, 'Serve with pretzels and crackers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C507C5B2F11D584EA2817943C3B3FDD8', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12ED4F747FB2F74182C6073139CBE0D6', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 1, NULL, NULL, 2, NULL, 0, 'Add the flour, yeast, salt, water, and one tablespoon of the olive oil to a bowl and stir until it becomes a shaggy dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED653319656EC647AF59BAB0AB2B3A62', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 2, NULL, NULL, 2, NULL, 0, 'Use your hands to help the dough come together into a loose dough. Place the dough in a oiled bowl and allow to rise, covered, for one hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9910EFF77911E341A3ED5A8257BDBB10', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 3, NULL, NULL, 2, NULL, 0, 'If you are making smaller rolls, divide the dough into two to four balls. If you are not, you can use the dutch oven method below to create the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8905B648ACE217448964FAA3BB0F3A1D', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 4, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 475 F. Place a medium sized dutch oven in the oven to heat for 15 minutes. Form the dough into a single, tight ball and place it on a piece of parchment paper. Transfer it to the hot dutch oven and bake, covered for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7618D5ED1473454EBD72E86A92F20F85', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 5, NULL, NULL, 2, NULL, 0, 'After 30 minutes, remove the lid and bake uncovered for an additional 15 minutes, or until browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F19F376A596C124EACD0AFC3BF26CEE7', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 6, NULL, NULL, 2, NULL, 0, 'Allow the bread to cool for ten minutes. Then cut a lid off of the top of the loaf and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E3D617DF742584DA6556FC7C32F75CF', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 7, NULL, NULL, 2, NULL, 0, 'Dig out the inside of the bread to remove most of the soft bread. Cut the top of the bread lid into cubes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A529AEA97166CB4BA5E6ACC66A6CCA25', X'9CAD9071C636F343BB2EB500AC76E820', X'9CAD9071C636F343BB2EB500AC76E820', 8, NULL, NULL, 2, NULL, 0, 'Brush the inside of the bread bowl with the remaining olive oil and bake again for 10 minutes to toast the inside of the bread bowl. Do this especially if you want to serve soup inside the bread bowl. It will keep the soup from making the bread bowl too soggy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'520CB87755D023409077F41E1EC1CCA3', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5184ABF4428D0044AFC69B3F092786D1', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 1, NULL, NULL, 2, NULL, 0, 'Chop the tomatoes. You may want to remove excess seeds and pulp.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AEE6DC49895734BB981E9BE534070E8', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 2, NULL, NULL, 2, NULL, 0, 'Chop the fresh basil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A83B85C55E7C1245A2691EE985F51528', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 3, NULL, NULL, 2, NULL, 0, 'Combine the tomatoes, oil, basil, and salt in a covered bowl and let marinate at least 4 hours. Do not refrigerate as the tomatoes will lose their flavor in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F192797939141047994B2018C1049AAB', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 4, NULL, NULL, 2, NULL, 0, 'Immediately before serving, toast the bread slices lightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'761E46068202DC4AA948ADD50CF0F58D', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 5, NULL, NULL, 2, NULL, 0, 'Use a slotted spoon to layer the tomato basil mixture onto bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DB55FAFB53AB948B67CD1797DB43E61', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', X'8CBD6A5ED1FBE44CA0750CCB27E8D7A9', 6, NULL, NULL, 2, NULL, 0, 'Crack fresh pepper over the top. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4B2EB51C16C664E98D29480A1FC12F7', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C2F8DC8699C0A41A3659C037A2FB8E6', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 1, NULL, NULL, 2, NULL, 0, 'Pour about 2 inches of the canola oil to a heavy bottom skillet or cast iron pan and heat to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A781413121790A48B9E85FC8E005E96E', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 2, NULL, NULL, 2, NULL, 0, 'Add the egg white to a small bowl and set it aside along with a pastry brush.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D414000511128645A2ED741C195B82B1', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 3, NULL, NULL, 2, NULL, 0, 'Finley dice the corned beef and the Swiss cheese and combine in a bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E3238A14963534AA15DF1B3BAD1EA80', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 4, NULL, NULL, 2, NULL, 0, 'Drain the sauerkraut into a fine mesh strainer and squeeze it as dry as you can. Add it to the bowl with the diced corned beef and Swiss cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'981CC937F7971841B9812F3DEF5E3480', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 5, NULL, NULL, 2, NULL, 0, 'Place each egg roll wrapper with one of the corners facing you so it looks like a diamond.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7795491363CE848AFEC601246F345DB', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 6, NULL, NULL, 2, NULL, 0, 'Spread a few tablespoons of the filling in a horizontal line. Be sure to leave about one inch of empty space on either side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'546D2A365182C745B7A47053F554F792', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 7, NULL, NULL, 2, NULL, 0, 'Roll the corner that''s closest to you over the filling and tuck it slightly under the filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D499DD72C732134CA442E2CF290AC64C', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 8, NULL, NULL, 2, NULL, 0, 'Brush the two sides with the egg white (you can also use your finger) and fold the left and right corners in.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82F05035A4E0A4C87AC463FEDBC9A74', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 9, NULL, NULL, 2, NULL, 0, 'Fold forward one more time, brush the remaining flap with the egg white, and seal shut. (Most egg roll wrapper packages will have a handy illustration on the back of how to fold the roll.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDE4E374A2EA4647BC4E364C08A6313C', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 10, NULL, NULL, 2, NULL, 0, 'Fry the egg rolls in batches (don''t overcrowd the pan) for a minute or two on each side until golden brown. Remove to a paper towel-lined plate and sprinkle with a bit of sea salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D76CF8E2ECF70143BA1249A150B61CF5', X'CE2D88EDC097784FA32407BF64C4C6F9', X'CE2D88EDC097784FA32407BF64C4C6F9', 11, NULL, NULL, 2, NULL, 0, 'Serve with store bought Russian dressing or make your own by combining mayonnaise with ketchup.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24F3F4E7887EE944A3F3ABA0B8AAAD7E', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'730DDEDFC6B6C34B8E8C1E80FC383F19', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the mayonnaise, ketchup, horseradish, garlic powder, paprika, oregano, salt, black pepper, and cayenne pepper. Stir to blend. Cover and refrigerate until serving time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20B40F387EA1144B977DE586E8D5EFA4', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 2, NULL, NULL, 2, NULL, 0, 'Cut the top off the onion and remove the outer peel.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F0F717ACE78CC4A99BC53C9662E9FA0', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 3, NULL, NULL, 2, NULL, 0, 'Place the onion on a cutting board, cut-side down. With a sharp knife, make a cut about 1/2-inch from the root.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2CD79E645203E46AEA7FD679F895EC5', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 4, NULL, NULL, 2, NULL, 0, 'Make three more evenly spaced cuts around the onion. You will have 4 evenly spaced portions. Make sure you leave the root intact.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FFCE88664A71848B801FDB64FAEDF89', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 5, NULL, NULL, 2, NULL, 0, 'Make 2 to 3 cuts in the center of each quarter. You should have 12 to 16 cuts, depending on the size of the onion.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EB39C05098A3047AF6D4EFD6BA9A940', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 6, NULL, NULL, 2, NULL, 0, 'Turn the onion over and separate the petals slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A978B9405C52CC449CBBA2766ECAEC43', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 7, NULL, NULL, 2, NULL, 0, 'Place the onion in a bowl of ice water and let stand for 1 to 2 hours. Remove the onion to paper towels to drain.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BB0D72D20867F42ACAA1AB6294B431E', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 8, NULL, NULL, 2, NULL, 0, 'Place the onion in a bowl and sift the flour over it while gently separating the petals. You might have to do this a few times to get good coverage. Gently turn the onion upside-down to shake off any excess.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7470CBAAF6124FA51A432A87F79551', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 9, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the eggs with the buttermilk. Place the onion in a bowl and pour the egg mixture over it, gently separating the petals. Remove the onion to a wide, shallow bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD98D3237CBE74E91DFAED52EA599D1', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 10, NULL, NULL, 2, NULL, 0, 'Combine the breadcrumbs and Old Bay seasoning; sprinkle over the onion petals.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD9F2A2F885654DBD0DEE027E65E1CA', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 11, NULL, NULL, 2, NULL, 0, 'Set the air fryer temperature to 375 F and preheat the following the manufacturer''s directions. Spray the tray with cooking oil spray and add the onion, root-side down. Cook for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50A3A1B8213320448BDBD3A158490E44', X'788E422F84A6A84787E8E4EF062EA98B', X'788E422F84A6A84787E8E4EF062EA98B', 12, NULL, NULL, 2, NULL, 0, 'Serve the blooming onion immediately with the dip.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A90FDD08A7DFC419C379D4DB83A3A3B', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A922F1A9D3F6154FBAC4FC498D6B4A6D', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 1, NULL, NULL, 2, NULL, 0, 'Peel 1 pound shallots. Trim both ends and separate the cloves. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38BEABBA6726DB47B0C35BE45E5A95A1', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 2, NULL, NULL, 2, NULL, 0, 'In a wide, nonreactive saucepan (stainless steel or enamel but not aluminum), combine 1 1/2 cups white-wine vinegar, 1/2 cup sugar, and 1/2 teaspoon salt. Bring to a boil, stirring to dissolve the sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'449B74B4B38DE24A8D1A9158705D1ACC', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 3, NULL, NULL, 2, NULL, 0, 'Add the peeled shallots and stir to coat evenly. Cook briskly for exactly 1 minute and remove from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B990F3BF2D78F544A56F0596514FC03F', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 4, NULL, NULL, 2, NULL, 0, 'Submerge the sprig of savory in the pickling liquid. Allow the mixture to stand until it cools to room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBD6C5F5554354EB83B7BECFC899E85', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 5, NULL, NULL, 2, NULL, 0, 'Transfer to an impeccably clean or sterilized glass bowl or jar with a lid.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A657213A7FE7264AAC83672A46A70F5E', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 6, NULL, NULL, 2, NULL, 0, 'Cover the bowl or cap the jar and refrigerate for at least 1 week before tasting. Shallots will keep this way for up to 2 months.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC8F1E18B6706B4D988F66B50C1D9A42', X'3E4B42F25D885F47A8B6BAA83A09693D', X'3E4B42F25D885F47A8B6BAA83A09693D', 7, NULL, NULL, 2, NULL, 0, 'Serve as an appetizer with wooden picks, or use as a salad component or part of a relish tray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5AED0818BA30241B707DC1A5CB9363A', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD6E8C461F59F49A2952F03B2D47A59', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas or charcoal grill to high heat. Ideally, the grill should hit 500 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F393F5F8DD1C35489DB4AB167724F444', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 2, NULL, NULL, 2, NULL, 0, 'In a small saucepan, gently melt the butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9EC75CE1306784F85568FCD6CB14810', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 3, NULL, NULL, 2, NULL, 0, 'Mix the melted butter with the garlic, pepper, and oregano. Reserve in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC5C01A5712DBB4D99453983B9A35FFF', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 4, NULL, NULL, 2, NULL, 0, 'Place the oysters on the half shell right over the hottest part. The oyster shouldn''t be cut loose from the shell as they can slip and fall right through the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5C9A6B64A8FBE4BA7B205552E40F151', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 5, NULL, NULL, 2, NULL, 0, 'Spoon enough of the seasoned butter over the oysters so that some of it will overflow into the fire and flame up a bit.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6808BED8DFE8A44AB4E5BDEC624D89F6', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 6, NULL, NULL, 2, NULL, 0, 'The oysters are ready when they puff up and get curly on the sides, about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E557912CA76244880BA33007BCAC0EB', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the grated Parmesan and Romano cheeses. Reserve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'980B94BB4E0BC347B431A172D066C4FA', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 8, NULL, NULL, 2, NULL, 0, 'Top with the cheese mix and parsley.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D3AE59DD4FFD44381B0F44CB60CA1B0', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 9, NULL, NULL, 2, NULL, 0, 'Serve on the shells immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1435BEDC6C57AF4A94E5EB6403CD168B', X'8E320C458402F941989D7B9B2D40742F', X'8E320C458402F941989D7B9B2D40742F', 10, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B911F5C4E79C3642B6B7F362DAB2B8AE', X'AB309450E9D92140B03D6E2B5744A366', X'AB309450E9D92140B03D6E2B5744A366', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEC70995B7F7AB4AAC20A91B9D7868DD', X'AB309450E9D92140B03D6E2B5744A366', X'AB309450E9D92140B03D6E2B5744A366', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine cream cheese, mayonnaise, milk, salt, garlic powder, and cayenne pepper or Tabasco sauce, to taste. Blend well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3BA82123DE664588133F3A9E9F6625', X'AB309450E9D92140B03D6E2B5744A366', X'AB309450E9D92140B03D6E2B5744A366', 2, NULL, NULL, 2, NULL, 0, 'Gently fold in the chopped crabmeat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A5A1490D08CF941B3A3BC082D9954F6', X'AB309450E9D92140B03D6E2B5744A366', X'AB309450E9D92140B03D6E2B5744A366', 3, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate the crab dip until it is thoroughly chilled.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8636C44A70BD164295CAFC6C6F19407B', X'AB309450E9D92140B03D6E2B5744A366', X'AB309450E9D92140B03D6E2B5744A366', 4, NULL, NULL, 2, NULL, 0, 'Serve this creamy crab dip with chips, crackers, or raw vegetable dippers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2139EE04169A62459859CBFCA8B0FADE', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 0, NULL, NULL, 2, NULL, 0, 'In a bowl combine cream cheese, drained crushed pineapple, chopped bell pepper, chopped green onion, seasoned salt, and 1 cup of the chopped pecans. Mix well to blend ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'849339D79AB30B40859EA2BA539CA35F', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 1, NULL, NULL, 2, NULL, 0, 'Chill the cream cheese mixture in refrigerator until it''s quite firm and then shape into a ball. Return it to the refrigerator until serving time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433A1DA1A264BE4AA8F468B824D4AEFB', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 2, NULL, NULL, 2, NULL, 0, 'Put the remaining chopped pecans in a container. Cover and set aside until serving time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E96D8E1FD5F01A4986743C926C449D46', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 3, NULL, NULL, 2, NULL, 0, 'Just before serving, roll the cheese ball in the remaining cup of chopped pecans.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F2A0DC8BEA6BC4485AA09050682DA94', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 4, NULL, NULL, 2, NULL, 0, 'Serve with assorted crackers or with simple baked crostini slices (see below) or croutons. It would be a delicious spread to serve with slices of ham as well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78CD09F4E8BB1E4DBC683F2723574984', X'0234996F498E044BBF6F75B443AAB7FB', X'0234996F498E044BBF6F75B443AAB7FB', 5, NULL, NULL, 2, NULL, 0, 'Makes about 3 cups of pineapple cheese spread.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79F553E726FBBE40AC774A5B48C33756', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E157D27EE10AD4F97E3A07AEABB0B4C', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB823A10501A58468334EADE325A2AE6', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', 2, NULL, NULL, 2, NULL, 0, 'Slice pita open so that one side is smooth and the other side is rough. Place open pita rounds, with the rough side up, on a cookie sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'863F177906BFDD4287A05082A889425E', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', 3, NULL, NULL, 2, NULL, 0, 'Mix oil and zaatar in a small bowl. A pinch of salt can be added if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04FA18287DC9634BBDBD9B016962D9EC', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', X'D59CD5AFE8052C4FB82053E6CD7D4BE0', 4, NULL, NULL, 2, NULL, 0, 'Brush the rough side of the pita rounds with the zaatar and oil mix.');
