INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B632A706A4EE5943A65F37A605F3B6ED', X'A2F112BCDCEC1543B16A57AA5334CF91', X'A2F112BCDCEC1543B16A57AA5334CF91', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51D588D707B99A479270E54FD03A4233', X'A2F112BCDCEC1543B16A57AA5334CF91', X'A2F112BCDCEC1543B16A57AA5334CF91', 1, NULL, NULL, 2, NULL, 0, 'If you''ve got a food processor, you can process all the ingredients for a few seconds. Don''t process too finely, since tapenade should not be completely smooth, but more of a thick and chunky paste. Of course, you can also process it to whatever texture you prefer!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'297C529002B38D459EE879D2B8B2B5F1', X'A2F112BCDCEC1543B16A57AA5334CF91', X'A2F112BCDCEC1543B16A57AA5334CF91', 2, NULL, NULL, 2, NULL, 0, 'Serve the tapenade with crackers, flatbread, baguette, or slices of toasted artisan bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C22075B71DCC40BA58F3141884D8F2', X'A2F112BCDCEC1543B16A57AA5334CF91', X'A2F112BCDCEC1543B16A57AA5334CF91', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F02B263EF5142D4D820347444F1CC66E', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F91A35338A9A64D92878E6F739A7064', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 1, NULL, NULL, 2, NULL, 0, 'Dredge oxtails in the 1/2 cup flour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C386482542444499006E126A6BFCAEC', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 2, NULL, NULL, 2, NULL, 0, 'Heat the beef drippings or cooking oil in a large, heavy pot over high heat. Cook to brown the meat, remove (keeping the fat in the pan), and drain on paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'212D1C0BBA1FFA479B90CEFF01FDE6A1', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 3, NULL, NULL, 2, NULL, 0, 'Add the minced onions to the pot, turn the heat to medium, and saute 8 to 10 minutes until golden.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'805F14D63A8B8A4F889F66A5F40BE231', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle in remaining 2 tablespoons flour, mix well, and brown lightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F06EC9255DE264A9F18D77A44F32DA7', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 5, NULL, NULL, 2, NULL, 0, 'Slowly add the water, and stir in tomato paste, salt, and pepper. Tie the bay leaf in cheesecloth with the thyme, cloves, and parsley and add to the pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA17F420E66A1C418E325AADDB3CAAB4', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 6, NULL, NULL, 2, NULL, 0, 'Return oxtail to pot, cover, and simmer 3 hours until meat is fork-tender. Cool and skim off fat; remove cheesecloth bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCA1666FE384E4498E63957D323A547A', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 7, NULL, NULL, 2, NULL, 0, 'Remove the oxtail from the soup and separate the meat from the bones. Cut the meat into bite-size pieces and return to pot. Add the carrots and celery.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24DAAEC51C40BD42980A3B0599FC0B4A', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 8, NULL, NULL, 2, NULL, 0, 'Cover and simmer 10 to 15 minutes until carrots are tender. If you like, mix in sherry or port.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D95EBA4490294CA1834A8CD4574657', X'C91CA7E946434B4F94E20C11A4D50F20', X'C91CA7E946434B4F94E20C11A4D50F20', 9, NULL, NULL, 2, NULL, 0, 'Serve as is, or strain the liquid and serve as a first course. Follow with oxtail and vegetables.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0451D6583A65304FAE33A5C9BD434979', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 0, NULL, NULL, 2, NULL, 0, 'Grate the zucchini on a large-hole grater into a large bowl. Move the zucchini over to one side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0982F0288ED33344A8824F2665CD98F2', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 1, NULL, NULL, 2, NULL, 0, 'Crack the egg into a small bowl and beat it until there is no distinct yolk and white bits. Add the egg to the zucchini and stir them together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDEB00E7830ED44895EDFDC30ABBE874', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the zucchini-egg mixture with the flour and salt and stir to combine thoroughly. Cover and chill for at least 30 minutes and up to several hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C0AD3F3AEBC44EB9432EE8359FC1EB', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 3, NULL, NULL, 2, NULL, 0, 'Heat a generous layer of oil (about 1/4 inch deep) in a large frying pan or pot over medium-high heat to 350 F to 375 F. How do you know when it''s hot enough? A bit of batter dropped into the pan should sizzle immediately; if it doesn''t, give the oil a bit more time to heat up (if it sizzles and browns instantly, then the oil is too hot—reduce the heat and let it cool off a bit).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5921FD03FC7BEC4E9BFB6CB0414773CA', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 4, NULL, NULL, 2, NULL, 0, 'Put generous spoonfuls of batter into the pan. Flatten them a bit with the back of the spoon to make fritter or pancake-shaped items. You should be able to fit about four fritters in a 10-inch frying pan at a time. Cook until the fritters are browned on one side, about 4 minutes. Use a spatula to flip the fritters over and cook until they are brown on both sides, about 4 more minutes. Go more on whether the fritters are browned and cooked through than the time since the exact temperature of the oil and the batter will vary the cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D73CAAD3151C44792CB82F5C7551464', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 5, NULL, NULL, 2, NULL, 0, 'While the fritters cook, line a paper plate or baking sheet with layers of paper towels. When the fritters are brown and tender, transfer them to the paper-towel-lined plate to drain. Repeat with the remaining batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02A26AEF32ABD4EAC25725372D8FF2E', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 6, NULL, NULL, 2, NULL, 0, 'Serve the fritters immediately. If that''s not quite possible, you can keep them warm by popping them in a warm oven (about 200 F), if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03691B096A4FC8488764CA62D14605B9', X'46AB14B0AB2D544DA8D4A107B00937F4', X'46AB14B0AB2D544DA8D4A107B00937F4', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E25FC95BF8A870468FEB19FDD6EDA770', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E349C907A2E1438CACFAB55F8BEF87', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', 1, NULL, NULL, 2, NULL, 0, 'Blend all the ingredients into a thick paste, using a small amount of water as necessary to achieve desired consistency.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A12E8A5A7DF61B40B4E8EBE0F73A08B4', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', 2, NULL, NULL, 2, NULL, 0, 'Serve chilled or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71D1083347559B459E7558BA24D39A58', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', X'C3DAD5BAB7BCA64292EC9C8B2ED0261F', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D78BF39ABAA84EA8E32E3946D69A7F', X'0B3540193083C3458D37B24129184CA3', X'0B3540193083C3458D37B24129184CA3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'712ECE1539BE58479F2A29B4884E2CA7', X'0B3540193083C3458D37B24129184CA3', X'0B3540193083C3458D37B24129184CA3', 1, NULL, NULL, 2, NULL, 0, 'Either by hand or using an electric mixer, whisk the cream until it has soft peaks and a texture that is similar to whipped cream. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01DD7E094A5AE34B9DF2F8D49DE829D5', X'0B3540193083C3458D37B24129184CA3', X'0B3540193083C3458D37B24129184CA3', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together goat cheese and milk until the goat cheese has softened. Mix in chives. Gently fold in whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875C666EA5F67F47B7F4BA3DB2A5E5EC', X'0B3540193083C3458D37B24129184CA3', X'0B3540193083C3458D37B24129184CA3', 3, NULL, NULL, 2, NULL, 0, 'Serve chilled with bread and/or crackers on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B92C9E44C5A3404C80905AC0E3C2B5BB', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
