INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6A3B50DE38194FB3CCAC0C404B85FA', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 2, NULL, NULL, 2, NULL, 0, 'Add your steamer basket to the pot, making sure that it sits well above the beer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'615B907780B1E740BF44A0C52000AF49', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 3, NULL, NULL, 2, NULL, 0, 'Add the celery and shrimp to the basket and cover.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28950B607EDC8B4AB502E7825531786B', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 4, NULL, NULL, 2, NULL, 0, 'Bring the beer to a boil, then reduce to a simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E94EFAC76328B4995D80694B5CD969B', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 5, NULL, NULL, 2, NULL, 0, 'Cook until all of the shrimp turn pink. The top ones tend to change color first, so make sure to move them around and check that all have turned pink.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2E85BD0D348943BF09611613651EFF', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 6, NULL, NULL, 2, NULL, 0, 'Serve warm or cold as they are or with cocktail sauce or melted butter. As these are served with the shell on, remember to provide a bowl for the shells.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39795DC124F064684FBC72995E94DEC', X'7336BF9C40CE404D8224E15F257C4B1E', X'7336BF9C40CE404D8224E15F257C4B1E', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5F9BA1292913A4EAF9396224B2B7A44', X'796E2F560A211F42AC2913EA1C3FC7D5', X'796E2F560A211F42AC2913EA1C3FC7D5', 0, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven or stockpot, combine potatoes, onions, chicken broth, and black pepper. Bring to a boil. Reduce heat, cover, and simmer until vegetables are tender, about 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8155705EA3620844A7FFF1F70F761351', X'796E2F560A211F42AC2913EA1C3FC7D5', X'796E2F560A211F42AC2913EA1C3FC7D5', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour water into a skillet to a depth of about 1/2-inch. Place the skillet over medium-high heat and bring to a boil. Put the sausages in the boiling water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CECD784492D0C4AB927D20BE4DD1763', X'796E2F560A211F42AC2913EA1C3FC7D5', X'796E2F560A211F42AC2913EA1C3FC7D5', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low, cover, and simmer the sausages for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'841B4347F237E74B8C78E45CA98C6800', X'796E2F560A211F42AC2913EA1C3FC7D5', X'796E2F560A211F42AC2913EA1C3FC7D5', 3, NULL, NULL, 2, NULL, 0, 'Drain and remove the sausage from the casings. Thinly slice the sausage or crumble and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C13AB1D019F4D64AA93ADE79E340245D', X'796E2F560A211F42AC2913EA1C3FC7D5', X'796E2F560A211F42AC2913EA1C3FC7D5', 4, NULL, NULL, 2, NULL, 0, 'Blend or process cooked potato mixture, a little at a time, until smooth. Return to the stockpot and add butter and chopped spinach. Cook, uncovered, over low heat for about 3 to 5 minutes, until spinach is wilted. Stir in sliced or crumbled sausage meat; taste and add kosher salt and freshly ground pepper, to taste. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F0E5D4A578E9B4CB280C032F5836654', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E9AE350802DE042BA2E91DBE66F960E', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in a medium saucepan over low heat. Add the flour, stirring with a fork until the mixture is thick and pasty.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E1254535359994D8B81BF75DB0937DC', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 2, NULL, NULL, 2, NULL, 0, 'Turn up the heat to medium-low and then slowly add in the milk, whisking it all together and stirring constantly to avoid lumps.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCFEBBC2BEE8C64E8049A8D27B92BB46', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic powder, Italian seasoning (or basil and oregano), salt, and pepper. Allow your sauce to simmer for 3 to 4 minutes, or until it has thickened.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EEC2A1D9D0F094D8CFF7418F5B2E0EC', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 4, NULL, NULL, 2, NULL, 0, 'Stir in the freshly grated Parmesan cheese until the cheese has melted and is well incorporated. Your white sauce will thicken further as it cools, so add a bit more milk if needed and, conversely, don''t worry if it seems too thin while on the stovetop over the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C02ACD07D78874E86C6EA585475EC3B', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 5, NULL, NULL, 2, NULL, 0, 'Serve with your favorite pizza crust or pasta.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C823EA1F4BCAA43A1CD14644E0944F6', X'33E5D35EF540C140983AB609FA216065', X'33E5D35EF540C140983AB609FA216065', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0134B9250D302946A334506EFF821C6D', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 0, NULL, NULL, 2, NULL, 0, 'Cut off the round, seed-containing part of the squash and reserve it for another recipe. Cut off the stem end of the squash''s neck. Slice off the thick skin.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD921870265787489BD93EE2F5C4978A', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 1, NULL, NULL, 2, NULL, 0, 'Cut the butternut squash into very thin rounds or slices. They should be almost transparent. You can use a mandoline, the slicing blade of a food processor, a vegetable peeler, or a knife (be careful!) to do this.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F2014760AA8CC4A9FF5CB834F6B74D2', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 2, NULL, NULL, 2, NULL, 0, 'Toss the squash slices with the oil, if using. Use your clean hands to separate the pieces and coat each of them with the oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'008D3069D65EAB439210068895E41E81', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 3, NULL, NULL, 2, NULL, 0, 'Arrange the butternut squash pieces on the trays of your dehydrator, making sure that none of the pieces are overlapping.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F83353F78D2B34D85E8048E6FA1E46C', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 4, NULL, NULL, 2, NULL, 0, 'Dry the squash at 145F/63C (the highest setting on most dehydrators) for 2 hours. Reduce the heat to 110F/43C and dry for an additional 8 - 10 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E953FCBD673024480406B57557D54F7', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 5, NULL, NULL, 2, NULL, 0, 'The squash chips should be crisp and curling up (like conventional fried chips), but still bright orange and not showing any signs of discoloration.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8799D5DB8AA74795944BF422747627', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 6, NULL, NULL, 2, NULL, 0, 'They may not seem fully crisp while they are still warm: let them cool for 10 minutes with the dehydrator turned off. They will crisp up the way cookies do after they come out of the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06145416ECB43B45A37059627ECA4B0C', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 7, NULL, NULL, 2, NULL, 0, 'If after the cooling off period they still aren''t crisp, turn the dehydrator back on at 110F/43C and dry them for an additional 1 - 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A318FE7827F58646BCD93BCD41EB0B2D', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', X'6D97E63FF4E1CD4B9494C0DD8EF90D11', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the salt, if using, over the chips. Eat immediately or transfer, once cooled to room temperature, into airtight containers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16FF295937090E44B6D9D350CA962C2E', X'D1EAAD3E1AA5344A93AD586BEA101EAA', X'D1EAAD3E1AA5344A93AD586BEA101EAA', 0, NULL, NULL, 2, NULL, 0, 'Place the cheese cubes and salsa in a slow cooker or crock pot. Cover and cook on HIGH, stirring occasionally until the cheese cubes are melted and blended with the salsa, about 2 hours. Stir in fresh chopped cilantro after the cheese has melted or leave them out.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F42226C7880E684783DAC11147F169DF', X'D1EAAD3E1AA5344A93AD586BEA101EAA', X'D1EAAD3E1AA5344A93AD586BEA101EAA', 1, NULL, NULL, 2, NULL, 0, 'Serve hot, right from the slow cooker, with tortilla chips or corn chips or assorted crackers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7460F25F83CC94CB4FA56BC2972F125', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 0, NULL, NULL, 2, NULL, 0, 'Dice the cucumbers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E26F31F7584BA342B2318A39674AB4DE', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 1, NULL, NULL, 2, NULL, 0, 'Peel and mince the garlic.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28343B22819D2F4E99CB20B9A62123AB', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 2, NULL, NULL, 2, NULL, 0, 'Chop the mint leaves.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A7329BF4576F41A7C1AF998FA591FE', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 3, NULL, NULL, 2, NULL, 0, 'Mix all ingredients in a bowl: combine the yogurt, diced cucumbers, garlic, lemon juice, olive oil, salt, pepper, fresh mint, and paprika, stirring well to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71103CB4C29FE44823F1EE62CB4923F', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 4, NULL, NULL, 2, NULL, 0, 'Chill the tzatziki in the refrigerator for at least an hour before serving to allow flavors to mingle.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9CD28BED2A15844B0AA6E02FC4E7652', X'656718141CDFF64EB8CA9723C51B4379', X'656718141CDFF64EB8CA9723C51B4379', 5, NULL, NULL, 2, NULL, 0, 'Taste and adjust seasonings. Give it all a quick stir again just before serving. Tzatziki should be served cold.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADC78E64BC535049888DCA00DA75A646', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F93EE6518591448A66722C0665DDF4', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9FDEBAAC2D3024DA23D333379CE92B1', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 2, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the softened cream cheese, mayonnaise, shredded cheese, 1/2 cup of the Parmesan cheese, the green chilies, and jalapeños and process until smooth. You do not need to drain the can of jalapeños or the can of chiles, but if you prefer a dip that''s a little less creamy, you can omit these juices.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C554D5374C163B48B8933F5A3A2B9F0A', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 3, NULL, NULL, 2, NULL, 0, 'Spread the dip into a greased 2-quart casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83DDB6F86E9F434CA7B0039C549E8C8F', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix the panko breadcrumbs, the remaining 1/2 cup of Parmesan cheese, and the melted butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19DFC2800CC84C46B5579D1EAD57A2B3', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the crumb mixture evenly over the dip.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C93249DF338FD64A89D331238A514933', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 20 minutes, or until the top is golden brown, the dip is heated through and bubbles gently on the edges.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2711F3160EE1C845BE9F6248AAB8D6B4', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 7, NULL, NULL, 2, NULL, 0, 'Serve the dip warm with buttery crackers or a sliced French bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF088A677FBA494D8713648A0F696EA0', X'780012B6C3B66042BCFA210B7AF53BD6', X'780012B6C3B66042BCFA210B7AF53BD6', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3BE2D9F675F3849B1734B8133E4E0DF', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 375 F. In a large bowl, combine the oats, flour, sugar, salt, and baking soda. Add the shortening or butter and use two knives, a large fork, or your fingers to work the fat into the dry ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0167B7D94A46DE4E994951451480EECC', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 1, NULL, NULL, 2, NULL, 0, 'Pour in the hot water and stir until everything comes together into a thick, sticky dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F77C889B9275314C8F9113AD94A2BA29', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 2, NULL, NULL, 2, NULL, 0, 'You can roll the dough out on a well-floured surface and cut it into shapes, and set them on a large baking sheet. Or, keep things simple and simply press the dough into an even 1/4-inch layer on a baking sheet. Score this large `cake` into smaller pieces: use a knife to cut the dough into squares or rectangles or whatever shapes you like, but don''t bother to separate the pieces; the pieces will bake back together, but be easy to cut or break along that original cut-line.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A15A9839356E074AAB5590987FB9D799', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 3, NULL, NULL, 2, NULL, 0, 'Bake until golden, about 12 minutes. You can take them out now for chewier oatcakes or reduce the oven to 325 F and bake until lightly browned, about 10 more minutes, for crisper oatcakes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717B9A9B0F4DF04D964EA133967B57A8', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 4, NULL, NULL, 2, NULL, 0, 'When they have finished baking, if you''ve cut them into shapes, let them cool; if you''ve scored them, cut them apart while warm so they cool into squares (or rectangle or triangle or whatever you''ve cut them into!).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2136AFE6E51AB4AB65556814D4B22D7', X'06F0217448CC6B4B9027FFF49384B233', X'06F0217448CC6B4B9027FFF49384B233', 5, NULL, NULL, 2, NULL, 0, 'Keep the oatcakes stored in an airtight container up to a week or even two, depending on the heat and humidity in your kitchen. They also freeze well (although they''re so easy to make there''s not a lot gained by freezing them).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C9CC9CC4EED3B42B4C0E1235DA5235A', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD13515C2C4E44595CD9A13591EC9CA', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the mayonnaise, Creole mustard, Creole seasoning, pepper, parsley, green onion, and bell pepper; set aside. If the Creole seasoning does not contain salt, add a dash of salt, or to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984ED3FF9FDCA34AA8A600885C6BA7DB', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 2, NULL, NULL, 2, NULL, 0, 'Drain the crabmeat and gently squeeze it to get as much of the liquid out as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4ABAFB2ED69E34A9C1C439F1D3C5F53', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 3, NULL, NULL, 2, NULL, 0, 'Put the crabmeat in a medium bowl. With a spatula or wooden spoon, fold in mayonnaise mixture and 1 cup of the bread crumbs, just until blended. Shape into 8 crab cakes, about 2 1/2 inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D56DB33DD912B54FB86C79AA1B5A2DBC', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 4, NULL, NULL, 2, NULL, 0, 'I use a biscuit or cookie cutter with an open top to shape the cakes and press the ingredients down to make them hold together. Press gently into reserved crumbs. Cover and chill for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6184F62C5691094CA83EFC1AC2181558', X'D9EC56CE3CEF844D9A02328000EEA7B6', X'D9EC56CE3CEF844D9A02328000EEA7B6', 5, NULL, NULL, 2, NULL, 0, 'Heat the butter and oil over medium heat. When the butter stops foaming, fry crab cakes for about 5 minutes on each side, carefully turning only once. Serve with lemon wedges and Remoulade or other sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D103E92D2F948D47A9D8F0F8C4B42611', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D04E45E95B3FC344B54FEB962BFEAD9D', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 1, NULL, NULL, 2, NULL, 0, 'Combine wild rice with 1/2 cup water in a small saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D542979EB7784DBE381B63DEF581F8', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 2, NULL, NULL, 2, NULL, 0, 'Bring to a simmer, then cover and simmer on low heat for 30 to 35 minutes until rice is almost tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'542DA6E0F4E09A42B3338DF23E38B6C8', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 3, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'316562B459B52849B22F535BEFC721B4', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5693C064E6D1C4B9C19A2897F4C8B17', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 5, NULL, NULL, 2, NULL, 0, 'In a small saucepan heat olive oil over medium heat; add onion and garlic and sauté until tender, about 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74BD863B5F385C42BD332B398702EBD2', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 6, NULL, NULL, 2, NULL, 0, 'In large bowl combine onion mixture, wild rice, seasoned salt, pepper, milk, and egg and combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ACE29A03E53BF48B47CDCAE569C53B9', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 7, NULL, NULL, 2, NULL, 0, 'Add ground beef, broken into chunks, and work gently but thoroughly with your hands to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCCBA2B2E86571468C82584D2024DA24', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 8, NULL, NULL, 2, NULL, 0, 'Form into meatballs about 1 inch in diameter and place on a broiler pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FC9E5BC5950B40BC054128BC09D4EF', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 9, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven for 25 to 30 minutes until meatballs are no longer pink in center.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4409204E8C2E64FB01CE6C3EBE4552C', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 10, NULL, NULL, 2, NULL, 0, 'Cool and freeze, or when baked, use in your favorite recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C6E756944A861498070156258EBEC5B', X'6DE695ED14E67A4497B48943243F3DA6', X'6DE695ED14E67A4497B48943243F3DA6', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31E79853E6021C498A6EEAFE81DEC726', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6018C05C9BBFBF4BB4782F43BB1F8A71', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 1, NULL, NULL, 2, NULL, 0, 'Put the eggs in a medium saucepan and cover with water to about an inch above the eggs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3621878F26F6F45B43269338DBF8EA1', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 2, NULL, NULL, 2, NULL, 0, 'Cover the saucepan and bring to a full boil over high heat. Leaving the cover on the pan, remove from the heat and let stand for 17 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ACF54AFAB70CA4B895B5E5B8773D4D6', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 3, NULL, NULL, 2, NULL, 0, 'Pour out the hot water and cover with ice cold water. When the eggs are cold, peel under running water. See some tips for peeling hard-cooked eggs below.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF29292867F9584E9631824E61901CCD', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 4, NULL, NULL, 2, NULL, 0, 'Slice the eggs in half lengthwise and set on a platter or egg plate. Scoop the yolks into a medium bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDBEC0B165DDBD42ADB996A9E46E5719', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 5, NULL, NULL, 2, NULL, 0, 'Mash the yolks with a fork, then add the mayonnaise, mustard, and salt, seasoned salt, or Creole seasoning. Add chopped fresh chives or parsley, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09E74526976AF14EA307D8408CD4D0CB', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 6, NULL, NULL, 2, NULL, 0, 'Taste and add freshly ground black pepper, as needed. If using salt-free seasoning, taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA1A7943146FA64C839DE6F417B01D73', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 7, NULL, NULL, 2, NULL, 0, 'Using a small spoon or cookie scoop, fill the egg white halves with the yolk mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4A9E1F0228C6C4D87986BAA24BE8C21', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the eggs lightly with paprika before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BC5C6030B9C54BA160DB62E538DF54', X'BE24FD004887D341830CEABA62B399DD', X'BE24FD004887D341830CEABA62B399DD', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488DB3F8C3527B43A6E8BDDA6CD278E8', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5131CE1DC334F749970C217478D3153E', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF7B905BBAC35946B0EC651AE9DC3592', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 2, NULL, NULL, 2, NULL, 0, 'Place the clean and dried pumpkin seeds on an ungreased cookie sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A28E53B563C9634CBA5855D65B001578', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 3, NULL, NULL, 2, NULL, 0, 'Mix together the butter and Tabasco sauce and drizzle over the seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB8D34F0E827A24395BECB92C4AB51F0', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 4, NULL, NULL, 2, NULL, 0, 'Combine the cayenne pepper, cumin, chili powder, and salt and mix well; sprinkle over the coated seeds. Toss well to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6713BAF5683A9B44851EF1C29A23BCBA', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 5, NULL, NULL, 2, NULL, 0, 'Bake for 35 to 45 minutes until the seeds are golden brown and crisp, stirring seeds once halfway through baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'624EC034BF33534784D3925987773B13', X'F8E5624C069F7041BB393298D0A5B863', X'F8E5624C069F7041BB393298D0A5B863', 6, NULL, NULL, 2, NULL, 0, 'Cool completely, stirring occasionally. Store in airtight container.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA163C7DD4E543418121265E239D5C08', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F88827922A8B42B7EC4DE5282F6CD4', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 F. Line a baking sheet with nonstick foil or parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B732E4B2EA8CA43986DFB3323BB489E', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 2, NULL, NULL, 2, NULL, 0, 'Brush cut sides of French bread with melted butter. Sprinkle lightly with onion powder and garlic powder. Top with blue cheese or Gorgonzola cheese, tomatoes, chives or green onions, mozzarella, and parsley.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5CCB1007B65B24BB07651EDF13359B6', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 3, NULL, NULL, 2, NULL, 0, 'Bake about 10 to 15 minutes, until bread is crusty on the outside and the top is melted and just beginning to turn golden.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8D80B1583E6244FA8C3B425DE4DFB0C', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 4, NULL, NULL, 2, NULL, 0, 'Serve as is for an appetizer or serve it as an accompaniment for a classic spaghetti dinner or any other pasta dish like linguine with clam sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20B8A8154C6764C9B64DDE47A7B0F92', X'2C651B324AEE6F4AADF43F72ED514794', X'2C651B324AEE6F4AADF43F72ED514794', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C288881A630BA04292FF6CB5E448DE1D', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8BA74AF7D42374386F341AE12EEA3B6', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 1, NULL, NULL, 2, NULL, 0, 'Heat a heavy-bottomed pan on high. Add the ground beef and chorizo and brown the meat until completely cooked through. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D827DD5EA415C948B06CB3B7C68830CB', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 2, NULL, NULL, 2, NULL, 0, 'Drain the meat from the pan and place it in a bowl. Leave some of the excess fat in the pan. Add the onion, garlic, peppers, seasoned salt, and ground black pepper to the pan and saute on medium-low heat until the vegetables are slightly softened. Do not overcook them—you want them to retain their shape and some of their texture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E65B96F7445F341B2363B23127D3CA1', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 3, NULL, NULL, 2, NULL, 0, 'Add the heavy cream, whole milk, and cream of mushroom soup to the pot. Simmer for two to three minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A7F40621CA7749A596AFE47010F352', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 4, NULL, NULL, 2, NULL, 0, 'Add the hot sauce, soy sauce, Worcestershire sauce, sour cream, and cream cheese to the pot and stir until combined completely. It should be thick and creamy. Cook for another minute or two, then remove from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4A45A07E460894F919A2011F62F9B91', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 5, NULL, NULL, 2, NULL, 0, 'Add the mixture to a large cast iron skillet or casserole dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB16425503ADAD4F8228E6842FC8013B', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle half of the shredded pepper jack onto the top of the casserole dish and begin arranging the tater tots on top of the casserole. You can make a pretty pattern or just cover the top of the casserole as best as you can.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F95539B78324B34CBE57B82D2300278E', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 7, NULL, NULL, 2, NULL, 0, 'Add the rest of the cheese to the top of the casserole and place it in the oven for 25 to 30 minutes or until the tater tots are golden brown and the casserole is heated through.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79E2A97E6E7C2A4F9BAB905B83BEBD61', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 8, NULL, NULL, 2, NULL, 0, 'Top the casserole with more diced bell peppers, chopped cilantro, and sliced jalapenos if you wish to make it extra spicy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD0889AEDD15D4FA20FB87CA81FC260', X'79EFE2991C57FD4DB76539BCF1893E7A', X'79EFE2991C57FD4DB76539BCF1893E7A', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D8AB5E48068F4AAD505F047CAA2411', X'CAB2FF37E432E4409B3AA9311B6EC7EE', X'CAB2FF37E432E4409B3AA9311B6EC7EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B3B85336BF56544A9CC7C416AD8B332', X'CAB2FF37E432E4409B3AA9311B6EC7EE', X'CAB2FF37E432E4409B3AA9311B6EC7EE', 1, NULL, NULL, 2, NULL, 0, 'Cut the stem ends off the squash and discard them, but keep the skin and the other end intact. Using a spiralizer, shred the squash into long strands, then cut them to a manageable spaghetti-like length before you cook them. Alternatively, draw a julienne peeler down the squash, making spaghetti-like strands. You can use a peeler to make thin, broader noodle-like slices as well. The spiralizer automatically removes the core, but if you use a peeler, stop just before you reach the seedy center.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'882045016101B442A9745499115884C5', X'CAB2FF37E432E4409B3AA9311B6EC7EE', X'CAB2FF37E432E4409B3AA9311B6EC7EE', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil or butter in a large skillet over medium heat. Add the squash noodles and garlic. Cook, stirring constantly, for a few minutes or until just slightly softened. Add the basil and continue cooking for about 30 seconds to 1 minute. Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'915AA76BFA1F4140BB1970601388EF88', X'CAB2FF37E432E4409B3AA9311B6EC7EE', X'CAB2FF37E432E4409B3AA9311B6EC7EE', 3, NULL, NULL, 2, NULL, 0, 'Toss the squash with the Parmesan cheese and serve immediately with extra Parmesan cheese on the side for sprinkling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBA06C59D5DA484389970DE3FBF84157', X'7ED6C6C01C538846817F574E63711DDD', X'7ED6C6C01C538846817F574E63711DDD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'134ADB272F9375429C13C4331FF26CE5', X'7ED6C6C01C538846817F574E63711DDD', X'7ED6C6C01C538846817F574E63711DDD', 1, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to package directions. Before you drain the pasta, remove ? cup of the pasta cooking water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12D4B436CA87A74C83024CD90A7AC921', X'7ED6C6C01C538846817F574E63711DDD', X'7ED6C6C01C538846817F574E63711DDD', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a large skillet over medium heat. Add the onions and sauté for 5 minutes until they are soft. Add the tomatoes and sauté for 3 more minutes until they start to soften, seasoning them with salt, pepper, and the red pepper flakes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'647D79E75FC7D64F8D93A60797160D11', X'7ED6C6C01C538846817F574E63711DDD', X'7ED6C6C01C538846817F574E63711DDD', 3, NULL, NULL, 2, NULL, 0, 'Drain the pasta, reserving the ? cup of cooking water. Turn up the heat to high, add the white wine to the pan, and scrape the bottom of the pan as the wine simmers to remove any little caramelized bits that may be stuck to the bottom. Add the reserved cooking water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC1ADFDD1856E74B88B78322C7990C6D', X'7ED6C6C01C538846817F574E63711DDD', X'7ED6C6C01C538846817F574E63711DDD', 4, NULL, NULL, 2, NULL, 0, 'Place the drained pasta in a serving bowl, pour the tomato mixture over, and toss to combine. Sprinkle over the fresh basil and serve hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8696BD61F5FDF34B85F2C2D97FF232CF', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A13CE65B368F60498EA85069A266B031', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Combine the ground beef, sausage, and pork rinds in a large bowl. Mix until the ingredients are evenly distributed, but make sure not to over mix or else the meat will be tough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20FCD1A98E73EC41A774AE6F463A9EB9', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the egg, milk, salt, pepper, parsley, Italian seasoning, and Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6CE2471864AF34F8F4F9C5FAC3FA6F5', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 3, NULL, NULL, 2, NULL, 0, 'Pour the egg mixture over the meat mixture. Add in the onion and garlic. Combine until all of the ingredients are evenly distributed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E214EAB252CB5E42B45EC8185C8E1667', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 4, NULL, NULL, 2, NULL, 0, 'Form the meat mixture into a loaf shape.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21CB59C031D6404DACB9EDC9B14E5211', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 5, NULL, NULL, 2, NULL, 0, 'Wrap the meatloaf with the strips of bacon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB9759F82F4EA64486F2E4EED263C363', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 6, NULL, NULL, 2, NULL, 0, 'Carefully pick up the meatloaf and place it on a cooling rack over an aluminum foil-wrapped baking sheet. Pour the tomato sauce over the top of the meatloaf.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93BC0C50717E143A019839E75DFCA83', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 7, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven uncovered for one hour or until the top is caramelized and the internal temperature is 165 F. You can increase the temperature to 375 F for the last 5 minutes to further caramelize the top and crisp up the bacon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1228CC247EF843912E9BBB1EFF31E1', X'9447FB49504C59428FEBE55762572CD1', X'9447FB49504C59428FEBE55762572CD1', 8, NULL, NULL, 2, NULL, 0, 'Remove from the oven and allow to cool slightly. Slice and serve immediately with your favorite keto sides, like mashed cauliflower and Brussels sprouts.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216413B890622044A4EEF678AFA7B4F5', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04E0799F382404499ABAA2A7E2902FF5', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 1, NULL, NULL, 2, NULL, 0, 'The night before cooking the prime rib, unwrap the roast and let it sit uncovered in the refrigerator. This will dry out the surface, which makes it easier to get a nice brown color on the roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EE6E4082617DE4C8B84CAA71727F44E', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 2, NULL, NULL, 2, NULL, 0, 'Three hours before you want to begin cooking, take the roast out of the fridge and place it on a sheet pan at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E26675424BD7F4D9FDDB401273C111D', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 3, NULL, NULL, 2, NULL, 0, 'Half an hour before you start roasting, pre-heat your oven to 500 F and season the roast generously with Kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'118F918BFECCC54FB8A0F68D61EA5A61', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 4, NULL, NULL, 2, NULL, 0, 'Now it''s time to do your calculation. Simply multiply the weight of your roast by five. That''s your total roasting time, in minutes. For instance, if you have a 4-pound roast, 4 × 5 = 20 minutes. An 8-pound roast? 8 × 5 = 40 minutes. Remember that number.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D45FFC37EEB5AD449621F30416F55459', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 5, NULL, NULL, 2, NULL, 0, 'When you''re ready to cook, set the roast in a roasting pan with a rack, fat-side-up. If you''re nervous about the cooking time, you can insert a meat thermometer or a digital probe thermometer into the deepest part of the meat, being careful not to hit bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72D8714212110540A7E9D63396EEAA05', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 6, NULL, NULL, 2, NULL, 0, 'Put the roast in the preheated oven and roast it for exactly however many minutes you calculated above. When the time''s up, turn off the oven and walk away. Do not open the oven door for any reason for the next two hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'535A9263E3357A47B5B747112242804A', X'0188474AA8B35B4E83500EFA7C141E94', X'0188474AA8B35B4E83500EFA7C141E94', 7, NULL, NULL, 2, NULL, 0, 'In two hours, take the prime rib out of the oven, carve, and serve right away. If you did use a thermometer, you''ll see that the internal temperature of the meat has reached 130 F, perfect medium-rare.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'253F6A860615C8478FC1088A6B5DEAA5', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D512DAB8F918A042A4122A1341CDCF03', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mash the beans until almost smooth with the help of a potato masher or for. Stir well with a wooden spoon once the desired texture is achieved.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA8F51425A76E444937C3AA48A2099A3', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 2, NULL, NULL, 2, NULL, 0, 'Add 1/2 tablespoon of oil to grease the bottom of a small frying pan. Add the onions and sauté them until they are soft and translucent, about 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78D0EED9147EAF44B4C79F1CB21217D5', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 3, NULL, NULL, 2, NULL, 0, 'Combine the sautéed onions into the mashed black beans. Add the crumbled bread slices, seasoned salt, garlic powder, onion powder, and salt and pepper to taste. Mix well with a wooden spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C026659F2F1FAB4B93CDB3CF40A6FF84', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 4, NULL, NULL, 2, NULL, 0, 'Once the mixture is well combined, add the flour a few tablespoons at a time. Your veggie burger mixture will be very thick, so use your hands to work the flour in well. You''ll obtain a firm dough after 2 to 3 minutes of working the mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85CC3931EE3AFE42B032E0C117757F18', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 5, NULL, NULL, 2, NULL, 0, 'Divide the black bean mixture into six individual patties, approximately 1/2-inch thick. Roll a handful into a ball, then gently flatten it.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D4F238AF7A77045A4FCBFF3438A5C7E', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 6, NULL, NULL, 2, NULL, 0, 'Use the remaining 1 1/2 tablespoons of olive oil to pan-fry the burgers over medium-low heat. Allow the patties to slightly firm-up and brown on each side, about 3 minutes. If your pan is too hot, the burgers will brown too quickly and not be heated through and cooked in the middle, so adjust the heat as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6053A91D2B48DD4C8BA2C49F424C85AA', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 7, NULL, NULL, 2, NULL, 0, 'Assemble your veggie burgers with the veggies');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFFB0064C14E6D408032730B8E80EC20', X'048563806669974AA60796E3265797F3', X'048563806669974AA60796E3265797F3', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C7CD3DB7B58D840857C70B93392F112', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'996C124ADB32004B9165DFDC057FACC5', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 1, NULL, NULL, 2, NULL, 0, 'Cut the tops off of the bell peppers and rinse them under cold water; remove seeds and cut away the white ribs, which might be bitter. Chop the edible part of tops and set aside. Place the peppers in a large pot and cover with salted water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98EF2B59744C49418F2583650C123490', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil. Reduce heat, cover, and simmer for 5 minutes. Drain and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BC8858E98D1D34A8BD8A1C0E2B2F14C', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 3, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter in a large skillet over medium heat until oil is hot and the butter is foamy. Sauté the chopped bell pepper (from the tops), chopped onion, and chopped celery for about 5 minutes, or until vegetables are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7013EF1554FB4B8B70DFAA94A3BA7A', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 4, NULL, NULL, 2, NULL, 0, 'Add the (undrained) canned diced tomatoes, tomato sauce, crushed garlic, oregano, basil, 1 teaspoon salt, and 1/4 teaspoon pepper. Bring to a simmer and cook for about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ACB49EC07C6AC4CAB021956D87EF33A', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 5, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the egg with the remaining 1 teaspoon of salt, 1/4 teaspoon pepper, and Worcestershire sauce. Gently stir to blend; add ground beef, cooked rice, and 1 cup of the tomato sauce mixture. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749288C2F21CF841BE3720F1B135CE1F', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 6, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF634C2D2AC5D49938B60428894F371', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 7, NULL, NULL, 2, NULL, 0, 'Stuff the peppers loosely with the ground beef mixture and place them in a 13 x 9 x 2-inch baking pan. Pour the remaining tomato mixture over the stuffed peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CE28E08FD2E3943BF2F456E9DD2AE5D', X'FB11560FE8E2CD47ABF10CD2CD87EA26', X'FB11560FE8E2CD47ABF10CD2CD87EA26', 8, NULL, NULL, 2, NULL, 0, 'Bake the peppers for about 45 minutes, or until the meat mixture is thoroughly cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C196F3AA0428E94BB11B3CCC7CC92229', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3174EED94BB7244AF76AA297F054250', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 1, NULL, NULL, 2, NULL, 0, 'Set aside 2 tablespoons of butter to soften while you proceed with the recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3359C75F2445B44B9CB01FD1B753265', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 2, NULL, NULL, 2, NULL, 0, 'Melt 4 tablespoons of butter in a medium skillet over medium heat. Add the onions, season with salt and pepper, then stir to coat with butter. Reduce the heat to medium-low and cook, stirring occasionally, until the onions are deep golden and very tender, about 10 minutes. If necessary, add water 1 tablespoon at a time to prevent the onions from sticking to the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026C0E9F675AF7488A80DF6CD9AEB786', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 3, NULL, NULL, 2, NULL, 0, 'Use the reserved softened butter to butter both sides of each slice of bread. Heat a large skillet over medium-high. Add the bread and cook until toasted underneath, about 2 minutes. Transfer to a cutting board, toasted side-up. Reserve skillet to cook the burgers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7255A3025B4504098656A50512346F1', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, shape the beef into four 5-inch round patties. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'802F38A5ECD6C7469D2EAB6BD177FF7A', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 5, NULL, NULL, 2, NULL, 0, 'In reserved skillet, cook burgers until browned and cooked through, 2 to 3 minutes per side for medium-rare.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC7BF8693548A84797DB3F004F991F66', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 6, NULL, NULL, 2, NULL, 0, 'Top each slice of toasted bread with a slice of cheese. Place one patty over cheese, then top with some of the onions. Put the remaining bread, cheese-side down, over the onions to form a sandwich.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E374B24CBB940488C105C2C0EA9E7D2', X'B82190914FFB444A8B6DEE6AEBD60312', X'B82190914FFB444A8B6DEE6AEBD60312', 7, NULL, NULL, 2, NULL, 0, 'In the same skillet, melt the remaining 2 tablespoons of butter over medium-high heat. Add the sandwiches and cook (in batches if necessary) about 2 minutes per side, or until golden brown and the cheese is melted. Flip once and repeat. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A425249D80F783458B9B8502A365E771', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B572C348EAD454997E355E22212447E', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 1, NULL, NULL, 2, NULL, 0, 'The night before you''re going to cook, unwrap the roast and let the meat sit on a sheet pan with a rack, uncovered, in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'322D39CB10A202468F782223FD6CDDB5', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 2, NULL, NULL, 2, NULL, 0, 'Three hours before you''re going to roast it, take the prime rib out of the fridge and let it sit at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFE29E07E179B44C916DE0C94858551F', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 3, NULL, NULL, 2, NULL, 0, 'Finally, 30 minutes before you start roasting, pre-heat your oven to 200 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB3FFDB5FB388D4D8C9BC11FED4AD698', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 4, NULL, NULL, 2, NULL, 0, 'Season the meat generously with Kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A0B1E162EC34C45BCE2585D74C7E300', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 5, NULL, NULL, 2, NULL, 0, 'For a boneless prime rib, set the roast in a roasting pan with a rack, fat-side-up. For a bone-in roast, just set the meat bone-side-down directly in the roasting pan. Insert a meat thermometer or a digital probe thermometer into the deepest part of the meat, being careful not to hit bone. If you''re using a digital probe thermometer, set it to alert you when the meat hits 128 F (see note below).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C07CFBACD25945A7A99FC05E4DA12F', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 6, NULL, NULL, 2, NULL, 0, 'When the oven is heated, transfer the roast to the oven and roast until the meat''s internal temperature reaches 128 F, which will be another 2 1/2 to 5 hours, depending on the size of your roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06EC197DA4672748A7C3E65FF9312634', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 7, NULL, NULL, 2, NULL, 0, 'When the temperature hits 128 F, take the meat out of the oven and transfer it to a cutting board and cover it with foil. Leave the thermometer in!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AF6A23B924B2C4E8141731BE028DFF2', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 8, NULL, NULL, 2, NULL, 0, 'As soon as you''ve covered the roast with foil, turn your oven up to 500 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F45EDBF58022469CFC2A2B38836550', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 9, NULL, NULL, 2, NULL, 0, 'Because we roasted the prime rib at 200 F, there won''t be much carryover cooking and you don''t need to rest it for very long. Once you take the roast out of the oven, the temperature should rise to 130 F, which is perfect medium-rare, and within 20 minutes or so it will drop back down to 120 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357DA25D43921B44A941C820CEE479EA', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 10, NULL, NULL, 2, NULL, 0, 'By which time, your oven will have fully reached 500 F. Now, put the meat back in the oven and let it roast on super-high for 6 to 10 minutes or until you have a lovely brown crust on the outside. Then take it out, carve right away.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C170F600D52029479328D395287D423A', X'10A0B648D3461A48B2322119BCD7B5D6', X'10A0B648D3461A48B2322119BCD7B5D6', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02DF0335596C9742A738A07494092002', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EE48C02D3C2A3428193B136D2A38560', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 1, NULL, NULL, 2, NULL, 0, 'Line a large shallow-rimmed baking pan with foil and then oil lightly. If desired, place a rack in the baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86EAD17C9B1F154CA7CD2D0A9648728C', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine all ingredients, using just enough water to moisten.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5D6D5D9F1E0442A8C140710E230A5D', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 3, NULL, NULL, 2, NULL, 0, 'Shape into meatballs, about 1 1/2 to 2 inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'032F40F1C88B2649ADB153D1BCD6D781', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 4, NULL, NULL, 2, NULL, 0, 'Arrange the meatballs in the pan or on the rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01409C8DF43B2A41963646FB7809F08F', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 5, NULL, NULL, 2, NULL, 0, 'Broil the meatballs about 8 inches from the heat source for about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD4535F6749A9249A858A27EE9333E80', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 6, NULL, NULL, 2, NULL, 0, 'Turn and broil for 10 minutes longer. Alternatively, the meatballs may be baked for about 30 minutes at 375 F, turning after about 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E549DFE6B5FA4683BEFCE43690DAA7', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 7, NULL, NULL, 2, NULL, 0, 'Put the meatballs in a large saucepan and cover with your favorite pasta sauce, homemade or store-bought.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8420E88A2A0BE64F9C3FA60E79762225', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 8, NULL, NULL, 2, NULL, 0, 'Cover and simmer for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BF60A0F562C86488265699BCC506C07', X'A1A8E5F30148C14788481B7E002A76C5', X'A1A8E5F30148C14788481B7E002A76C5', 9, NULL, NULL, 2, NULL, 0, 'Serve over pasta, in a sub roll, or by itself and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEDB30A45F89BC429CE97948024A0779', X'41553386E4B91F42931A80DC4A5EA1E0', X'41553386E4B91F42931A80DC4A5EA1E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86FB6B05FA6EAD4E82B2C5C1643D7BDD', X'41553386E4B91F42931A80DC4A5EA1E0', X'41553386E4B91F42931A80DC4A5EA1E0', 1, NULL, NULL, 2, NULL, 0, 'Place tomatoes, olive oil, lemon juice, and fresh basil in a large bowl. Season with salt and pepper. Mix gently and set aside to marinate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60CA354FF0D4DF4390D714DADA9800CD', X'41553386E4B91F42931A80DC4A5EA1E0', X'41553386E4B91F42931A80DC4A5EA1E0', 2, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a fast boil and add spaghetti; cook until al dente according to package directions, about 7 to 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1B881B7FDE6974F9A66088B5306186D', X'41553386E4B91F42931A80DC4A5EA1E0', X'41553386E4B91F42931A80DC4A5EA1E0', 3, NULL, NULL, 2, NULL, 0, 'Drain pasta and immediately toss with the tomato mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AD80BD2A4FD72458A0D718D94FC417E', X'41553386E4B91F42931A80DC4A5EA1E0', X'41553386E4B91F42931A80DC4A5EA1E0', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately, sprinkled with Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32DC1080ECE6DB46B7C243F847CF85BD', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14EADCE56AE47C4591C2245A5CAF500C', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 1, NULL, NULL, 2, NULL, 0, 'The night before you are going to cook the prime rib, unwrap the roast and let it sit uncovered in the refrigerator. This will dry out the surface, which makes it easier to get a nice brown color on the roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCE85CE1BE2AC146B9B16BDA38B8162D', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 2, NULL, NULL, 2, NULL, 0, 'Three hours before you want to begin cooking, take the roast out of the fridge and place it on a cutting board at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB052CC42EA9C48AC7B0B9C0EF37B57', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 3, NULL, NULL, 2, NULL, 0, 'Half an hour before you start roasting, pre-heat your oven to 450 F and season the roast generously with Kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFB49AD476C14B4F83F5102DFBDC3DCF', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 4, NULL, NULL, 2, NULL, 0, 'When you''re ready to cook, set the roast in a roasting pan with a rack, fat-side-up for a boneless prime rib. Or for a bone-in prime rib, skip the roasting rack and just set the roast bone-side-down in the roasting pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D033BE6068FAE448D3CD019FA33C4D2', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 5, NULL, NULL, 2, NULL, 0, 'Insert a meat thermometer or a digital probe thermometer into the deepest part of the meat, being careful not to hit bone. If you''re using a digital probe thermometer, set it to alert you when the meat hits 120 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05ACEBF33594E44AACDA69D075A5CE85', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 6, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes, then lower the heat to 325 F and roast until the meat''s internal temperature reaches 120 F, which will be another 1 to 3 1/2 hours, depending on the size of your roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172C53A0C9A94348B839DD4E7BADB5A1', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 7, NULL, NULL, 2, NULL, 0, 'When the prime rib hits 120 F, take it out of the oven and transfer it to a cutting board and cover it with foil. Leave the thermometer in! You''re going to want to rest the meat for 30 to 45 minutes, during which time the temperature will continue rising to around 130 F, which is perfect medium-rare, and then drop back down to 120 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81584E7B248D446A4EE06453053AC4A', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 8, NULL, NULL, 2, NULL, 0, 'When it hits 120 F it''s fully rested and ready to slice and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FD415FB68E654087EE398FA94661F7', X'5F4D0706BB3B044583981B70A706C30B', X'5F4D0706BB3B044583981B70A706C30B', 9, NULL, NULL, 2, NULL, 0, 'Try pairing it with a simple au jus recipe you can make while the meat is resting, or a creamy horseradish sauce. Also feel free to serve it with a variety of delicious sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16EADAA82C79DF40B9E46044BBED716B', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E243C1D077E06478C3B4D825002A21C', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F. Rinse the lentils and drain.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26655C94EA5511468B83B34BF2B45005', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 2, NULL, NULL, 2, NULL, 0, 'Fill a large soup or stockpot with water or vegetable broth and add the lentils. Bring to a boil, reduce the heat, and simmer until cooked–about 30 minutes. Don''t skimp on the cooking time for these lentils because you want them to be very soft so they will mash up easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF82B130589604592BF8176625B1070', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 3, NULL, NULL, 2, NULL, 0, 'Drain thoroughly then mash the lentils while they are still hot and until they are a little more than half mashed. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206B2010FBB4B3448ED926AE9C9EDBAE', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 4, NULL, NULL, 2, NULL, 0, 'In a skillet, sauté the onions and garlic in olive oil for 3 to 5 minutes or until soft.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F40AF52795C146B6A46EEE969D0EAD', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the onions, garlic, and olive oil with the mashed lentils and add the rice, salt, ketchup or barbecue sauce, sage, and Italian seasoning. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3133B897B0484995E09A1A35A2D4F1', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 6, NULL, NULL, 2, NULL, 0, 'Gently press the entire lentil mixture into a greased loaf pan. Drizzle a bit of extra ketchup on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F95EE5AA943DE4EA10A5B8CA31DBEB5', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 7, NULL, NULL, 2, NULL, 0, 'Bake the lentil loaf for 1 hour, basting with additional ketchup every 15 minutes or so. Allow it to cool slightly before slicing and serving, as this will help the lentil loaf firm up.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A5CCF1EACFE04F9D87721928ED771A', X'97BED4E6A190EF44A126FD38BFCC3932', X'97BED4E6A190EF44A126FD38BFCC3932', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFEE08FE654CC842A587A7A6F1EA787B', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CED305419E9BDB49842BB7A26426A9A7', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 1, NULL, NULL, 2, NULL, 0, 'Peel about 12 to 15 large leaves off of the cabbage head.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'519BA24E2755744697ABE7651290A88C', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 2, NULL, NULL, 2, NULL, 0, 'Drop the cabbage leaves into a large pot of well salted boiling water; cover and cook for 3 minutes. Drain well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E47857D58CA64F8F9AA09391DE5FB3', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 3, NULL, NULL, 2, NULL, 0, 'Position a rack in the center of the oven and preheat to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF460C9BF42BD48B962AA0E15FFD952', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 4, NULL, NULL, 2, NULL, 0, 'For the filling, combine the ground beef, rice, onion, egg, milk, salt, and pepper in a medium bowl. Mix well and divide into 12 portions.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5C154D8312E8441B3CB25E8CF297DDE', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 5, NULL, NULL, 2, NULL, 0, 'Place a portion of the beef mixture (about 2-1/4 oz.) onto the center of each cabbage leaf.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD706AF2F37E264AA164DED3A44B9526', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 6, NULL, NULL, 2, NULL, 0, 'Roll the leaf around the filling, burrito-style, folding in the edges to seal it in.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E081CCE6416BE64B98469A87CDC171DD', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 7, NULL, NULL, 2, NULL, 0, 'Fasten the rolls with toothpicks. Place the rolls in a single layer in a large baking dish or an oven-safe Dutch oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2532D7B811AE9A4D945D86CF7BFE84E1', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 8, NULL, NULL, 2, NULL, 0, 'For the sauce, combine the diced tomatoes, tomato sauce, sugar, vinegar, and 1/2 cup of water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2353BBB1C8F5DD49B42FE1C8D6C5BDE1', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 9, NULL, NULL, 2, NULL, 0, 'Pour over the cabbage rolls. Cover with a lid or aluminum foil (carefully avoiding the toothpicks) and bake in the preheated oven for about 1 hour until the cabbage is tender and the sauce is bubbling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A6280999176924DB163B92D107D3DD9', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 10, NULL, NULL, 2, NULL, 0, 'Remove the rolls with a slotted spoon and discard the toothpicks.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FFF84D6590AC942A2180F0B66FA2EF7', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 11, NULL, NULL, 2, NULL, 0, 'Carefully pour the juices into a medium saucepan and place over medium heat. Stir the cornstarch with 1/4 cup cold water until dissolved. Add to the sauce and bring to a boil. Reduce to a simmer and cook, whisking constantly until thickened.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6632DBDDD9C9204799B2153C090029B8', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 12, NULL, NULL, 2, NULL, 0, 'Serve the cabbage rolls topped with the sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'845F7FC7EB6FEF48A6E8E56233B23F5B', X'5664928BAD66BE43952E118632DCFCEE', X'5664928BAD66BE43952E118632DCFCEE', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E515F0A9C880E49A8D102FD75618B9D', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBAB41375B85642880FC64C6AEB3DEF', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 1, NULL, NULL, 2, NULL, 0, 'Sift the flour to bring in more air and combine with sugar, baking powder, and salt in a large bowl until well mixed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF1E2CCF1C03A49A920EDB133AB2063', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 2, NULL, NULL, 2, NULL, 0, 'Mix in the soy milk or milk substitute of choice and oil and beat together until batter is smooth. If choosing to use the apple cider vinegar tablespoon remember to remove one tablespoon of the milk substitute before adding it to the mix to avoid a runny batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C61DA12944C069418DF6B4C2C991A337', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 3, NULL, NULL, 2, NULL, 0, 'Drop 1/4 cup of batter at a time onto a hot oiled griddle or a well greased frying pan over medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83BB3C88D31CA84382DAA8BC8896A2D5', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 4, NULL, NULL, 2, NULL, 0, 'When bubbles appear on the surface of the pancake, approximately 3 minutes, flip and cook the other side for another 2 minutes.?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8DAB6DA8653BC4B85FAB7049E0629AC', X'DB9501010DC8B544840D5C589FE99E25', X'DB9501010DC8B544840D5C589FE99E25', 5, NULL, NULL, 2, NULL, 0, 'Serve with your favorite toppings and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1D3F0FFBA320A4C8FA92C8C9D8C7F7D', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F99ED825258524F82E9224A78EDB7C4', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 1, NULL, NULL, 2, NULL, 0, 'About 30 minutes before you plan to start roasting, take the pork loin out of the fridge and let it sit at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB4ED48B7BBDC34AA45A46415C8E4F6D', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E35AD36CF2F584DACEF56E19FE76224', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the oil, garlic, salt, pepper, sage, rosemary, and thyme, and mix until a paste forms.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A84D41E1256C4CA14603634421A9C1', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 4, NULL, NULL, 2, NULL, 0, 'Dry the pork loin with paper towels and rub it all over with the seasoning paste. Set it on a rack in a shallow roasting pan. Insert an oven-safe meat thermometer into the very center of the roast on a slight angle. If you''re using a digital thermometer with a temperature alert function, set it to 135 F. Transfer the pork to the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C349D1288D6644B8D4C312523500321', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 5, NULL, NULL, 2, NULL, 0, 'Roast for 15 minutes, then turn the oven temperature down to 300 F. Let the loin continue roasting for another 40 to 60 minutes or until the internal temperature reaches 135 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A8A648434DB01418CD268848845DCB6', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 6, NULL, NULL, 2, NULL, 0, 'Take the roast out of the oven and cover it with foil. Let it rest for 10 to 15 minutes; during this time, the roast''s internal temperature will increase to around 140 F, which means it is ready to slice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AC6DBD22C8FFA41805F649724093424', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', X'F5F1F6E313CA5B4EB34EDFED3D13FC54', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3B05C802EB764D90D4BD5693265F60', X'BDEE41D74ABC124F85190C3ABA70E16B', X'BDEE41D74ABC124F85190C3ABA70E16B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C41A7BED195048826B73296F4F7B60', X'BDEE41D74ABC124F85190C3ABA70E16B', X'BDEE41D74ABC124F85190C3ABA70E16B', 1, NULL, NULL, 2, NULL, 0, 'Toast two slices of bread in the toaster until they are lightly toasted.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31164DD1EDFEBE418FEEB17D3859774A', X'BDEE41D74ABC124F85190C3ABA70E16B', X'BDEE41D74ABC124F85190C3ABA70E16B', 2, NULL, NULL, 2, NULL, 0, 'Remove the toast from the toaster, and top one slice of bread with the thick slices of fresh tomato. Then, sprinkle the dash of salt and ground black pepper over the tomatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51773402CA9C034EB7C67CA5055024A7', X'BDEE41D74ABC124F85190C3ABA70E16B', X'BDEE41D74ABC124F85190C3ABA70E16B', 3, NULL, NULL, 2, NULL, 0, 'Place the avocado slices on the second slice of toast and use a knife to gently spread the avocado on the bread. Then place this slice of toast, avocado side down, on the tomato sandwich.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F778CE61237E64CBD26284343E1F4AB', X'BDEE41D74ABC124F85190C3ABA70E16B', X'BDEE41D74ABC124F85190C3ABA70E16B', 4, NULL, NULL, 2, NULL, 0, 'Slice the sandwich in half, if you desire, and serve immediately. This sandwich is delicious on its own but if you''re looking for something crunchy on the side, you can''t go wrong with carrot sticks. Leave the chips for your splurge day.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EA63970D2D6F544BE870FB51F565173', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E8D997F4AC624BB8F2A8054DA1F069', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 1, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut them into 2-inch cubes. Put the potatoes in a large saucepan and cover with water. Add 2 teaspoons of salt to the water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60F011446CEF5144A09358D07D987E8A', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 2, NULL, NULL, 2, NULL, 0, 'Place the saucepan over high heat and bring to a boil. Reduce the heat to medium-low and cover the pan. Continue cooking for about 20 to 25 minutes, or until the potatoes are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9D751F57616CE4D8BA09C98B0EF9607', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, just before the potatoes are done, heat the milk in a small saucepan over medium-low heat until hot. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70905D9E61BACC40B2F2483699B96811', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 4, NULL, NULL, 2, NULL, 0, 'Drain the potatoes and place the pan over the heat for about 20 to 30 seconds longer, or just long enough to dry the potatoes. Remove the potatoes from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AF312CAE4F1A14292624277867408B9', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 5, NULL, NULL, 2, NULL, 0, 'Break the potatoes up with a potato masher. Alternatively, for a smoother texture, put them through a potato ricer first.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B37A3B55E1ED844AAC1E873681A5A5B', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 6, NULL, NULL, 2, NULL, 0, 'Add the warmed milk, melted butter, and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78261D1E48159847BC4E1F7CE3F307EE', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 7, NULL, NULL, 2, NULL, 0, 'Continue mashing until smooth, adding salt, to taste, and more milk, as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66DB065BBC477A44A7E4D8B050C1C7E2', X'0E8D185AFDE7EB4D80C44628772430A1', X'0E8D185AFDE7EB4D80C44628772430A1', 8, NULL, NULL, 2, NULL, 0, 'Transfer the potatoes to a serving bowl and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE6518B472C7548B01B3577D98E71C7', X'712EAA1004D9E44AB076CCDD91E98C86', X'712EAA1004D9E44AB076CCDD91E98C86', 0, NULL, NULL, 2, NULL, 0, 'Place the walnuts on a cookie sheet and toast in a preheated oven at 350 F for 3 to 5 minutes. Remove, let cool and roughly chop.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C555FA33F2397149923434549EA6CA11', X'712EAA1004D9E44AB076CCDD91E98C86', X'712EAA1004D9E44AB076CCDD91E98C86', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, toss together all the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'155F8BEB9A55A34C9A330F9F0573EFF8', X'712EAA1004D9E44AB076CCDD91E98C86', X'712EAA1004D9E44AB076CCDD91E98C86', 2, NULL, NULL, 2, NULL, 0, 'Serve plain or on a bed of romaine lettuce or mixed greens.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ACA733152E13B4BA4F5E9ADF0F9F3F1', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'133B7F88DA2C9C48B7E51C217FA17D03', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 1, NULL, NULL, 2, NULL, 0, 'Peel the potatoes. Cut generous 1/2-inch slices, then cut again into fries the same width. Place the cut potatoes into a colander and rinse under cold running water until the water runs clear.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE4D55AFCFA834AA1DE0652236B0A9D', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 2, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil, add the fries, and cook for 4 minutes. Drain, then spread them onto either a clean kitchen towel or paper. Pat to dry thoroughly and leave to cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D6AB557E9C47C48BF88639DFCF98366', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 3, NULL, NULL, 2, NULL, 0, 'In a large pot, melt the duck fat and heat to 375 F. If you don’t have a thermometer, the fat is hot enough when you drop in one fry and it sizzles and floats to the surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A8F47FC4A21649B91DAFF558BF3C59', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 4, NULL, NULL, 2, NULL, 0, 'While the fat heats up, line a large roasting tin with paper towels and have a slotted spoon on hand.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F5ECC80E438D4B85A36378D11557D8', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 5, NULL, NULL, 2, NULL, 0, 'Put only enough fries into the hot oil to form a single layer. Once they are in, turn the heat up slightly to maintain 375 F as the temperature will drop slightly. Let the fries sizzle and rise back the surface, then cook for 4 to 5 minutes, stirring from time to time, until they are starting to brown. Scoop them up with the slotted spoon and lay onto the paper towels to drain.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02DEEB8CEFB7A54CAFFC80318983535B', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 6, NULL, NULL, 2, NULL, 0, 'Wait for the temperature of the fat to rise again and add another layer and repeat. Continue until you have partially cooked all the fries. At this point, you can keep the fries to finish off when ready to serve by covering with a tea towel and popping the tray into the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B60B78A7E388C49B466B0310EE72800', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 7, NULL, NULL, 2, NULL, 0, 'Once you are ready to serve, reheat the fat as before and prepare another dish with a kitchen towel. This time, you can add more fries than before, but not so many that the pan is crowded. The fries will crisp up and turn golden within minutes, so keep an eye on them. As soon as they look like they are going from golden to dark, whip them out and drain on paper towels. Keep warm in the oven until you have cooked the rest.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0E0A10B76ED5840A0EAE7C8BA1C0646', X'6162CD38BD4EB341A1CB4BE2A66C751B', X'6162CD38BD4EB341A1CB4BE2A66C751B', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately sprinkled with a little salt. Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BD3912A179F4841BA10028FFF9B798B', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E48501E48F5A5E46AFFE5AD4368B5EEA', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 1, NULL, NULL, 2, NULL, 0, 'First, in a large saucepan, heat the vegetable oil over medium heat. Next, add the cucumbers, garlic, and onions to the pan and heat just until the onions are transparent, about 6 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2BF192DF27BD84CA5F3391A270A8B73', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 2, NULL, NULL, 2, NULL, 0, 'Next, add the vegetable broth to the pan, and simmer until the cucumber is fully soft, about 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2075DC8F370A4D4C830CAD5CCF6302E9', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 3, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat. Carefully transfer the mixture to a blender or food processor and then carefully blend just until the mixture is completely smooth. While the mixture is still warm, stir in the fresh dill and season with fresh ground black pepper, to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAD70C5EA0268A48AD5D61C16A397DCE', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 4, NULL, NULL, 2, NULL, 0, 'Place the entire mixture in the refrigerator and allow to chill until fully cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B483A24D356884F86A23E0F87CA539D', X'A801C4C6194130499C3E72C5918FD5F5', X'A801C4C6194130499C3E72C5918FD5F5', 5, NULL, NULL, 2, NULL, 0, 'Stir in the soy milk just before serving. Taste, and adjust seasonings to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A39CEA03A0ED74CBB597B2043A67E38', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'733E15DA5946B345BD19E8B0CB164292', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 1, NULL, NULL, 2, NULL, 0, 'Cut the potatoes into 3/4-inch sticks. Leave the skins on for a more rustic look as well as saving time. If you don’t like the skins, you can peel them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEB379CACC982943889F3D677EC545C3', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 2, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a large bowl immediately after cutting. Cover them with cold water and soak them for at least 30 minutes or up to 12 hours. If you''re soaking for a longer period, make sure to cover them with plastic wrap and place them in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E467359AED81C439E2E57B0D4134462', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 3, NULL, NULL, 2, NULL, 0, 'Drain the potatoes and place them on a tray lined with paper towels. Pat them completely dry. Toss the cornstarch, paprika, salt, and black pepper in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC7683531AF8E478634C7F544D61AE3', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 4, NULL, NULL, 2, NULL, 0, 'Add the potatoes to a large bowl. Drizzle olive oil over the top and then toss them to completely coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A75DB600D583254A9BCC700CFD473E00', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the cornstarch mixture over the top of the potatoes and toss them to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C85E0CC32CA7F941ACC5B937D3191CCE', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 6, NULL, NULL, 2, NULL, 0, 'Add the French fries to the basket of the air fryer. Fry at 360 F for 25 to 30 minutes, pausing the air fryer to toss the fries every 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'395FBAEC5F282E44A0E21D5276B9C2CA', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 7, NULL, NULL, 2, NULL, 0, 'Increase the temperature to 425 F and cook for another 5 minutes until they are golden brown and crisp.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F412D6C3AFBEB4EAD62D3110B55EE19', X'C2761DCC93DA254395F27CDA0543F26C', X'C2761DCC93DA254395F27CDA0543F26C', 8, NULL, NULL, 2, NULL, 0, 'Serve the fries alongside ketchup or your favorite dipping sauce. Sprinkle on more salt if you wish or top with chopped parsley or chives.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1125A57D6C29AD4AB7F32EFD5AC6BB10', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD6A2FEA696744EA67FE720D104D41B', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 1, NULL, NULL, 2, NULL, 0, 'Prepare a gas or charcoal grill to medium heat: You should be able to hold your hand about an inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat. If you are also using the grill to cook something else that requires more heat, place the peaches around the edges; they''re cooler. Or cook dinner, let the fire cool down, and then grill the peaches.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81B6C0C4E0FEA48A7CABE1CC5C5C716', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 2, NULL, NULL, 2, NULL, 0, 'Cut the peaches in half and pit them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D23E9A0C21FA45BB4F24AFF380677C', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 3, NULL, NULL, 2, NULL, 0, 'If you want to add cinnamon and/or sugar, grill entire peach halves; otherwise, you can cut them into wedges, if you like. Peeling the peaches isn''t necessary for the grill, but if you want to, you can.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC307F886C93BF41B5B773736F751D26', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 4, NULL, NULL, 2, NULL, 0, 'Lightly brush the peaches with the oil, being particularly aware of coating the cut sides well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7566A956C7BDB249943BEF9505817FED', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 5, NULL, NULL, 2, NULL, 0, 'Set the oiled peaches on the medium hot grill. Make sure they aren''t touching so the hot air of the grill can circulate around each piece.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D0AFAF2C7A41C4F92AFB0C9B6BD76FF', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 6, NULL, NULL, 2, NULL, 0, 'Cook the peaches until grill marks form, turning as necessary to mark all cut sides for the best presentation. Turn the peach halves over, sprinkle with the cinnamon and sugar, if using. Cook until the peaches are tender, but not falling apart, about 8 minutes total.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4413F7A1943CE5449B15EC4C9DD7E435', X'ECB578B98B9A1549BA1BABB6E25D338B', X'ECB578B98B9A1549BA1BABB6E25D338B', 7, NULL, NULL, 2, NULL, 0, 'Serve grilled peaches hot, warm, or even at room temperature. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42A9AEB75C60D64792F93BB85E2821E0', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 450 F. Trim off and discard the stem and root ends of the rutabaga. Peel the rutabaga.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'537F1AC20EBC1E419E1D8B9BAF8C5341', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 1, NULL, NULL, 2, NULL, 0, 'Use a sharp knife or a kitchen mandoline, if you have one, and cut the rutabaga into evenly sized sticks or fries. If using a knife, first cut the rutabaga in half, then cut each half into even 1/4-inch thick slices. Stack the slices and cut them the other direction into 1/4-inch thick sticks. Repeat with the remaining half of the rutabaga. If using a mandoline, set it up for wide julienne and push the rutabaga through—note that rutabagas have a fairly thick or dense texture, and it can take some force to get them through the julienne setting on mandolines.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD45EA1B5721E0418906C74F6BC9BD16', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 2, NULL, NULL, 2, NULL, 0, 'Put the rutabaga sticks in a large bowl and drizzle with the oil. Toss the sticks so they are thoroughly and evenly coated with the oil. Sprinkle with the pepper and/or garlic powder, if using them, and toss to distribute them evenly over the rutabaga.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ADF71AE8656E142A90E9BDA538C6095', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 3, NULL, NULL, 2, NULL, 0, 'Spread the rutabaga sticks in a single layer on a lightly oiled baking sheet or a baking sheet lined with parchment paper. Sprinkle the sticks evenly with the salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FAC40662521D14983D6A5174A4DC8A7', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 4, NULL, NULL, 2, NULL, 0, 'Bake the rutabaga sticks for 20 minutes. Remove the pan from the oven and turn over the rutabaga sticks. Return to the oven and bake until they are tender and well browned, about 40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DB4B832C08C0245B7B6EBAC8F4B87FB', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 5, NULL, NULL, 2, NULL, 0, 'If the rutabaga sticks towards the edges are more browned earlier, you can take them off the pan and return the rest to brown all the way. The appeal of these baked fries is the contrast between the crispy edges and the sweet tender centers, so don''t skimp on the cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FEF7BAA5DC94D49B84D05A79C604B29', X'EE0F063A782C2B41A07CA4607D4E4D5E', X'EE0F063A782C2B41A07CA4607D4E4D5E', 6, NULL, NULL, 2, NULL, 0, 'Serve the fries hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7A366E4516F044B9AF4875835D4085C', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2C9FDE3CBE65F4C9C443FFA3E25B874', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'923E3ADEA1F3EA48ADD8D60B5BB45EB3', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 2, NULL, NULL, 2, NULL, 0, 'Cut each onion in half and then thinly slice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA507B98DC0B9A499782420BAE8C3BB8', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 3, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter in a medium-sized sauté pan over medium-low heat. Add the onions to the pan and spread them evenly. Sprinkle salt over the onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD177B06FBF3D74C8347F0C982385FAF', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 4, NULL, NULL, 2, NULL, 0, 'Stir the onions every few minutes until they are caramel-colored, fragrant and completely soft, about 30 minutes. Set the onions aside and begin the pasta.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78EA6450013D0948AEBFF3E22D306C57', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 5, NULL, NULL, 2, NULL, 0, 'Cook the orecchiette according to package directions, leaving the pasta al dente. Set the pasta aside and make the cheese sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6172CCFFE363334D8BB2CE89F054C672', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 6, NULL, NULL, 2, NULL, 0, 'Add the butter to a large pot over medium heat. Once the butter has melted, whisk in the flour. Cook the butter and flour mixture for about 3 minutes, stirring frequently, to cook off the flour taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'826648D9FE418A45A79B5C596FA5E81D', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 7, NULL, NULL, 2, NULL, 0, 'Add the milk and whisk until combined. Heat the milk mixture until it is hot, but not boiling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'876E86F069694C4F9D6963FC1BD51CB0', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 8, NULL, NULL, 2, NULL, 0, 'Slowly add in the cheeses and whisk until they are melted. Add the salt and pepper and stir. Take off of the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDAED24C8012EA42974D4EEB3AEFEB66', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 9, NULL, NULL, 2, NULL, 0, 'Add the breadcrumbs to the melted butter and stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C57BF1310687CB4CBB72214568C9EF78', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 10, NULL, NULL, 2, NULL, 0, 'Butter a 9x13-inch casserole dish and add the prepared macaroni and cheese mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3565E809658048A8314A515AE44796', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 11, NULL, NULL, 2, NULL, 0, 'Top the macaroni and cheese with the caramelized onions. Cover the top with the buttered breadcrumbs and place in the oven on a center rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'769D6F4A6ADA0C42B8F8F396F05BA886', X'3CA72D38345A0747A7BC27A451D64E44', X'3CA72D38345A0747A7BC27A451D64E44', 12, NULL, NULL, 2, NULL, 0, 'Bake for about 20 minutes, until the top is golden brown and the edges are bubbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4BAF9C991D58A4BB10B2A4BC4B20C18', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 200 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5E4C23743F9B44DB3EC119074716FAF', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 1, NULL, NULL, 2, NULL, 0, 'Cut off both ends of the onions and peel off the skin.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B336B8A9024DB045A6E71A606623FBEB', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 2, NULL, NULL, 2, NULL, 0, 'Slice the onions into thick rings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0034FBA29E2D6C409F520A22914E66F9', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 3, NULL, NULL, 2, NULL, 0, 'Carefully separate the rings from each other. Set aside the innermost rings for another recipe. If the rings break, you can still use them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'346A8BF785F7BE42B83B8A7A029B765D', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 4, NULL, NULL, 2, NULL, 0, 'Prepare the 3 dredging bowls. Put the flour in 1 shallow bowl. Beat the eggs and milk in another shallow bowl. Add the panko breadcrumbs to a third shallow bowl. In a separate small bowl, mix the smoked paprika, onion powder, garlic powder, and salt, then divide the mixture evenly among each of the bowls. Whisk each bowl to disperse the seasonings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F4D5EAA07633B458D01643A439942F7', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 5, NULL, NULL, 2, NULL, 0, 'Dip the onion rings in the flour first. Completely coat both sides of the rings and the inner portion.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E12688B74024B2468496F84A95E9A780', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 6, NULL, NULL, 2, NULL, 0, 'Dip the flour-coated rings into the egg mixture. Make sure to completely coat them with the egg so you cannot see any of the flour. You may need to flip the rings a few times.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879A3ECCCCB6CF40AE22C28546E46FC7', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 7, NULL, NULL, 2, NULL, 0, 'Dip the rings in the breadcrumb mixture. To thoroughly coat them, use your fingers to press the breadcrumbs onto the rings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92C302F4865E3542B79FA99048C0028D', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 8, NULL, NULL, 2, NULL, 0, 'Generously spray the inside of the air fryer with cooking spray. Carefully place a single layer of the onion rings into the air fryer and spray the tops with cooking spray. Cook on 375 F for 10 minutes, flipping each ring halfway through the cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'387153F2E8A2A44093146F975EA718FA', X'A67BA764A50DF148A8F48950B6B8E625', X'A67BA764A50DF148A8F48950B6B8E625', 9, NULL, NULL, 2, NULL, 0, 'Transfer the first batch to a baking sheet and place in the oven to keep warm. Cook the second batch of rings. Serve with creamy dipping sauces, honey mustard, or ketchup if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86BA3E0C7968484BA03F82E537DF5E1E', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F24C3E54D1028A4AB712B1C198C8187C', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, dissolve the 2 tablespoons of salt in 1 cup warm water; stir until the salt is fully dissolved. Add 6 to 8 cups cold water. Set the brine aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEF678402B6CF749AD737F75406F44CC', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 2, NULL, NULL, 2, NULL, 0, 'Cut off and discard the stem end of the eggplant.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D14A54FB392DEC4A9CA4EF813A4B2129', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 3, NULL, NULL, 2, NULL, 0, 'Cut the eggplant into 3/4-inch thick slices (either diagonal, crosswise, or lengthwise).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D89F726754ADC948A557C2B3EFC4FD50', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 4, NULL, NULL, 2, NULL, 0, 'Place the slices in the saltwater brine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D82CEEC0D3A3D4BA5BCC8CE631E2568', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 5, NULL, NULL, 2, NULL, 0, 'Weigh the eggplant slices down with an upside-down plate; let soak for at least 30 minutes or up to an hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EC3296E92DF84E968428B25E4513DE', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat a charcoal or gas grill to medium-high heat. You should be able to hold your hand about an inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5673FF8A9E1C884A82EB2E42B5D086FC', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 7, NULL, NULL, 2, NULL, 0, 'Drain the eggplant and pat it dry with paper towels or a clean kitchen towel.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2601E39176950942941B8B3F2338CA09', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 8, NULL, NULL, 2, NULL, 0, 'Lay the slices on a large baking sheet or tray. Brush one side with olive oil and sprinkle with salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'887196D923FCB04588A778B51BCBCC3A', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 9, NULL, NULL, 2, NULL, 0, 'Place the slices with the oiled side on the grill. Close the lid if using a gas or electric grill and cook until grill marks appear; about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3740CEEAED181144BFD23A33A295617B', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 10, NULL, NULL, 2, NULL, 0, 'Brush the top sides of the slices with oil and sprinkle them with salt. Turn the slices over, close the lid of the grill, and cook until grill marks appear on the other side and the eggplant is very tender; about 5 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'690B8EF414A59841B169CCADFF182AAF', X'CCD02D8950C8CD42AE6148ECA86A97EC', X'CCD02D8950C8CD42AE6148ECA86A97EC', 11, NULL, NULL, 2, NULL, 0, 'Serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EFC2344C3949C429DC7FE213C70790A', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 0, NULL, NULL, 2, NULL, 0, 'Remove stem and blossom ends of squash and cut them into large chunks. Place in a large saucepan and cover with water. Bring to a boil over medium-high heat. Reduce the heat to low, cover the pan, and cook until squash is very tender, about 10 to 15 minutes. Drain thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CAC4816EDB9B84097F8E85A48C527E4', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mash the drained squash. Add the remaining ingredients to the mashed squash and blend well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB09F9C25532EE48A852F013C2117EB6', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 2, NULL, NULL, 2, NULL, 0, 'Cover the bowl and refrigerate the batter for a few hours or fry them right away.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AD8D6FE110A9429CFF51BB93F6A7CD', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 3, NULL, NULL, 2, NULL, 0, 'Heat about two inches of oil in a deep saucepan or deep fryer to 370 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DEA16FBD8AD944EB4C72A595593D72E', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 4, NULL, NULL, 2, NULL, 0, 'Drop squash batter by spoonfuls into the hot oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'000BA898B36A8C4EADBE1EE2052557E1', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 5, NULL, NULL, 2, NULL, 0, 'Cook the squash fritters until they are golden brown, about 3 to 4 minutes, turning them as they fry to brown all sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'848740D2368A174D933F967A5F978C48', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 6, NULL, NULL, 2, NULL, 0, 'Remove with a metal slotted spoon and drain on paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACE8B4B9868C9241802DBE292B7EE3C6', X'EF0CFE7F5264584380298D62239A1C7F', X'EF0CFE7F5264584380298D62239A1C7F', 7, NULL, NULL, 2, NULL, 0, 'Serve hot with Sriracha mayonnaise (see below) or another dipping sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD9A1F7D5685649B19776AE48F5ADCD', X'032FA073A5AB074BB4BC24D77C7CA6DF', X'032FA073A5AB074BB4BC24D77C7CA6DF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C03F57A7D1A254BAD1F12A214906AB1', X'032FA073A5AB074BB4BC24D77C7CA6DF', X'032FA073A5AB074BB4BC24D77C7CA6DF', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51998BA08522B64086893DBA76D2D903', X'032FA073A5AB074BB4BC24D77C7CA6DF', X'032FA073A5AB074BB4BC24D77C7CA6DF', 2, NULL, NULL, 2, NULL, 0, 'Toss the squash, shallot, thyme, olive oil, and salt together in a bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE8CF3E624E37E4C93EE9166016F9F02', X'032FA073A5AB074BB4BC24D77C7CA6DF', X'032FA073A5AB074BB4BC24D77C7CA6DF', 3, NULL, NULL, 2, NULL, 0, 'Spread the mixture out onto a sheet pan. Bake 15 to 25 minutes or until the squash is tender. Monitor it periodically by testing it with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4252EA37B02FB549B3AFB796DB0A268F', X'032FA073A5AB074BB4BC24D77C7CA6DF', X'032FA073A5AB074BB4BC24D77C7CA6DF', 4, NULL, NULL, 2, NULL, 0, 'Serve hot or at room temperature. Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91195EA0C991EC4EB55A8002EA265E3F', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AF2D2CC2EB8224BB4440C7D4AC1AD76', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 1, NULL, NULL, 2, NULL, 0, 'Trim and chop the zucchini, getting rid of the ends. Reserve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F92719EEA47FA848B9ECD6A92126C360', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 2, NULL, NULL, 2, NULL, 0, 'Heat a medium pot over medium-high heat and add oil, onions, and salt. Cook onions, stirring occasionally until translucent and tender, for about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A0058BB130464FBC974CE8AF63B8EE', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 3, NULL, NULL, 2, NULL, 0, 'Add zucchini and broth to the onions. Set the heat to high to bring to a boil. Reduce the heat immediately after boiling and maintain a steady simmer until zucchini is very tender, for about 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0D941FB3DA2E64F9D4D328C9742C4B7', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 4, NULL, NULL, 2, NULL, 0, 'Once the zucchini is cooked, turn off the heat. To avoid burns, let the soup cool for at least 20 minutes before blending. Working in batches if necessary, blend soup on high speed until very smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'907888B3F804C94D9CC5DD9E5640E380', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 5, NULL, NULL, 2, NULL, 0, 'Transfer pureed soup to a large bowl or sealable container. Cover and chill until cold, for at least 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89223B5709F9EE4FAD4AF777F07BB886', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 6, NULL, NULL, 2, NULL, 0, 'When ready to serve, stir with cream or yogurt, if using, and add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE66910E71D1042831AA017838FC1C7', X'1005A0CB115CE44C8CD02D138431A66E', X'1005A0CB115CE44C8CD02D138431A66E', 7, NULL, NULL, 2, NULL, 0, 'Decorate with fresh cilantro and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0264AFC82FC024D961B09E54FC56D39', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9155508F774ABF4D84A1E0057D90AC69', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 1, NULL, NULL, 2, NULL, 0, 'Grease a 2 1/2- to 3-quart casserole dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D64E7B21754E7641969BB0351D581CE7', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 2, NULL, NULL, 2, NULL, 0, 'Cook the spaghetti in boiling salted water following the package directions. Drain well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352600BE636431459656D1D97F6A8A21', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the cooked and drained spaghetti with 1 cup of cheddar cheese and 2 tablespoons of the Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14FD173640C35F43A323D9ED3833F7EB', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 4, NULL, NULL, 2, NULL, 0, 'Add the diced turkey and pimientos.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED3D50677C72C340B34DD699F1E3D562', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 5, NULL, NULL, 2, NULL, 0, 'In a skillet or saute pan, melt butter and saute the mushrooms and onions until they are tender and most of the liquid has evaporated; add to the turkey mixture along with remaining ingredients. Taste and add salt and pepper, as needed. Gently stir to combine ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D054B9D1C41F5543B1B1E3FF972973C9', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 6, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to the prepared casserole and sprinkle with remaining cheddar and Parmesan cheeses.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5191CF2FF7DEF14A9476B2551BCE65F9', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 7, NULL, NULL, 2, NULL, 0, 'Cover the baking dish with a lid or foil and bake for about 45 minutes, or until hot and bubbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7051F6C37061B5449339B9394F8EE261', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 8, NULL, NULL, 2, NULL, 0, 'If desired, place the casserole under the broiler (high heat) for about 4 to 6 minutes, or until the top is lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2918D5AA048EE4D849E035C2B5C473D', X'BE51B9EBB21C4D468FAD00EE56387471', X'BE51B9EBB21C4D468FAD00EE56387471', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83C1726EE385674B92EC92310CC3CDAF', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3410191E6AC2547B6CC549A3A530616', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 1, NULL, NULL, 2, NULL, 0, 'Warm the olive oil in a skillet over medium heat. Add the garlic and sauté for 1 minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B12699C7C4D184F97C7EAA38AE5FE77', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 2, NULL, NULL, 2, NULL, 0, 'Add the broccoli florets. Cook for 2 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'863DDD7B1D148F4EA549508EDE3F3EA6', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 3, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons water, then cover the pan and steam the broccoli for about 2 minutes until it becomes bright green and the water has evaporated.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE30EDD5E831540BAE6B0DD5E5E509E', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and cook for 1 additional minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A11E2F955EF88F4182EFBFBA5E601264', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 5, NULL, NULL, 2, NULL, 0, 'Add the herbs and salt to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EEB1AB41C04C3428CA7B6723B6EE8BC', X'624409364DC3C942AD96583035378AF8', X'624409364DC3C942AD96583035378AF8', 6, NULL, NULL, 2, NULL, 0, 'Stir gently and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBA9CFCBA613BD48B79E71F85D8FE8B0', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7A11390EB3E3C4199B5B5A9FD0F7705', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 1, NULL, NULL, 2, NULL, 0, 'Combine the flour and salt in a large shallow bowl or on a clean work surface. Make a well in the center, almost like a `bowl` of flour, to hold the eggs. Crack the eggs into it.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496F96CBA535354CB5C6DB40346DB66E', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 2, NULL, NULL, 2, NULL, 0, 'Use a fork to beat the eggs and then gradually start incorporating the flour into the eggs (as you beat them, they will slowly but surely take up some flour from along the edge of where the eggs meet the flour).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEF7A449D2914744ACF517CCC723A72D', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 3, NULL, NULL, 2, NULL, 0, 'Keep stirring and pulling in more flour until a solid dough forms. The dough will be sticky. Don''t worry, you''ll be working in more flour in a moment. Add too much flour now and the extra flour needed to roll out the noodles will lead to a dry rather than tender noodle in the end.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D4B5257AB9D94A95E55F0106C827DC', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a well-floured work surface. With well-floured hands, knead the dough, incorporating more flour as necessary to keep it from sticking to either the work surface or your hands, until the dough is smooth and firm and no longer sticky. This takes roughly 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E3B9A9DB6B7EC4E8ACB4E53064D62AE', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 5, NULL, NULL, 2, NULL, 0, 'Wrap the dough with plastic wrap and chill it for at least 30 minutes and up to overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA7886C3CC0D8E45BF553AD8894BFD1D', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 6, NULL, NULL, 2, NULL, 0, 'Divide the chilled dough into 2 pieces and work with one half of the dough at a time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BC78DBFAB162E46BCDDABAF40AF2F7E', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 7, NULL, NULL, 2, NULL, 0, 'On a well-floured surface, roll out the dough to the desired thickness (anywhere from 1/4 inch to paper thin, depending on your preference). Be sure to rotate, or otherwise move the dough, between each pass of the rolling pin to keep the dough from sticking to the work surface underneath. Sprinkle everything with flour—including lifting the dough and flouring the work surface again—as necessary to keep the dough from sticking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6EE65D9DB99344E8BC424FADCCA6AA9', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 8, NULL, NULL, 2, NULL, 0, 'Use a sharp knife or pizza cutting wheel to cut the noodles. You can make them as narrow or wide as you like but cut them as evenly as possible to ensure a uniform cooking time. If you fold the dough to cut, make sure it is floured so it does not stick together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F289E654E0A10945A3BFDBB0D2D5B00A', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 9, NULL, NULL, 2, NULL, 0, 'Lay the noodles on a cooling or drying rack and let them sit until ready to cook. Repeat rolling and cutting with the remaining half of the dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0DB2C424679848A6B76A344CC86164', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 10, NULL, NULL, 2, NULL, 0, 'Boil the noodles in well-salted water until tender to the bite (2 to 10 minutes for fresh noodles depending on the thickness). Drain and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4091B5B58181E74B97CB2983B1320EEE', X'AFF0CCEF70659D4F960F65DA254CED3F', X'AFF0CCEF70659D4F960F65DA254CED3F', 11, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D554A30F4407E409CA2D5C697A0F756', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 0, NULL, NULL, 2, NULL, 0, 'Slice off the stem and lower ends of the cucumbers and compost or discard them. Slice the cucumbers lengthwise. Scoop out the seeds with a small spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CE25A121AD2BD408E98FEB662E529FE', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 1, NULL, NULL, 2, NULL, 0, 'Chop the cucumbers very finely, or pulse a few times in a food processor. You should have about 4 cups of finely chopped cucumber. Transfer to a large, non-reactive bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0165DAA06A43F54FABB702ACFB2DE398', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 2, NULL, NULL, 2, NULL, 0, 'Peel the onion and slice off the ends. Slice off the stem ends of the bell pepper and remove the seeds. Finely chop the onion and bell pepper, or pulse a few times in a food processor. The vegetables should be in tiny pieces, but not totally pulverized into a purée. You should have about 1 1/2 cups of finely chopped onion and bell pepper. Add to the cucumber in the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FE921657C4F7C47ADA3780D3F86E2A2', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 3, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons of kosher salt to the vegetables and mix well. If that seems like a lot of salt, don''t worry: You''ll rinse most of it off later. The salt is there to draw water out of the vegetables, a step that results in better texture and flavor in the finished relish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDA11FE1872C764FAC2FE4DC13382295', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl of vegetables and leave it in the refrigerator for 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25DCE271F955234AB9877286237A8EF7', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 5, NULL, NULL, 2, NULL, 0, 'Transfer the vegetables to a finely meshed sieve or strainer and let them drain for a couple of minutes. Rinse them well with cool water and let drain again. Get out even more of the liquid by squeezing with your clean hands (squeeze hard), or by pressing the vegetables against the sieve with the back of a wooden spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B570DB0ED5E258429123A7DBC49DC99B', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 6, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, whisk the cornstarch or ClearJel into the cool or room temperature vinegar. Stir in the sugar and spices. Bring to a boil over medium heat, stirring often to help dissolve the sugar and prevent lumps.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'917D2E6A768FDD4DA4A4CC30DBF2653E', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 7, NULL, NULL, 2, NULL, 0, 'Once the syrup is boiling and becoming translucent, add the vegetables. Simmer the relish for 10 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'831C8F3AF338F841A57C5A3E724BDF85', X'C46A96E1E3D3F441A2B13B2B2313597B', X'C46A96E1E3D3F441A2B13B2B2313597B', 8, NULL, NULL, 2, NULL, 0, 'Scoop the hot dog relish into clean, hot canning jars leaving 1/2-inch of head-space. It is not necessary to sterilize the jars for this recipe. Screw on canning lids. Process in a boiling water bath for 10 minutes. Once the jars are processed and sealed, sweet relish will keep at room temperature for up to 1 year. It is still safe to eat after that, but the quality will decline. Once opened, store the jars in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CB5E371324C834F874CA80C0E0992BA', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B626CE74136F44BB0B2D2DBFA7FA0D0', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 1, NULL, NULL, 2, NULL, 0, 'Add the carrots, onions, ginger, and curry powder to a soup pot and cover with the vegetable broth. Simmer for 20 to 25 minutes, until carrots are soft.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9977DE44BF7AD4A881F1EE9E1B5D563', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly, and then puree in a blender, working in batches if needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A18DE670CD554DAFEB72C4322A97C6', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 3, NULL, NULL, 2, NULL, 0, 'Return to the heat and stir in coconut milk until well combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A64A994136A149BECE7A2BC9AB82F1', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 4, NULL, NULL, 2, NULL, 0, 'Season generously with sea salt, to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B85A9FA3B42C748BC7F028F45F20B79', X'108CF9914EB179418A1664DCE9959654', X'108CF9914EB179418A1664DCE9959654', 5, NULL, NULL, 2, NULL, 0, 'Serve hot or chill until cold. This carrot soup will thicken as it cools, so if eating this vegan carrot soup cold, you may want to add a bit extra liquid before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4396BF41C18E5F40ABE3646602124AE8', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F19469EBE1FD342B3976AC69861348B', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B183AAF52940E4A8C6B92944FF95361', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 2, NULL, NULL, 2, NULL, 0, 'Spray a 2 1/2- to 3-quart casserole dish with cooking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB2CB90E584FE74D8DB27B2B0D19A6E7', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 3, NULL, NULL, 2, NULL, 0, 'Combine the noodles, soup, peas, cheese, tuna, and milk. Add salt and freshly ground black pepper. Spoon into the prepared baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'806094FE4881974E9C9FDE7F394B3DF8', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, toss the breadcrumbs with the melted butter and sprinkle over the top of the casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF0B51533D15674BB75FA561710C2EE7', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes until hot and bubbly and the breadcrumbs are browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C700976D6597254BA844E3C854BE0A46', X'18ABAAC1BFEEB74D94C324F87037E308', X'18ABAAC1BFEEB74D94C324F87037E308', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139FCBD611EEEB44949CD005BFD98503', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F1B4FD006A1D47BF18B143F7A536CD', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry the oranges. Using a sharp vegetable peeler or paring knife, remove the brightly colored zest—and only the brightly colored zest—from the oranges. Be sure to leave behind any and all of the white pith directly underneath as it is very bitter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E44ED057A1A6B4CA4ACCAD68A14E400', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 2, NULL, NULL, 2, NULL, 0, 'Chop the zest. Bigger pieces for chunkier marmalade, ribbon-like strips for a more spreadable result. Set the zest aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72978E701AC05143BBCCB6FBD9EB46B3', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 3, NULL, NULL, 2, NULL, 0, 'Cut the ends off the zested oranges and then, working with one orange at a time, cut off the thick white pith from around each orange.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4616F1EDE39C5848970B42F6D6A11E47', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 4, NULL, NULL, 2, NULL, 0, 'Working over a bowl to catch the juices, hold a fully peeled orange and use a sharp knife to cut out each segment from the membrane holding the sections together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE995AD6B9185944A2AD378CA7874BC9', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 5, NULL, NULL, 2, NULL, 0, 'Squeeze any juice out of the membranes once you''ve cut out all the fruit. Set the membrane aside, along with any seeds—the pectin in these will help `set` the marmalade later. Discard the ends and white pith.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78D5E9C7ABAEF242A6B353D936F0F718', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 6, NULL, NULL, 2, NULL, 0, 'Combine the zest, fruit, juice, sugar, and water in a large heavy pot and bring to a boil. Stir just until the sugar dissolves and stop stirring.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DF7E5F3AA4C2442889E17A87DCB0B62', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, lay a double layer of cheesecloth in a medium bowl and put the membranes and seeds on top. Lift up the corners and tie the cheesecloth into a bag to hold the membranes and seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5FE08D6638F264DA633780BFD8DF4D5', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 8, NULL, NULL, 2, NULL, 0, 'Add this `pectin bag` to the pot. Bring the mixture tso a boil, and place a couple of small plates in the freezer to chill them.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B09AE00E8695642BD0EEFD10923453A', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring the marmalade to 220 F and hold it there for 5 minutes. Be patient, this can take quite a while. Do not stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FDAE9380427184D82F7220C18AA577C', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 10, NULL, NULL, 2, NULL, 0, 'Put a dollop of the mixture on a chilled plate. Swirl the plate to spread the mixture a bit and drag a spoon through the mixture. If your marmalade is set, your spoon will leave a trail, and you''ll still be able to see the plate where you dragged the spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DE646252550A34AB1CA5AC16F513D9A', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 11, NULL, NULL, 2, NULL, 0, 'Remove the pectin bag, squeezing any marmalade out and back into the pot, and discard the bag. Take the marmalade off the heat and let sit for 5 minutes. Set up clean jars (if canning, they should be hot and sterilized) next to the pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2254EDE64B18464DBA94D478A4355B2A', X'4F151B040DB5864BA08A24DA93243753', X'4F151B040DB5864BA08A24DA93243753', 12, NULL, NULL, 2, NULL, 0, 'Stir the marmalade to distribute the zest evenly in the mixture. Use a ladle to transfer the marmalade into the jars, leaving 1/2 inch of headspace at the top of each jar. Put the lids on the jars, and refrigerate. Marmalade will last for up to 4 weeks. Or, you can proceed with canning, as directed below.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D335FE396C15A4D84AA9E3AD34066CA', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8904C7BBF4E2346B49B701F94FB9C54', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 1, NULL, NULL, 2, NULL, 0, 'Bring the water to boil in a medium saucepan. Add the salt and butter and allow the butter to melt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78BD3A9FEFE2D84E879A60524ACD7903', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 2, NULL, NULL, 2, NULL, 0, 'When the water has returned to a boil, stir in the rice. Let the water return to a light simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88B6D099509174FBDFDAB19B5FD86D9', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 3, NULL, NULL, 2, NULL, 0, 'Stir again, cover the pot, and turn the heat down to low. Keep the rice simmering slightly, and keep the pot covered (you may have to peek after a few minutes to make sure the heat is at the correct temperature, but then let it cook, covered).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'233A18D636A47344ABF47CC1D08CCB09', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 4, NULL, NULL, 2, NULL, 0, 'Start checking to see if the rice is tender and all of the liquid is absorbed in about 17 minutes. It may take up to 25, especially if you are making a larger quantity of rice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFCB46139D8B1D4AA91C8A044DFFF141', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 5, NULL, NULL, 2, NULL, 0, 'When the rice is cooked, turn off the heat and let it sit for another couple of minutes to finish absorbing any liquid.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'986ADCC3BC31654490223B9885292872', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 6, NULL, NULL, 2, NULL, 0, 'Take off the lid, fluff the rice with a fork, and let it sit for another 2 minutes so that some of the excess moisture in the rice dries off.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02FEFD9AE1563B48955A6B06DC164412', X'31D4BFE69D9AF0408F4DD7108D85425D', X'31D4BFE69D9AF0408F4DD7108D85425D', 7, NULL, NULL, 2, NULL, 0, 'Serve with your side of protein and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8156337B8866804A94A0CE680AF59AE6', X'3349467245FC96458064B83224C307D4', X'3349467245FC96458064B83224C307D4', 0, NULL, NULL, 2, NULL, 0, 'To prepare the round zucchini, first cut off any of the remaining stem. Then cut it into quarters, do so from the top so that it resembles something like apple wedges. Each of the four quarters should now be sliced into even-sized pieces. Prepare the remaining ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCCC6E36DD2A0A4D9C293B2819236EF8', X'3349467245FC96458064B83224C307D4', X'3349467245FC96458064B83224C307D4', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large pan. Once the oil is hot add the onion and sauté it until soft and translucent. Next add the chiles, zucchini pieces, minced garlic, and dried epazote if using. Sprinkle a little bit of fine sea salt over the vegetables and sauté until the chiles and zucchini have softened to your liking. Taste and if necessary add a bit more salt. Serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB15819BA2CC954F8F996C6EBFBAAFE0', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400 F / 200 C / Gas 6.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'688D0F0E0D81EE489AC175972E3C5D8E', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 1, NULL, NULL, 2, NULL, 0, 'Slice the ends off of the sprouts and remove loose, discolored, or damaged leaves from the outside. Cut each sprout in half lengthwise and put them in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E5B2528A0FCD148B3E6886FDB129011', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 2, NULL, NULL, 2, NULL, 0, 'In a small saucepan over medium-low heat, heat the olive oil. Add the sliced garlic, lower the heat, and cook for about 4 to 5 minutes. Don''t let the garlic brown or burn. It should be gently bubbling around the garlic pieces. Remove the garlic with a slotted spoon and discard.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6759F02AE723C4CB05EFB7F5106483D', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 3, NULL, NULL, 2, NULL, 0, 'Pour the olive oil over the sprouts and sprinkle with the garlic powder, about 1/4 teaspoon of kosher salt, and 1/4 teaspoon of freshly ground black pepper. Toss to coat the sprouts with the oil and seasonings.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9CF2641D750445BD64440E80BCCFE2', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 4, NULL, NULL, 2, NULL, 0, 'Arrange the sprouts in a single layer in a large rimmed baking pan, such as a half-sheet pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E8E2CD4674D4344A40A6CCB1587F336', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 20 minutes, shaking the pan a few times. Turn the sprouts and continue roasting for about 10 to 15 minutes, until they are tender and browned on the outside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B39ACAC01A20E4DBF41CBCE740DE3E1', X'F824D3FAEAD73A409CE5BE7EB4764F98', X'F824D3FAEAD73A409CE5BE7EB4764F98', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer to a serving dish. Sprinkle with more kosher salt for serving, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93FB21896B3FC64EB1C80AF43AC295D0', X'CD00CD24E423734891A55516B9B09F20', X'CD00CD24E423734891A55516B9B09F20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C9F8FA3D20AF849B8CA834572C83019', X'CD00CD24E423734891A55516B9B09F20', X'CD00CD24E423734891A55516B9B09F20', 1, NULL, NULL, 2, NULL, 0, 'Combine everything in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D10E8D234E4454D86C6621688CE24EE', X'CD00CD24E423734891A55516B9B09F20', X'CD00CD24E423734891A55516B9B09F20', 2, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into a greased 1 1/2-quart casserole dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'068CC08B4F49E645833796517DCAE134', X'CD00CD24E423734891A55516B9B09F20', X'CD00CD24E423734891A55516B9B09F20', 3, NULL, NULL, 2, NULL, 0, 'Cover and bake 30 to 40 minutes, until bubbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F073937A64805147B980F583806350A8', X'CD00CD24E423734891A55516B9B09F20', X'CD00CD24E423734891A55516B9B09F20', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC4E550DD22AF4CB319B28C387360DE', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the milk with the egg and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96DE6AF517F57A4A853031928B88423D', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 1, NULL, NULL, 2, NULL, 0, 'To the bread machine pan in the order suggested by your machine''s manufacturer, add the milk mixture, flour, sugar, salt, butter, and yeast.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F2E88CD496B184585244ED177BF9853', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 2, NULL, NULL, 2, NULL, 0, 'Start the machine on the dough cycle. Add the blueberries and ground cinnamon at the beep.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73750DFB1913D84AB9AC02D09B0E17CA', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 3, NULL, NULL, 2, NULL, 0, 'Grease a 9-inch round baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EB9EF4E8883A34D9E5C8520CFFC103D', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured surface and punch it down. Knead a few times, adding a bit more flour, if needed, to keep it from sticking to the surface and hands.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD19959429498240B11226493E5EA4F4', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 5, NULL, NULL, 2, NULL, 0, 'Shape the dough into 16 uniform balls and place them side-by-side in the prepared round pan. Cover the pan with a kitchen towel and let the rolls rise in a draft-free place for 40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D08BDF31FE4E54D873001483A4F7E09', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 6, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99E4B0583BB021438BE9CAAE04F3023A', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the water and egg yolk. Brush the mixture over the tops of the rolls.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3D0D91E7BA0A64E923818AF8BD8B148', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 8, NULL, NULL, 2, NULL, 0, 'Bake, the rolls for 20 to 25 minutes, or until the tops, are golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CAD042E3B3E1B47BDF694C27B24B3E5', X'59B72F56EF739949B20B77B2D70B61D1', X'59B72F56EF739949B20B77B2D70B61D1', 9, NULL, NULL, 2, NULL, 0, 'Remove the rolls to a rack and let them cool for 5 to 10 minutes while you prepare the icing.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01517027DAC2D949BB3C32650F9D7CEF', X'F1752FD9212B2A4FAFD4D34255213427', X'F1752FD9212B2A4FAFD4D34255213427', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC2D9821B636334C99AAC2E64E623A8A', X'F1752FD9212B2A4FAFD4D34255213427', X'F1752FD9212B2A4FAFD4D34255213427', 1, NULL, NULL, 2, NULL, 0, 'Combine the carrots, potatoes, onion, garlic, water, olive oil, salt, and pepper in a 3 to 4-quart crockpot and stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6DB3E671B45CA4DB7B5D6DAF5BC52E6', X'F1752FD9212B2A4FAFD4D34255213427', X'F1752FD9212B2A4FAFD4D34255213427', 2, NULL, NULL, 2, NULL, 0, 'Cover the crockpot and cook on low for 7 to 9 hours or until vegetables are tender when pierced with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB77631196EBEA429BBC2FB294E6B1A6', X'F1752FD9212B2A4FAFD4D34255213427', X'F1752FD9212B2A4FAFD4D34255213427', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51E555286205C541B60FD76BD968DF30', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1580BE1FE212541B4C1DBA092BCCF8A', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 1, NULL, NULL, 2, NULL, 0, 'Peel, de-stem, or remove seeds from the fruit, as appropriate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'052EF34EB15F3E418185423DD247BBF0', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 2, NULL, NULL, 2, NULL, 0, 'Place the fruit, water, and sugar into a pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2588AB9BDAFB404086057B7C5B484DD8', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 3, NULL, NULL, 2, NULL, 0, 'Cook the fruit with the sugar and water, bringing it to a boil and then lowering the heat to simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9418F1B8714FF3429CE0C04F229ACFE1', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 4, NULL, NULL, 2, NULL, 0, 'Smash the fruit while it is cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'442F6E7372F1B046ADDCEAD66C8F6872', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 5, NULL, NULL, 2, NULL, 0, 'Simmer until the mixture has thickened to a syrupy consistency. This should take about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D7B93072DEE0A4084623F355803F8F2', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 6, NULL, NULL, 2, NULL, 0, 'Once the mixture has thickened, you can press it through a strainer to make a thin syrup.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FFFB413E244BE43BE69D71E97C1F46D', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 7, NULL, NULL, 2, NULL, 0, 'If you prefer a thicker syrup, process the cooked syrup in a food processor.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B06A36A1E9C1E40AE7E8955A2944668', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 8, NULL, NULL, 2, NULL, 0, 'Serve with your favorite breakfast item.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'008640C84A134948BC7BFAF82293D1E7', X'C308CD9C04EAC04FB9E1A04A2765EDD9', X'C308CD9C04EAC04FB9E1A04A2765EDD9', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF953D4540E90E4FB4E58989165C089B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together oil, vinegar or lemon juice, garlic (if using), and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52B66E53DD144844AF30CB3FC431F779', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 1, NULL, NULL, 2, NULL, 0, 'Shuck the corn and cut off kernels. To help keep the kernels from escaping, cut over a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58F23BD08786F47A2AD38F218E4CE76', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 2, NULL, NULL, 2, NULL, 0, 'Peel the cucumbers and cut them in half lengthwise. Use a spoon to scrape out and discard the watery seeds in the center. Dice the cucumbers fairly small and add them to bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38C638F2B3B9CF4589676E92E78D28C5', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 3, NULL, NULL, 2, NULL, 0, 'Core or hull the tomatoes, cutting out the stiff bit at the top where they attached to the vine. Cut the tomatoes in half crosswise (if the tomato were a globe, cut it along the equator). Gently squeeze out any of the watery seeds that come out easily. Dice the tomatoes and add them to the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED067DD04986E4479769C219CED230A2', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 4, NULL, NULL, 2, NULL, 0, 'Cut off the stem end of the onion and cut it in half lengthwise. Remove and discard the peel. Hold onto the end to keep the onion halves steady as you dice the onion. To mitigate the pungent flavor of the onion, feel free to put the onion in a strainer and rinse it with cool water. Drain completely. Spread the diced onion on layers of paper towels to dry before adding to the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38BE87CA5F59224CBCD16BCDAD6F0FD7', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', X'4EC329B3D5FE7B40BE06EF8EE643FC6B', 5, NULL, NULL, 2, NULL, 0, 'Toss the vegetables with the dressing well. For an extra burst of flavor, add minced fresh herbs on top before serving.');
