INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2210731AC905654FBCB4DC3857878128', X'FC01D16B2ED7794EB7DBA890A51DE9AB', X'FC01D16B2ED7794EB7DBA890A51DE9AB', 5, NULL, NULL, 2, NULL, 0, 'Move the funnel or zip-top bag over the hot oil to make a pattern, starting at the center of the pan and swirling batter outward in a circle. Fry for 2 to 3 minutes, or until brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EDBD9559533324EA4D0636A4A37B52C', X'FC01D16B2ED7794EB7DBA890A51DE9AB', X'FC01D16B2ED7794EB7DBA890A51DE9AB', 6, NULL, NULL, 2, NULL, 0, 'Once the funnel cakes are browned on both sides, remove from the hot oil using a slotted spoon. Drain excess oil on a paper towel-lined plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77A8C99C65F7F746B99FABB21DDBF6C0', X'FC01D16B2ED7794EB7DBA890A51DE9AB', X'FC01D16B2ED7794EB7DBA890A51DE9AB', 7, NULL, NULL, 2, NULL, 0, 'Transfer to a rack and dust with confectioners’ sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71D0870E73CA1D4693C5D88840833E85', X'FC01D16B2ED7794EB7DBA890A51DE9AB', X'FC01D16B2ED7794EB7DBA890A51DE9AB', 8, NULL, NULL, 2, NULL, 0, 'Serve funnel cakes immediately. They can be enjoyed just with the powdered sugar, or with additional toppings like fruit sauce, hot fudge, whipped cream, or ice cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D06F4C92CE774483D92D3CB7A228D8', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0CD810A6EE3DF4584B602F9650AD3EF', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Strain an 8-ounce can of pineapple rings in a colander and reserve the liquid from the can. This flavorful juice will be used several times through the recipe to infuse pineapple flavor into every bite.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA9739B734A11D43A03D020B9885B0AA', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 2, NULL, NULL, 2, NULL, 0, 'Pour melted butter into the bottom of a 9-inch cake pan and distribute evenly. In a small bowl, combine brown sugar and 1 tablespoon of the reserved pineapple juice. Sprinkle into the bottom of the cake pan and distribute evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D913C63E9D1CCC4F82AC307B0E40B5A3', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 3, NULL, NULL, 2, NULL, 0, 'Arrange the pineapple rings in the pan on top of the brown sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0595ACE254CCDD48AE4CA4841252656A', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 4, NULL, NULL, 2, NULL, 0, 'Fill the centers of each pineapple ring with maraschino cherries. Set aside while you make the batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A053E986FF5877469FFEF544A865207A', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 5, NULL, NULL, 2, NULL, 0, 'Beat shortening, granulated sugar, and egg together until well-blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93C828199917DF47A2DBA6E5CC50082F', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 6, NULL, NULL, 2, NULL, 0, 'Combine the flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECC7FAD3FE9B8F479534B491AA91F2A2', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 7, NULL, NULL, 2, NULL, 0, 'Measure the remaining pineapple juice and add water as necessary to make 2/3 cup.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BAACBE27AA1364DB150CD4A6EC1BB34', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 8, NULL, NULL, 2, NULL, 0, 'Add half of the flour mixture to the shortening mixture and beat to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49FBC79E3D27F140AD3C524329B6B7A2', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 9, NULL, NULL, 2, NULL, 0, 'Add pineapple juice with vanilla and remaining dry mixture, beat to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E5E49B374A844DB4DC503C60D3D157', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 10, NULL, NULL, 2, NULL, 0, 'Pour over pineapple rings and place in oven. Bake until batter is done in the middle, about 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4170EE1092E2749BF5C6E5FEC0A29A6', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 11, NULL, NULL, 2, NULL, 0, 'Cool 5 minutes before running a knife around the edge to loosen cake from pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28DA27C1EF79114AA7114F969BE7F75E', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 12, NULL, NULL, 2, NULL, 0, 'Invert cake onto serving plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44B0B907A4DEF47829A0E5F78FEB76D', X'61CEA76824F2C5479F659E8841B8CAF2', X'61CEA76824F2C5479F659E8841B8CAF2', 13, NULL, NULL, 2, NULL, 0, 'Serve with ice cream or whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'299308A362E60C4AB2A528741874014E', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA1B4375F1BD64092A6A5EACC987CD6', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F. Grease a 9x13 pan with non-stick cooking spray or softened butter and line with parchment.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CA4D9C98AFDD42BD0DAC798E224754', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 2, NULL, NULL, 2, NULL, 0, 'Place the oil and butter in a large microwave-safe bowl and microwave on high in 30-second bursts until the butter melts.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9465429DD63CF74AACBEE2D4C12643C1', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 3, NULL, NULL, 2, NULL, 0, 'Add the cocoa and espresso powders and whisk to combine. Add the sugars and whisk to combine. Add the eggs and vanilla, all at once, and whisk very gently until combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3512C7B69C0D1348A87B7A9793332D03', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour and the salt over the batter and, using a rubber spatula, gently fold the dry into the wet until no visible flour remains.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82D76CBA3FABD145AF185E57A90B10A9', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 5, NULL, NULL, 2, NULL, 0, 'Transfer the batter to the prepared pan, spread with an offset spatula, and sprinkle with the flaky sea salt, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74F9563250470F498648DAE947875A99', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 20 to 22 minutes, rotating the pan at the halfway point. The brownies are ready when a cake tester comes out with a few moist crumbs. Don''t overbake.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE2BF1A114FD7141BB7B1CFA4B076A59', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 7, NULL, NULL, 2, NULL, 0, 'Let the brownies cool on a rack until room temperature. If desired, freeze them briefly before cutting, as this makes for cleaner squares.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C162520725C8BE4283719548F59C601B', X'0144CD4B0DC6F6408B63A086F65B4BC0', X'0144CD4B0DC6F6408B63A086F65B4BC0', 8, NULL, NULL, 2, NULL, 0, 'Serve with glasses of cold whole milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5F048F42E3BF84CB42EE48B81B504E1', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7607B52AADCFB343AC33B831FAED8481', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F. Butter a 9-by-13-inch baking pan. Mix the graham cracker crumbs and 1/3 cup of sugar. Stir in the melted butter. Pat into the bottom of prepared baking pan and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D014A305292CA847AD1D05F3EAA3E18C', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 2, NULL, NULL, 2, NULL, 0, 'Beat the 2 eggs, 3/4 cup sugar, and cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'796510F0F9261E4BBF1116B9B1BED13F', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 3, NULL, NULL, 2, NULL, 0, 'Pour over the graham cracker crust and smooth out evenly. Bake for 20 minutes. Set on wire rack to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6471D5385F928841A39015E26D26CE45', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 4, NULL, NULL, 2, NULL, 0, 'Beat pumpkin, egg yolks, 1/2 cup sugar, the milk, salt, and cinnamon in top of a double boiler. Cook over boiling water, stirring frequently until thick, about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'042F74CC6851CC4F8B8F9E5719F8D6A8', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 5, NULL, NULL, 2, NULL, 0, 'In a small saucepan, sprinkle the gelatin in cold water. Stir over low heat just until dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EDF84C2AAB99B4B9A1EAF03E6EF64CF', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 6, NULL, NULL, 2, NULL, 0, 'Stir the gelatin into the pumpkin mixture. Set aside to cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA79EFD0A191424FA484361D9EDE4BDB', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 7, NULL, NULL, 2, NULL, 0, 'When cooled, beat the egg whites in electric mixer until foamy. Gradually beat in the 1/4 cup sugar and beat until stiff and glossy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C151A506F36D5C4E979669EB2170DA4D', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 8, NULL, NULL, 2, NULL, 0, 'Pour over the cream cheese layer in pan and refrigerate until very cold and set, at least 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1996CF42842E1342A3E0AAFB83E75FFE', X'A37B2859E0EC244295A38ADB21DB96CB', X'A37B2859E0EC244295A38ADB21DB96CB', 9, NULL, NULL, 2, NULL, 0, 'To serve, cut into 15 squares. Each square can also be topped with sweetened whipped cream, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E086D75A7E2A048B3F73D7B00F177A0', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0861D38C03D03D41ABB76884C994F92A', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 1, NULL, NULL, 2, NULL, 0, 'Sift flour and 3/4 cup of the sugar together 3 times.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F7DA0C9CEB344C9C91E2B2DAC2F6D4', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 2, NULL, NULL, 2, NULL, 0, 'Beat egg whites with salt and cream of tartar in large bowl on the highest speed of an electric mixer until it is light and fluffy. If you have no mixer, you can use a whisk but be prepared for a workout.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36C4EFC7A8DE27448E87D871D9D1CC1C', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the remaining 3/4 cup of sugar over egg whites, 2 tablespoons at a time, beating thoroughly after each addition.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D11E82A363D9454C857E676BFC58F3A0', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 4, NULL, NULL, 2, NULL, 0, 'Continue beating until stiff peaks form.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40ADDE560FD18F4D8AE77E5E159F584E', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 5, NULL, NULL, 2, NULL, 0, 'Fold in vanilla and almond extracts.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9727FAE91874A429A4252AC7152D4DD', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 6, NULL, NULL, 2, NULL, 0, 'Sift dry ingredients, 2 tablespoons at a time, onto the beaten egg whites. Fold in gently but thoroughly with a wire whip or rubber spatula.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65810088595651489BFEBA239B600044', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 7, NULL, NULL, 2, NULL, 0, 'Pour batter into ungreased tube pan (10 x 4 inches). Make sure that there is not a trace of any fat or grease in pan, or cake won’t rise.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE8C47DD6D29B478DD8BED44508EEAF', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 8, NULL, NULL, 2, NULL, 0, 'Draw a skewer gently through batter in a swirly pattern to remove large air bubbles.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C558A31B1781A479F0909F284A53D1B', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 9, NULL, NULL, 2, NULL, 0, 'Bake in preheated 350 F. oven for 40-50 minutes or until crust is golden brown and cracks are very dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222742898D546E4A82E1346D9D2F1652', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 10, NULL, NULL, 2, NULL, 0, 'If your tube pan has legs (like the one seen here), invert and allow to cool at least one hour. If your tube pan does not have legs, invert the pan over a bottle to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269EDEFD7E8C47479C1DB8CEBF99EDFB', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 11, NULL, NULL, 2, NULL, 0, 'Once cooled, run a knife along the outside and carefully remove the cake from the pan. Place on a serving plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5CC3161B131341B777C4447975E02F', X'4CFC5D3D852C61448915AD0144513D15', X'4CFC5D3D852C61448915AD0144513D15', 12, NULL, NULL, 2, NULL, 0, 'Dust with powdered sugar and serve with fresh fruit.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29FB4A0846D9A8419D7787B0D1B50729', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Top a baking sheet with waxed paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E2351C562CF444F84E6786BC4F8092F', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, combine chocolate chips and evaporated milk. Warm over gentle heat until melted, stirring constantly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2C77F6AFC62BA4D89E3CE36068BD95A', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 2, NULL, NULL, 2, NULL, 0, 'Stir in the powdered sugar and sweetened flaked coconut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFCD4B21FEFC4444B8BB15B5E3FFBF53', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 3, NULL, NULL, 2, NULL, 0, 'Scoop 1-inch balls and place on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FACC6E11654C6E4393357ED986099A5D', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 4, NULL, NULL, 2, NULL, 0, 'Press one whole almond into the top of each ball. Gently coax the dough into the signature oblong shape of an Almond Joy candy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F29DDE35C090B34093DCE5E7B2E344A1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 5, NULL, NULL, 2, NULL, 0, 'Chill for a couple of hours until firm. You know the cookies are set when they easily peel away from the waxed paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE93CCEBE2C6141AAE9E7E0D8CA19E7', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', X'6F5B39EE36EF514BA2C6430FF6AE0BE1', 6, NULL, NULL, 2, NULL, 0, 'Gently peel the cookies away from the waxed paper to remove from baking sheet. Store between layers of waxed paper in an airtight container in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BAAF0745C09804AAE7514C55D49FEAC', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04FDE70C2FB3E349B33D3D01A4BCDA1A', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees F. Prepare baking sheets by covering them with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67DFB1143FF86A4E9D34486F9A670F4C', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, cream together butter, granulated sugar, and light brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD1E956BC957384BBC65FD7070F585CE', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 3, NULL, NULL, 2, NULL, 0, 'Beat in the eggs and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'543C84D0E3786640972BA29C0823444E', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 4, NULL, NULL, 2, NULL, 0, 'In a separate medium bowl, combine the flour and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A9237ED7B9D447ABF09D50432EE51C', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 5, NULL, NULL, 2, NULL, 0, 'Gradually add the flour mixture to the creamed butter mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A27132C1E6E2B42BC75C3DE45A40B34', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 6, NULL, NULL, 2, NULL, 0, 'Mix in the macadamia nuts and white chocolate chips.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E12EC5517FA8043B0DFC9325E8B53EF', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 7, NULL, NULL, 2, NULL, 0, 'Drop by rounded spoonfuls onto the prepared baking sheet. Use an ice cream scoop to create uniform sized cookies, about 1 1/2-inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'971C83E170986347A2DC255E9E5FB3D5', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 8, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes until the cookies are lightly golden and set. Allow to cool on a wire rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24984911B1FA3C4EB65B6517966466E6', X'C36F4FD7AFD84D4F884F1B3667AD073F', X'C36F4FD7AFD84D4F884F1B3667AD073F', 9, NULL, NULL, 2, NULL, 0, 'Store cookies in airtight container.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'972F126230C60948AA015BEF3257CE9B', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 325 degrees F and butter and flour an 8` square baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CC6068675566B4DB48647198284145D', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 1, NULL, NULL, 2, NULL, 0, 'Add the butter and chocolate to a heat-proof bowl and set it over a pot of simmering water. Stir until melted and allow to cool slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11B4CB5386D12243A300425C10598453', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 2, NULL, NULL, 2, NULL, 0, 'Whisk the granulated sugar into the butter and chocolate mixture, then whisk in the eggs and vanilla.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24A964BAB670340915C6715AC43B54B', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 3, NULL, NULL, 2, NULL, 0, 'Sift together the all-purpose flour, baking powder and salt and stir into the chocolate batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A16A923FF2DF64C8319E63AC5F18313', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 4, NULL, NULL, 2, NULL, 0, 'Make the peanut butter swirl filling by stirring together the melted butter, confectioners sugar, peanut butter and salt until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1E0301FA2A3A443A8C102082CE249DC', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 5, NULL, NULL, 2, NULL, 0, 'Pour the chocolate batter into the prepared pan and spread out evenly. Drop tablespoons of peanut butter filling on top of the batter and use a butter knife or a long skewer to gently swirl the peanut butter filling into the batter. Do not mix all the way so that the pretty swirl pattern remains on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB1F3ABC95662546955F5CB2EA4007D6', X'2F9136292A592E49B3360898942F36A3', X'2F9136292A592E49B3360898942F36A3', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 35 to 40 minutes, or until a toothpick comes out clean. Let cool and cut into 9 squares. Note that you can add a cup of chocolate chips or peanut butter chips to the batter before baking, if you like.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D2FFACA65BE44BBE600C34E3AF5AF6', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8854F4D45FA43458AEECFF5C76F64EF', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 degrees F and cover baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4674E744E601F344BDE12FC62B8C0FC9', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 2, NULL, NULL, 2, NULL, 0, 'Stir in flour, 1 cup of the crushed potato chips, baking soda, and baking powder until dough forms.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B99C3107AC0D4DB261A36DB55AFA30', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 3, NULL, NULL, 2, NULL, 0, 'Shape dough into 1-inch balls. Place balls 2 inches apart on prepared baking sheets and press each ball to flatten slightly. You can use your fingers or the bottom of a glass to flatten.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BA974816974264885FF789BE6AE3875', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 4, NULL, NULL, 2, NULL, 0, 'Bake 8 to 10 minutes or until edges are light golden brown. Cool 3 minutes; remove from cookie sheets to cooling racks. Cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2D275ABA9B3FA4EB5F99C0F9F831B22', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 5, NULL, NULL, 2, NULL, 0, 'In small microwavable bowl, microwave chocolate chips and peanut butter chips uncovered on high for 1 minute to 1 minute 30 seconds, stirring every 30 seconds, until melted and smooth. The chips can also be melted gently in a double boiler over simmering water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F80073A80C6C2145850393804252233A', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 6, NULL, NULL, 2, NULL, 0, 'Dip each cookie halfway into melted chocolate mixture, letting excess drip off.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D064DDB9DA3A44488CD92FB86DA07CD', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 7, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle the dipped half of each cookie with remaining crushed potato chips. Place on waxed paper until set, about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D97D8DEBADC16429DF91FA5EEADE666', X'A4B378FC44148840850ECC5B2652881E', X'A4B378FC44148840850ECC5B2652881E', 8, NULL, NULL, 2, NULL, 0, 'Store between sheets of waxed paper in tightly covered container.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCCFC5C95A53104DA25CD9B8A402DEC2', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B01A06DEB609644B95EFE7956696424', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 1, NULL, NULL, 2, NULL, 0, 'Start by preparing the baking powder pastry. In a large bowl, combine flour, sugar, baking powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFECF75DDBECAE4F8211E96D8154E79D', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 2, NULL, NULL, 2, NULL, 0, 'Using a pastry blender or two knives in a criss-cross fashion, cut in the cold butter until mixture resembles coarse crumbs.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73E40B633E495343A4BB878395C35E5C', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 3, NULL, NULL, 2, NULL, 0, 'In a separate small bowl, whisk together the milk and egg.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12E8F4ABC05CD04798E3413D45584291', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 4, NULL, NULL, 2, NULL, 0, 'Gradually stir the egg mixture into flour mixture with fork until just combined and the dough cleans the side of bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23AA185E360754EA32B6781F4719B38', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface, lightly knead for about 1 minute to form a soft dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89797CE1EF6F1D47B2B33CF168DADBB2', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 6, NULL, NULL, 2, NULL, 0, 'Divide dough in half and wrap each piece in plastic wrap. Refrigerate while you prepare the rest of the recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'628A61B7BAD95B44980EC445C018BE4F', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 7, NULL, NULL, 2, NULL, 0, 'To make the apple pie filling, heat the apples and water, covered, in a medium saucepan to boiling, then reduce heat to low. All to simmer, covered, until apples soften slightly, about 5 minutes. Remove the cover and cook, stirring constantly until liquid evaporates, about 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D9E293C637CB345BDF8D80AE7561D16', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 8, NULL, NULL, 2, NULL, 0, 'Stir in the granulated sugar and butter. Cook, uncovered for 3 minutes and then stir in the cinnamon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633F6B539347E9488394A4F94A7DDB5F', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 9, NULL, NULL, 2, NULL, 0, 'Mash the mixture with a fork or potato masher to form a chunky puree. Cover and refrigerate until cold, about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0385DDD4BAB6754490E26FFF94CD2297', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 10, NULL, NULL, 2, NULL, 0, 'When you are ready to prepare the pies, roll one half of dough 1/8-inch thick on floured surface. Cut out 4-to-5-inch circles with floured cutter. (If you don''t have a cutter large enough, you can trace around the edge of an inverted bowl to form the circles.)');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6634772F69C2874A9D9794D5DCF0DD95', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 11, NULL, NULL, 2, NULL, 0, 'Spread some of the apple mixture over half of each circle, leaving a 3/4-inch border.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC10C4DFEFC0F4C887609166C1CD3A5', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 12, NULL, NULL, 2, NULL, 0, 'Lightly brush borders with a little water. Fold the pastry in half and pinch the edges lightly with fingers. Seal the edges with a fork. Turn the pies over and again press the seams with a fork. (Do not pierce the dough). Repeat with the remaining dough and filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7655475E3D94D1418F468E7C77E7F88D', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 13, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet to 375 degrees F. Fry the pies, 4 at a time so as to not crowd the pan. Turn the pies once, until they are golden brown on both sides, about 4 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'808BB93F4AF0064EB3ABEEE7054D6D0F', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 14, NULL, NULL, 2, NULL, 0, 'Using a slotted spoon, lift the pies out of the oil and drain on to a paper towel lined plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9A27DD393D7234582F9CB1D3DC88AE4', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', X'C96905E4012D6B4A9BFCEFEB2D4D30F7', 15, NULL, NULL, 2, NULL, 0, 'Dust the pies with powdered sugar and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'262D4C240284C54082EC0CFBB901A6FC', X'8302DB4E172D1C45AA178CC0D525F170', X'8302DB4E172D1C45AA178CC0D525F170', 0, NULL, NULL, 2, NULL, 0, 'Place the chopped chocolate into a bowl and set over a pan of gently simmering water to melt. Take care not to let the bowl touch the water, or it will scorch. Or, melt the chocolate in the microwave a few seconds at a time as the chocolate can quickly burn if done too quickly. Once the chocolate is melted, remove from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5BD3AE8A182234C84ED4763150121EB', X'8302DB4E172D1C45AA178CC0D525F170', X'8302DB4E172D1C45AA178CC0D525F170', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer if possible (this makes the job so much easier!), beat the sugar and butter together until light and fluffy and pale in color. If you don''t have an electric mixer, then use a balloon whisk. Add the salt and mix, then add the vanilla extract and mix again.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBCC0741483FB442BC348B6595C63198', X'8302DB4E172D1C45AA178CC0D525F170', X'8302DB4E172D1C45AA178CC0D525F170', 2, NULL, NULL, 2, NULL, 0, 'With the mixer still running, slowly pour the melted chocolate into the butter and sugar mixture and continue to blend until thoroughly incorporated. Then, add the eggs one at a time, mixing each well after each one before adding another.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DDC785F0DEF06449DFEA675F90ADF8A', X'8302DB4E172D1C45AA178CC0D525F170', X'8302DB4E172D1C45AA178CC0D525F170', 3, NULL, NULL, 2, NULL, 0, 'Pour the chocolate filling into the crust and smooth the surface with a spatula. Pop the pie into the fridge and leave for 3 to 4 hours, preferably overnight until entirely set.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E86F056C93F77C46BDA3A75211B07E9E', X'8302DB4E172D1C45AA178CC0D525F170', X'8302DB4E172D1C45AA178CC0D525F170', 4, NULL, NULL, 2, NULL, 0, 'Once set firmly, decorate with whipped cream and grated chocolate, shavings or curls. The pie can be made a day ahead of serving, but only decorate just before.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E050CCD08DE9C469905D897318B3769', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B45AD1BF9776CE4196894B9FE59B4239', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 1, NULL, NULL, 2, NULL, 0, 'Place the strawberries in a bowl and mash with a wooden spoon. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1ECC703815E6443B86F9060B9EF37FD', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 2, NULL, NULL, 2, NULL, 0, 'If desired, strain the mixture to remove the seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEC6E8E0E376514E85CFCC6492F379A4', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 3, NULL, NULL, 2, NULL, 0, 'Using a hand mixer or a stand mixer with the whisk attachment, whip the cream with sugar until it reaches soft peaks.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F0229078863DB49A31CEC0684FC820E', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 4, NULL, NULL, 2, NULL, 0, 'Add the mashed berries and continue whipping until the mixture forms stiff peaks and a traditional whipped cream texture develops.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4444C226A28D6D42BD4FE11BE0123215', X'F1A97659D11F5845AA0962D54665BE06', X'F1A97659D11F5845AA0962D54665BE06', 5, NULL, NULL, 2, NULL, 0, 'Use the whipped cream immediately, as it may not retain its texture after a day or so in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F6AFC1197AFD74FACE870B68BFB3DC9', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the 1 cup of flour, the granulated sugar, baking powder, and salt. Pulse to combine thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51D733550E0FF240933997B62B4E8193', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 1, NULL, NULL, 2, NULL, 0, 'Cut 10 tablespoons of cold butter into small pieces. Gradually add the butter pieces to the flour mixture, pulsing briefly after each addition. Pulse just until many of the crumbs are about the size of peas. Drizzle the ice water into the food processor while pulsing.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74CA0AA47FA9345A924B3E405C19D43', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 2, NULL, NULL, 2, NULL, 0, 'When the mixture begins to clump together, transfer it to a floured surface. Knead a few times to form a cohesive dough. Flatten the dough into a disk and wrap in plastic wrap. Refrigerate for at least 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1585659FFFB63543A416C4DA9231DCD9', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 3, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with wax paper or parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F69DAB05E9535B4A8162FDC1490B27CD', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 4, NULL, NULL, 2, NULL, 0, 'Remove the dough to a floured surface and roll it out into a large square about 1/4-inch in thickness. With a pizza cutter or knife, cut it into squares, rectangles, or other shapes. Place the dough pieces on the lined baking pan and place the pan in the freezer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED5FF53EA51BA448877F72E24F05084', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 5, NULL, NULL, 2, NULL, 0, 'Peel a pear and slice in half lengthwise. With a melon baller, remove the core and cut out the stem and blossom end; remove any tough fibers. Place a pear half on a cutting board, cut-side down. Slice it into 1/4-inch wedges. Toss the wedges in a large bowl with the 1 tablespoon of lemon juice. Peel, core, and slice the remaining pears and add them to the bowl. Add the vanilla and toss to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E39F6B288837479ACAF2AD7C59D4BA', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 6, NULL, NULL, 2, NULL, 0, 'In another bowl, combine the brown sugar, cinnamon, nutmeg, and the remaining 3 tablespoons of flour. Mix well. Sprinkle the brown sugar mixture over the pears while tossing to coat thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34F68358B402314A87C850A3EA1EECBD', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71992842C93C8945A586E03CF33DB59C', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 8, NULL, NULL, 2, NULL, 0, 'Put the pears in a 10-inch cast iron skillet. Cut the remaining tablespoon of butter into small pieces and scatter them over the pear filling. Drizzle with syrup or molasses, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6222EFCCCF9B3B48B48D43C17D98A6E3', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 9, NULL, NULL, 2, NULL, 0, 'Remove the dough pieces from the freezer and arrange them over the pear filling. Overlap them as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181910B0B6F199408F8BB7B5BBD27E36', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 10, NULL, NULL, 2, NULL, 0, 'Combine the egg white and water and whisk to make the egg wash. Brush the egg wash mixture lightly over the pieces of dough. Sprinkle with sanding sugar, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9CDDF653AB9F944B8A65B7362CE7FCA', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 11, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 45 to 55 minutes, or until the topping is golden brown and the pears are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E3ABF0C5E10A9409A433B0B3BD2B657', X'B6A162AF04A0184197DDE9251017E009', X'B6A162AF04A0184197DDE9251017E009', 12, NULL, NULL, 2, NULL, 0, 'Serve warm or cold with ice cream or whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D98E7BA7800FA4BB2C09C60FF712C68', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3C4A2B755E1004F99DB47BA26A5C62B', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0519E50FF595A74EBDCC3B692C6E1827', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 2, NULL, NULL, 2, NULL, 0, 'Spray doughnut pans with nonstick cooking or baking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B460FAF600954E8F30BEF966788316', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the flour, granulated sugar, baking powder and salt. Blend thoroughly with a whisk or spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEDE14B5AC6F524BAFCB20F768974D2C', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk the eggs with the milk, melted butter, 2 teaspoons of orange zest and 1 1/2 teaspoons of vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0321F1AA29804D9078D4C15FF5E97C', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet mixture along with the finely chopped cranberries. Mix just until the ingredients are moistened.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'955DCA764199D24A865FB2101E321E4E', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 6, NULL, NULL, 2, NULL, 0, 'Spoon the batter into the cavities of the doughnut pans, filling about half full. Alternatively, spoon the batter into a plastic food storage bag, cut off a small piece of the corner and pipe the batter into the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83FD45AA280692428B21AE518A6D6E6A', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 7, NULL, NULL, 2, NULL, 0, 'Bake the doughnuts for about 10 to 12 minutes (about 7 to 9 minutes for mini doughnuts), or until a toothpick comes out clean when inserted into a doughnut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E70609C3B9E741BDAF9988679CC107', X'CFDFEBB9681BC44288DEF83AD1B57548', X'CFDFEBB9681BC44288DEF83AD1B57548', 8, NULL, NULL, 2, NULL, 0, 'Let the doughnuts cool for about 5 minutes and then carefully remove them to a rack to cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86E95ABB3D29034C801F04B8C653ACB6', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CDF89C26DA4F346BE109EAC714144B6', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan sift the cocoa powder, flour, sugar, and salt and whisk to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'616F2D502F73224C9562C6963927E13A', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 2, NULL, NULL, 2, NULL, 0, 'Slowly add milk and whisk until all the lumps are gone.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBF739DAC5E6304BA5F2DD4ACE03CFC8', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 3, NULL, NULL, 2, NULL, 0, 'Place the saucepan over medium heat and cook for 5 to 7 minutes, stirring constantly, until the gravy just begins to boil and then thickens.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63CDAC19BF49E14DA7181C855F2E3A86', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 4, NULL, NULL, 2, NULL, 0, 'Once thick, remove from the heat and stir in butter and vanilla. Continue stirring until the butter has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7151F3A232C9FA42B1AE91237CAC38AF', X'66C779B8A271E34DB2AFCD8E4C586B07', X'66C779B8A271E34DB2AFCD8E4C586B07', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately, spooned over biscuits.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D632B2A0304864A8D5A1BE393009FD1', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C27398B20AA9CB4BB4901E84FF5F935F', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 1, NULL, NULL, 2, NULL, 0, 'Combine 1 1/2 cups flour and salt. Add shortening and cut into flour and salt with a pastry blender until coarse and crumbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B950B46928D29141A2DFC56774F27002', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 2, NULL, NULL, 2, NULL, 0, 'Stir in ice water, a little at a time until dough forms in a ball. Wrap dough in plastic wrap and chill for 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2232ABFE9C65C4387F34384B43DA835', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 3, NULL, NULL, 2, NULL, 0, 'Roll out dough into a circle and fit into a pie plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAA0F36762575C4684A88F04DF0A1FDB', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 4, NULL, NULL, 2, NULL, 0, 'Preheat charcoal or gas grill for indirect cooking at 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083E066DF2AB9D41881F6EB2007E76A4', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the granulated sugar, 2 tablespoons flour, nutmeg, cinnamon, and cloves.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3204162EC38434EA174BDED0DC7AEA9', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 6, NULL, NULL, 2, NULL, 0, 'In a separate large bowl, toss apple slices with lemon juice (to prevent the apples from turning brown). Then add the sugar/flour/spice mixture to the apples and toss to coat the apple slices with the spice mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D5BB2D73C0224488EAB2EE53C96771C', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 7, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the remaining 1/2 cup flour with the brown sugar. Add the pieces of butter, and cut in with a pastry blender, until the mixture resembles coarse crumbs. Sprinkle evenly over the apples.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'661961551F5DFB47BAF8BBB8389C1811', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 8, NULL, NULL, 2, NULL, 0, 'Place the pie on a cookie sheet, and bake over indirect heat until the apples are tender and bubbly about 50 to 60 minutes. Remove the pie and let sit for at least 10 minutes to cool before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87EDE6CAEB79D3418ED31ED6DD64FD7C', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 9, NULL, NULL, 2, NULL, 0, 'Serve with generous scoops of ice cream and slices of extra sharp Cheddar cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1257AFB8DDAB234CAECCF7F7490ECDE2', X'C02544D8269F3549813B7BA42A35A69F', X'C02544D8269F3549813B7BA42A35A69F', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
