INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE5270F01EC4242ADE7F8D713DFC21E', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine crushed vanilla wafer cookies, melted butter, and sugar. In this recipe, vanilla wafer cookies are crushed and used in the crust, while whole vanilla wafer cookies are also used as a garnish on top of the finished cheesecake. When you purchase an 11-ounce box of vanilla wafer cookies, first remove 12 cookies for the garnish. Then, pulse the remaining cookies in the box in the food processor. They will measure out to the 2 cups of cookie crumbs necessary for the crust.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFFA90547F83FF47872456876DFDB202', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 2, NULL, NULL, 2, NULL, 0, 'Pat the crust mixture into the bottom and halfway up the sides of a 10-inch springform pan. To prevent leaks, line the outside of the pan with aluminum foil or place the springform pan on a rimmed baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9CC588178923543BB04F59833B63FBF', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat the cream cheese, sugar, sour cream, eggs, mashed bananas, and lemon juice until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F04563156F3B8E419A684E5A60F45181', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 4, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the prepared pan and bake for 1 hour, or until the cheesecake is light brown on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C52554BF77D7A242AAB772963D2D0E50', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 5, NULL, NULL, 2, NULL, 0, 'Cool completely at room temperature. Wrap in plastic wrap and chill for at least 12 hours before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD54298C093CE64B8F0CE1705FEBF0B7', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 6, NULL, NULL, 2, NULL, 0, 'When ready to serve, prepare the topping. In a medium bowl, whisk together the milk, pudding mix, and powdered sugar. Beat for 2 minutes. Gently stir in the whipped topping.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653F1B926A88FA4C8389C5F788772D0F', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 7, NULL, NULL, 2, NULL, 0, 'Spread the topping over the top of the cheesecake.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'207C072AEB1289488A7FC3FA17B3371F', X'96C5AB4B04805B4686B56A014931A08F', X'96C5AB4B04805B4686B56A014931A08F', 8, NULL, NULL, 2, NULL, 0, 'Remove the side of the springform pan and place on serving platter. Garnish with vanilla wafer cookies and fresh banana slices.');
