INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D94FE62A02D6459C55B3AC105F9524', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A167DA5B72429D48A585774400005C98', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 1, NULL, NULL, 2, NULL, 0, 'Thoroughly butter a 9 x 13-inch baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF0675EFC57CCF4EAA8E95F77F32C629', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 2, NULL, NULL, 2, NULL, 0, 'Put the cake mix into a large bowl. Using a box grater, shred the cold butter into the dry ingredients. Toss to combine. If you don''t have a box grater you can just cut the butter into small pieces with a pastry blender or mash it through the tines of a fork. Work quickly to avoid melting the butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D668F8F5CAF32349ADBE873398AAB264', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 3, NULL, NULL, 2, NULL, 0, 'Add the blueberries to the butter and flour mixture and toss to distribute the blueberries. Dump into the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D20D0CD87F7644B918A1B820DBF9C54', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 4, NULL, NULL, 2, NULL, 0, 'Add the buttermilk and vanilla to a measuring cup with a spout. Stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85728C571FD8654B99C1F90F4AAAF411', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 5, NULL, NULL, 2, NULL, 0, 'Pour the buttermilk mixture evenly over the dry mixture in the baking pan. Do not mix. Bake for 35 to 40 minutes, or until the cake is golden brown and the blueberries are bubbling. Remove from oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25B62AE188C1C94C9532FD489C3D58BF', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 6, NULL, NULL, 2, NULL, 0, 'While the dump cake is cooling, prepare the whipped cream. In a large bowl, or in the bowl of a stand mixer, whisk the whipping cream and confectioners sugar until past the soft peak stage, but not quite stiff.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CCAD82EB30C344AA35A6B9CAB7D18F9', X'F075CC3583D70B448922104C5B9BBADB', X'F075CC3583D70B448922104C5B9BBADB', 7, NULL, NULL, 2, NULL, 0, 'Serve the warm dump cake with a generous helping of whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C5C953145BCE4D93575CCE97DB5664', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Grease a 9-inch springform pan with cooking spray or softened butter. Line the bottom and sides with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F63C5CE035DD4FB8F2BF6FEE0913D2', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 1, NULL, NULL, 2, NULL, 0, 'To make the crust, place 36 Oreos in a food processor fitted with the metal blade and process until fine crumbs form.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D490CF1F9022F34C9C88C85FA7A64FB9', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 2, NULL, NULL, 2, NULL, 0, 'Add the melted butter and process until combined, scraping the bowl periodically with a rubber spatula.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9724B7B61A24D42A5A13773249948FF', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 3, NULL, NULL, 2, NULL, 0, 'Transfer the crumbs to the prepared pan and firmly press the crumbs into the bottom and 3/4 of the way up the sides of the pan. Place the crust in the freezer while you prepare the filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ED35EAF85E0934FA1ECA400517147ED', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 4, NULL, NULL, 2, NULL, 0, 'Place the heavy cream and vanilla in the bowl of a stand mixer fitted with the whisk attachment and whisk on medium-high until stiff peaks form. Transfer to a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966EDCB038673D4E90E26302C2F3F154', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 5, NULL, NULL, 2, NULL, 0, 'Place the cream cheese, sugar and salt in the bowl of the stand mixer, now fitted with the paddle attachment (no need to clean it) and beat on medium until smooth, scraping the bowl with a rubber spatula as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAAD1F6AF030104299D5DB5FFAFE64E7', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 6, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the cream cheese in several installments with a rubber spatula, being mindful not to deflate the air you just whipped into the cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84EE6E8AF5787C4FBD3CD707790BD1C5', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 7, NULL, NULL, 2, NULL, 0, 'Process the remaining Oreos until coarse crumbs form. Gently fold the coarse crumbs into the cheesecake batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4C34C9F8C866448EA485EEF8416FE7', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 8, NULL, NULL, 2, NULL, 0, 'Transfer the filling to the prepared crust, smoothing the top with an offset spatula or butter knife. Add filling just to the top of the crust. Cover with plastic wrap and refrigerate for at least 5 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A7430377E8C1945921F860AFEF9A376', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 9, NULL, NULL, 2, NULL, 0, 'When ready to serve, remove the sides of the pan and then slide a long knife between the parchment paper and the metal bottom to loosen and remove the sides. Peel off the parchment. Sprinkle crushed Oreos over the top of the cake to decorate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27FFB7C1C7E084418750A5DC7D2F5456', X'7F575DBE4E51EB49A609A455FB9AF23D', X'7F575DBE4E51EB49A609A455FB9AF23D', 10, NULL, NULL, 2, NULL, 0, 'Place the cake on a serving plate, slice into wedges with a serrated knife, and serve. Run the knife under hot water each time you make a cut to ensure clean slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D2146337E7BDE46AE0313E3711EB4A1', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Grease a 9 x 13-inch baking dish. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96BDDA0057AE6541A99D940FDC11D327', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 1, NULL, NULL, 2, NULL, 0, 'To make the bread pudding, combine the heavy whipping cream, whole milk, and sugar in a medium heavy saucepan over medium heat. Bring to a simmer, stirring until the sugar dissolves.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE6D0A5FFC616478B3B186B807F1F17', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 2, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat. Add the white chocolate chips and whisk until melted and smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'701532725C2F6B4091F6A84A0575C690', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk the egg yolks and whole eggs.');
