INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC43E76D4F019E4B991462A836BE0A66', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 5, NULL, NULL, 2, NULL, 0, 'Cut the challah bread into 1-inch cubes.');
