INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FB236E8BE7A1746B3BCC30BA83E3B6D', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 7, NULL, NULL, 2, NULL, 0, 'Transfer the bread mixture to the prepared dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF24225B95BB6B49A79B686CD03A6377', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 8, NULL, NULL, 2, NULL, 0, 'Cover with aluminum foil. Bake for 45 minutes. Remove the foil and bake for about 15 minutes until the pudding is golden brown and set. Cool slightly on a rack.');
