INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76D110FB357538499120F62E60E9C2BF', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', X'6DF05AC3CD02A5469C9F4CDEDD2D3211', 10, NULL, NULL, 2, NULL, 0, 'Serve the warm white chocolate bread pudding with a drizzle of the warm white chocolate sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F2980A5564AD046982184EE8C571ACF', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Position an oven rack in the center of the oven and preheat to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56FCBADD28A57C4F9B15C0E6DE595421', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 1, NULL, NULL, 2, NULL, 0, 'Unroll one can of the refrigerated crescent roll dough. Place in the bottom of an ungreased 13 x 9-inch baking dish. Stretch the dough to cover the bottom of the baking dish. Press the perforations to seal.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC35CD5FA719ED42A62578BB5591420C', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 2, NULL, NULL, 2, NULL, 0, 'In medium bowl, beat the softened cream cheese with 1 cup of the sugar, until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C9C67FE74638408174D7CB78196154', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 3, NULL, NULL, 2, NULL, 0, 'Beat in the egg and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DBFFD29B47A9B4593FFAD71FA283341', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 4, NULL, NULL, 2, NULL, 0, 'Gently spread the cream cheese mixture over the dough in the baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC5506DC92473458DAEF6E9065F2486', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 5, NULL, NULL, 2, NULL, 0, 'Unroll the second can of dough. Before placing it in the baking dish, press the perforations to seal. Carefully place the layer of dough on top of the cream cheese layer and gently stretch as needed to cover. Pinch seams together, sealing the top and bottom crescent roll layers together.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B631BE1B3CADE419D90BB3ED7A8B8F4', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 6, NULL, NULL, 2, NULL, 0, 'Pour the melted butter evenly over the top of the dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D342CE87D2EA6741B4096DC084D765A6', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 7, NULL, NULL, 2, NULL, 0, 'Mix remaining 1/2 cup sugar with the cinnamon, and sprinkle evenly over butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'941262906E49C848AC148D343CEF4837', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 8, NULL, NULL, 2, NULL, 0, 'Bake 30-35 minutes until the dough is golden brown and the cinnamon-sugar has formed a crust on top of the dough. Cool for 15-20 minutes before cutting into squares to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA44C13D12EAE42B7D2DEF3E291364C', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 9, NULL, NULL, 2, NULL, 0, 'Drizzle each bar with desired amount of honey.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB9B84C7A100524980647546B00013BC', X'EDD28A398BB7244DBE27518B9F4B9D61', X'EDD28A398BB7244DBE27518B9F4B9D61', 10, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate any remaining bars for up to one week.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A156812594C27489F526A292C365C63', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F. Grease an 8 x 8-inch square baking pan and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBAFE5A69EEA641BA855DF417BD8F23', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284C78CC35BC0245A2DBD8F9C2C96342', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine the sugar, melted butter, eggs, and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E47E6F905298346B19E33D32588C0B9', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 3, NULL, NULL, 2, NULL, 0, 'Stir the flour mixture into the butter mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'425280F6DA00E249AD4B47F069FAF450', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 4, NULL, NULL, 2, NULL, 0, 'Divide the batter evenly into two separate bowls.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94035044CF7E7047B9BE9516029CD336', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 5, NULL, NULL, 2, NULL, 0, 'In one bowl, stir in the unsweetened cocoa powder. In the other bowl, stir in the pumpkin puree and pumpkin pie spice.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C6C477090566A4EA348D625312C860C', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 6, NULL, NULL, 2, NULL, 0, 'Spread half of the chocolate brownie batter into the bottom of the prepared baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'368606DB3759E845A1904615EBE64B37', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 7, NULL, NULL, 2, NULL, 0, 'Gently spread half of the spiced pumpkin batter on top of the chocolate layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A168CCFDEDDB274E80BD071407D011F5', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 8, NULL, NULL, 2, NULL, 0, 'Spoon dollops of the remaining chocolate and pumpkin batters on top of the pumpkin layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75EAB4864EB39E4E82D933F3875EB7E4', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 9, NULL, NULL, 2, NULL, 0, 'Drag a kitchen knife back and forth through the batter dollops to create a swirl. Bake for about 45 minutes or until set.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30F16BB8B001C24590576D537C457F07', X'742A93C288483F45AF76FC249BD8F579', X'742A93C288483F45AF76FC249BD8F579', 10, NULL, NULL, 2, NULL, 0, 'Cool on a wire rack and then cut into squares to serve. ?Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F2B2D6B6D1174B8DA58752FE5939E6', X'41B0D5367732F643BE4A76F02C46125A', X'41B0D5367732F643BE4A76F02C46125A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Place a 12-ounce serving glass in the freezer to chill. You want the glass to be ice cold before making and serving an egg cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314B62942BFF8E4C84610D42764D15B3', X'41B0D5367732F643BE4A76F02C46125A', X'41B0D5367732F643BE4A76F02C46125A', 1, NULL, NULL, 2, NULL, 0, 'Pour the cold milk into the chilled glass.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86D806E378659742B10A9C7544130865', X'41B0D5367732F643BE4A76F02C46125A', X'41B0D5367732F643BE4A76F02C46125A', 2, NULL, NULL, 2, NULL, 0, 'Pour the seltzer into the glass until the white head reaches the top of the glass.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A03E6CB0A0B804F8A3704A10FB1EA5C', X'41B0D5367732F643BE4A76F02C46125A', X'41B0D5367732F643BE4A76F02C46125A', 3, NULL, NULL, 2, NULL, 0, 'Spoon the syrup into the glass and stir to combine. Serve with a straw.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D934FF3DB01B034FBC42E16140CF8876', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC55C22AC1C094E82B83025F25AAA8F', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 1, NULL, NULL, 2, NULL, 0, 'Add the sugar and eggs to a large mixing bowl and whisk to combine. Whisk until the eggs are completely incorporated into the sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D95DBBFC64C12498F11C2404CF8C71E', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 2, NULL, NULL, 2, NULL, 0, 'Add the evaporated milk, coconut cream, and vanilla to the sugar mixture. Then whisk in the melted butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2909F2F66E4DA3479D9DA88DE9F8D1DB', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 3, NULL, NULL, 2, NULL, 0, 'Whisk in the rice flour, baking powder, and salt to the batter. Fold in the shredded coconut.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDA6A541E5C8BA4694DBCDD82A9ECBF2', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 4, NULL, NULL, 2, NULL, 0, 'Line a 9 by 13-inch baking pan with parchment paper. Leave enough paper around the edges so you can use them to lift the cake out of the pan. You can also butter the pan or grease it with non-stick cooking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E96F7D6350E034AAF0F66A35EE7C308', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 5, NULL, NULL, 2, NULL, 0, 'Bake for about an hour. The baking time will vary based on your oven and climate. It will be golden brown on top and when you insert the toothpick in the middle it will come out mostly clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41C93A02E0815A4AA79B819AB09AD7D3', X'672D286BF824F14395E3657A788DD336', X'672D286BF824F14395E3657A788DD336', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool. Then remove it using the edges of the parchment paper. Cut it into squares and serve. Top with more shredded coconut if you wish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23F71421A791634CADE9D362E3FFE4B4', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB94C0EB3D82A842B3875C23160AC23B', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 1, NULL, NULL, 2, NULL, 0, 'In a medium sized bowl, whisk together the flour, sugar, soda, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBADF7E3006802469CB6ED2DFAB2B527', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 2, NULL, NULL, 2, NULL, 0, 'Add the water, vanilla, vinegar, and oil and whisk until fully incorporated and smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE2EC5FB4FC83243861D8910FD159EAD', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 3, NULL, NULL, 2, NULL, 0, 'Transfer to a 9-inch pie plate and bake for 25 to 35 minutes, until a toothpick inserted into the center of the cake comes out with a moist crumb or two.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF8695A321CDE54EA0A5241336D5FF32', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 4, NULL, NULL, 2, NULL, 0, 'Make the frosting while the cake bakes and cools. Place the butter in the bowl of a stand mixer fitted with the paddle attachment and beat the butter on medium speed until soft and smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBC935314263044883F55D249B79A512', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 5, NULL, NULL, 2, NULL, 0, 'Add the sugar 1 cup at a time to the butter, mixing to combine on low, and alternating with the cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C4D5329A9BBE41A5C1C9F3A5ACDCCA', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 6, NULL, NULL, 2, NULL, 0, 'Add the salt and vanilla and mix for about 5 minutes on medium until smooth, fluffy and spreadable.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B27FEDA0BDEF1F4A90F0B39C529E2D06', X'8C182E833890B946915EB546B2F43988', X'8C182E833890B946915EB546B2F43988', 7, NULL, NULL, 2, NULL, 0, 'Generously frost the cake, decorate with rainbow sprinkles, and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'739731EDB9263145813385D102E49D6C', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F48FEF704DF6F54BB051437CD1AE43B1', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F and grease three 8-inch pans with cooking spray or softened butter. Line the bottoms with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7BCADC98D66A243AE682AC3A91B50D2', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 2, NULL, NULL, 2, NULL, 0, 'To make the cake, combine the sugar, oil and vanilla in a large mixing bowl and whisk vigorously to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A18FF94673D1A644A86603320576771E', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 3, NULL, NULL, 2, NULL, 0, 'Add the eggs and the yolks, one at a time, whisking after each addition to incorporate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'499C1D0D9C946C4FBEE04E273B64FFDD', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 4, NULL, NULL, 2, NULL, 0, 'Add the milk and whisk a final time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56EC88BA5B816548B3B100FE88DD1163', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 5, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the flour, baking powder, soda and salt and with a flexible spatula, gently fold the dry into the wet in 3 additions. The batter will be lumpy, but it is forgiving, and you can give it a quick whisk before filling the cake pans.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49A85ABC73E0B0418A7F60C541BF2BAB', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 6, NULL, NULL, 2, NULL, 0, 'Pour a scant 2/3 of a cup of batter into each cake pan and bake for about 10 minutes, rotating at the halfway point. The cake layers are ready when a toothpick comes out with a moist crumb or two.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC054D04A0644049A5A146D6727A4BEF', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 7, NULL, NULL, 2, NULL, 0, 'Let cool until the pans are easy to handle and then invert the layers onto cooling racks and repeat twice more with the remaining batter, cleaning, greasing and papering the pans between each round. When you are done, you should have 8 to 9 layers total.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A44B4F6DCEC6034B94FE47EF8AE3FE27', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 8, NULL, NULL, 2, NULL, 0, 'Make the frosting while the layers bake. In a medium saucepan over medium heat, combine the sugar and evaporated milk and cook until the sugar melts.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75FEB0D2D2579E41B1980834F5BB216C', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 9, NULL, NULL, 2, NULL, 0, 'Add the chocolates and butter, increase the heat to medium high, and cook until melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD26891820D2564EB489BFF4C898C07F', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 10, NULL, NULL, 2, NULL, 0, 'Once melted, reduce the heat back to medium and stir occasionally until the mixture thickens, about 10 to 15 minutes. It should resemble hot fudge sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73C1376BE78BCC44981FE649BF9D8F34', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 11, NULL, NULL, 2, NULL, 0, 'Add the vanilla and salt, stir and transfer to a different vessel (to help it come to room temperature more quickly) and set aside to cool and thicken. You may place it in the refrigerator, stirring frequently, to speed up this process.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FE9283399C9B345920389230A2FC5C3', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 12, NULL, NULL, 2, NULL, 0, 'To frost the cake, place a layer on a cake stand and spread a quarter cup of frosting over the layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'680BDE4427C41A489CE7D9E6E9DC6E58', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 13, NULL, NULL, 2, NULL, 0, 'Place another layer on top and repeat. Continue layering and frosting until you run out of layers.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B37325348F78E24BB7ED9B3C2526F5DD', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 14, NULL, NULL, 2, NULL, 0, 'Spread the frosting on the top of the cake and on the sides. Let the frosting set up for about 30 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35FDF0366D537B4B8F3961523A33D317', X'D240B3F40242D34FBABBDBF44C6F5837', X'D240B3F40242D34FBABBDBF44C6F5837', 15, NULL, NULL, 2, NULL, 0, 'Decorate with sanding sugar or sprinkles if you wish.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69609224516F834B96F236C2E1DA5A90', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 375 F. Prepare baking sheets by lining with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE96E1BA0ABBC4BAA7AB30880DDDA0D', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the flour and baking soda. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C74D8BC3301FE46B4FB370F7CAF80B1', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 2, NULL, NULL, 2, NULL, 0, 'In a large mixer bowl, combine butter, brown sugar, granulated sugar, instant vanilla pudding mix, and vanilla. Beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'540BE03C31D8C64B8932F953351BC600', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 3, NULL, NULL, 2, NULL, 0, 'Beat in the eggs until well combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D56D1175AB7E846BFB44046164E0D66', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 4, NULL, NULL, 2, NULL, 0, 'Gradually add the flour mixture to the butter mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08C5AC2DE46628469CC317A51060D176', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 5, NULL, NULL, 2, NULL, 0, 'Stir in the chocolate chips.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BE53F59247BB74FAC1CD29F7DA5857D', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 6, NULL, NULL, 2, NULL, 0, 'Drop by heaping spoonfuls about 2 inches apart on prepared cookie sheets. Bake in the preheated oven for 8 to 10 minutes or until lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7302C851FC5D804A94A4C4461C4D100B', X'159D8E3300C15A4488D62EF6422B61E3', X'159D8E3300C15A4488D62EF6422B61E3', 7, NULL, NULL, 2, NULL, 0, 'Cool on a wire rack. Store in an airtight container if not eating right away.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E012E2B944C4445810DC2324CC9F1E7', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89FE86E048216F4FB752A0CFC19B0827', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 1, NULL, NULL, 2, NULL, 0, 'Combine the oil and sugar in a 12-ounce microwave safe mug and use a fork to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD04EDBCF935F47B00726E49582E56E', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 2, NULL, NULL, 2, NULL, 0, 'Add the yolk and vanilla and mix again.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8172CB91660345BD2A7A858C8FF7C4', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 3, NULL, NULL, 2, NULL, 0, 'Add the heavy cream and mix again.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF967A46993D643A5374B3CD65EB1C2', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 4, NULL, NULL, 2, NULL, 0, 'Add the peanut butter and mix a final time.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3819165A47B6C54F879B2FF60B0FBD59', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 5, NULL, NULL, 2, NULL, 0, 'Add the flour, baking powder, and salt to the mug and again, using the fork, mix to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6F4C1D3B692CF47A400B07B741115C3', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 6, NULL, NULL, 2, NULL, 0, 'Add the chocolate chips and mix again.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020FEA4DD8193E4D83D6E68E2141D5EE', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 7, NULL, NULL, 2, NULL, 0, 'Place the mug in the microwave and microwave on high for about 1 1/2 minutes. The cake is done when its top has risen and is lightly sticky, but not wet. The cake will sink as it cools. do not be alarmed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99C79019C186C14B8FA7EE75605D6225', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 8, NULL, NULL, 2, NULL, 0, 'Serve warm with a dollop of whipped cream or a scoop of vanilla ice cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7515439962925349AADEA7201639FF02', X'5431025F93352D4BAF1689C356D09F86', X'5431025F93352D4BAF1689C356D09F86', 9, NULL, NULL, 2, NULL, 0, 'RECIPE VARIATIONS:');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'772A9A182B10214FAD0EBCC1505EE24F', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F. Line two baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9E70FE96211D14C82E4AEC2CE3845CA', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 1, NULL, NULL, 2, NULL, 0, 'Place the flour, baking powder, baking soda, and salt in a medium bowl and whisk to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43E2833340B1514798C536806E6395C2', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle attachment, cream the butter, sugar, and vanilla on medium speed for 2 minutes, scraping down the bowl with a rubber spatula as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1708939076FDCB4ABDF78EFC74A4DE7A', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 3, NULL, NULL, 2, NULL, 0, 'Add the egg and cream and mix on low speed for one minute, scraping down the bowl as needed. The mixture will look curdled.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08DE530D4EB4D040903A17A82C1310D1', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 4, NULL, NULL, 2, NULL, 0, 'Add the flour mixture all at once and mix just to incorporate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02017B4197898F4F9E5E523E95A89E48', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 5, NULL, NULL, 2, NULL, 0, 'Using a teaspoon-sized measuring spoon, portion the dough, roll into balls, and place on the prepared sheets. Use the heel of your hand to slightly flatten each ball. Place the pans in the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78AE4ED946768F4B84FE8A8153C46ADF', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 6, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 18 minutes, rotating the pans at the halfway point, until golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'785D2F47E0B0EC4BB406B39C3B00E6C9', X'0F91A4E24A168145AE2D5A5E17EED514', X'0F91A4E24A168145AE2D5A5E17EED514', 7, NULL, NULL, 2, NULL, 0, 'Place the pans on cooling racks and let the wafers come to room temperature before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E7A2998A1650446857E9B15327FB618', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10C4A7E3ED924B42B3D6E4BD358728C7', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 1, NULL, NULL, 2, NULL, 0, 'In a medium sized bowl, whisk together the flour, baking soda and powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8046FFC220404CB87EC6C4759CD03A', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer, fitted with the paddle attachment, combine the butter, shortening, the sugars, and vanilla, and mix on medium until incorporated, about two minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A8FB1DC082AE3479504849F2C894625', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 3, NULL, NULL, 2, NULL, 0, 'Add the eggs and yolk all at once, and mix on low until incorporated, scraping down the bowl as needed with a rubber spatula. Add the peanut butter, and mix on medium until smooth and uniform, scraping the bowl as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21286D79C0C5EC4D8BFA2B69C7206D23', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients all at once and mix on low just until combined. Place the mixing bowl in the refrigerator for about an hour, or until the dough is firm enough to roll into balls.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47AD59EDDAF0A846AC2053B6159775C2', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 5, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F and line a cookie sheet with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4504E364288BC74182A74AA6B7C504DC', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 6, NULL, NULL, 2, NULL, 0, 'Bake cookies for 10 minutes, rotating the pan at the halfway point. The cookies will be just lightly browned and starting to crack slightly when they are done. Immediately upon removing the sheet from the oven, gently place a chocolate kiss in the center of each cookie.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C762A987A340246A19B959F1F5F8242', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 7, NULL, NULL, 2, NULL, 0, 'Immediately upon removing the sheet from the oven, gently place a chocolate kiss in the center of each cookie.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7C74156A06A704CA02967ED80344C4E', X'3D4E3DD043B27A4D83750BCCAE6D65A8', X'3D4E3DD043B27A4D83750BCCAE6D65A8', 8, NULL, NULL, 2, NULL, 0, 'Repeat with the remaining balls of cookie dough, being careful to keep extra dough chilled between batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C661B479C0094DA657BAD5E920270C', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7563DEA4B0E424B8A210FA8B01C4F28', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the flours, 1/3 cup of the sugar, yeast, potato starch (if using), and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6168A6FADA2E194AA5B3B45A52445FE4', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle attachment, beat the eggs on medium-high speed until light and frothy, about 3 minutes. Add the butter and evaporated milk and beat on medium to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1866846843CB7F47BB170263F7C8DDE8', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 3, NULL, NULL, 2, NULL, 0, 'Replace the paddle attachment with the dough hook, and on low speed, add the flour mixture and mix for about 5 minutes until the dough is smooth and slightly sticky.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19DF28B5D450AE4C9196123026E90395', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out into a medium bowl that has been greased with cooking spray or softened butter. Turn the dough ball over in the bowl to coat it in spray or butter, cover the bowl with plastic wrap, and set aside to rise for about 1 1/2 to 2 hours, until it has practically doubled in size.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D041F0C2918FF148B41C58A60EE012D6', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 5, NULL, NULL, 2, NULL, 0, 'Remove the dough from the bowl, place it on your work surface (you should not need to flour it, but do so lightly, if the dough is too sticky to work with) and pat the dough or roll it out into a 10 x 12-inch square, about 1/2-inch thick.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BF58D6B74C9064EAC0506DC5C923359', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 6, NULL, NULL, 2, NULL, 0, 'With a 3-inch cookie cutter, cut out the doughnuts and place on the prepared sheet. You may re-roll your scraps and cut out additional doughnuts, but they will be tougher than your first 12.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0195AB9D1EC384D8DEA3ACC527CAD48', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 7, NULL, NULL, 2, NULL, 0, 'Place the baking sheet in a warm spot, lightly covered in plastic wrap, and let the doughnuts rest on the counter until they have doubled in size, about 1 1/2 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D75B21751908F47B3EB629ED05C606B', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 8, NULL, NULL, 2, NULL, 0, 'When ready to fry, fill a large, heavy pot with 2 inches of oil. Attach a candy thermometer to the side of the pot and heat the oil on medium-high heat until the temperature reaches 350 F, or slightly above (the temperature will drop when you add your doughnuts, but while frying, you want your temperature to stay at 350 F).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'673A41875CD4C748A9E14375CD1D18A0', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 9, NULL, NULL, 2, NULL, 0, 'Once the oil is at temperature, carefully transfer a couple of doughnuts to the oil and fry for about 90 seconds per side, or until lightly browned. Repeat with the remaining doughnuts, being careful not to overcrowd the pan and lower the temperature of the oil too much.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F95118CB9197D34EA146320CF6650000', X'C8E1521C1A10CE419A9000D702975AE0', X'C8E1521C1A10CE419A9000D702975AE0', 10, NULL, NULL, 2, NULL, 0, 'Without burning your fingers, dip the warm doughnuts into the remaining sugar and flip to coat. Serve immediately. The doughnuts will keep for a day or two, but they are best eaten within a few hours of making.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C0BEEF36F8C894CAD0FD905CC8708C9', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 450 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946F76CA3F1D0D4683207AF56D149FAA', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, beat the egg yolks. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A370E9B3633CF241BF792AC20A839DCC', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 2, NULL, NULL, 2, NULL, 0, 'In another large bowl, beat the egg whites until stiff. Slowly mix in the sugar until stiff peaks form.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D1CC7FE9D3A1341815B08BB992BA2EA', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 3, NULL, NULL, 2, NULL, 0, 'Fold the beaten whites into the beaten yolks.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E95423293A7A3546935DECB464D034CE', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the flour, nutmeg, cinnamon, allspice, cloves, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6F86C394833ED4CAEFD47DFED2EA449', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 5, NULL, NULL, 2, NULL, 0, 'Alternately add the flour mixture with the sour cream to the egg mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0B7B2520663044483E396C1B1F6D5F5', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 6, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the butter and vinegar. Stir in the walnuts and raisins.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EF15487FD3BEF489A81609F9E996CC1', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 7, NULL, NULL, 2, NULL, 0, 'Add the nut mixture to the egg and flour mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A17BDD3EE3A6F43AEFC35A7AF1411EC', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 8, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the prepared pie shell. Bake in the preheated oven for 10 minutes. Then reduce heat to 400 F and bake 5 minutes more. Finally, reduce the heat to 350 F and bake until the filling begins to set, about 15 minutes more.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27541E8EAD523746B8DF1380DF5B7A0C', X'C0C06AA6F47A70489FFBF121AEAFC0A2', X'C0C06AA6F47A70489FFBF121AEAFC0A2', 9, NULL, NULL, 2, NULL, 0, 'Cool completely. Top with whipped cream to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22B9AD3C3CB79E4FA3E1C4AE9FF33D72', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B0647A0B7A613459CCF69D44487AA4B', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 1, NULL, NULL, 2, NULL, 0, 'Whisk together flour, baking soda, and salt in a bowl and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF0F2982C7CE3A4C950E2C688D373E44', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 2, NULL, NULL, 2, NULL, 0, 'In a different bowl, beat softened butter, sugar, and brown sugar until creamy.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345CBEE4287AB74392F0A417BDF35B2D', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 3, NULL, NULL, 2, NULL, 0, 'Add vanilla extract and egg, and beat until mixed well.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'840856C839578F419B1221BEF349A89A', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 4, NULL, NULL, 2, NULL, 0, 'Combine the flour mixture with the wet mixture and mix well until all of the flour is incorporated.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2DFAAEC2DC3FE43B8ABE4385E8DF3C6', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 5, NULL, NULL, 2, NULL, 0, 'Add the chocolate chips. Mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'084715E530C776458F9B4BEF16894B00', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 6, NULL, NULL, 2, NULL, 0, 'Grease a skillet and add half of the cookie dough. Press it into the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C41E3CD354FA04CB23AA5D6A485F1E4', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 7, NULL, NULL, 2, NULL, 0, 'Spread peanut butter generously over the dough. Then carefully top with the other half of the cookie dough. Be sure to work the dough so it completely covers and encases the peanut butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A2DB8BE7629E046A18CEA2B3DB1314F', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 8, NULL, NULL, 2, NULL, 0, 'Bake for 35 to 40 minutes or until the edges are golden brown');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59E3B6B5E09C9842970B2C07A5DF5521', X'FAEC98B117DAFF4684D0D1FF6BA68091', X'FAEC98B117DAFF4684D0D1FF6BA68091', 9, NULL, NULL, 2, NULL, 0, 'Cut and serve with ice cream and whipped cream if you like. ?');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98F11336E2F4FE4080116BFF1BE3006C', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4940E4C6FB80474FBD9B3B0EC80F520B', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 1, NULL, NULL, 2, NULL, 0, 'Set a large baking sheet on a sturdy counter or tabletop and sprinkle it lightly with water.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62C21859E90A4F45BBAA1679508FC786', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 2, NULL, NULL, 2, NULL, 0, 'Combine the sugar, water, and corn syrup in a medium saucepan over medium-high heat. Stir until the sugar dissolves, then cover the pan and allow the sugar syrup to boil for 2 to 3 minutes. Remove the lid, and continue to cook the syrup, without stirring, until it reaches 240 F (115 C).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2593A6D9F3A6684390671B3001C1229B', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 3, NULL, NULL, 2, NULL, 0, 'Pour the sugar syrup onto the prepared baking sheet. Allow it to sit at room temperature for several minutes. After 2 to 3 minutes, lightly touch the syrup with a fingertip. When it is warm, but not hot, you can work with it.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDAA6C5AB413A246BBF1EA9A66B33C3C', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 4, NULL, NULL, 2, NULL, 0, 'Dampen a metal spatula or dough scraper with water, and use the scraper to push the syrup into a pile in the middle of the sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5813FAF475A2347885B17F3E3BFA2F7', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 5, NULL, NULL, 2, NULL, 0, 'Using a dampened plastic spatula or wooden spoon, begin to “cream,” or work, the fondant in a figure 8 pattern.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'831F54EC84A98E4181B510988AAC0535', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 6, NULL, NULL, 2, NULL, 0, 'Continually scrape the fondant into the center, draw a figure 8, then scrape it together again. At first, the fondant will be very clear and fluid, but it will gradually become more opaque and creamy. After 5 to 10 minutes, the fondant will become very stiff, crumbly, and hard to manipulate.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A545CC1431715F4AA5B1E8A8B05B2011', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 7, NULL, NULL, 2, NULL, 0, 'Once the fondant reaches this state, moisten your hands and begin kneading it into a ball like bread dough. It will come together and will become softer and smoother. Stop kneading once your fondant is smooth, with no lumps.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA64BD102BF5BB4D85CB4CD489331AF8', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 8, NULL, NULL, 2, NULL, 0, 'At this point, your fondant can be used for melting and pouring. Once the fondant is melted and smooth, pour it into your desired receptacle such as small candy cups and topped with other candies or nuts. Once the poured fondant hardens, it can be served as is, as the paper cups easily peel away. If it is difficult to pour the fondant from the saucepan, pour it into a measuring cup with a spout.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3DF694E1E1473459252D28E89C71925', X'2663426C3941FE4C857812C24F9B6690', X'2663426C3941FE4C857812C24F9B6690', 9, NULL, NULL, 2, NULL, 0, 'If you want to make flavored fondant candies, it is best to “ripen” your fondant for at least 12 hours to obtain the best flavor and texture. To ripen the fondant, place it in an airtight plastic container, press plastic wrap directly onto the surface of the fondant, and seal the lid on tightly. Ripen the fondant at room temperature, or if it is hot, in the refrigerator. After ripening, the fondant can be flavored, rolled, and shaped in whatever manner you wish. If it is stiff, you can always knead it by hand on a surface dusted with powdered sugar, until it is easy to manage.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3257FCBD110CB544A943C50793664351', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE211B3A0467D64A988EF08EF1A461C5', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 1, NULL, NULL, 2, NULL, 0, 'Coarsely chop up the almond paste into quarter-size chunks, then place the almond paste in the bowl of a large stand mixer fitted with a paddle attachment.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44FE62839C1B4540B896B40A8E3FF441', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 2, NULL, NULL, 2, NULL, 0, 'Add powdered sugar to the bowl and mix on low speed until the mixture has a fine, sandy texture. If you don''t have a stand mixer, feel free to use a food processor.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBF46869085A844CA475BDFEE8207768', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 3, NULL, NULL, 2, NULL, 0, 'Pulse the almond paste and powdered sugar until the mixture resembles wet sand.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'364C898E33A8AC46B97D3E282769B8C2', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 4, NULL, NULL, 2, NULL, 0, 'With the mixer on low, slowly add the egg whites (and almond extract, if using) until the mixture pulls away from the sides of the bowl and forms a ball. If you are using a food processor, you''ll notice a similar change. You may not need to add all of the egg whites.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38AED6F9889C7F4398E033FC9174D6B3', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 5, NULL, NULL, 2, NULL, 0, 'Dust a countertop with powdered sugar and knead the marzipan until it develops a smooth, pliable texture. It should have no lumps or dry patches.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3C953ED512AE3418C77F9A3C69CFBD8', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 6, NULL, NULL, 2, NULL, 0, 'Use the marzipan immediately or store it for later use. To store your marzipan, cover it tightly in plastic wrap, then seal it in a plastic bag. Not only will your marzipan stay moist, but it also won''t absorb any unfortunate odors or tastes. Marzipan will keep for three months in the refrigerator or up to six months in the freezer. Make sure to bring marzipan to room temperature before using it.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DFA924DF4F7CA4D9D17CE697D634D74', X'40E9BB324F4CCB40A9238EE8621F8D6C', X'40E9BB324F4CCB40A9238EE8621F8D6C', 7, NULL, NULL, 2, NULL, 0, 'Enjoy it on your favorite cake or as a tasty, nutty snack.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A76172FCA151E49B5ABA2296DF01B2A', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B57DED4239903F46915C74A518929130', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 1, NULL, NULL, 2, NULL, 0, 'Place the white chocolate in a microwave-safe bowl and microwave it gently until melted, stirring after every 30 seconds to prevent overheating.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2069E300B99A1A49937FBEEAF7740792', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 2, NULL, NULL, 2, NULL, 0, 'Place the strawberries in a small saucepan and heat them until they release their juices and become a chunky strawberry sauce. Blend them in a food processor or blender and then strain this mixture to remove any seeds. You will need 1/3 cup of strawberry puree for this recipe. If you have extra, save it for another purpose.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75220EDD1C4AFF4795DD2F187F80DAB4', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 3, NULL, NULL, 2, NULL, 0, 'Combine the 1/3 cup of puree, the corn syrup, and the heavy cream in a saucepan over medium-high heat and heat, stirring, until the mixture comes to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B324E81678A3FA4AA6AB8D4A59EB3292', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 4, NULL, NULL, 2, NULL, 0, 'Pour the hot puree over the melted chocolate and whisk until combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2821DE756FDAF54A917C33723FA33F9F', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 5, NULL, NULL, 2, NULL, 0, 'Stir in the citric acid and the lemon zest, and a drop or two of red food coloring, if using. This is your ganache. Cover the top of the ganache with cling wrap and refrigerate it until the mixture is firm enough to scoop and roll (about 2 hours).');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C04D9A711C67A40A05286CD1A77D8A6', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 6, NULL, NULL, 2, NULL, 0, 'If you are using the candied lemon zest and/or freeze-dried strawberries for decoration, prepare those while you wait.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBE962DB191F3846B7100298317D233B', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 7, NULL, NULL, 2, NULL, 0, 'Chop the candied zest into small strips, and place the freeze-dried strawberries in a food processor and process until they are a fine powder.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D4F215217B1646BECF50DAD6167EE4', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 8, NULL, NULL, 2, NULL, 0, 'Melt the white chocolate candy coating in the microwave, stirring after every 30 seconds to prevent overheating.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0D81E558EF8B440A9E4E3D425900C84', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 9, NULL, NULL, 2, NULL, 0, 'Dust your hands with powdered sugar and roll the ganache into small balls between your palms, re-applying powdered sugar whenever it starts to get sticky.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D66AC16299674BBD8B1C13245CA75D', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 10, NULL, NULL, 2, NULL, 0, 'Using dipping tools or a fork, dip a truffle in the white chocolate coating, tap the fork against the sides of the bowl to remove excess chocolate and place the truffle on a foil-lined baking sheet. If you’re using the optional decorations, sprinkle the top of the truffle with candied lemon zest and strawberry powder while the chocolate is still wet. Repeat until all of the truffles have been dipped.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAAADAF264A5054D80FA79FCD24AC9B2', X'179D3C253494DC44B4E83A6BE0F0C66D', X'179D3C253494DC44B4E83A6BE0F0C66D', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A0D37C958468E4D95ECD8D4114F71A3', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Prepare a 9 x 9-inch pan by lining it with aluminum foil and spraying the foil with nonstick cooking spray. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46F640D43C92224EB52743E89856720A', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 1, NULL, NULL, 2, NULL, 0, 'Place the granulated sugar, 1 1/2 cups of the water, and lemon juice in a medium saucepan over medium heat. Stir until the sugar dissolves and bring the mixture to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70B20ACBB3E3C849997A9BBEEC7C64C3', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 2, NULL, NULL, 2, NULL, 0, 'Brush down the sides of the pan with a wet pastry brush to prevent sugar crystals from forming and insert a candy thermometer to keep track of the temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F5B86BFD7213F429CF4CFC7F23712E6', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 3, NULL, NULL, 2, NULL, 0, 'Allow the sugar mixture to continue boiling, without stirring, until it reaches 240 F on the candy thermometer. This process can take from 45 minutes to an hour. When the sugar syrup is around 225 F, gather the rest of the candy ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F45ECDFD812C8341BD0B64BA1475846A', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 4, NULL, NULL, 2, NULL, 0, 'Place the remaining 3 cups of water in another, slightly larger saucepan. Add the cornstarch and cream of tartar and whisk until the starch dissolves and there are no lumps.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49BD9C4E40040B4181B3AE120E5475B4', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 5, NULL, NULL, 2, NULL, 0, 'Place the saucepan over medium heat and bring the mixture to a boil, stirring or whisking constantly. The mixture will become thick and pasty.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E26E8CF3E40041889A833EC420417B', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 6, NULL, NULL, 2, NULL, 0, 'Once the sugar syrup is at 240 F, remove it from the heat. Slowly and carefully pour it into the cornstarch mixture, whisking until it is fully incorporated.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D386E92062B81240951B0F015352BE17', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 7, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and simmer, whisking it every 8 to 10 minutes, for about an hour, until the candy has turned a light golden yellow color and is very thick and gluey.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE37396A09058B439C9D19A6233EFD72', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 8, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and stir the rosewater and the food coloring into the preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C085AF7B9F30814AB64C877ECB3AC604', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 9, NULL, NULL, 2, NULL, 0, 'Pour the candy into the prepared pan and allow it to set uncovered overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FF6329C7EA9E64D8C8AAF21796C24C3', X'4BE666785812D249A69C69CA9F8AFE0B', X'4BE666785812D249A69C69CA9F8AFE0B', 10, NULL, NULL, 2, NULL, 0, 'The next day, remove the candy from the pan using the foil as handles. Dust a clean and nonporous work station with powdered sugar and flip the candy onto it. Carefully peel off the foil from the back and dust the top with more sugar. Use an oiled and sharp chef’s knife to cut the Turkish delight into small squares. Dust each side of the square with powdered sugar to prevent stickiness.');
INSERT INTO `VWR INTERNAL AmericanRecipes recipeRegion` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'8DA620D8523D414A89B7130F9C769453', X'A7CE2A18598ACE4183A0FC284B079E1E', X'A7CE2A18598ACE4183A0FC284B079E1E', 0, 'https://www.thespruceeats.com/american-4162675', 'Classic American Recipes', 2, NULL, 0);
