INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B80790FF6F38C84DBD2CAE6DE44335A5', X'F7D5F933C703A5449E6AF9ECC82E2746', X'F7D5F933C703A5449E6AF9ECC82E2746', 7, NULL, NULL, 2, NULL, 0, 'Place the biscuits on a parchment-lined baking sheet and brush the tops with milk. Bake for about 15 minutes, or until golden and well-risen. Serve immediately.');
