INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F75FA2233BE1914A96DC317C4CC1EF61', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the flour, baking powder, sugar, and salt. Whisk or stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'834F7D2FFC3D474FA0CBC53488E760C3', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 2, NULL, NULL, 2, NULL, 0, 'In another bowl, whisk together the eggs and milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9F523C354B0C4EA4C0D41F9C04797D', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 3, NULL, NULL, 2, NULL, 0, 'Add the egg-milk mixture to the flour mixture, stirring just until blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'817906345CFD5746880450C1FF6D03EC', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 4, NULL, NULL, 2, NULL, 0, 'Mix in the melted butter. If the batter seems too thick to spread, add a little more milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C456E8FBBC33C44B6ADFEAA1DD3DE4E', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 5, NULL, NULL, 2, NULL, 0, 'Using about 1/4 cup of batter for each pancake, drop onto a hot, greased griddle.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6116F3FE09504B91FCDB0BED5DCFF2', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 6, NULL, NULL, 2, NULL, 0, 'Cook until you see that the bubbles over the surface are breaking and the edges appear somewhat dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'288C1487DEA3DD459591C3013C7CFAD7', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 7, NULL, NULL, 2, NULL, 0, 'Turn to brown the other side and finish cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD322167D67B247B6DFC5445AB66680', X'9E88155B4324064BA4F8109D62497246', X'9E88155B4324064BA4F8109D62497246', 8, NULL, NULL, 2, NULL, 0, 'Top with your favorite toppings and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57ACCA3E2607B46919FF1CD49A92868', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 0, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces and put on a plate in the freezer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A94E1D4F9A34624A83D9B0EDB9B446DC', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 1, NULL, NULL, 2, NULL, 0, 'Spread the pecan halves out in a dry skillet and cook over medium heat, stirring, until they are lightly browned and begin to smell toasty. Remove to a plate and let them cool. Alternatively, toast the pecans on a baking sheet in a 400 F oven for about 5 minutes, turning about halfway through the cooking time. Remove the pecans to a plate to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69467961F6506F4C8A120ECCB156E96D', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 2, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F or reduce the heat from 400 F to 350 F if you toasted the pecans in the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0496570C93F49C45A25A70830A516FB5', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 3, NULL, NULL, 2, NULL, 0, 'Line a large baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F1ABA3E43C464C8AA2C48FC37E2707', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 4, NULL, NULL, 2, NULL, 0, 'Put the 1/2 cup of granulated sugar, all-purpose flour, baking powder, and salt, if using, in the bowl of the food processor. Add the cooled pecans. Pulse until the mixture is fine.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08AA98ED2801D440AD391F4488889F0F', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 5, NULL, NULL, 2, NULL, 0, 'Add the frozen pieces of butter to the flour and pecan mixture and pulse until the mixture resembles fine meal. Pour the flour mixture into a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E1A9E6D98DD744ABF534E683B5F327', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 6, NULL, NULL, 2, NULL, 0, 'Stir the mini chocolate chips into the dry ingredients and then make a well in the center. Pour the 1 cup plus 2 tablespoons of heavy cream into the center along with the vanilla extract. Blend the flour mixture into the cream with your hands until moistened.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD3508DE7B6F9D4FB44F67DE3D541002', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 7, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured surface and knead a few times, just enough to make a smooth ball. Do not overwork the dough.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A443A2C33F3A804F85FC755F8DE0091F', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 8, NULL, NULL, 2, NULL, 0, 'With floured hands or a floured rolling pin, roll the dough out into a circle about 3/4-inch thick. It will be about 10 to 12 inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80BB5246C957D843A49A4B99BC7F8265', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 9, NULL, NULL, 2, NULL, 0, 'With a round or square biscuit cutter  any size will do  cut out the scones. Gather the scraps of dough together and lightly press them together  again, not overworking  and cut out the remaining scones.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C4B082F85F24240B54B447B5F590EAB', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 10, NULL, NULL, 2, NULL, 0, 'Place the cut-out scones on the prepared baking sheet, leaving about 2 inches between them.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E421649A58CC1A4890A83CF1BF5B15BD', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 11, NULL, NULL, 2, NULL, 0, 'Brush the scones lightly with some heavy cream and sprinkle with plain granulated sugar or vanilla sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68749BD6280D704DAF83808BCFB51C69', X'2EB690700423FB4B8457586888202671', X'2EB690700423FB4B8457586888202671', 12, NULL, NULL, 2, NULL, 0, 'Bake the scones for about 18 to 22 minutes, or until the scones are browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'788351EDF344A44AB2923EAFE0D593BF', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 425 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11A0F8B58FB4474D9CB1D1EBA345324B', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 1, NULL, NULL, 2, NULL, 0, 'Add the flour, sugar, baking powder, and salt to a large bowl and mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0DF1A30B72C8B4B9F9CFB807A657359', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 2, NULL, NULL, 2, NULL, 0, 'Using the large side of a box grater, grate in the cold butter and then stir with your fingers to make sure it''s coated in the flour mixture. Alternately, you can cut the butter into 1 inch pieces and work it in with your fingers, a fork, or a pastry cutter until the largest pieces are the size of peas.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8498D6E417EB5140B7031AE3E09366F6', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 3, NULL, NULL, 2, NULL, 0, 'Pour in the milk and lemon zest and stir to roughly mix. Add the blueberries and mix just until barely incorporated.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4B1D8DEA0679A4FB48344A482F49BD1', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto well-floured surface and roll it out to about a 1-inch thickness.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DF347D541D42D45BCEC0CE71694E02E', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 5, NULL, NULL, 2, NULL, 0, 'Using a 2 1/2-inch cookie or biscuit cutter, cut out the biscuit rounds and place them on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D1C6C5E978EC43A6F14086F193563D', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 6, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes or until the biscuits are a light golden brown. Let cool slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD026FC31442C046ADCA4E02AAADC1EC', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 7, NULL, NULL, 2, NULL, 0, 'To make the lemon glaze, whisk together the powdered sugar, milk ,and lemon juice until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F210CFE55598740930002C62847CE92', X'742FE8AC5DD00946ACAC5D1794E95F64', X'742FE8AC5DD00946ACAC5D1794E95F64', 8, NULL, NULL, 2, NULL, 0, 'Pour over the slightly cooled biscuits and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE8F0233463764391655D0B9DEB0C03', X'44C3EDB0ACD4B441A6B1657063920CC5', X'44C3EDB0ACD4B441A6B1657063920CC5', 0, NULL, NULL, 2, NULL, 0, 'Butter a 9-by-13-inch baking dish. Heat the oven to 325 F (165 C/Gas 3).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'347C8FC78705244BBB721917FB135DA4', X'44C3EDB0ACD4B441A6B1657063920CC5', X'44C3EDB0ACD4B441A6B1657063920CC5', 1, NULL, NULL, 2, NULL, 0, 'Cube bread and place evenly in the prepared baking dish. Sprinkle evenly with sausage and cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5799D38E67340D489708961E06A309CF', X'44C3EDB0ACD4B441A6B1657063920CC5', X'44C3EDB0ACD4B441A6B1657063920CC5', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the eggs with the milk, salt, and pepper. Pour over the layers in the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A623BCD5B86724A9797FF24897CA75C', X'44C3EDB0ACD4B441A6B1657063920CC5', X'44C3EDB0ACD4B441A6B1657063920CC5', 3, NULL, NULL, 2, NULL, 0, 'Bake 45 minutes or until set. Cut into squares to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55DD775C0CF93B47841DA9900DB2AC0F', X'44C3EDB0ACD4B441A6B1657063920CC5', X'44C3EDB0ACD4B441A6B1657063920CC5', 4, NULL, NULL, 2, NULL, 0, 'This casserole may also be prepared the night before. Just cover and refrigerate the unbaked casserole. Take it out of the refrigerator about 15 minutes before baking time; bake as directed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57F4304B989D564195CF9B5F0FBECA50', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4AA8FB2B857074987BF5C0A4B8D2701', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Grease a 12 cup muffin tin or spray with baking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'188823B5A8F03E49AF37B0B622840B12', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet or saute pan over medium heat, melt the butter. Add the chopped onions and mushrooms and cook, stirring, until the onions are softened and mushrooms are tender. Add the spinach, about 1/3 at a time, and cook, stirring, until the greens are all wilted. Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B7EEC484DA8143BCBACF2861A70109', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk the eggs and egg whites until blended. Stir in the shredded cheese, pepper, salt, and nutmeg. Stir in the cooled spinach mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09134210C4C6B44FA404E2B719855970', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 4, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into muffin cups, filling each about half full.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD021DD415E85146AFB8CCFA1A30DA3F', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 5, NULL, NULL, 2, NULL, 0, 'Bake for about 25 minutes, until a knife inserted into the center comes out clean. The quiches will puff up but will deflate soon after removing them from the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5728B989D790134D8549FC87823710D6', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 6, NULL, NULL, 2, NULL, 0, 'Let them cool for a few minutes, then use a spatula to lift them out onto a tray or plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4714C0940C83B84F888E090D3E5A69D7', X'2A66C7FA46DECF49B31197E594F24D0F', X'2A66C7FA46DECF49B31197E594F24D0F', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C73FF850CB5BFB4DB6AB70BBED9272FD', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57951F39E92F4048B5EFE2AD1A12842C', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the eggs, milk, salt, sugar, vanilla, cinnamon, and allspice.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7DE34652C5B5E46928ED27A972E0104', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 2, NULL, NULL, 2, NULL, 0, 'Slice the French bread into slices that are at least 1-inch thick. You can use a whole-grain French loaf, but any French or Italian bread should work nicely. Slice at a slight angle to make a longer piece of bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A198B1AD6D7A044EAEFA09A773306431', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 3, NULL, NULL, 2, NULL, 0, 'Soak each slice in the egg custard mixture. Turn the slices until all of the mixture has been absorbed into the bread. Depending on how stale the bread is, this may take from 5 to 10 minutes. The secret to this recipe is to completely saturate the bread. This is why thick slices of stale bread are used since thinner fresh bread would fall apart too easily. Make sure to flip the bread so all sides can soak evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCFA34CC666F8F438E94056B347E74D0', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. In a large non-stick skillet over medium heat, very lightly brown the slices in the butter and oil for about 2 minutes per side. Don''t make it too dark as it will also be baking in the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'554A696F867CF849A8F1AF420E0C1803', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 5, NULL, NULL, 2, NULL, 0, 'From the frying pan or skillet, transfer to a baking sheet or casserole dish. Bake for 10 minutes. After 10 minutes, remove from the oven, turn over, and put back in the oven for another 5 minutes to brown on the other side.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC806D94FCA6B5499E2676D87F3A5317', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 6, NULL, NULL, 2, NULL, 0, 'Traditionally, pain perdu is served with powdered sugar sprinkled over the top. If you want it to look like it would in the French Quarter, then dust away and dig in. You can also top it with maple syrup or fruit sauces like jellies, jams, or marmalades.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A5CAB63FFE65458EB0A1010B1A4C39', X'749662E1A0CC3D4F9BF17C3141FC82B6', X'749662E1A0CC3D4F9BF17C3141FC82B6', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3374F9C694D1944981FD7A0079328A3', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B9875FA423D5E4096EACF2AA9487A14', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour 12 muffin cups or line with paper liners. Preheat the oven to 375 F (190 C).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B54E4306EA3BC418639CAAE3A817CD2', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the salt, baking powder, flour, sugar, and cinnamon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59D9C056276B58478EFDE5DE82BBC899', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 3, NULL, NULL, 2, NULL, 0, 'Toss the cherries in 2 tablespoons all-purpose flour, and stir gently to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE580B4983462F47907C812E71EB6D62', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk the egg, vanilla extract, milk, and melted butter until well-blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52242F08DC2B5B4D8692E7FE1551F131', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 5, NULL, NULL, 2, NULL, 0, 'Stir the egg and milk mixture into the dry mixture until moistened.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51E3C996C64BA34E9363CA516A3526D5', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 6, NULL, NULL, 2, NULL, 0, 'Fill the muffin cups about two-thirds full.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95B147B1434C5C4CA3A15B222327C8C0', X'182EF5CDFAEB704CA5D593B9115C4D1A', X'182EF5CDFAEB704CA5D593B9115C4D1A', 7, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes or until an inserted toothpick comes out clean. Let the muffins cool for 10 to 15 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB92E8D9E149440B07C31707CD46C9B', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F / 190 C / Gas 5. Line a 12-cup muffin tin with muffin/cupcake papers.');
