INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01408F8738335E44B7DB684D0FBA8517', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 2, NULL, NULL, 2, NULL, 0, 'Beat the egg and 1 1/2 teaspoons of vanilla into the creamed mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D17471CD310CE544820BEF32FC21390D', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 3, NULL, NULL, 2, NULL, 0, 'In another bowl, combine the flour, baking powder, cinnamon, nutmeg, and salt; mix well to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1579EA1E4CF1D46BB53AD47E77A0DC2', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 4, NULL, NULL, 2, NULL, 0, 'Add about one-third of the flour to the first mixture along with half of the milk; blend well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3525E437DEFBC4496D34D7A82E451C5', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 5, NULL, NULL, 2, NULL, 0, 'Add another one-third of the flour mixture and the remaining milk; mix until blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8455C459BBA8594595A047CA4C4F1DC0', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 6, NULL, NULL, 2, NULL, 0, 'Add the remaining flour and mix until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9220AED0CD24FD4A86E3613B0452063F', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 7, NULL, NULL, 2, NULL, 0, 'Stir in the shredded carrots and chopped walnuts.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F36C66D1B301F41A1048CDF4E2E9748', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 8, NULL, NULL, 2, NULL, 0, 'Fill the prepared muffin cups almost full.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC95E7F959E77746B0B294B85BD95D31', X'E41A224ED724A54DAABD389D246148F6', X'E41A224ED724A54DAABD389D246148F6', 9, NULL, NULL, 2, NULL, 0, 'Bake the muffins for about 25 minutes, or until browned and a toothpick inserted into the center comes out clean.');
