INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B760A2544A76904BA41EC3CAB50CD40C', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 2, NULL, NULL, 2, NULL, 0, 'Cut in cold butter with a pastry blender until coarse crumbs form.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'137794B0DEA6DD43AE9D26B2926C16EF', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 3, NULL, NULL, 2, NULL, 0, 'Add water, a little at a time, until dough holds together and forms a ball.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'230C04AED3280849902DA80F2B8E9A77', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 4, NULL, NULL, 2, NULL, 0, 'Shape into a disk, wrap in plastic wrap, and refrigerate for about 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EFFD8D63F61BF4EA7DC3E0262FB2EA7', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface with a floured rolling pin, roll out dough into a circle about 12 inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F1279467E4F00458DAC2A986A75E473', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 6, NULL, NULL, 2, NULL, 0, 'Fit dough into a 9-inch pie plate or quiche pan. Trim edges, leaving a little overhang, about 1/4-inch all around the edge. Prick dough with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3275615D07C91D4E9B83E34AD132F5AA', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 7, NULL, NULL, 2, NULL, 0, 'Line with foil and fill with dried beans or pie weights.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31517B83198CA242BA31152506495FC0', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 8, NULL, NULL, 2, NULL, 0, 'Bake quiche crust for 10 minutes at 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A476C90E45927D45B709CD76F840DD87', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 9, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the eggs, milk, thyme, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'887A8324345E8245A5A538156ADEA9B2', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 10, NULL, NULL, 2, NULL, 0, 'Pour into the baked crust and crumble bacon over the top with the shredded Cheddar cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5195979DDE0D464BBD5A5A2AAE6AD221', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 11, NULL, NULL, 2, NULL, 0, 'Bake at 375 F for about 30 minutes, or until quiche filling is set and the top is lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBDFF5C3B599D74E8A38E3A6169BD77F', X'70E2B7DEDA439F44BAE77B1CA4A5239B', X'70E2B7DEDA439F44BAE77B1CA4A5239B', 12, NULL, NULL, 2, NULL, 0, 'Serve the quiche with fresh tomato wedges or a tossed salad.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A353B8617C318F4BA49B35F019D018B5', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB2D758F5F31F5419AF079D1A6C4F26F', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 1, NULL, NULL, 2, NULL, 0, 'Bring the water and salt to a boil in a large heavy saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C538CBE716C7447ADBA1B032A0F12EA', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 2, NULL, NULL, 2, NULL, 0, 'Add the grits and continue to simmer, stirring constantly, over medium heat until the grits are cooked and thick like mush, about 15 to 20 minutes. If necessary, add a little more boiling water.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F5FCEBE93DEB948AA4E5F911CC45C24', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 3, NULL, NULL, 2, NULL, 0, 'Pour the hot grits onto a large plate or medium baking sheet to make a layer about 3/4-inch deep. Cover and let stand to cool, then refrigerate to chill thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2CB6D78F563DD4E8C6EC00F22FB016D', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 4, NULL, NULL, 2, NULL, 0, 'When the grits are cold and firm, cut them into rectangular pieces or use biscuit cutters and cut rounds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7900B6F9A1CCC458FBAB00210483B01', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 5, NULL, NULL, 2, NULL, 0, 'Dredge the grits pieces with flour, shaking off any excess flour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D54B628CF0B4744A6AAE6050BF4CB9A', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 6, NULL, NULL, 2, NULL, 0, 'Heat about 1/2-inch of vegetable oil in a large, heavy skillet over medium-high heat. Fry the grits cakes until golden brown on both sides, about four to five minutes total.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E86628C22308C45AC584A8E9A7BD0CF', X'188BA7A00EA83D43935098169E47499E', X'188BA7A00EA83D43935098169E47499E', 7, NULL, NULL, 2, NULL, 0, 'Drain on paper towels and sprinkle with salt.');
