INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5C5B392F7418D4F97E7B962FCCD1A24', X'4F8EC90BBAB56D44AB35447FFA2A8ED7', X'4F8EC90BBAB56D44AB35447FFA2A8ED7', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring the 1/3 cup of lemon juice and 1/4 cup of sugar to a boil in a small saucepan. Cook, stirring until sugar is completely dissolved. Using a spoon, drizzle a little of the juice mixture over each muffin.');
