INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6573DBD0834681408E5350985182B640', X'508B63026D481D478B6DF723738F17D5', X'508B63026D481D478B6DF723738F17D5', 4, NULL, NULL, 2, NULL, 0, 'Put the pan in the hot oven and melt 2 teaspoons butter.');
