INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5605096172FA24488C7503022B5A8628', X'6F7AF1A9453EBC46B165F4AC8E1B2654', X'6F7AF1A9453EBC46B165F4AC8E1B2654', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring gently until the eggs are set and cooked through. Be sure to reduce the heat and keep stirring so the egg mixture doesn''t stick or burn on the bottom of the pan. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71CD053B52F3EB44B9DD54C7D52E7021', X'6F7AF1A9453EBC46B165F4AC8E1B2654', X'6F7AF1A9453EBC46B165F4AC8E1B2654', 3, NULL, NULL, 2, NULL, 0, 'Crumble the bacon and sprinkle it and the shredded cheese onto the cooked eggs. Let it stand a minute or two, just until the cheese melts.');
