INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B40930B0EA1801429A34DF07C8D63422', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 2, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a strainer and rinse with abundant cold water to release some starch. Shake to remove excess water and transfer the potatoes to a clean kitchen towel. Squeeze well, many times, to remove excess moisture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF2ECCD20BD51C4CAB2A889CA1D4F27B', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 3, NULL, NULL, 2, NULL, 0, 'In a medium-size bowl, combine the potatoes with the finely chopped onion, salt, and pepper.');
