INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ABC033ECCD55E478FE8BF931AF993EC', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 5, NULL, NULL, 2, NULL, 0, 'When the butter and oil are hot, add the potato and onion mixture. Using a spatula, press them down firmly, but do not stir the potatoes. You want to create a crunchy layer at the bottom.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C53F2E2D4AAB4EB24BBC103DDCA67C', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 6, NULL, NULL, 2, NULL, 0, 'When the bottom is golden brown, turn the potatoes carefully and brown the other side: Using silicon gloves or oven mittens, place a round plate on top of the pan, turn the pan upside down so the potatoes are on the plate, and then slide the potatoes back into the pan. Alternatively, you can cut the potatoes in four quarters and turn them upside-down in sections.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9FEC44D808F6749A9795B13822D1C65', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle extra salt and pepper to taste. Once the potatoes are crunchy and gold on both sides, transfer to a plate to serve or keep warm in a 200 F oven until it''s time to use.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E09DD6486233C4E89A53C1C4745D510', X'A0475613D64D9B4EA1EAAFFB1E10C308', X'A0475613D64D9B4EA1EAAFFB1E10C308', 8, NULL, NULL, 2, NULL, 0, 'Serve with the breakfast foods of your liking and enjoy!');
