INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'754ECEAF4502A241B2DA800450850E7A', X'86EEF96BDE4BF2448BE21E3E3D8A2FA7', X'86EEF96BDE4BF2448BE21E3E3D8A2FA7', 3, NULL, NULL, 2, NULL, 0, 'Cut each bacon slice crosswise in half; wrap each half around a scallop and secure with a toothpick.');
