INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96A7FF5C455A0A4BA01ECA1FF3A818B9', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', 2, NULL, NULL, 2, NULL, 0, 'Drain the crabmeat and gently squeeze it to get as much of the liquid out as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'328DCD73759ABF4EB8B422364C5F4C22', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', 3, NULL, NULL, 2, NULL, 0, 'Put the crabmeat in a medium bowl. With a spatula or wooden spoon, fold in mayonnaise mixture and 1 cup of the bread crumbs, just until blended. Shape into 8 crab cakes, about 2 1/2 inches in diameter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C8D88EB4520134081ACE8506C2CDE79', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', 4, NULL, NULL, 2, NULL, 0, 'I use a biscuit or cookie cutter with an open top to shape the cakes and press the ingredients down to make them hold together. Press gently into reserved crumbs. Cover and chill for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEB1EE1AD48D7E459BE2BA761BC5DFA8', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', X'8ED00E15BEE3234F8FCCFA7AA1D82CFD', 5, NULL, NULL, 2, NULL, 0, 'Heat the butter and oil over medium heat. When the butter stops foaming, fry crab cakes for about 5 minutes on each side, carefully turning only once. Serve with lemon wedges and Remoulade or other sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5182BAF055A5B41BFC93974A639D7E4', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9A84A9E50A4F64495797F39C91ACEDA', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 1, NULL, NULL, 2, NULL, 0, 'Wash and trim the end of stems from mushrooms. Pop remaining stem out. Chop stems finely and set them aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D968E798A48A2949A71F24206EAD5A13', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, melt 2 tablespoons of the butter. Once melted, brush the melted butter over mushroom caps.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76F48630BF961E44A84E00F5CBE2D68B', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 3, NULL, NULL, 2, NULL, 0, 'Spray a shallow baking dish (about 8-inch square, or one which will fit mushrooms in one layer) with nonstick cooking spray, line with parchment paper, or grease the pan with butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19EEEA250250C24AA7EA986EBD13E7D8', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 4, NULL, NULL, 2, NULL, 0, 'Melt the remaining butter in the saucepan over medium heat. When the butter is hot add the reserved chopped stems, minced green onions, and red bell pepper. Cook for 3 to 5 minutes, or until the vegetables are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB353AECA383A942BA71F1FD8A18B520', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 5, NULL, NULL, 2, NULL, 0, 'Combine the onion and butter mixture with the bread crumbs, crabmeat, Cajun seasoning, and pepper. Taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E383AA3C4E2604AB26ED707F49C4956', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 6, NULL, NULL, 2, NULL, 0, 'Fill the mushrooms, mounding the stuffing in each cap.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACEDA2B4ADC5B04199F9117C5738DC3B', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle each filled mushroom cap with a little Parmesan cheese, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC72438B5156448A86D9979CC2C5463', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 8, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes, until the crab stuffing is hot and mushroom caps are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D130A17B605147B1D2A573AA0E5458', X'F174FF2A2F9793418A74F17063416A08', X'F174FF2A2F9793418A74F17063416A08', 9, NULL, NULL, 2, NULL, 0, 'Serve them hot. If you need to keep them warm for a lengthy serving, use a warming tray, chafing dish, or slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCDC1C363D7E5408C25B26692808FF6', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9B9C890037E6548AF1C0FC0CE1A1DD4', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 1, NULL, NULL, 2, NULL, 0, 'Cook the spinach following the package directions. Drain it in a colander, pressing with the help of a bowl or big cooking spoon to squeeze out as much of the liquid as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0BAA30B73112C4DA07DABC03B94DD1E', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the warm spinach with the softened butter. Add the stuffing crumbs, Parmesan cheese, and minced onions. Mix well and add salt and pepper to taste, plus the nutmeg. Be mindful that stuffing crumbs already have salt in them so go light at first and check the seasoning again right before baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5822FEC441AE78408D12B32C21B557F6', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the eggs lightly and add them to the spinach mixture. Combine thoroughly and chill the mixture for about 30 minutes, covered.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C667394D3717C43A4C70FB82988B4BF', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F478200843B194B92F61A565DB737F6', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 5, NULL, NULL, 2, NULL, 0, 'While the mixture chills, line a large rimmed baking sheet with parchment paper or spray it with nonstick cooking or baking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B79F5B30FCCEF4182F4EA6CF145C015', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 6, NULL, NULL, 2, NULL, 0, 'If using, put the fine dry breadcrumbs in a plate or bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55EE59F40EF14B4D9421C89472F550B4', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 7, NULL, NULL, 2, NULL, 0, 'Once the mixture is cool, use a rounded teaspoon or small cookie scoop and shape the spinach mixture into small balls. If the mixture is sticking to your hands, dampen them with cold water as you work.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A645E2CEE819044DB0CEB0B3CC164459', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 8, NULL, NULL, 2, NULL, 0, 'Roll each ball in the bread crumbs until it''s lightly coated and place it on the baking sheet, leaving about 1/2-inch of space in between.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'349E7E45FF27924A8699A0BB6D556D3D', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 9, NULL, NULL, 2, NULL, 0, 'Bake for about 15 to 20 minutes, or until they feel firm to the touch.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A493FE8436AD954C9C318600C22E7444', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 10, NULL, NULL, 2, NULL, 0, 'If you''re serving them as an appetizer, keep them warm on a warming tray, or in a buffet server, chafing dish, or slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20E6C72F87F27546B6C52F5452D4A3ED', X'A83061574BD98C4BB9FD06CE3B2222ED', X'A83061574BD98C4BB9FD06CE3B2222ED', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93469308E085FC49A05A9AC91A39D0EC', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'662E3F3CA2417943B9CA3EC7E50B9ABE', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 1, NULL, NULL, 2, NULL, 0, 'Combine the turkey, mashed potatoes, parsley, and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF1155407FE1BD4DAAE9EA14455E2016', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan over medium heat, melt butter. Add green onions and cook, stirring, for 1 minute. Add the flour and stir until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF92445E36DC0842B1E477C6C0DAC9D9', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 3, NULL, NULL, 2, NULL, 0, 'Stir in chicken broth and milk. Continue cooking, stirring, for 2 minutes. Add salt, to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DCA244E87C9BF49B47FF53ECF0E9E7F', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 4, NULL, NULL, 2, NULL, 0, 'Combine with the turkey mixture, blending well. Cover and refrigerate until thoroughly chilled, about 1 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89807CF0F54CC64690A9CABE8A683B29', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 5, NULL, NULL, 2, NULL, 0, 'Put the 1/2 cup of flour on a plate. Beat eggs in a bowl with 1 tablespoon of water. Put the finely ground bread crumbs in another bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE8594CEE73D3845810ABD94357AABF4', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 6, NULL, NULL, 2, NULL, 0, 'Shape croquette mixture into 1 1/2-inch balls (a little smaller if you''re making appetizers).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AEE8AAECB5AB441868B7E5A0E80E4C1', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 7, NULL, NULL, 2, NULL, 0, 'Roll gently in the flour to coat, then in the egg until coated. Coat with the breadcrumbs. Place on a waxed paper or foil-lined cookie sheet. Repeat with remaining turkey mixture and crumbs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBDB20FB454A72438220B3B687E21D5D', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 8, NULL, NULL, 2, NULL, 0, 'Let the croquettes stand for about 30 to 45 minutes to dry a bit.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7FB26F7D714604BAA57784A0F606E51', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 9, NULL, NULL, 2, NULL, 0, 'Heat oil to about 360 F. Fry the croquettes in small batches for about 1 1/2 to 2 minutes, depending on size, or until golden brown. Drain on paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0824FEA394839B45968CB5FE5F203CEF', X'6D48312C99574147B73BBD9E5B166581', X'6D48312C99574147B73BBD9E5B166581', 10, NULL, NULL, 2, NULL, 0, 'Serve with hot turkey gravy and cranberry sauce as part of a post-holiday meal or as appetizers for guests.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FE2C2912CC0B34D8E5E2ABF3090EEFA', X'4E23540DE4B6FF4AA8DE7F2CB764CAC0', X'4E23540DE4B6FF4AA8DE7F2CB764CAC0', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the cream cheese, sour cream, chilies, onion, red bell pepper, and cheddar cheese. Mix thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8726915676B0C743A303747B679B3887', X'4E23540DE4B6FF4AA8DE7F2CB764CAC0', X'4E23540DE4B6FF4AA8DE7F2CB764CAC0', 1, NULL, NULL, 2, NULL, 0, 'Spread onto tortillas and roll up. Cover tightly and chill for 2 hours or overnight. When ready to serve, cut each roll into 1/2-inch slices. Serve with a mild or medium salsa or a Picante sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB05FACFD2B0F645A113567A10FCE3A9', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09781ABBCA0AF34CA231A9EAAF4CA889', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 1, NULL, NULL, 2, NULL, 0, 'Scoop out the seeds. Discard the big chunks of stringy pulp and put the seeds in a colander.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'411065A8B4442643AC530BCE787F78CA', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 2, NULL, NULL, 2, NULL, 0, 'Rinse the pumpkin seeds under cold running water. Swish them around to remove any pieces of pulp clinging to the seeds. Drain.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3C84AFC807D8442A5344248E0E88E8B', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 3, NULL, NULL, 2, NULL, 0, 'Spread the seeds out onto a kitchen towel to dry. Don''t use paper towels to dry the seeds; they will stick.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47AB41518160449BD4F6D4F404AF791', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F. Line a baking sheet with foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'864DAC79A9A3DD4C94FA1AC25014EB46', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 5, NULL, NULL, 2, NULL, 0, 'Spread the seeds out on the foil-lined baking sheet. Sprinkle the pumpkin seeds with the salt and drizzle with vegetable oil. Toss to coat the seeds thoroughly. Bake the pumpkin seeds in the preheated oven for about 35 to 45 minutes, stirring and turning once or twice. They should be lightly browned and crisp.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A42B7F599242DB47A5E3D2AFEA497D7C', X'860CA2D511507C478C493693DDEF4FBB', X'860CA2D511507C478C493693DDEF4FBB', 6, NULL, NULL, 2, NULL, 0, 'Serve the pumpkin seeds in snack dishes and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E6F5E96B5941409699870BAE7168CB', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5E815BED417AD4886CEC1DCE8A19E94', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F. Butter a shallow baking dish large enough to hold the mushroom caps in a single layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0676B346C255448AD4D1CCF4607709C', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 2, NULL, NULL, 2, NULL, 0, 'Carefully clean the mushrooms with a wet paper towel, remove the stems, leaving the caps whole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F0C7186CF61424CA582A07322C5D84E', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 3, NULL, NULL, 2, NULL, 0, 'Finely chop the mushroom stems.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26946A807ED6F0478258AF63D109D70C', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 4, NULL, NULL, 2, NULL, 0, 'Sauté the mushroom caps in 3 tablespoons of melted butter until tender. Transfer to a plate and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899496BD450989428F928E3CA12DDB59', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 5, NULL, NULL, 2, NULL, 0, 'Sauté the chopped stems, green pepper, and chopped onion in the remaining 5 tablespoons of butter until the onions are translucent.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A30E393E29BB747A7875FEC126C8605', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 6, NULL, NULL, 2, NULL, 0, 'Add the breadcrumbs, salt, pepper, and cayenne pepper to the onion and pepper mixture. Stir to blend the ingredients thoroughly and remove from heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653044ED81F42845BA0478A22465F3F7', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 7, NULL, NULL, 2, NULL, 0, 'Fill each mushroom cap with the breadcrumb mixture and then sprinkle each with a small amount of Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C138548DCE4B348BE17D0614D7DC195', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 8, NULL, NULL, 2, NULL, 0, 'Arrange the stuffed mushroom caps in the prepared baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7A3F307183E3D47AAB08DF31C11BCEA', X'12734CDB7BB699459F6D9CC36D79E68B', X'12734CDB7BB699459F6D9CC36D79E68B', 9, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes in the preheated oven, or until the filling has browned and the cheese is melted and crunchy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8E0EDA9D39A5D48A0F0C38CC4A29F08', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0FDC6D7B920C34F9C3F2301018B01DE', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'799DE82525DAA04E8E093C8C2D6DB2B9', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 2, NULL, NULL, 2, NULL, 0, 'Arrange the tortilla chips in one layer on parchment-lined baking sheets.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5793A5CA4D7741448F0FAE5ACA074536', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 3, NULL, NULL, 2, NULL, 0, 'Gently spread some bean dip on each large chip.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A67872414A388449A77ADD1465F26E63', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 4, NULL, NULL, 2, NULL, 0, 'Place a jalapeno ring or two on the bean dip. Drizzle a little salsa or chili sauce over the top and place a quarter-slice of cheese on each.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5AAE54769DA8945972E571BDB5FD22B', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 5, NULL, NULL, 2, NULL, 0, 'Bake in the heated oven until cheese melts, about 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF0F8FD0F7E1304883F334E9C5407A0C', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 6, NULL, NULL, 2, NULL, 0, 'Top each appetizer with a small dollop of guacamole or sour cream and add another jalapeno ring for garnish, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5DD2B9F2A12644A58EAE1E110BBFC2', X'65DAC7A674A7004D8B515CE39A68EDE6', X'65DAC7A674A7004D8B515CE39A68EDE6', 7, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy! Note that these do not store well as they will become soggy. You might consider baking these in batches so you can serve your family and guests hot, tasty appetizers, instead of cold, soggy ones.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BDF0F302AEF9D489233239DD81571B2', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AFAE3BE2F26A24A8B0D468A9B5074A8', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Scrub the potatoes and prick several times with a skewer or fork tines.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'180323B1A244D741818CA2A49CE2B7A0', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 2, NULL, NULL, 2, NULL, 0, 'Bake the potatoes for about 1 hour to 1 hour and 15 minutes, or until the potatoes are fork-tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B931E913CA90AB4C9D5975741BB7C8D2', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 3, NULL, NULL, 2, NULL, 0, 'When the potatoes are cool enough to handle, cut them in half lengthwise. Scoop out much of the potato from each half, leaving about 1/4 to 1/2 inch of potato in the shell. Save the potato for another use, such as baked potato soup or mashed potatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578AB46D305DE54B975457B324EB9F3F', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 4, NULL, NULL, 2, NULL, 0, 'Cut the halves in half again lengthwise. Brush all over with olive oil or vegetable oil, then sprinkle the tops with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C2B98E45B88A44AAB66DFB8DCC830D5', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 5, NULL, NULL, 2, NULL, 0, 'Arrange on a baking sheet and bake for 10 minutes, until slightly crisp around the edges.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0CB8719BAE5E74B87841A6F18C313D5', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 6, NULL, NULL, 2, NULL, 0, 'Remove the potato skins from the oven and mound about 1 tablespoon of shredded cheese on each piece; sprinkle with crumbled bacon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'225BA71737F41F44BEE4A1FB27CA2821', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 7, NULL, NULL, 2, NULL, 0, 'Return the potato skins to the oven and bake for another 7 to 10 minutes, or until the cheese has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCF42F8A54E75A4F8FFE3DEC6B36ED0C', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 8, NULL, NULL, 2, NULL, 0, 'Top with thinly sliced green onion.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF380A07CB95F54C8B2A199048EF5188', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 9, NULL, NULL, 2, NULL, 0, 'If desired, serve with small bowls of salsa, guacamole, and sour cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C9F9480214D7249950B558818743DD8', X'F5076C047EBC6F47A8417729FC28A480', X'F5076C047EBC6F47A8417729FC28A480', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D24E17FD0262A46AE1559726DE4BF71', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 0, NULL, NULL, 2, NULL, 0, 'Heat oil up to about 350 F in the deep fryer, or use a deep heavy skillet or Dutch oven with enough oil to cover several wings lying flat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E882B349043DB14E8644578D237FBC43', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 1, NULL, NULL, 2, NULL, 0, 'Whisk together eggs and water. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADE2404B4AFACC46A5BA2479A3061647', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 2, NULL, NULL, 2, NULL, 0, 'Mix together the barbecue sauce and honey. Make the sauce as sweet as you like.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DA94A960E68924B90925470B336F40E', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 3, NULL, NULL, 2, NULL, 0, 'Put the flour into a food storage bag, then shake the wings in it to lightly coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD09D5735BE9B4480DD1A9E60C48006', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 4, NULL, NULL, 2, NULL, 0, 'Roll the wings in the egg wash, then toss them back into the bag. You want a fairly heavy coating of flour so the barbeque sauce has something to hang onto. You may want to dip and shake 2 to 3 times, using more flour and eggs as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C04A866D873549B2B8EBC06B29206B', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 5, NULL, NULL, 2, NULL, 0, 'Fry the wings in batches until they''re golden brown and crispy. You''ll be baking them later, so don''t worry about the wings being thoroughly cooked. Drain chicken wings on paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A64C8A13299642AF7D4AF10D7D0816', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 6, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'549D1BC63235404B95FB90CF16CFA68A', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 7, NULL, NULL, 2, NULL, 0, 'Once the chicken pieces are browned and drained, dip each piece into the barbeque sauce and honey mixture and place onto cookie sheets. Make sure the pieces don''t touch each other.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77079A245471D04580E130E9A58304BA', X'8F03F234D89F2A4F9E116121259A38AF', X'8F03F234D89F2A4F9E116121259A38AF', 8, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes, or until they no longer look shiny and the chicken tests done (juices run clear when poked with a knife). Baking dries the coating out a bit and sets the coating so it doesn''t fall off.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D49B6298F589841A8772428AD505648', X'322EC75A1243844383625BB5D5859544', X'322EC75A1243844383625BB5D5859544', 0, NULL, NULL, 2, NULL, 0, 'In large mixing bowl, combine bread crumbs with minced onion, salt, pepper, and 1/2 cup water. Add ground beef and egg. Shape beef mixture into 16 meatballs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E826B10C4E7C1F4BB84DAC2D8A9E9723', X'322EC75A1243844383625BB5D5859544', X'322EC75A1243844383625BB5D5859544', 1, NULL, NULL, 2, NULL, 0, 'Brown the meatballs slowly in a large skillet over medium-low heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D28C5300935E49ABA1FF1D599D0586', X'322EC75A1243844383625BB5D5859544', X'322EC75A1243844383625BB5D5859544', 2, NULL, NULL, 2, NULL, 0, 'When the meatballs are browned and cooked through, remove from the skillet and set aside. Cook celery and pepper strips in skillet for about 5 minutes. Add remaining water, vinegar, brown sugar, and soy sauce. Mix cornstarch with a little cold water then add to skillet; stir well and cook for about 4 minutes. Add meatballs and pineapple. Cook until heated through.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787C15CDAC4C604F8515F634A5D155B6', X'322EC75A1243844383625BB5D5859544', X'322EC75A1243844383625BB5D5859544', 3, NULL, NULL, 2, NULL, 0, 'Serve with hot cooked rice or noodles, along with steamed broccoli or green beans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ECCC843676D6E4FBD08BA347A1BBD53', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', 0, NULL, NULL, 2, NULL, 0, 'Wash and trim the end of stems from mushrooms. Pop the remaining stem out. Chop stems and set aside. Melt 2 tablespoons butter; brush over mushrooms. Spray a shallow baking dish (about 8-inch square, or one which will fit mushrooms in one layer) with butter-flavored spray or grease with butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCC62060AACF084E9649138ABA3091E1', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', 1, NULL, NULL, 2, NULL, 0, 'Heat remaining 2 tablespoons of butter in a skillet. Chop green onions; combine with reserved chopped mushroom stems, ham, and garlic. Add to skillet along with the spinach; sauté until tender. Add bread crumbs, salt, and pepper to vegetable mixture; stir well. Add a little cream to moisten more if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30AFE8D1CE6DE94B9C0B6D395FC7C43D', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', 2, NULL, NULL, 2, NULL, 0, 'Fill each mushroom cap with a little of the stuffing, mounding the filling. Top each filled mushroom cap with some of the shredded Mozzarella cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13C2E3474D433C47B313886F3D68EE67', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', X'6FFC2FC387DC9E4B8DE788758F9AE4AB', 3, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for about 20 minutes, until ?the cheese is melted and mushroom caps are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E493695BF2D24D996FA790592D5B9A', X'40D7AD65CBEE4B409AF7D294971C0325', X'40D7AD65CBEE4B409AF7D294971C0325', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
