INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60D2CDC3C2C1894FA1D7FB2BF8EDA576', X'97DF48AAB245E54381AD0F59C10241F6', X'97DF48AAB245E54381AD0F59C10241F6', 2, NULL, NULL, 2, NULL, 0, 'Remove stems from mushrooms; chop stems and set aside.');
