INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA0F4BB9D13CDF41BEDD4A206EB76814', X'97DF48AAB245E54381AD0F59C10241F6', X'97DF48AAB245E54381AD0F59C10241F6', 7, NULL, NULL, 2, NULL, 0, 'Fill mushroom caps with the sausage mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A08B89F9DAEBA4EAB73D2A2DE2A81C8', X'97DF48AAB245E54381AD0F59C10241F6', X'97DF48AAB245E54381AD0F59C10241F6', 8, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 15 minutes.');
