INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F245B941FCA9BD4DB20706331E005A08', X'97DF48AAB245E54381AD0F59C10241F6', X'97DF48AAB245E54381AD0F59C10241F6', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05DEE47E0C199146B5D66C54A750D4D0', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579A4FDA0F84444FA5762BFFFE6C5F2A', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 1, NULL, NULL, 2, NULL, 0, 'Lay a large sheet of heavy duty foil in a roasting pan. Place the pork roast on foil. Rub pork all over with garlic and seasonings. Arrange the medium onion slices over the top. Wrap the foil around the pork roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB95D57AE844649B1054BC7C8FC0567', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 2, NULL, NULL, 2, NULL, 0, 'Bake the pork in the preheated oven for about 4 to 5 hours, or until the pork is very tender and falling apart. When the meat is cool enough to handle, remove the bones and begin to shred the meat with forks or chop, discarding as much of the excess fat as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20F2958BDAA82E4E96F77384AA149084', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 3, NULL, NULL, 2, NULL, 0, 'Sauté the chopped onions in oil until tender. Put the shredded pork in a slow cooker. Blend in the 1 cup barbecue sauce and vinegar, along with the sautéed onion. Heat the pork on LOW setting for about 2 hours, or until hot and flavorful. Or, simmer, covered, over low heat for about 30 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A81246BEE28F42856E99519131AD3B', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 4, NULL, NULL, 2, NULL, 0, 'While the pork is in the slow cooker, make the buns.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97106BF7576EAB4388B48A5D832AECB1', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 5, NULL, NULL, 2, NULL, 0, 'In a bowl or large cup, whisk together the milk and yeast. Let the mixture stand at room temperature until the yeast has dissolved. Place the milk and yeast mixture, melted butter and beaten egg, flour, sugar, and 1 teaspoon of salt in the bread machine in the order suggested by your bread machine manufacturer. Run on dough cycle. Remove finished dough to a lightly floured surface. Roll half of the dough to about 1/4-inch thickness or slightly more. Cut out with a 1 1/2-inch cutter. Or make your sandwiches slider-size using a 2-inch cutter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1412B297D987B04B8E97D6AC23DDE81D', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 6, NULL, NULL, 2, NULL, 0, 'Place the dough rounds on a parchment paper-lined baking sheet. Cover with a kitchen towel and let them rise for about 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FA168D817650E4DBBEB3B59C67FEC63', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 7, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC80A318350C6A429B3693D489589461', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 8, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 18 minutes, or until browned. Brush tops with a little butter while they''re hot. Let the buns cool completely on a rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACDDC72D001CF74E9AA4D987C985D940', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 9, NULL, NULL, 2, NULL, 0, 'Split the cooled buns.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA1F72B69BAD64092CB4A6324CA4400', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 10, NULL, NULL, 2, NULL, 0, 'Fill each small split bun with a little of the shredded pork, then add a small spoonful of slaw. Or serve slaw on the side with a small spoon. Replace the top of the bun and secure with a toothpick, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D103A5FBAB20174E89BC00F65CB9DF82', X'CEBC1A2DDB86F849AFCD00B131410FD8', X'CEBC1A2DDB86F849AFCD00B131410FD8', 11, NULL, NULL, 2, NULL, 0, 'Pass the mini pulled pork sandwiches on a tray with a small bowl of barbecue sauce and more slaw, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4ADF5A0E038454E87F094F05881361B', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
