INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C48291BC75A044CA5A316BFAF7AD656', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 2, NULL, NULL, 2, NULL, 0, 'Cut large scallops in half horizontally, if desired. Set aside.');
