INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46BB0E276742504DA57958CC188550B7', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 4, NULL, NULL, 2, NULL, 0, 'Heat over medium heat until hot and butter is foamy or about 1 1/2 to 2 minutes.');
