INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3B8090B855F40408A579990BC92156A', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 6, NULL, NULL, 2, NULL, 0, 'Cook the scallops in batches, if necessary to keep them in a single layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19732A418B35E249AFF7F4AE88C797A2', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 7, NULL, NULL, 2, NULL, 0, 'Put the scallops on a warm plate and keep warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E74C0EC8DBD39C4690490625CD16CEEB', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 8, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of butter to the pan, along with the finely chopped shallots or onion.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70DADD72BC122A47A0F9E6195E086C6E', X'9F45B591B56DB14D8E4B26DD2789B109', X'9F45B591B56DB14D8E4B26DD2789B109', 9, NULL, NULL, 2, NULL, 0, 'Cook, stirring until the onion is translucent and softened.');
