INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D95B9FCC03827541A4864C3338B5F55A', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 4, NULL, NULL, 2, NULL, 0, 'Roast for 45 minutes, turning a few times so the beef browns evenly.');
