INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B786DD0D7E15A448A18915007C5FAD49', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 7, NULL, NULL, 2, NULL, 0, 'Add 2 cups of water and bring to a simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12AC738192EF6E4CBF3F219C61B64649', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 8, NULL, NULL, 2, NULL, 0, 'Add the tomato paste mixture to the stockpot, along with the remaining 3 quarts of water. If the water doesn''t quite cover the bones, add a little more water.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92D60AAD406A834DA55DB4065D321C06', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 9, NULL, NULL, 2, NULL, 0, 'Add the bay leaf, parsley sprigs, peppercorns, and thyme.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BDB4853302674AA174FC736DA491F0', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 10, NULL, NULL, 2, NULL, 0, 'Put the stockpot over medium-high heat and bring to a boil. Skim off any foamy scum from the top.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FEAC18766B9114E99674278565E613D', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 11, NULL, NULL, 2, NULL, 0, 'Then reduce heat to the lowest setting and simmer for 3 to 4 hours. The stock should be flavorful and reduced slightly. If you want a richer, more concentrated flavor, cook longer to reduce more.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'159D9AAAA3087349AEE4A8F0BF1411D7', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 12, NULL, NULL, 2, NULL, 0, 'Strain through a cheesecloth-lined strainer into a large bowl. Cover and refrigerate until chilled.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7843B45DA0D2B47B9D956991431AF18', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 13, NULL, NULL, 2, NULL, 0, 'Remove the solid fat from the surface.');
