INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CBC1EDBBF643B468AACBE01EC4F2D5F', X'A22A5B9B7819324B90301B1C99EAFDED', X'A22A5B9B7819324B90301B1C99EAFDED', 15, NULL, NULL, 2, NULL, 0, 'Refrigerate and use within 4 days or freeze for up to 3 months.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CC2DC53B27C5D4BB70AAE5DE021E114', X'5671895074EC56439622A707DF864A06', X'5671895074EC56439622A707DF864A06', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a deep, heavy saucepan to about 360 F, or until a cube of bread browns in about 30 seconds.');
