INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CE9651ED3836B4D8E4E245B722885E7', X'5671895074EC56439622A707DF864A06', X'5671895074EC56439622A707DF864A06', 2, NULL, NULL, 2, NULL, 0, 'In a shallow bowl, beat the eggs lightly with 2 tablespoons of milk.');
