INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'603F51E25CEAF742AA0D6BB7E9D9995C', X'5671895074EC56439622A707DF864A06', X'5671895074EC56439622A707DF864A06', 5, NULL, NULL, 2, NULL, 0, 'Remove to paper towels to drain; keep warm while making subsequent batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235E6141E0F5BE4098736FBFA223C651', X'4BF67862D287394E81D164C7B52267AF', X'4BF67862D287394E81D164C7B52267AF', 0, NULL, NULL, 2, NULL, 0, 'Wrap a piece of bacon around each piece of hot dog and secure it with a toothpick.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B04345BE6959FE488634FBE57C796E90', X'4BF67862D287394E81D164C7B52267AF', X'4BF67862D287394E81D164C7B52267AF', 1, NULL, NULL, 2, NULL, 0, 'Put one layer of bacon-wrapped hot dogs in the bottom of your slow cooker insert and add a generous sprinkling of brown sugar. Repeat with the remaining hot dogs and brown sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'233B2004E807F241A92395FAF2BAA25B', X'4BF67862D287394E81D164C7B52267AF', X'4BF67862D287394E81D164C7B52267AF', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 5 to 6 hours or high for 3 to 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F309BF7D5BDE564C8F31CAF4B4B1A438', X'70877E5EEA5B4B4990EC04D9392F3D01', X'70877E5EEA5B4B4990EC04D9392F3D01', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
