INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'945C67AD403BF34A8C4F472C4AB673F2', X'70877E5EEA5B4B4990EC04D9392F3D01', X'70877E5EEA5B4B4990EC04D9392F3D01', 2, NULL, NULL, 2, NULL, 0, 'Spread mixture thinly and evenly over tortillas to within about 1/4-inch of the edges.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF8913DDFD574B4999A595AB67C64DCE', X'70877E5EEA5B4B4990EC04D9392F3D01', X'70877E5EEA5B4B4990EC04D9392F3D01', 3, NULL, NULL, 2, NULL, 0, 'Roll each filled tortilla up jelly-roll fashion, wrap each tortilla in plastic wrap, and refrigerate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355CB2DF87D9334487BF7AD6AFCBA5F7', X'70877E5EEA5B4B4990EC04D9392F3D01', X'70877E5EEA5B4B4990EC04D9392F3D01', 4, NULL, NULL, 2, NULL, 0, 'Slice, serve, and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B6D820A62D87149ADCCE6C2CD2D747C', X'70877E5EEA5B4B4990EC04D9392F3D01', X'70877E5EEA5B4B4990EC04D9392F3D01', 5, NULL, NULL, 2, NULL, 0, 'Serve with your favorite salsa or a few different dips. Ranch dressing, guacamole, and sour cream are all good choices.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'233BE14341BCCB4AAFCA980C48D05EAB', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 0, NULL, NULL, 2, NULL, 0, 'Combine sauce ingredients in slow cooker; cover and cook on high while preparing meatballs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03BEEEAB78C62B4B8B818C0306A12432', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 1, NULL, NULL, 2, NULL, 0, 'Combine meatball ingredients and mix until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283640ECDF9BE540A6FC33FF79E92F24', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 2, NULL, NULL, 2, NULL, 0, 'Shape into 1 to 1 1/2-inch meatballs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7E8A397F1DA324B87FA949A8BCEBBD0', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 3, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat. Add meatballs; brown on all sides. You might have to do this in batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E876C2420CB4469814B6AF51C77A66', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 4, NULL, NULL, 2, NULL, 0, 'Place browned meatballs in a 325 F oven and bake for 30 to 40 minutes, or until cooked through (if the skillet isn''t ovenproof, transfer to a baking dish). Ground beef should be cooked to at least 160 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BE4896DF2E9B4A8707E4096B30B529', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 5, NULL, NULL, 2, NULL, 0, 'Transfer meatballs to slow cooker with a slotted spoon. Cover and reduce to low for 2 to 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'635619F99CB08241BCD9149B6D446C06', X'19B4EBA77256F04D90F8FA0FBA11AC17', X'19B4EBA77256F04D90F8FA0FBA11AC17', 6, NULL, NULL, 2, NULL, 0, 'Serve hot as an appetizer or over rice for a main dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60115C5233AE6849AC78626942E3F22A', X'98F625D91242B744BBE5896793C6CB47', X'98F625D91242B744BBE5896793C6CB47', 0, NULL, NULL, 2, NULL, 0, 'Cut 6 of the hard-cooked eggs in half lengthwise.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D745FB6FB9F8246941542F438E746E5', X'98F625D91242B744BBE5896793C6CB47', X'98F625D91242B744BBE5896793C6CB47', 1, NULL, NULL, 2, NULL, 0, 'Scoop the cooked yolks out of egg halves. Press yolks and remaining whole hard-cooked egg through a sieve into a small bowl. Stir in 2 tablespoons of mayonnaise and the mustard; season to taste with salt and pepper. Add more mayonnaise, as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0833CD3E307CB4782051C346C292647', X'98F625D91242B744BBE5896793C6CB47', X'98F625D91242B744BBE5896793C6CB47', 2, NULL, NULL, 2, NULL, 0, 'Mound the yolk filling in the egg white halves.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7782B4F485B9343AA7B71FC8203DAD2', X'98F625D91242B744BBE5896793C6CB47', X'98F625D91242B744BBE5896793C6CB47', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the tops with a little sweet paprika, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1D0CF3BB083141ADF42AA58203115F', X'98F625D91242B744BBE5896793C6CB47', X'98F625D91242B744BBE5896793C6CB47', 4, NULL, NULL, 2, NULL, 0, 'Garnish with sliced olives, sliced grape or cherry tomatoes, finely chopped the red onion, or snipped fresh chives.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D09232336507841BA926CD6A156BB9C', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 0, NULL, NULL, 2, NULL, 0, 'Rinse and pick over crab meat, discarding any pieces of shell or cartilage. Try to leave lumps as large as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82193892ED90024897757B14C78EDEBA', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 1, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons butter in a large skillet over low heat; slowly saute finely diced onion, celery, red pepper, and garlic until tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3212B591238F4FA6ECEDB0C3E33468', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 2, NULL, NULL, 2, NULL, 0, 'Whisk egg in a bowl; add mayonnaise, Worcestershire sauce, and Creole seasoning.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CB8FA08BBCD348B8AFFB6276E430B1', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 3, NULL, NULL, 2, NULL, 0, 'Combine with sauteed vegetables and soft bread crumbs, mixing well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF30FA3DC1D35A45A45F7ABED0B36E80', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 4, NULL, NULL, 2, NULL, 0, 'Add the crab meat and form into loose patties; place on a waxed paper-lined plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88CADAF162C0A2449125B5BFBB551593', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 1 hour, or until firm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E73F4CE7C538E4B988073CC9DB39547', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 6, NULL, NULL, 2, NULL, 0, 'Heat remaining 4 tablespoons butter in large skillet over medium-low heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18E807F6F06BF14D934FC5D310030548', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 7, NULL, NULL, 2, NULL, 0, 'Gently place crab cakes in skillet and cook for about 5 minutes. Turn and cook for 4 to 5 minutes longer, until cooked through.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'196145813578E842A75E479F99CA84AC', X'A205DF08DF35FD42918E92A0FF51B6A6', X'A205DF08DF35FD42918E92A0FF51B6A6', 8, NULL, NULL, 2, NULL, 0, 'Serve with a remoulade sauce or tartar sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90CC69B7A2E1774EA2CD9AB7CFFBEE18', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 0, NULL, NULL, 2, NULL, 0, 'Put the butter and cheese in the container of a food processor. Add the flour, salt, cayenne, and Worcestershire. Cover and pulse until thoroughly blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D2B6E6484EABF40A665328D315CBA08', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 1, NULL, NULL, 2, NULL, 0, 'If a food processor is not used, put the flour, salt, and cayenne in a bowl. Add the butter, cheese, and Worcestershire, and using two knives or a pastry blender, blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B70A57179C10D45A75820DF4950F7A5', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 2, NULL, NULL, 2, NULL, 0, 'Wrap in waxed paper or plastic wrap and chill for at least 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AF9C084EA6654592CC5D0E6D7178FF', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 300 F. Line a baking sheet with parchment paper or leave it ungreased.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE1D2B96974DF458F78A751FE7BE68B', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 4, NULL, NULL, 2, NULL, 0, 'Roll small amounts of dough into a long tube about the width of a straw and cut into desired lengths. Alternatively, roll dough out on a floured surface to about 1/8-inch to 1/4-inch thickness, cut into strips, and gently twist. If you roll them into straw shapes, use a flat block or flat wide dish bottom to roll them uniformly. Arrange the strips on the baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2816699D9B235A47B665FD1FE57E29DA', X'1A079162AF352D4EBAE46F8FD85DA2FB', X'1A079162AF352D4EBAE46F8FD85DA2FB', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 20 to 25 minutes, or until the straws are crisp and lightly browned. Remove and let cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E7F742628F53D46BCB2EE4CF23E368F', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 0, NULL, NULL, 2, NULL, 0, 'Cut peppers in half lengthwise. Discard pulp and seeds and rinse carefully.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0ACF480D28CB44BB784C19679C9223', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine remaining crab filling ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A382A4208E8B324F962147A30372B5D5', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 2, NULL, NULL, 2, NULL, 0, 'Stuff pepper halves with crab mixture and press stuffing around the pepper. Set peppers aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C8B5B5A314F33409F676968DD18731A', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 3, NULL, NULL, 2, NULL, 0, 'Prepare breading mixture. Place the 2 cups cracker meal in a wide, shallow dish or pie plate. In another bowl mix together milk, eggs and salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99371ABF9BC78344A915F1DF5C41B566', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 4, NULL, NULL, 2, NULL, 0, 'Dip peppers in egg mixture, then roll in the cracker meal to coat thoroughly. Repeat procedure with all peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'645DDADC59679D43963C528D3A22E690', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 5, NULL, NULL, 2, NULL, 0, 'Deep fry at 365 F, until golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44C83A8DC408994FAEB2156AC3335AAF', X'5C5ED1AC5863814F9AF5B02528EA2A76', X'5C5ED1AC5863814F9AF5B02528EA2A76', 6, NULL, NULL, 2, NULL, 0, 'Drain on paper towels or brown paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD9207B40D56A47AEE55E1937120151', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E11A14748DC7F44B0C4AF4658EC0052', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 1, NULL, NULL, 2, NULL, 0, 'Spread lettuce in an even layer in a deep-dish pie plate, gratin dish or serving bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC7D109942950F4E99D0334FD1EA9B7F', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 2, NULL, NULL, 2, NULL, 0, 'Spread refried beans over lettuce. (Hint: To make the beans easier to spread, transfer them to a microwave-safe container, and heat on high for 20 seconds.)');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'492571EAFC050147A563A6B2E118371E', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 3, NULL, NULL, 2, NULL, 0, 'Top with salsa, then guacamole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66F605DAA5D85A4188C4C329A08A2D82', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 4, NULL, NULL, 2, NULL, 0, 'Stir together sour cream and chili powder. Spread over the guacamole layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFA408622B97324D83DD54FF9FFCEAC1', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle cheddar cheese over the sour cream layer. Dot with sliced black olives.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64FC67FDECE8254EA08B6DCCF916A8F7', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', X'1CBB5BF0E1A9004E9BB77AF251FDDF70', 6, NULL, NULL, 2, NULL, 0, 'Serve with tortilla chips.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A519952CA7DC44DBADCE60CC3AFD457', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 250° F (130° C/Gas 1/2). Grease a baking pan or line with foil and grease the foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'364FF4D6F221E1499AAE01A351143776', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the brown sugar, flour, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269C7853A19082478CBEA73049D35EDC', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, beat the egg white until foamy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89708C429751EE46974DC94D4744C781', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', 3, NULL, NULL, 2, NULL, 0, 'Slowly beat the brown sugar and flour mixture into the egg white. Add the vanilla extract and then fold in the pecan halves.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26BAF10B55F15E4191B5F46B8EA07D28', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', X'ECF9C38F8EBB184E97FFD4190F1BC3EA', 4, NULL, NULL, 2, NULL, 0, 'Arrange the pecans in a single layer in the prepared baking pan and bake for about 45 to 55 minutes, stirring about every 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD38C4ADFADEC478DE51BB32D034BE5', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90F46FCDE468E7429F4AAC66915BA6B0', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 1, NULL, NULL, 2, NULL, 0, 'Toast the pecans: Preheat oven to 350 F. Spread chopped pecans out on a large baking sheet. Bake for about 5 minutes, or until the chopped pecans are lightly browned and aromatic.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB9326D5FCCE264793A467E12BF1B078', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, combine the brown sugar, granulated sugar, cream, milk, butter, and salt. Cook over medium heat, stirring constantly, to 230 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA4362617921643B586564A1AE66104', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 3, NULL, NULL, 2, NULL, 0, 'Lower heat slightly if the mixture threatens to boil over. Add the toasted pecans and continue cooking, stirring constantly, to 236 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314C0B5AD555464B87ACF68F39140488', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 4, NULL, NULL, 2, NULL, 0, 'The mixture should form a soft ball when a little is dropped in cold water. Remove from the heat and add vanilla; let stand for about 5 minutes. Stir with a wooden spoon until the mixture is thickened and slightly creamy, about 1 to 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCFEB8AA4A80E4F82CF642246BE55D7', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 5, NULL, NULL, 2, NULL, 0, 'Using a tablespoon or small cookie scoop, spoon the pralines onto a sheet of parchment paper or waxed paper. If the mixture becomes grainy, heat and stir over medium heat for a few seconds, or until it can be easily scooped and dropped.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'041461A758574C49B514E6BA3D8BF158', X'BD693E96CB98444DB801AB7C5144E5AE', X'BD693E96CB98444DB801AB7C5144E5AE', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AA50D136C501A47AC8DE197E9DE468E', X'92029E8FF0B7C34EB189283906DB915D', X'92029E8FF0B7C34EB189283906DB915D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10610F3402CEAE4F82F717B144287FCD', X'92029E8FF0B7C34EB189283906DB915D', X'92029E8FF0B7C34EB189283906DB915D', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
