INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA1564E3F49F3F4496FBEE04E76CF309', X'6C3816477FE7094D9B1FFA3E007BE3CC', X'6C3816477FE7094D9B1FFA3E007BE3CC', 2, NULL, NULL, 2, NULL, 0, 'Remove and mash the yolks; combine mayonnaise, mustard, and relish. Taste and add salt and pepper, as needed. Add more mayonnaise, if desired.');
