INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB7E6F0F3824904E972E02524FFB124C', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
