INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50984B5D9BE9C74FB05D116CA2BFA102', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 2, NULL, NULL, 2, NULL, 0, 'In a large, heavy saucepan or deep skillet over medium heat, melt the butter. Add the corn syrup and brown sugar and cook, stirring constantly, until the mixture boils.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3062CFF3DE86DB4EA3D332B45162F641', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 3, NULL, NULL, 2, NULL, 0, 'Add the pecans to the syrup mixture and cook for about 3 minutes, stirring constantly.');
