INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'445DCC6CDD83284A92E0EC47DC7EA200', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 7 minutes, or until the pecans are browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C69371999B83B9438CF450A3CCD685E3', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 6, NULL, NULL, 2, NULL, 0, 'Cool completely and transfer to a covered container.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1598100FF3149A40B0480D2A09A45DCF', X'7782FBBA651D504C889D54ED636C4FF5', X'7782FBBA651D504C889D54ED636C4FF5', 7, NULL, NULL, 2, NULL, 0, 'Store at room temperature for up to 2 weeks.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D058C1BF0E6F58418F4E916478D60AE6', X'A9150AED4E3ED0418761942EFA502583', X'A9150AED4E3ED0418761942EFA502583', 0, NULL, NULL, 2, NULL, 0, 'In a bowl or food storage bag, toss scallops gently with the Cajun seasoning, 2 teaspoons olive oil, salt, and pepper. Refrigerate for about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EEBA8DE40683247BC4F8017BDD55BC3', X'A9150AED4E3ED0418761942EFA502583', X'A9150AED4E3ED0418761942EFA502583', 1, NULL, NULL, 2, NULL, 0, 'Heat the 1 tablespoon olive oil and 2 teaspoons of butter in a large skillet over high heat. With tongs, arrange the scallops in the hot fat and cook for about 2 to 3 minutes on each side, or until nicely browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E119B3133F443B40A9A776B82D8198C0', X'A9150AED4E3ED0418761942EFA502583', X'A9150AED4E3ED0418761942EFA502583', 2, NULL, NULL, 2, NULL, 0, 'Serve as a first-course salad with Mimosa Dressing or serve with rice and steamed veggies for a delicious meal.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9647C08E3C1DF345A5E979BD88B025A6', X'A4EE602667A39F4F8D75256BDAB531D0', X'A4EE602667A39F4F8D75256BDAB531D0', 0, NULL, NULL, 2, NULL, 0, 'In a large food storage bag, combine buttermilk, 1 teaspoons salt, and 2 teaspoons Creole seasoning. Add chicken; seal and refrigerate for about 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7AFD952F01127429DAFE499B0318E4C', X'A4EE602667A39F4F8D75256BDAB531D0', X'A4EE602667A39F4F8D75256BDAB531D0', 1, NULL, NULL, 2, NULL, 0, 'Combine flour and remaining 1 teaspoon Creole seasoning in a food storage bag. Add the chicken and toss to coat thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B5A37C037046848AFF345AC7DF6135F', X'A4EE602667A39F4F8D75256BDAB531D0', X'A4EE602667A39F4F8D75256BDAB531D0', 2, NULL, NULL, 2, NULL, 0, 'Heat about 1/4 to 1/2-inch oil in a deep heavy skillet to about 350°.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7D48846EA29EF4180BC5FA15A8296F9', X'A4EE602667A39F4F8D75256BDAB531D0', X'A4EE602667A39F4F8D75256BDAB531D0', 3, NULL, NULL, 2, NULL, 0, 'Fry several chicken pieces at a time. Fry, turning several times with tongs, until deep golden brown, about 6 to 8 minutes for each batch. Serve with Come Back Sauce, Jezebel Sauce, or Sweet and Sour Sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B021730173ACB1459389DF9BC9685836', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE6708AC7426784284665827E32E458A', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the paprika, garlic powder, oregano, onion powder, kosher salt, thyme, and the black and cayenne peppers. Stir to combine. Transfer the mixture to a jar with a lid.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3318329CB06C54478FA743ED6EB0333B', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 2, NULL, NULL, 2, NULL, 0, 'In another small bowl, combine the mayonnaise with the finely chopped green onion, Dijon mustard, hot sauce, and dill relish. Add 3/4 teaspoon of the Cajun seasoning (or to taste). Cover and refrigerate the remoulade until serving time.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C517C32A137A5D47AB8D72A65EBE9ABB', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef, minced onion, pressed garlic, and 1 1/2 teaspoons of the Cajun seasoning mixture. Mix to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D4A35B1B23184DA6FD855BFC92600E', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 4, NULL, NULL, 2, NULL, 0, 'With your hands of a burger press, shape the meat mixture into four burgers. Each burger will weigh approximately 6 ounces. With your thumb, make an indentation in the center of each burger. Refrigerate the burgers until it is time to cook. Store the remaining Cajun seasoning in a cool, dark place.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32F2B5AF58D00A448599CFC097F6E3F6', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 5, NULL, NULL, 2, NULL, 0, 'Heat the grill to medium-high heat. When the grill is hot, brush the grate with olive oil or canola oil. Place the burgers on the grill, indentation side up and grill for about 5 minutes. Carefully flip the burgers and continue grilling for about 4 to 5 minutes longer, or until the burgers register 160 F, the minimum safe temperature recommended by the USDA.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C87EDDCC66307F44B53AC0258C8D8544', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 6, NULL, NULL, 2, NULL, 0, 'Transfer the burgers to a plate or rack and tent with foil. Let them rest for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE795CAF06A8F41872A5AC67E89A55A', X'247590C68ABEB144B3CFA445EB9C60E0', X'247590C68ABEB144B3CFA445EB9C60E0', 7, NULL, NULL, 2, NULL, 0, 'Assemble the burgers on buns with your choice of toppings, along with a generous amount of remoulade sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45FB96CE9CA07440AA844B3E549E0042', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56CB04602313334682DDB33EE54ADEDE', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the brown sugar, salt, paprika, chili powder, garlic powder, onion powder, black pepper, and cayenne pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9D957829B2F7F42A6193EF9E2C5AEAD', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 2, NULL, NULL, 2, NULL, 0, 'Pat the chicken with paper towels to dry. Rub the chicken pieces all over with the seasoning mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87DB65B58BC39748ADE1B75E5EEC3968', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 3, NULL, NULL, 2, NULL, 0, 'Place the trivet in the instant pot and add 1 cup of water. Place the seasoned chicken pieces on the trivet.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D620F5CDAAD34183C1D1D30E82C83B', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 4, NULL, NULL, 2, NULL, 0, 'Pour about 1/4 cup of barbecue sauce into a small bowl and brush it over the chicken pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8474BABBA90144AA8507D8068A0FE46', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 5, NULL, NULL, 2, NULL, 0, 'Secure the Instant Pot lid and turn the steam release valve to the sealing position. Choose the pressure cook function (or manual on early models), high pressure, and set the time for 18 minutes. When the time is up, let the steam release naturally for 10 minutes. Carefully turn the steam release valve to the venting position to release any remaining steam.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95DE418458F14A46A6B9454B41945921', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven broiler. Arrange the chicken on a foil-lined baking pan and brush the pieces with more barbecue sauce. Broil the chicken for about 2 to 4 minutes, or until slightly charred. Broiling gives the chicken extra color, but feel free to skip this step.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34387CE2BA91DD479A78692686A0EAA9', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 7, NULL, NULL, 2, NULL, 0, 'Serve the chicken with potatoes or rice and offer extra barbecue sauce at the table.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'397595B796456D47A8B67025DFC90722', X'098D41A53013F14DA58213BDEF24C671', X'098D41A53013F14DA58213BDEF24C671', 8, NULL, NULL, 2, NULL, 0, 'Enjoy your meal!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8406E9E086817443B29DBEC8D5CBFC58', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8170274E5ECE3741A7B5F85040753FB0', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 1, NULL, NULL, 2, NULL, 0, 'Put the bacon in a large skillet and place the skillet over medium heat. Cook until the bacon has rendered its fat. With a slotted spoon, remove the bacon pieces to paper towels to drain. You should have about 1/4 to 1/3 cup of drippings left in the pan. Refrigerate the crumbled bacon to use as a garnish or save it for another use.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561D327ADE53C84F98B6A12312960124', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 2, NULL, NULL, 2, NULL, 0, 'Remove 2 tablespoons of oil from the 3/4 cup and set it aside. Add the remaining oilabout 2/3 cupto the bacon drippings. Add the flour to the bacon drippings and oil mixture and cook over medium heat, constantly stirring, for about 25 to 30 minutes, or until the roux is the color of milk chocolate. Remove the roux from the heat and set it aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D0BC2E629C0F74CA5506E1466CC5466', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 3, NULL, NULL, 2, NULL, 0, 'Heat the remaining 2 tablespoons of oil in a large Dutch oven over medium heat. Add the smoked sausage and cook for about 5 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1C509A0D3CBA43A941876D3AFEFBF3', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 4, NULL, NULL, 2, NULL, 0, 'Add the onions, celery, green and red bell pepper, and the white and light green parts of the green onions to the sausage; continue to cook for about 4 minutes, or until the vegetables are soft. Add the garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D433D963288401468115B70118FE7A1D', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 5, NULL, NULL, 2, NULL, 0, 'To the vegetables and sausage, add the reserved roux; stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50F9998E010B6748865C60BDEC44C704', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 6, NULL, NULL, 2, NULL, 0, 'Gradually add 2 cups the chicken stock to the pot a little at a time, stirring constantly. Add the chopped turkey, Worcestershire sauce, bay leaves, salt, Cajun seasoning, thyme, and black pepper. Bring to a boil and then reduce the heat to low and simmer for 1 1/2 hours, stirring occasionally. If too thick, add more stock in small amounts.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21EFEEDB406FEB49AF9220D000ACB56F', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 7, NULL, NULL, 2, NULL, 0, 'Stir the parsley and chopped green onions (dark green parts) into the gumbo and serve it over hot steamed rice.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0524025A6F609D428C2D20B1380AC538', X'79921BB7285EA141987BFFE3C34BCCBF', X'79921BB7285EA141987BFFE3C34BCCBF', 8, NULL, NULL, 2, NULL, 0, 'Garnish the turkey gumbo with extra green onions, parsley, and the crumbled cooked bacon, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DE934461528242A9DD6BFA1AD02C08', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE21254A4F1CEA418C22CF29F13ED9EB', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200 F. Line a rimmed baking sheet with foil and place a rack in the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79E63B76238B854B9A93ACB64151E6A1', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 2, NULL, NULL, 2, NULL, 0, 'Arrange the catfish in a wide, shallow bowl or pie plate; pour the buttermilk over the fish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F14E850FB70845A3A4AF9730CC444A', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 3, NULL, NULL, 2, NULL, 0, 'Pour about 1 inch of oil in a deep, heavy skillet or heavy Dutch oven and place the pan over medium-high heat. (If you will be frying hushpuppies in the same pan, use a Dutch oven and 1 1/2 inches of oil.)');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66AB97AB5631234FB681385DCC426701', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine the cornmeal, flour, salt, paprika, pepper, cayenne, and garlic powder on a pie plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8363B76385F8B4FBF703CEF403C4007', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 5, NULL, NULL, 2, NULL, 0, 'Take the fish out of the buttermilk and dredge the fish fillets in the flour mixture to coat thoroughly. Shake off excess flour mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BE98FE1821B8048876545E7C3CBF875', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 6, NULL, NULL, 2, NULL, 0, 'You are ready to fry when the oil reaches about 350 F; you can use a candy thermometer or drop a pinch of the flour mixture into the oilif it bubbles and floats the oil is ready. Just be mindful of maintaining the oil temperature while cooking the fish in batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A38E97B2DBF946A9788C6E231864F9', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 7, NULL, NULL, 2, NULL, 0, 'Carefully arrange 2 to 3 fillets in the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'664401E75F73924CAA8F53531783A0B3', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 8, NULL, NULL, 2, NULL, 0, 'Cook for about 5 to 6 minutes, or until golden brown. If the oil isn''t deep enough to cover the fish, turn the fillets carefully after about 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87253F7DD8E83449827AD6EC90F16625', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 9, NULL, NULL, 2, NULL, 0, 'Remove the fish to the rack in the baking pan and place in the oven while you cook another batch. Repeat until all of the fish are cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875A40E90C8A9E499DCE8BC8C8D3CBE9', X'5FCCDF7901F6EB4B99F6F39E52744A40', X'5FCCDF7901F6EB4B99F6F39E52744A40', 10, NULL, NULL, 2, NULL, 0, 'If making hush puppies, bring the oil up to 370 F and fry them in the same pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'788A914C369BC54ABB62D4F1A2D82380', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6832EAC9963AEC41A9A8F89E04890C93', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 1, NULL, NULL, 2, NULL, 0, 'Start by making the rub. Place all the rub ingredients in a food processor and blitz to create a dark, lump-free powder. Pour into a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'417F65285DCBB34EBFDD937D59B9752C', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 2, NULL, NULL, 2, NULL, 0, 'Dry the chicken breasts with kitchen paper. Place in a large dish, pour enough spice rub over the breasts to coat both sides of the chicken breasts. Cover the dish with a kitchen towel and set aside for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F07CBF2761AE24492CB5068A3C12FFC', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55E5A8BD639B724E9F0A20F280809EB8', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a non-stick ovenproof pan to hot, but not smoking. Carefully remove the chicken breasts one at a time from the dish. Shake gently to remove any loose rub, then place in the pan to sear. Cook for 3 minutes on each side, then place the pan in the center of the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E57238834A4E3744B49FE56EEA8CBAE4', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 5, NULL, NULL, 2, NULL, 0, 'Bake the chicken for 15 minutes or until cooked by checking using a meat thermometer reading 165 F or press the breast firmly and it should feel solid to the touch.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99110608315D624D8360B9AA2DA43055', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 6, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the oven, cover with a tea cloth and leave to rest for 10 minutes, which will soften the chicken and make it more tender. Slice the chicken into even slices and serve over hot white rice (or pasta if you prefer) sprinkled with chili flakes and a little lime zest.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E8A122FEEFA4847BF22406AE6B1AC3A', X'CEE9C76516AC074C99E8485C31EC1196', X'CEE9C76516AC074C99E8485C31EC1196', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6CB19CD18D96848A47F2FF047A23DF1', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 0, NULL, NULL, 2, NULL, 0, 'First, prepare the pulled pork filling. Place half a sliced onion in the bottom of your slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E5087BC6F2C6841847237D3230D38A1', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 1, NULL, NULL, 2, NULL, 0, 'Stud the pork roast with cloves and season with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'901B81627465B049A54856637A566799', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 2, NULL, NULL, 2, NULL, 0, 'Place roast in slow cooker on top of the sliced onion. Add the remaining sliced onion and add enough water to cover.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D02499D843549E4EB9C9B610DC7070A4', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on low 8 to 12 hours, or until the pork is very tender and falling apart.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C52774431CAE6E4A9D1D906CC48AF355', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 4, NULL, NULL, 2, NULL, 0, 'Remove roast. Remove and discard cloves, bone and fat, as well as any water, onions and grease remaining in pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D93C87A1F96D42B639616C635C2216', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 5, NULL, NULL, 2, NULL, 0, 'When pork roast is cool enough to handle, use a fork or your fingers to pull it apart until the entire roast is shredded.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D884A752D111C5479FC65F588509AD53', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 6, NULL, NULL, 2, NULL, 0, 'Return the pulled pork to the slow cooker. Add the chopped onion and BBQ sauce and cover. Heat on high for 1 to 3 hours or until the onions are soft and the mixture is very tender');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C7FC616C7DA84479EE2C81DF3841ABD', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 7, NULL, NULL, 2, NULL, 0, 'When the pulled pork is almost ready, prepare the cornbread taco shells. In a bowl, combine cornmeal, all-purpose flour and salt. Then add milk and the egg, and stir until mixture is nicely incorporated. Dont overwork the battersome lumps are okay. You want a pretty thick consistency, but still a little runny. If the mixture seems too dry, add a little more milk; if it seems too wet, add a touch more cornmeal.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA68B0BD539B134E929EE7C6CBA19669', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 8, NULL, NULL, 2, NULL, 0, 'Grease a small skillet with oil and place over medium heat. When the pan is very hot, spoon a small amount of batter into the skillet. Cook the cornbread taco shell 2 to 3 minutes on the first side, then flip, and cook 1 to 2 minutes more on the second side. The taco shells should be soft and flexible. Repeat with remaining batter. Depending on the size of your tacos, recipe should yield 4 to 8 taco shells.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA3AADE81CAB4246A8CB29E1CC96C82D', X'B8D6D35F75E1914192DB8921BCBDFEC3', X'B8D6D35F75E1914192DB8921BCBDFEC3', 9, NULL, NULL, 2, NULL, 0, 'To assemble tacos, fill taco shells with the pulled pork, coleslaw, jalapeños and drizzle with lime juice. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F517B9D265A51B4689A8A9A5B5A7322A', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CCF89417DB21041A76393E07C8B45F4', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 1, NULL, NULL, 2, NULL, 0, 'Dry the chicken pieces with a paper towel and arrange them in pan or baking dish. Rub the chicken with 1 tablespoon of kosher salt and 1 teaspoon of black pepper. Cover the pan with plastic wrap and refrigerate for 4 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC31022238CE934B84DF824747472470', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 2, NULL, NULL, 2, NULL, 0, 'In a deep, heavy skillet or saucepan, heat about 2 inches of oil to 325 F. Place a cooling rack in a rimmed baking pan. Preheat the oven to 200 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064C94439D62534AB5316696F68EEEA7', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the milk, eggs, and hot sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00FEA4C754DCE742A1CF4AE3E541A535', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 4, NULL, NULL, 2, NULL, 0, 'Pour the milk and egg mixture into a wide, shallow bowl. Put the flour in a separate wide, shallow bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3100608D66EE448B6765054537891D0', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 5, NULL, NULL, 2, NULL, 0, 'Dip a chicken piece in the flour and turn until it is thoroughly coated.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9937CE8143BCE943AD16F4BF7CAA8AD8', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 6, NULL, NULL, 2, NULL, 0, 'Dip the flour-coated chicken in the milk and egg mixture and turn to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F17747D15670459FF8C4CDA7F28F77', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 7, NULL, NULL, 2, NULL, 0, 'Dip the chicken piece in the flour once again, turning to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0562019E59EECE409E362FCABFBE4ED6', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 8, NULL, NULL, 2, NULL, 0, 'Repeat with the remaining chicken pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'851B8D1A4BA4C741A9CC80FAFD355E83', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 9, NULL, NULL, 2, NULL, 0, 'Fry the chicken in small batches in the hot oil for 16 to 19 minutes, or until the pieces reach about 165 F at their centers on an instant-read thermometer. Chicken breast pieces will take 15 to 17 minutes. Remove the chicken pieces to the prepared pan with the cooling rack and place the pan in the oven. Repeat with the remaining chicken pieces. Set the cooking oil aside to cool slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21DCF9B71820BA418629007995862B02', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 10, NULL, NULL, 2, NULL, 0, 'In a heatproof bowl, combine the cayenne pepper, brown sugar, paprika, 1 teaspoon of kosher salt, 1/2 teaspoon of black pepper, and the garlic powder. Add about 1/2 cup of the hot cooking oil to the spices and stir to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66644E6743B2174BB6CCC004FE0D89E3', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 11, NULL, NULL, 2, NULL, 0, 'Brush the hot oil over the chicken pieces. Stir the oil frequently as you brush.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D5EC6F8AD71854889D571CB06E49BE4', X'595EE1DBEBCA5641B767CF9D7899B8F7', X'595EE1DBEBCA5641B767CF9D7899B8F7', 12, NULL, NULL, 2, NULL, 0, 'Serve with white bread and pickles. Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72731B414DD1CB4F8A220CF3F832F650', X'8D993D8B72D4DD4EAD77DFC0C6968866', X'8D993D8B72D4DD4EAD77DFC0C6968866', 0, NULL, NULL, 2, NULL, 0, 'Heat oil to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'634F50A0DF71E747A1C0AC8A3B68B904', X'8D993D8B72D4DD4EAD77DFC0C6968866', X'8D993D8B72D4DD4EAD77DFC0C6968866', 1, NULL, NULL, 2, NULL, 0, 'Beat egg in a bowl with milk. In another bowl, mix flour, cornstarch, salt, pepper and cayenne in a bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'271C2D61E614A5448241269E1645C922', X'8D993D8B72D4DD4EAD77DFC0C6968866', X'8D993D8B72D4DD4EAD77DFC0C6968866', 2, NULL, NULL, 2, NULL, 0, 'Dunk chicken in liquid mixture, then flour mixture, then add to the hot oil and fry until golden brown and chicken cooked through. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06850019F0EEA34BB909ED2F19D879F8', X'8D993D8B72D4DD4EAD77DFC0C6968866', X'8D993D8B72D4DD4EAD77DFC0C6968866', 3, NULL, NULL, 2, NULL, 0, 'Slice donuts in half, spread butter on insides and place cut side down on griddle until toasty.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'808A23A6A32BBC4D97314817EBF580C3', X'8D993D8B72D4DD4EAD77DFC0C6968866', X'8D993D8B72D4DD4EAD77DFC0C6968866', 4, NULL, NULL, 2, NULL, 0, 'To assemble the sandwich, first put donut halves, grilled sides on the outside. On one piece, top with 2 pieces of fried chicken, a few dashes of hot sauce, add several slices of cheese, then top with the other donut half to close the sandwich.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3397A2D4E9EAC34F85FDF78BD112FC84', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72F41E4BA50D1B47B66E071DF05DED67', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 1, NULL, NULL, 2, NULL, 0, 'Dry the pork with paper towels. In a large bowl, combine the brown sugar, smoked paprika, kosher salt, garlic powder, chili powder, black pepper, onion powder, cumin, dry mustard, and cayenne. Add the pork chunks to the bowl and toss to coat them thoroughly with the spice mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8459D3AF8421254B8DD1A8C106F540E5', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 2, NULL, NULL, 2, NULL, 0, 'Place the trivet in the Instant Pot and add the water (or other liquid) and vinegar. Place the seasoned pork on the trivet.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE96CB1E8FAF664EB45F8488674BFE1B', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 3, NULL, NULL, 2, NULL, 0, 'Secure the lid on the Instant Pot and turn the steam release valve to the sealing position. Select the pressure cook or manual button (high pressure) and set the timer to 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'077659A1D4261843AA8DA6EB555A0989', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 4, NULL, NULL, 2, NULL, 0, 'When the time is up, allow a natural release for 15 to 20 minutes. Carefully turn the steam release valve to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A565D4AD78544D4EA208E8879585134A', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 5, NULL, NULL, 2, NULL, 0, 'Drain and discard the liquids. Remove the pork to a bowl and shred it. Add 1 1/2 cups of barbecue sauce to the pork and stir to blend. Return the pork to the Instant Pot and select the lowest sauté setting. Cook the pork until it is thoroughly heated.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17ACF9EFFD7C9F45985E4ED5C9FD779D', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 6, NULL, NULL, 2, NULL, 0, 'Serve the pulled pork in split buns or as an entrée with mac and cheese, buttermilk biscuits, and coleslaw.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DA39C413C33064EA477C938643994C5', X'8E5D7CD86FC20948BEA2478A7972B981', X'8E5D7CD86FC20948BEA2478A7972B981', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE7176F36727754DA5D6B0FF5AB1BB07', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54D261E2F32B64E8BB66620181E02C4', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 1, NULL, NULL, 2, NULL, 0, 'Cut the tops off of the bell peppers and rinse them under cold water; remove seeds and cut away the white ribs, which might be bitter. Chop the edible part of tops and set aside. Place the peppers in a large pot and cover with salted water.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'622F932761714A48A8BA774F5917A5EE', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil. Reduce heat, cover, and simmer for 5 minutes. Drain and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7491D1C40E42B44BBF705DD4F37416FA', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 3, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter in a large skillet over medium heat until oil is hot and the butter is foamy. Sauté the chopped bell pepper (from the tops), chopped onion, and chopped celery for about 5 minutes, or until vegetables are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'339E17622A5C934A8E674DC748CD7F72', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 4, NULL, NULL, 2, NULL, 0, 'Add the (undrained) canned diced tomatoes, tomato sauce, crushed garlic, oregano, basil, 1 teaspoon salt, and 1/4 teaspoon pepper. Bring to a simmer and cook for about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3188E5988EA1B949BDF7CD8F70872254', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 5, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the egg with the remaining 1 teaspoon of salt, 1/4 teaspoon pepper, and Worcestershire sauce. Gently stir to blend; add ground beef, cooked rice, and 1 cup of the tomato sauce mixture. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77F34CEF92321D449F29682622782C26', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 6, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDCE25D799342C4DA5796B6AAE7285E6', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 7, NULL, NULL, 2, NULL, 0, 'Stuff the peppers loosely with the ground beef mixture and place them in a 13 x 9 x 2-inch baking pan. Pour the remaining tomato mixture over the stuffed peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39D7E57AAEA9774993AD6BEBB59946D0', X'3B37733CF4251041903C9845D5086FBB', X'3B37733CF4251041903C9845D5086FBB', 8, NULL, NULL, 2, NULL, 0, 'Bake the peppers for about 45 minutes, or until the meat mixture is thoroughly cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1629C59A6CD974F8441B136D088E3F5', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78076E0F9853874AACDE3C25E96CBEE8', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 1, NULL, NULL, 2, NULL, 0, 'In a skillet over medium heat, brown the sliced sausage in 2 to 3 teaspoons of vegetable oil; remove the sausage to a plate and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EFADD07AF713D4FB9C8E6C1798C79D3', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 2, NULL, NULL, 2, NULL, 0, 'In a large stockpot or Dutch oven, combine the chicken broth and tomatoes. Place the pot over medium heat to bring to a simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749810BEC5CD0F4D95F214E56FB963A7', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large skillet over medium-high heat, combine the 1/2 cup of oil and the flour. Cook, whisking constantly until the roux is medium to deep brown. Don''t let the mixture burn, or you will have to start over with fresh oil and flour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D1252ADB5A6E4991383CC272D295F4', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 4, NULL, NULL, 2, NULL, 0, 'To the skillet with the roux, add the chopped onion, celery, and bell pepper; cook, stirring constantly with a spoon until vegetables are softened. Stir the vegetable and roux mixture into the simmering tomato and broth mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C22E69875A1204BB43CC43CB5562657', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 5, NULL, NULL, 2, NULL, 0, 'Put the skillet back on the heat and add 2 more teaspoons of oil; reduce to medium heat. Add the okra and cook, stirring, until lightly browned and not stringy or sticky.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9297E957C8AB5844973AE84D407E18D7', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 6, NULL, NULL, 2, NULL, 0, 'Transfer the cooked okra to the simmering pot along with the Creole or Cajun seasoning, the bay leaf, and cooked sausage. Cover and cook over medium-low heat, stirring occasionally, for 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'503F5BC7AF07FE438CBC66FE8AEA0EAE', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 7, NULL, NULL, 2, NULL, 0, 'Add the prepared shrimp and cook for about 10 minutes longer, until the shrimp is cooked through.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A2616D27C11E9499F84033D7EDDB646', X'61C43660E6D8144981878E3E2723BD8A', X'61C43660E6D8144981878E3E2723BD8A', 8, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper, as needed.');
