INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AFECE91BA147C4C8FB479FDBC49523B', X'5A3AB7906D8F024BAB630C5D5C2A826C', X'5A3AB7906D8F024BAB630C5D5C2A826C', 3, NULL, NULL, 2, NULL, 0, 'Spoon the macaroni and pimiento cheese mixture into the prepared baking dish.');
