INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F674A6936B58594A972F9CF6FBBA4A81', X'E7789BA2A097944FB0B8B1E520CBA579', X'E7789BA2A097944FB0B8B1E520CBA579', 7, NULL, NULL, 2, NULL, 0, 'Return the chopped ham to the pot. Add the onions, bay leaf, celery, and optional carrots to the pot, as well as the extra leftover chopped ham, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4206010CECEA944AA4689EAC73BC4AE', X'E7789BA2A097944FB0B8B1E520CBA579', X'E7789BA2A097944FB0B8B1E520CBA579', 8, NULL, NULL, 2, NULL, 0, 'Add enough water or unsalted vegetable stock to make about 16 cups of liquid. Taste the broth and add salt and pepper, as needed.');
