INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73AFC05464AA14AA6EF4270B37F714D', X'C61022FBFAEA9F49852D08C749A34C69', X'C61022FBFAEA9F49852D08C749A34C69', 3, NULL, NULL, 2, NULL, 0, 'Stuff the peppers with equal portions of the ground beef and rice mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E975135AD400D45A8F6090AAAEDD102', X'C61022FBFAEA9F49852D08C749A34C69', X'C61022FBFAEA9F49852D08C749A34C69', 4, NULL, NULL, 2, NULL, 0, 'Place the stuffed peppers in a crock pot; cover with the spaghetti sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B99DA0A54425DE44AF0923CE4246C2BD', X'C61022FBFAEA9F49852D08C749A34C69', X'C61022FBFAEA9F49852D08C749A34C69', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 6 to 7 hours or on HIGH for 3 to 4 hours. Or until the ground beef is thoroughly cooked and the peppers are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E0268001F15449B9B2899BE64730A3', X'C61022FBFAEA9F49852D08C749A34C69', X'C61022FBFAEA9F49852D08C749A34C69', 6, NULL, NULL, 2, NULL, 0, 'To check for doneness, insert an instant-read food thermometer into the center of the filling. It should read at least 160 F / 71 C or 165 F / 74 C for ground turkey.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284E32480C2A2E478C7234DC2549C12D', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA4239247D6384EAB48F4671272D677', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 1, NULL, NULL, 2, NULL, 0, 'Turn the salmon and liquid into a medium-size bowl. Flake the salmon with a fork, removing any skin and bones. Smaller bones are edible and may be mashed. Add the grated onion, parsley, and pepper; blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5B2FBBCBEA194F8C91F25376C6075A', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 2, NULL, NULL, 2, NULL, 0, 'Add the beaten eggs and blend well. Add enough breadcrumbs, about 1/2 to 3/4 cup, to make the mixture thick enough to shape into about 12 small patties.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA5CF5C3A53C1F469B2C8599B4869F4E', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 3, NULL, NULL, 2, NULL, 0, 'Roll the patties in the remaining breadcrumbs to coat thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C204BCD70AB4B46A16C1CE2DC73FBF3', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 4, NULL, NULL, 2, NULL, 0, 'In a large heavy skillet or sauté pan over low heat, melt 2 tablespoons of butter. When the butter is hot, add the salmon patties. Fry patties slowly on one side until browned. Add the remaining butter, turn the patties, and fry them until browned on the other side.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40591396EF664D44859080021E856F10', X'D8F57EB74C651146B85D1B8669B7B807', X'D8F57EB74C651146B85D1B8669B7B807', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
