INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9361A2823E1274288BDC9565667CF4D', X'CECCC3350404734CB58D3BCE41608492', X'CECCC3350404734CB58D3BCE41608492', 4, NULL, NULL, 2, NULL, 0, 'Cover the bottom of a 6- to 7-quart slow cooker insert with the potatoes, onion, and carrots. Drizzle the vegetables with the olive oil and add the parsley, rosemary, oregano or basil, dill, salt, and pepper. Toss to thoroughly coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E28C80E50FF5D4185B363C275EB3D6B', X'CECCC3350404734CB58D3BCE41608492', X'CECCC3350404734CB58D3BCE41608492', 5, NULL, NULL, 2, NULL, 0, 'Position the tri-tip roast in the center on top of the vegetables. Add the beef stock.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93FEAE1C02950C41BF4945E4A811DDE5', X'CECCC3350404734CB58D3BCE41608492', X'CECCC3350404734CB58D3BCE41608492', 6, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 6 to 7 hours, or on high for about 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'009279BDC318014E9DDFA3CF1873BFB8', X'CECCC3350404734CB58D3BCE41608492', X'CECCC3350404734CB58D3BCE41608492', 7, NULL, NULL, 2, NULL, 0, 'Move the meat to a cutting board and slice it thinly against the grain. Serve the meat with the vegetables and any of the braising liquids that accumulated in the slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC5B82454D1FF745A88BF36E1760A82A', X'CECCC3350404734CB58D3BCE41608492', X'CECCC3350404734CB58D3BCE41608492', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
