INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589059FC3F12D941AA385DC7F069129F', X'C3C156260F3969409FECA07B320A4715', X'C3C156260F3969409FECA07B320A4715', 3, NULL, NULL, 2, NULL, 0, 'Repeat with the remaining shrimp. Pat them dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F131C4BAC99A21418C731F39233D9579', X'C3C156260F3969409FECA07B320A4715', X'C3C156260F3969409FECA07B320A4715', 4, NULL, NULL, 2, NULL, 0, 'Slice the green onions, keeping the white and light green parts separate from the dark green. Set the dark green sliced green onions aside for garnish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F959AEA53F6F0E408C1AA544BF495F02', X'C3C156260F3969409FECA07B320A4715', X'C3C156260F3969409FECA07B320A4715', 5, NULL, NULL, 2, NULL, 0, 'Heat the butter in large skillet over medium heat. Cook the garlic for 1 or 2 minutes or until softened, but not browned.');
