INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'124E50B74F7E7D4380CDFF8A26EF4B44', X'7AAEF42B214B72429C4FE34308C4BE81', X'7AAEF42B214B72429C4FE34308C4BE81', 3, NULL, NULL, 2, NULL, 0, 'Scatter the sliced onions in the bottom of the slow cooker, then place ribs on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43AC13C1FF42043931D891F91255E63', X'7AAEF42B214B72429C4FE34308C4BE81', X'7AAEF42B214B72429C4FE34308C4BE81', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the pork with salt and pepper, brown sugar, and minced garlic; turn the pork ribs to coat all pieces. Pour apple juice evenly over the pork. Cover and cook on LOW for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E61B1A92B19A3745B76C820B3A938175', X'7AAEF42B214B72429C4FE34308C4BE81', X'7AAEF42B214B72429C4FE34308C4BE81', 5, NULL, NULL, 2, NULL, 0, 'Drain liquids from the pork and discard. Pour barbecue sauce over the pork and stir slightly to distribute the sauce. Cover and cook on LOW for 1 hour longer. Serve and Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56F9CA9D338A7A479A001B265E51DC28', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99F5711D80196E46B9DD1D5818199D2B', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 1, NULL, NULL, 2, NULL, 0, 'Line a roasting pan with foil and place a rack on it.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F6B512C0143C14599654ACAFC55EC20', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C40ED099150C3245B80356BD9005A52F', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 3, NULL, NULL, 2, NULL, 0, 'Place the ham, fat side up, on a rack in the prepared roasting pan. Score the fat all over in a diamond pattern, cutting to a depth of about 3/4 of an inch. If desired, stud with whole cloves, inserting one clove into each intersection of the diamond pattern.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5C6968A34D592439A6BFEF79096128D', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 4, NULL, NULL, 2, NULL, 0, 'Bake the ham in the preheated oven for about 18 to 20 minutes per pound. For a 6-pound ham, the cooking time is around 1 hour and 40 minutes and up to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24642FDBF2086742B46E729744940B81', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 5, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, combine the maple syrup, brown sugar, apple juice, mustard, and ground spice (if using). Place the saucepan over medium-high heat and bring the mixture to a boil. Boil the mixture for about 2 minutes, but be careful not to let it boil over.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D448C4B6BB2F41B24A45D0B37087BD', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 6, NULL, NULL, 2, NULL, 0, 'About 20 minutes before the ham is done, spoon about half of the glaze over the ham, and return it to the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEFDF303E56C7F4BA3A1C7E2CE846B96', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 7, NULL, NULL, 2, NULL, 0, 'After 10 minutes, brush the remaining glaze mixture over the ham.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'267F64613A13A844AD26221D47ECE21D', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 8, NULL, NULL, 2, NULL, 0, 'Remove the ham from the oven when a thermometer or instant-read thermometer registers at least 140 F in the thickest of the meat away from fat or bone (140 F is the optimal temperature for a fully cooked ham).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97FA95A5450DED4988A6842833CFB0AC', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 9, NULL, NULL, 2, NULL, 0, 'Cover the ham loosely with foil and let it stand for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02B032949B71644DB54F2BF0DA698929', X'351B8E9EFE52C64EB07FACF2A5BB560F', X'351B8E9EFE52C64EB07FACF2A5BB560F', 10, NULL, NULL, 2, NULL, 0, 'Carve, serve, and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF1C2FC2386F4546B96B75DDF8EDB2C0', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F89B6FB954E8048AD7E119A37EF7267', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 1, NULL, NULL, 2, NULL, 0, 'If scallops are quite large, cut them so the sizes are fairly uniform.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C1D45A691371428E22BB28D7A54F8D', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 2, NULL, NULL, 2, NULL, 0, 'Rinse the scallops under cold water and pat them dry with paper towels. Remove the tough white side muscle if not already removed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70B1E715226B9C429736DA28B77CEFDE', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 3, NULL, NULL, 2, NULL, 0, 'Heat about 2 to 3 inches of vegetable oil in a deep, heavy cast iron skillet, saucepan, or deep fryer to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05AAE6D0E38E6A4393B46F9EAC1C40EB', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 4, NULL, NULL, 2, NULL, 0, 'Line a baking pan or baking sheet with two or three thicknesses of paper towels and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF87751EA1662C40BA7FDDAE643CA7B3', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 5, NULL, NULL, 2, NULL, 0, 'In a bowl, combine flour, salt, paprika, and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC0FCA28F579CB4C966445691DA87A3A', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 6, NULL, NULL, 2, NULL, 0, 'In another bowl, whisk eggs with the milk.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98925B498B71F24694A60F6357654FB7', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 7, NULL, NULL, 2, NULL, 0, 'Put the fine breadcrumbs in a third bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886363FA2FB8644AADA11AF7010FC82F', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 8, NULL, NULL, 2, NULL, 0, 'Dip a few of the scallops in the seasoned flour mixture, then in the egg mixture, and then roll them in the breadcrumbs to coat. Repeat with the remaining scallops.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2509F2289F15AA49814CC6FEE00DEF8B', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 9, NULL, NULL, 2, NULL, 0, 'Once the oil has reached the temperature (350 F), add several scallops?about 6 to 10 depending on the pan size?to the hot oil and fry until golden brown, about 1 1/2 to 2 minutes. Take care not to overcook. They should be warm in the middle, but still moist.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBEDCD975978940B79309F0E2314286', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 10, NULL, NULL, 2, NULL, 0, 'Remove them from the oil with a metal slotted spoon and transfer them to the paper-towel-lined pan. Shake the pan gently for a few seconds so the scallops roll around and excess oil is absorbed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A07C3AC4D8DD45B34BE8FC0B2DB0FA', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 11, NULL, NULL, 2, NULL, 0, 'Repeat with subsequent batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BC9A8C23BC0E74BB63181562AB7CCDF', X'9711632436851B45B70AB26566D89524', X'9711632436851B45B70AB26566D89524', 12, NULL, NULL, 2, NULL, 0, 'Serve immediately with your choice of cocktail sauce, remoulade sauce, or tartar sauce. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C5A5C8E76BFF340A95AB4A7BC87933E', X'2A7F3FE7E240C642940966324D2C2D67', X'2A7F3FE7E240C642940966324D2C2D67', 0, NULL, NULL, 2, NULL, 0, 'In a slow cooker, layer the ingredients in the order given; add the water or stock. Do not stir the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C28F9BA881CA643B94F88BB3480A54A', X'2A7F3FE7E240C642940966324D2C2D67', X'2A7F3FE7E240C642940966324D2C2D67', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 4 to 5 hours or on low for 8 to 10 hours until peas are very soft and the ham falls off the bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C99C5F62F422F4692C2FCF90641B277', X'2A7F3FE7E240C642940966324D2C2D67', X'2A7F3FE7E240C642940966324D2C2D67', 2, NULL, NULL, 2, NULL, 0, 'Remove the meat from the bones and chop. Save some of the chopped ham for a garnish, if desired. Remove the bay leaf.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45E2BECD7EEEEC4CA07D5F9B41B04814', X'2A7F3FE7E240C642940966324D2C2D67', X'2A7F3FE7E240C642940966324D2C2D67', 3, NULL, NULL, 2, NULL, 0, 'Before serving, mash the peas slightly to thicken the soup or blend the soup in batches for a smoother texture (safety tips below).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92F7E61A69CE8943808618594F5A8698', X'2A7F3FE7E240C642940966324D2C2D67', X'2A7F3FE7E240C642940966324D2C2D67', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate or freeze any leftovers.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F6DDAC33110348821F1D3EA11D1102', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAA0BC7FB72E540B7F44D0C5F0C68B5', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the brown sugar and mustard. Stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFB1F642FE8E624494DFEF0B76BAF0B3', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 2, NULL, NULL, 2, NULL, 0, 'Moisten the mixture with just enough cola to make a smooth paste. Reserve the remaining cola and set the mustard-sugar paste aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC48657642AA034A84428DAEA376D407', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 3, NULL, NULL, 2, NULL, 0, 'With the help of a sharp knife, score the ham with shallow slashes in a diamond pattern.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFBB7CEA83E93E40A13E7D819C1123C9', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 4, NULL, NULL, 2, NULL, 0, 'Rub the ham with the mustard-sugar paste mixture. If desired, insert a whole clove in each intersection of the diamond pattern.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDE24F7D63EFB6499D55E18F4CE80C3A', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 5, NULL, NULL, 2, NULL, 0, 'Place ham in the slow cooker and add the remaining cola.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09BC4997746AE4F8BBCDC49A9672846', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 6, NULL, NULL, 2, NULL, 0, 'Cover the pot and cook on HIGH for 1 hour. Turn the slow cooker to LOW and continue cooking the ham for about 7 to 9 hours. Use an instant-read food thermometer inserted into the thickest part of the ham, not touching bone or fat, to ensure the ham has reached the safe temperature  140 F for fully cooked, and 145 F if not pre-cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EBA3D552829EB409FA6F911061B78B1', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 7, NULL, NULL, 2, NULL, 0, 'Baste the ham with the juices it has yielded about one hour before it''s done.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6ACD9D397F8C24D9B3A5ADED75732E3', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 8, NULL, NULL, 2, NULL, 0, 'Remove from crockpot and leave to rest for 15 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E1A7B684C35342BDD1E310AD0BB890', X'68FD7EE8A87FB74DA119E6FF137F4DD0', X'68FD7EE8A87FB74DA119E6FF137F4DD0', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D90DE96DD300514C8B2229C22CA8EAFA', X'E5E33411D4BCDB449D6BA47E292AC47F', X'E5E33411D4BCDB449D6BA47E292AC47F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F82755BFD7051347AA716B693BC03C54', X'E5E33411D4BCDB449D6BA47E292AC47F', X'E5E33411D4BCDB449D6BA47E292AC47F', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 F. Lightly oil a 9-by-13-by-2-inch baking dish or line it with foil.');
