INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5DDD8CD681B12439312E6C0867705EF', X'E5E33411D4BCDB449D6BA47E292AC47F', X'E5E33411D4BCDB449D6BA47E292AC47F', 3, NULL, NULL, 2, NULL, 0, 'Snip off any excess skin and fat deposits from the chicken. Arrange the leg quarters in the prepared baking pan and rub the garlic-spice mixture over each piece.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F75FCD6211DF674A8797359E10929A0E', X'E5E33411D4BCDB449D6BA47E292AC47F', X'E5E33411D4BCDB449D6BA47E292AC47F', 4, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of chicken broth into the bottom of the baking dish. If desired, use 1/4 cup of dry white wine with 1/4 cup of chicken broth instead.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0220B6C39AE1D4293A6DC804216681A', X'E5E33411D4BCDB449D6BA47E292AC47F', X'E5E33411D4BCDB449D6BA47E292AC47F', 5, NULL, NULL, 2, NULL, 0, 'Bake the chicken in the preheated oven for 40 to 50 minutes, or until the chicken registers at least 165 F on a meat thermometer.');
