INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F489A95F956C6B49803BDE3FFF5F62E1', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5639320BE6022A4D9DD3DDEEF964B761', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 1, NULL, NULL, 2, NULL, 0, 'Bring the water and beans to a boil in a large saucepan or Dutch oven; boil for 2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B7AFD2E78340C43A52E3FA781AA33A0', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 2, NULL, NULL, 2, NULL, 0, 'Remove the beans from heat, cover, and let stand for 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FCBB81BBDFE14FA55BE4D98B7A02E4', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 3, NULL, NULL, 2, NULL, 0, 'Add the ham and ham bone or ham hock to the beans, along with onion, carrot, celery, bay leaf, and pepper. Bring to a boil.');
