INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72D256809561C419BFDF0CA284F6EBA', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 5, NULL, NULL, 2, NULL, 0, 'Reduce heat, cover, and simmer until beans are tender - about 1 1/2 to 2 hours. If soup is too thick, add a little more water.');
