INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE06173C146AB42A4A5B9AABDAC3D28', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 7, NULL, NULL, 2, NULL, 0, 'Dice or shred the meat and return it to the soup.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65D85702A532374B9EB2BD8460EB8B5A', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 8, NULL, NULL, 2, NULL, 0, 'Add the tomato sauce and salt, to taste, and simmer for about 15 minutes longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D5EDFD6929ACE4A8C7661AA72AE4668', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 9, NULL, NULL, 2, NULL, 0, 'Remove the bay leaf.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81E06B9DFFBE34F802F6E89F84167C3', X'D2464FCA069C334A80C47EC675D49A9D', X'D2464FCA069C334A80C47EC675D49A9D', 10, NULL, NULL, 2, NULL, 0, 'Serve in bowls and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AEB0C6ABF6FDC4DA362987B87A4C7D9', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13DBB681D471A74EBB197161F3DDADA6', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 450 F/230 C. Line two 9x13x2-inch baking pans with heavy-duty foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E206A56579DBEF4FAC704280AC315DB5', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 2, NULL, NULL, 2, NULL, 0, 'In a food storage bag, combine the flour, salt, pepper, paprika, curry powder, and poultry seasoning.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72552EE153EB114E870A54F783F56ED3', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 3, NULL, NULL, 2, NULL, 0, 'Cut the butter into pieces and place half (1/4 cup) in each foil-lined pan. Put the pans in the oven to melt butter. (Keep an eye on it to make sure it doesn''t burn.)');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'881F9D663A25E541825CDEE0F6BD604A', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 4, NULL, NULL, 2, NULL, 0, 'Coat the chicken with the seasoned flour mixture, shaking about three legs at a time in the bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C28FFAE1D7A4F641A1132072A170D178', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 5, NULL, NULL, 2, NULL, 0, 'Place the chicken in the melted butter in the prepared pans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4273FAC6F9B979418BBA100F65629DA7', X'8CC52F41D317E04DA395D57CF1386F1B', X'8CC52F41D317E04DA395D57CF1386F1B', 6, NULL, NULL, 2, NULL, 0, 'Bake the drumsticks for 30 minutes. Turn the chicken over and bake 15 to 20 minutes longer, or until tender and nicely browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E238CDA63FAD04BBC93827003FC2D9B', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 0, NULL, NULL, 2, NULL, 0, 'Line a baking dish with foil. Brush the foil with olive oil. Use a garlic or lemon infused oil if you have it.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'019FE4A793AB9B41A96868AD5B1FE528', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 425 F (220 C/Gas 7).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B5172CEA103D64DAE43D01F803229C5', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 2, NULL, NULL, 2, NULL, 0, 'Cut the fish into serving size pieces. Sprinkle lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BD8E648A703244B8E0097BB9074A47C', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 3, NULL, NULL, 2, NULL, 0, 'Put the flour in a wide bowl, pan, or plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9962F0284222794B8E9BAE29058A333C', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 4, NULL, NULL, 2, NULL, 0, 'Whisk egg and milk together in a wide bowl until the mixture is smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1EFEE03E0E3E74E9D5220F33763EBBD', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 5, NULL, NULL, 2, NULL, 0, 'Spread the breadcrumbs in a wide bowl, pan, or plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F878C0BF567A4C4F885731CB1B0E76C0', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 6, NULL, NULL, 2, NULL, 0, 'Arrange the bowl and plates in this order: flour, milk, and egg, then breadcrumbs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9566EA0B4C63347B334DFF180059D7C', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 7, NULL, NULL, 2, NULL, 0, 'Dip the fish fillets in the flour, coating thoroughly, then dip in the milk and egg mixture. Coat with breadcrumbs. Arrange on the baking pan and repeat with the remaining fillets and breading.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1FCCB68530D3547B15D4E5F56A3F1BC', X'A2093AD79088664BA2B31968C456E150', X'A2093AD79088664BA2B31968C456E150', 8, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 10 minutes. Turn and continue baking for another 8 to 10 minutes, or until cooked through. The fish will flake easily with a fork. Allow a little more time for thicker pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17FB2403D08DA943BBEDAF1A08A12367', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EBE4DCE01D29F478EED8B27336ECD7A', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 1, NULL, NULL, 2, NULL, 0, 'Place the pork shoulder in the slow cooker. Add the water and sprinkle with salt and pepper. If desired, add chopped onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'890030294AADCD43BF41E008F7A87045', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for 1 hour. Turn to LOW and cook 7 to 9 hours longer, until very tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF59E1B5956734E9AACFDF7ECD01E0B', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 3, NULL, NULL, 2, NULL, 0, 'Remove roast and discard the fat and juices. Chop or shred the pork and then return the meat to the slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'259C8E549E2D074898EFA720F8FB6A76', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 4, NULL, NULL, 2, NULL, 0, 'Mix some barbecue sauce into the meat for flavorabout 1/2 to 1 cup of sauce. Cover and cook on LOW for about 30 minutes to 1 hour longer, until hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB524752460A4C48834B454E3426D835', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 5, NULL, NULL, 2, NULL, 0, 'Serve as-is or on warm split sandwich buns with coleslaw and extra barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16581C9C1414554AA90305BA2A6BCDBA', X'EF9505D82C40D04BAFC099F1FB825681', X'EF9505D82C40D04BAFC099F1FB825681', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AC411285F4D5249B7418C2C6728C626', X'137D9CE97FA42D4C85A69FD3B3FB0F01', X'137D9CE97FA42D4C85A69FD3B3FB0F01', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
