INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F18E90E5FF4E3F4893FC48269FFB025B', X'137D9CE97FA42D4C85A69FD3B3FB0F01', X'137D9CE97FA42D4C85A69FD3B3FB0F01', 4, NULL, NULL, 2, NULL, 0, 'Drain off the excess fat and then add the tomatoes, tomato sauce, basil, oregano, garlic powder, salt, and pepper. Bring to a simmer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEB6897F11FCE24A8A743C6E6D3A9BF5', X'137D9CE97FA42D4C85A69FD3B3FB0F01', X'137D9CE97FA42D4C85A69FD3B3FB0F01', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked pasta and half of the mozzarella cheese.');
