INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A57CF2EEE8844BB09378AE1EA6329C', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour over the meat and vegetables and stir to blend. Cook for 2 minutes longer.');
