INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EF1DEFB8727D34291B8E196C973A0CE', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 6, NULL, NULL, 2, NULL, 0, 'Add the oxtail mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB74785F97E8604D96A7438B0B54AF68', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 7, NULL, NULL, 2, NULL, 0, 'Deglaze the pan with the red wine and pour over the oxtails in the slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B652A0A8F4020D4783E8FB2D726A8977', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 8, NULL, NULL, 2, NULL, 0, 'Add the beef broth to the slow cooker along with the tomato sauce, peppercorns, oregano, chile pepper, cloves, bell pepper, and parsley. Add salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623A01BE83803A41B2F84391511514AD', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 9, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 9 to 11 hours, or until the oxtails are very tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A21857691A909D44A2EF0141530D6653', X'0E28878C58B0AD4891A611C7F7E44662', X'0E28878C58B0AD4891A611C7F7E44662', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'995C7CF03CBBB748A88F33666D777036', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 0, NULL, NULL, 2, NULL, 0, 'Put the diced carrots and potatoes in a small saucepan and cover with water. Place the pan over medium heat and bring to a boil. Cover and cook for about 10 minutes, or until the vegetables are tender. Drain and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BCD910C5A8C9419C82CC265E5EBD6F', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 1, NULL, NULL, 2, NULL, 0, 'In a deep skillet or saute pan over medium heat, melt the 5 tablespoons of butter. Add the onions and celery and saute for about 2 minutes. Add the chicken pieces and continue cooking, stirring, until the chicken pieces are opaque.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC8EC3B859AD747A431C7D4AD170E1D', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 2, NULL, NULL, 2, NULL, 0, 'Add the 5 tablespoons of flour to the chicken mixture and cook, stirring, for 2 minutes. Add the chicken stock and thawed frozen peas. Cook, stirring, until thickened. Add the potatoes and carrots, thyme, salt, to taste, pepper, and heavy cream. Cook, stirring, for about 3 minutes longer. Pour the mixture into a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49589F4B8A77064EA8520CC1F5BC2913', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 3, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1EA7B43AE89F24C834C9E367E5F593B', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 4, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the 2 cups of flour, baking powder, salt, and baking soda. Pulse to mix well. Add the pieces of chilled butter and pulse several times, or until the mixture resembles ?coarse meal.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B203421B894ADD4D9B7913BC94A79BF1', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 5, NULL, NULL, 2, NULL, 0, 'Pour the flour mixture into a large bowl and add the cheese, green onions, and cayenne, if using. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A86C64BC04257E49841ED0FE52EF2344', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 6, NULL, NULL, 2, NULL, 0, 'Add the buttermilk to the flour mixture and mix with your hands or a spoon just until the dough begins to hold together.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D3475DD0457914C94E3E8A10ACE508B', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 7, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a lightly floured surface and knead a few times, just to combine. For the softest biscuits, take care not to overwork the dough. Pat the dough into a rectangle and cut out as desired using a knife, pizza cutter, or biscuit cutters.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'092DAF9119B4A041AE25DC074E7321CE', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 8, NULL, NULL, 2, NULL, 0, 'Place the biscuits on the chicken pie filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8CCD78767EACD46B96B4727445EBE51', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 9, NULL, NULL, 2, NULL, 0, 'Combine the egg yolk with 2 teaspoons of water; brush lightly over each biscuit.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172A83A8104E0E4AB301B27BDAD6E747', X'CD387D491984654DB18F8EF40F1627FD', X'CD387D491984654DB18F8EF40F1627FD', 10, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 25 to 30 minutes, or until the biscuits are golden brown and the filling is bubbling.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D781D270ADB2E4FB1B29F05BC4AB0EC', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F with a rack in center.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4C09452A045F949824410D1806564BE', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 1, NULL, NULL, 2, NULL, 0, 'Generously grease the sides and bottom of a 1 1/2-quart casserole dish with butter or cooking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C83C35DEE2F4E4AB9A7B73B3E33ACC5', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a medium skillet over medium-high heat. Add scallion whites and corn. Cook until fragrant and corn is cooked through, 2 to 3 minutes. Remove from heat and set aside to cool for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C354611F2F0F7943A0022DEBE03FDD34', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, add the cornbread mix and salt; whisk until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB8A484B15C0154E93F160BAE1005CE3', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 4, NULL, NULL, 2, NULL, 0, 'Add the eggs, butter, and milk to the bowl with the cornmeal mixture. Using a fork, stir together until the wet and dry ingredients are completely combined, with no visible traces of flour or cornmeal.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E94C92D11B5E149928BD3360EC33A26', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 5, NULL, NULL, 2, NULL, 0, 'Stir in the corn, scallions, and half of the cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94AD5B86527D474085BD1DD748A9A58C', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 6, NULL, NULL, 2, NULL, 0, 'Spoon into the prepared casserole dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A168D34CD35A54095593F02C247F855', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 7, NULL, NULL, 2, NULL, 0, 'Bake until pudding is set and top is golden brown, 35 to 40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E07809A9CFD50949BF44371CBE8E3AA8', X'8AC1472632090B4AB7C742CBFE905550', X'8AC1472632090B4AB7C742CBFE905550', 8, NULL, NULL, 2, NULL, 0, 'Garnish with the green scallions.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E21836702166B47A64A6AEA7EE291B6', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0ACC3F818B60A408E30DF3DB1F3BB49', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 1, NULL, NULL, 2, NULL, 0, 'Lightly brush a rimmed baking sheet with extra-virgin olive oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51282FA9421DD445AB6D304EC348A14E', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 2, NULL, NULL, 2, NULL, 0, 'Trim the stem ends of the Brussels sprouts. Remove loose outer leaves and any leaves. Cut the trimmed sprouts in half lengthwise.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF26DE20CF31474CBB65DEFED65CC922', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 3, NULL, NULL, 2, NULL, 0, 'Core the pear and cut it into 1-inch pieces or wedges. There''s no need to peel the pear.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C364C64C71EC54C8B590AF452490180', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 4, NULL, NULL, 2, NULL, 0, 'Slice the small onion or shallot, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D911A60693D42447A42767BE113B2AD6', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the sliced sprouts, pear and sliced onion or shallot. Drizzle with the 3 tablespoons of olive oil and toss to coat thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3B0425B778A344F9A836F57EFE89870', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 6, NULL, NULL, 2, NULL, 0, 'Spread the sprouts and pear mixture out on the prepared baking sheet and sprinkle lightly with kosher salt and a few grinds of pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F9D8864F7914498E21B0F38E8FDBB0', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 30 to 40 minutes, or until the vegetables and pears are browned, turning about every 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EDC16397E373D4DB007AC0774B440FA', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 8, NULL, NULL, 2, NULL, 0, 'Meanwhile, toast the walnuts and cook the bacon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A841DDC3D49D114D9C128D96069C116C', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 9, NULL, NULL, 2, NULL, 0, 'Place a dry skillet over medium heat. Add the chopped walnuts and cook until lightly browned and aromatic, stirring and turning frequently.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'442D577EE78BD548BCDA7CB1113ADF47', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 10, NULL, NULL, 2, NULL, 0, 'Dice the bacon and cook in a skillet over medium heat until crisp. Remove the bacon to paper towels to drain. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A12802B9930FA54A8802678784F2307C', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 11, NULL, NULL, 2, NULL, 0, 'Remove the Brussels sprouts to a serving bowl. Add the diced bacon and toasted walnuts and toss.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36BA4CE962A9C848BEFCA5FF2894EBFD', X'858B88D09FA5EB44AC9DEED000FFE8F4', X'858B88D09FA5EB44AC9DEED000FFE8F4', 12, NULL, NULL, 2, NULL, 0, 'Drizzle 1 teaspoon of balsamic vinegar over the sprouts and toss. Taste and add more balsamic vinegar, as desired. Serve hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57FF66F2DC8C514FA068B228147F52A9', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5834DD863EAD142A92AAA6ABD84391B', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Butter a 1-quart casserole or baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0256E5073AE2934EB1606B8484B1F3EF', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 2, NULL, NULL, 2, NULL, 0, 'Slice the summer squash and place it in a medium saucepan. Cover the squash with water and add 1 teaspoon of salt. Place the pan over high heat and bring the squash to a boil. Reduce the heat to medium-low and cover the pan; continue to cook until tender, or for about 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABAB82A39F7083459BDD593171D8605C', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, peel the onion and cut it into halves; chop it finely.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'666463EA3301634986DA90C9C2803A83', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 4, NULL, NULL, 2, NULL, 0, 'Drain the squash thoroughly; return it to the saucepan and mash it. Taste the squash and add salt and pepper, as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D279EB39133E2C4D8708FBF274354127', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 5, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the egg and sugar lightly. Add the mayonnaise, chopped onion, 2 tablespoons of the melted butter, and 1/2 cup of the cheddar cheese. Stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69AE484C5019544094DBC537DE759F77', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 6, NULL, NULL, 2, NULL, 0, 'Blend the mashed squash into the egg and mayonnaise mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'034B8991BB0A0C43BA9E0BAE3E12951C', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 7, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the prepared casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB951E611725844C8B6FAB7E4AE52B90', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 8, NULL, NULL, 2, NULL, 0, 'Top the casserole with the remaining 1/4 cup of shredded cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A05D87F0C331264DAF44042DCF89F07D', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 9, NULL, NULL, 2, NULL, 0, 'Toss the breadcrumbs with the remaining 2 tablespoons of melted butter and then sprinkle them over the casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF60D4EA3F22E49934D304351B550DF', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, or until bubbly and lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CA7349015F663418CF39A284E87DE3F', X'198894FC8F5BE64CAE52E0155622E3CB', X'198894FC8F5BE64CAE52E0155622E3CB', 11, NULL, NULL, 2, NULL, 0, 'Serve squash casserole hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7FEC9C4010CCF43A9D6C595DD4CF78F', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'595F4035C0FC914A99A162ED5B565199', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Butter a 9-by-13-by-2-inch baking pan or shallow 3-quart casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7223D24E7AE18B4686F0A6B384046A52', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 2, NULL, NULL, 2, NULL, 0, 'Peel the sweet potatoes and cut them into 3/4-inch cubes. You should have about 10 to 12 cups when finished. Arrange the sweet potato cubes in the prepared baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659A6575FD2DE7408BEADB7663916621', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 3, NULL, NULL, 2, NULL, 0, 'In a 1 1/4-quart saucepan, combine the brown sugar, butter, cinnamon, salt, nutmeg, and ginger and place it over medium heat. Bring the mixture to a boil; stir until the butter is melted and sugar is dissolved, about 7 to 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8F54515AE08B6419738CFB1BD90CF23', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 4, NULL, NULL, 2, NULL, 0, 'Stir the vanilla extract into the sauce mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42F9A90F749DAF419294B609F011742D', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 5, NULL, NULL, 2, NULL, 0, 'Pour the sugar and butter mixture evenly over the sweet potatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD47060E4DD90247AA9714BF4EE50838', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 6, NULL, NULL, 2, NULL, 0, 'Cover the baking pan tightly with foil and bake for 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43247958B89264B915C5006ACAC3FE6', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 7, NULL, NULL, 2, NULL, 0, 'After 45 minutes, uncover the sweet potatoes and gently stir them to coat with the liquid.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D3F9ABBA6E6AA4CA47E355F938855EF', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 8, NULL, NULL, 2, NULL, 0, 'Continue baking, stirring occasionally, for about 30 minutes, or until the potatoes are tender and the liquid is syrupy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A49DBE38708E342857797CA96E3718B', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 9, NULL, NULL, 2, NULL, 0, 'Remove the baking pan to a rack and increase the oven temperature to 450 F. When the oven has reached 450 F, cover the sweet potatoes with the miniature marshmallows.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345F0AC757B51C40A7A518A2A4339314', X'6DFDCD888073114698DB145D200E7DE9', X'6DFDCD888073114698DB145D200E7DE9', 10, NULL, NULL, 2, NULL, 0, 'Return the pan to the oven and bake for about 3 to 5 minutes, or until the marshmallows are partially melted and golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBE8568A586B13408421E6AA66BA2C67', X'AB5A05DF98277D4A97B37F1EB6691D7E', X'AB5A05DF98277D4A97B37F1EB6691D7E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B47C49A2BAE61498528AEDB4B7E26FB', X'AB5A05DF98277D4A97B37F1EB6691D7E', X'AB5A05DF98277D4A97B37F1EB6691D7E', 1, NULL, NULL, 2, NULL, 0, 'Spray a large skillet with cooking spray and heat it over medium heat for 2 to 3 minutes. Sauté the sliced summer squash, stirring constantly, for about 2 minutes. Add the vegetable broth to the skillet and continue cooking and stirring for a couple more minutes, until the squash feels almost tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6CAC26CB43C3443AC24BB70DD191EC7', X'AB5A05DF98277D4A97B37F1EB6691D7E', X'AB5A05DF98277D4A97B37F1EB6691D7E', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and green onions and continue cooking and stirring until the squash turns tender but still feels a bit firm, approximately another 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE4371D778890F4C8CCF7344D0D19415', X'AB5A05DF98277D4A97B37F1EB6691D7E', X'AB5A05DF98277D4A97B37F1EB6691D7E', 3, NULL, NULL, 2, NULL, 0, 'Use a slotted spoon to scoop the squash from any remaining liquid into a serving bowl. Scatter the basil and add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE63D790A2AF9E43BBC4776ED4CC5199', X'AB5A05DF98277D4A97B37F1EB6691D7E', X'AB5A05DF98277D4A97B37F1EB6691D7E', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D3B7D4C95DDC04CB8EFC7CB4FD358FD', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C416A20FE2664CBD7EC224517A4E54', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 1, NULL, NULL, 2, NULL, 0, 'Heat about 2 to 3 inches of vegetable oil in a heavy saucepan to 360 F. Or use an electric deep fryer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B9DE4A5D884F8499D61DC67F326AF94', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 2, NULL, NULL, 2, NULL, 0, 'Combine eggs, milk, and salt in a bowl and whisk to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'254B86DE87FFB54BB5A2126171596A92', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 3, NULL, NULL, 2, NULL, 0, 'Put the flour in a wide, shallow bowl or plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9CB3F7E55C171478BBD947973094586', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 4, NULL, NULL, 2, NULL, 0, 'Dip squash in the egg batter and then dredge the slices in flour until well coated.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'221664E50C65EF47A292CA2353F7FEEB', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 5, NULL, NULL, 2, NULL, 0, 'Fry in batches in deep hot fat, about 360 F, until golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250B1A06017EFA43AF3619B619820544', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 6, NULL, NULL, 2, NULL, 0, 'Drain on paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B64358352C80442BEAFE47A68A6F741', X'5D5D8800A78DD2449015DEFB933F82AF', X'5D5D8800A78DD2449015DEFB933F82AF', 7, NULL, NULL, 2, NULL, 0, 'Season the fried squash slices with salt and pepper or a Creole or seasoned salt blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1732A75BA8466C48AB962516357B1870', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'032B61FA723739459B53579B2CF19ACD', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in an electric pressure cooker on the sauté setting. Add the onion and bacon and cook, stirring occasionally, until onions are softened and just beginning to brown and bacon has rendered some of its fat about 5 to 6 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2454FFD951F1C541A7AF1086D14F85CE', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 2, NULL, NULL, 2, NULL, 0, 'Add the collard greens to the pot and season with salt and pepper. Cook, tossing frequently, until the collards are shiny and wilted, 3 to 4 minutes. If all of the greens dont fit into the pot at the same time, cook and wilt them in batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'136F39CE111BBF459BD57059D8765CEA', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 3, NULL, NULL, 2, NULL, 0, 'Stir in the apple cider vinegar, brown sugar, and hot sauce. Lock lid and turn to the manual setting. Cook on high pressure for 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18FE86558F925C42A4AB72981E4056EF', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 4, NULL, NULL, 2, NULL, 0, 'Release the pressure manually, then open lid and taste collard greens. Adjust the seasoning as needed. If theres a lot of excess liquid in the pot, return to the sauté setting and simmer until the liquid is reduced enough to just lightly coat the greens.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7CD42CFAEB1F647A53C97D9ACFC3DBA', X'EB1474B9EE262B41BF87C4B7D8CB9952', X'EB1474B9EE262B41BF87C4B7D8CB9952', 5, NULL, NULL, 2, NULL, 0, 'Transfer the cooked collard greens to a platter. Season with additional hot sauce as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D67FF044FE20541BAD4B4B2697ADD94', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDDCB91FF7BD704DA5533309AE7C2540', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 1, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and slice them into 1/8-inch to 1/4-inch rounds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF75783124CCA49817A37BB081AA68C', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 2, NULL, NULL, 2, NULL, 0, 'Butter a 7-by-2-inch baking pan (or one that will fit in the Instant Pot with the trivet).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3A06872CC8CEB4FADCF8F406D298F6A', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 3, NULL, NULL, 2, NULL, 0, 'Arrange a layer of potatoes in the bottom of the pan. Follow the potato layer with about 1 tablespoon of finely chopped onion, a few tablespoons of cheddar cheese, and a sprinkling of the salt, pepper, and nutmeg.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287B45530F55BA4CBD71B9632FF4C253', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 4, NULL, NULL, 2, NULL, 0, 'Repeat the layers two times and then finish with another layer of potatoes, a sprinkling of salt and pepper, and a few tablespoons of cheddar cheese. Don''t fill the pan completely; there should be at least 1/2-inch of headspace.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F38B4B012E030489CB6EB47D0F97B8D', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 5, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the cream and milk with the flour. Pour the cream and milk over the potatoes. The mixture should come almostbut not quiteto the top of the potatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67B737FC28F164DBB9AB46C6E206B3A', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 6, NULL, NULL, 2, NULL, 0, 'Add 1 1/2 cups of water to the Instant Pot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76518AC185BD2140A9148CA9082B1B7B', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 7, NULL, NULL, 2, NULL, 0, 'Place the uncovered baking pan on the trivet. Holding the trivet handles, lower it into the Instant Pot.');
