INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1017541DAF69ED45A457E679791FC7B8', X'22B1B201E986AD439D3F6927B1BBB5CA', X'22B1B201E986AD439D3F6927B1BBB5CA', 9, NULL, NULL, 2, NULL, 0, 'Using the manual setting, choose high pressure for 30 minutes.');
